INSERT INTO "tbnews" VALUES ('231', 'cl000001', 'u0000003', 'ថាកុំឲ្យគេឮ ទី​សត្តឃាត​កណ្តាល​ក្រុង​សៀមរាបពុំ​មាន...', 'ទី​សត្តឃាត​កណ្តាល​ក្រុង​សៀមរាប ត្រូវ​បាន​មហាជន​រិះគន់​ថា មានការ​ឃុបឃិត និង​មាន​ភាព​ធូរ​លុង ឱ្យ​ក្រុម​ឈ្មួយ​ដឹក​សត្វ​ដែល​មាន​ជំងឺ ចូល​ទី​សត្តឃាត ដើម្បី​កាប់​យក​សាច់​ទៅ​លក់​នៅ​តាម​ទីផ្សារ​នានា', 'http://kohsantepheapdaily.com.kh/article/113620.html', 'http://i.kspg.co/2015/1/41745b35-6ee8-4715-9ee0-1484445cca38.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('232', 'cl000001', 'u0000003', 'វា​ចុះថ្លៃ​តែ​ប្រេង​...', 'ពេល​ប្រេងសាំង​ឡើងថ្លៃ ទំនិញ​សព្វសារពើ​ឡើងថ្លៃ​តាម​ដែរ ជា​ពិសេស​រថយន្តឈ្នួល ។ សូ​ម្បី​តែ​សេវា​កោស​ជប់​ក៏​ឡើងថ្លៃ​ដែរ ព្រោះ​គេ​ថា​ចំណាយ​លើ​សោ​ហ៊ុយ​ធ្វើ​ដំណើរ​ថ្លៃ ', 'http://kohsantepheapdaily.com.kh/article/113619.html', 'http://i.kspg.co/2015/1/55f7c8d7-3a73-4898-a35b-b448d2cbc3b1.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('233', 'cl000001', 'u0000003', 'ថាកុំឲ្យគេឮ «នៅ​តែ​យក​លុយ​ភាស៊ី​បេន​លើស​សៀវភៅ​បន្ទុក​១​គុណ​និង​៦​ដង»', 'ជិត​១​ខែ​មក​ហើយ​ដែល​ក្រុម​អ្នក​រត់​តាក់ស៊ី​នៅ​ចំណត​ផ្សារ​ដើម​គ​ច្រើន​នាក់ បាន​រអ៊ូរទាំ ដោយសារ​តែ​លោក សេង សា​រ៉​ន ប្រធាន​ផ្សារ នៅ​តែ​យក​លុយ​ភាស៊ី​បេន​លើស​សៀវភៅ​បន្ទុក​១​គុណ​និង​៦​ដង ', 'http://kohsantepheapdaily.com.kh/article/113463.html', 'http://i.kspg.co/2015/1/267a59a7-6c98-4409-9b17-81b8de23d9f8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('234', 'cl000001', 'u0000003', 'ក្រុម​បូ​កូ​ហា​រ៉ា​ម​វាយប្រហារ​គ្រឿងផ្ទុះ​3 នៅ​ទីក្រុង​ធំ​មួយ​របស់​នី​ហ្សេ​រី​យ៉ា​ស្លាប់​58​នាក់', 'ទីក្រុង​ធំ​មួយ គឺ​ទីក្រុង​ម៉ា​អីុឌូ​ហ្គូ​រី ជា​កន្លែង​កកើត​នៃ​ចលនា​បូ​កូ​ហា​រ៉ា​ម​នៅ​ភាគ​ឦសាន​នី​ហ្សេ​រី​យ៉ា ត្រូវ​ញ័រ​រញ្ជួយ​កាលពី​ថ្ងៃ​ទី​៧ មីនា ដោយសារ​ការ​ផ្ទុះ​យ៉ាង​ខ្លាំង​៣ ជា​ស្នាដៃ​របស់​ពួក​អ៊ិ​ស្លា​ម', 'http://kohsantepheapdaily.com.kh/article/116174.html', 'http://i.kspg.co/2015/3/d37f626c-c6ed-4cb3-b61d-7ff2a4e8548b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('235', 'cl000001', 'u0000003', 'ជា​លើក​ដំបូង​ពួក​លោក​ខាងលិច​ត្រូវ​ស្លាប់​ដោយសារ​ការ​វាយប្រហារ​ភេរវកម្ម​មួយ​នៅ​ប្រទេស​ម៉ា​លី', 'ជនជាតិ​បារាំង​ម្នាក់ បែ​ល​ហ្ស៊ិក​ម្នាក់ និង​ម៉ា​លី​៣​នាក់ បាន​ត្រូវ​ស្លាប់​កាលពី​យប់​ថ្ងៃ​ទី​៦ មក​ទី​៧ មីនា ដោយសារ​ការ​វាយប្រហារ​ក្នុង​ភោជនីយដ្ឋាន​មួយ​នាទី​ក្រុង​បា​ម៉ា​កូ រដ្ឋធានី​ម៉ា​លី ។ ', 'http://kohsantepheapdaily.com.kh/article/116172.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('236', 'cl000001', 'u0000003', '1​ឆ្នាំ​ក្រោយ​បាត់​យន្តហោះ MH 370 អាជ្ញាធរ​នៅ​តែ​សង្ឃឹមថា​អាច​រក​ឃើញ​យន្តហោះ​នេះ', 'មួយ​ឆ្នាំ​ហើយ​ក្រោយ​ការ​បាត់ដាន​យ៉ាង​អាថ៌​កំបាំង​នូវ​យន្តហោះ​ក្រុមហ៊ុន​ម៉ា​ឡេ​ស៊ី MALAYSIA AIRLINES ជើងហោះហើរ MH 370 ដោយ​អាជ្ញាធរ​ម៉ា​ឡេ​ស៊ី​នៅ​មិន​ទាន់​បោះបង់​ចោល​ឡើយ', 'http://kohsantepheapdaily.com.kh/article/116166.html', 'http://i.kspg.co/2015/3/d03b3352-3a1c-4fc2-be36-71addf75143c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('237', 'cl000001', 'u0000003', 'រុ​ស្ស៊ី​ដោះ​លែង​ជនជាតិ​ចិន​ទាំងអស់​ដែល​ជាប់​ឃុំឃាំង​ពី​បទ​រត់ពន្ធ​គ្រឿងញៀន', 'ស្ថានទូត​ចិន​ប្រចាំ​ប្រទេស​រុ​ស្ស៊ី អះអាង​កាលពី​ថ្ងៃ​ទី​៧ មីនា ថា ពលករ​ចិន​ចុង​ក្រោយ​គេ​ដែល​ជាប់​ឃុំឃាំង​ត្រូវ​បាន​គយ​រុ​ស្ស៊ី ដោះ​លែង​ក្រោយ​ការ​ត្រួតពិនិត្យ​ខាង​សន្តិសុខ ', 'http://kohsantepheapdaily.com.kh/article/116163.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('238', 'cl000001', 'u0000003', 'ពលរដ្ឋ​អីុស្រា​អែ​ល​ច្រើន​សិប​ពាន់​នាក់​ធ្វើ​បាតុកម្ម​ប្រឆាំង​លោក​នេ​តាន់​យ៉ាហ៊ូ', 'ពលរដ្ឋ​អីុ​ស្រា​អែ​ល​ជា​ច្រើន​សិប​ពាន់​នាក់ កាលពី​ថ្ងៃ​ទី​៧ មីនា បាន​ប្រមូលផ្តុំ​គ្នា​ធ្វើ​បាតុកម្ម​ប្រឆាំង​នាយក​រដ្ឋមន្ត្រី​ចប់​អាណត្តិ លោក​បេន​ចា​មីន នេ​តាន់ យ៉ាហ៊ូ នៅ​ទីក្រុង​តែ​ល​អា​វីវ ', 'http://kohsantepheapdaily.com.kh/article/116159.html', 'http://i.kspg.co/2015/3/8a7fe964-d7c0-46d8-aa96-d46c8cc23500.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('239', 'cl000001', 'u0000003', 'ភេរវ​ករ​ម្នាក់​លាតត្រដាង​គម្រោង​សម្លាប់​លោក អូ​បា​ម៉ា និង​វាយប្រហារ​វិមាន​កា​ពី​តូ​ល', 'ទូរទស្សន៍​អា​មេ​រិ​កមួយ រាយការណ៍​កាលពី​ថ្ងៃ​ទី​៦ មីនា កន្លង​មកនេះ​ថា បុរស​អា​មេ​រិ​ក​ម្នាក់​ដែល​ចាប់ខ្លួន​ពី​បទ​ភេរវកម្ម លាតត្រដាង​ថា រូបគេ​មាន​ផែនការ​បាញ់​សម្លាប់​លោក​ប្រធានាធិបតី បា​រ៉ា​ក់ អូ​បា​ម៉ា ', 'http://kohsantepheapdaily.com.kh/article/116157.html', 'http://i.kspg.co/2015/3/6008db3b-43bf-4f78-86c2-fe1b13c45ccf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('240', 'cl000001', 'u0000003', 'បិទទ្វារ​សេត​វិមាន​ក្រោយ​ឆេះ​រទេះ​អ្នក​លក់ដូរ​មួយ', 'សារព័ត៌មាន​ក្នុងស្រុក​រាយការណ៍​ថា សេត​វិមាន​ត្រូវ​បិទទ្វារ​កាលពី​ព្រឹក​ថ្ងៃ​ទី​៧ មីនា ក្រោយ​ពី​រទេះ​របស់​អ្នក​លក់ដូរ​ម្នាក់​នៅ​ក្បែរ​នោះ​បាន​ឆេះ ខណៈ​ប្រធានាធិបតី​អា​មេ​រិ​ក​លោក​បា​រ៉ា​ក់ អូ​បា​ម៉ា ', 'http://kohsantepheapdaily.com.kh/article/116152.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('241', 'cl000001', 'u0000003', 'យន្តហោះ​ចម្បាំង​ឥណ្ឌា​1​គ្រឿង​ធ្លាក់​នៅ​ភាគ​ខាងជើង​ប្រទេស', 'អ្នក​ទទួលខុសត្រូវ​ជាន់ខ្ពស់​ម្នាក់​នៃ​ក្រសួង​ការពារ​ប្រទេស​ឥណ្ឌា​អះអាង​ថា យន្តហោះ​ប្រយុទ្ធ​១​គ្រឿង​របស់​កងទ័ពអាកាស​បាន​ធ្លាក់​កាលពី​ថ្ងៃ​ទី​៥ មីនា ក្នុង​រដ្ឋ​ហារី​យ៉ា​ណា ភាគ​ខាងជើង​ប្រទេស ។', 'http://kohsantepheapdaily.com.kh/article/116097.html', 'http://i.kspg.co/2015/3/1f0dcedf-c93e-4cc4-84ee-1d1b3012f98f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('242', 'cl000001', 'u0000003', 'ក្រុម​រដ្ឋ​អ៊ិ​ស្លា​ម​ប្រើ​ប៊ុល​ដូ​ហ្ស័​រ​កម្ទេច​ក្រុង​បុរាណ​មួយ​នៅ​អីុរ៉ា​ក់', 'ក្រុម​រដ្ឋ​អ៊ិ​ស្លា​ម កាលពី​ថ្ងៃ​ទី​៦ មីនា ចាប់ផ្តើម​កម្ទេច​ដោយ​ប្រើ​ប៊ុល​ដូ​ហ្ស័​រ​បំផ្លាញ​ទីតាំង​បុរាណវត្ថុវិទ្យា​នៅ​ទីក្រុង NIMROUD ភាគ​ខាងជើង​អីុ​រ៉ា​ក់ ១​ស​ប្តា​ហ៍​ក្រោយ​ពួក​ជី​ហា​ដ​ចាក់​ផ្សាយ​វី​ដេ​អូ​មួយ', 'http://kohsantepheapdaily.com.kh/article/116096.html', 'http://i.kspg.co/2015/3/b0ed163c-06e4-4d6b-9e83-0291d85222b2.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('243', 'cl000001', 'u0000003', 'ថវិកា​រ​ការពារ​ចិន​បង្កើន​ត្រឹម​10,1%​ក្នុង​ឆ្នាំ​2015', 'ប្រទេស​ចិន​ប្រកាស​កាលពី​ថ្ងៃ​ទី​៥ មីនា ពី​ការ​បង្កើន​ត្រឹម​១០,១%​នៃ​ថវិកា​ការពារ​ជាតិ​ក្នុង​ឆ្នាំ​២០១៥ ដែល​នេះ​ជា​ការ​បង្កើន​ខ្សោយ​បំផុត​មួយ​ពុំ​ធ្លាប់​មាន​រយៈពេល​៥​ឆ្នាំ​មក​ហើយ ខណៈ​ដែល​ចិន​ប្រឈម', 'http://kohsantepheapdaily.com.kh/article/116095.html', 'http://i.kspg.co/2015/3/3831c8eb-d0dd-487a-8624-ff9266d1ec14.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('244', 'cl000001', 'u0000003', 'ក្រុង​អា​ឡែ​ប ស៊ី​រី ធ្លាក់​ក្នុង​ការ​ប្រយុទ្ធ​គ្នា​យ៉ាង​សាហាវ​បំផុត', 'ការ​ប្រយុទ្ធ​គ្នា​យ៉ាង​សាហាវ​កើត​មាន​កាលពី​ថ្ងៃ​ទី​៥ មីនា នៅ​ទីក្រុង​អា​ឡែ​ប ចំណែក​ប្រមុខ​ពួក​ប្រឆាំង​សុ​ី​រី​រស់នៅ​និរទេស ប្រកាស​ជា​លើក​ដំបូង​ថា លោក​នឹង​លែង​ទាមទារ​ឱ្យ​លោក​ប្រធានាធិបតី បា​ចា', 'http://kohsantepheapdaily.com.kh/article/116094.html', 'http://i.kspg.co/2015/3/f1565e52-0dd2-497b-ac1a-abaaad1682b8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('245', 'cl000001', 'u0000003', 'តារាភាពយន្ត​អា​មេ​រិ​ក​តួអង្គ​រឿង​សង្គ្រាម​ផ្កាយ​របួស​ដោយ​ធ្លាក់​យន្តហោះ​បើក​ខ្លួនឯង', 'តារាភាពយន្ត​អា​មេ​រិ​ក លោក HARRISON FORD រង​របួស​កាលពី​ថ្ងៃ​ទី​៥ មីនា នៅ​ជិត​ទីក្រុង​ឡូ​ស​អេ​ន​ចឺ​ឡេ​ស ដោយសារ​ការ​ធ្លាក់​យន្តហោះ​ធុន​តូច​មួយ​គ្រឿង​ដែល​លោក​បើក​ខ្លួនឯង', 'http://kohsantepheapdaily.com.kh/article/116075.html', 'http://i.kspg.co/2015/3/e0f43370-840c-496b-988d-2384e1f38b54.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('246', 'cl000001', 'u0000003', 'ជនសង្ស័យ​ចំនួន​៣​នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​​ឃាត់ខ្លួន', 'ខេត្តរតនគិរី ៖ ជនសង្ស័យ​ពាក់ព័ន្ធ​គ្រឿងញៀន​ចំនួន​៣​នាក់​ត្រូវ​បាន​កម្លាំង​នគរបាល​ស្រុក​វ៉ើ​ន​សៃ​ធ្វើការ​ឃាត់ខ្លួន​បាន​កាលពី​ថ្ងៃ​ទី​០៤ ខែ​មីនា ឆ្នាំ​២០១៥ វេលា​ម៉ោង​៤​ និង​៤៥​នាទី​ល្ងាច នៅ​ត្រង់​ចំណុច​ទំនប់​ស្រែ ភូមិ​ហ្វាំ​ង ឃុំ​ប៉ុង ស្រុក​វ៉ើ​ន​សៃ ខេត្តរតនគិរី​ ។', 'http://kohsantepheapdaily.com.kh/article/116063.html', 'http://i.kspg.co/2015/3/499ca957-7284-44b2-aadb-646d100d314f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('247', 'cl000001', 'u0000003', 'ស្ត្រី​អាយុ​វែង​ជាងគេ​លើ​ពិភពលោក​ជនជាតិ​ជប៉ុន ធ្វើ​ខួបកំណើត​គម្រប់​117​ឆ្នាំ', 'ស្ត្រី​អាយុ​វែង​ជាងគេ​លើ​ពិភពលោក ជនជាតិ​ជប៉ុន លោក​យា មី​សាវ អូ​កា​វ៉ា កាលពី​ថ្ងៃ​ទី​៥ មីនា នៅ​អូ​សា​កា បាន​ប្រារព្ធ​ខួបកំណើត​គម្រប់​១១៧​ឆ្នាំ​របស់​លោកយាយ នៅ​លើ​កោះ​មួយ ដែល​ល្បី​ថា​ប្រជាជន​មាន​អាយុ​វែង ។', 'http://kohsantepheapdaily.com.kh/article/116015.html', 'http://i.kspg.co/2015/3/4e0c5e4a-e65e-445c-ab39-1c5afd1beb4e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('248', 'cl000001', 'u0000003', 'ពួក​ឧទ្ទាម​វាយប្រហារ​លើ​ទីបញ្ជាការដ្ឋាន​កងទ័ព​ស៊ី​រី​មួយ​នៅ​ក្រុង​អា​ឡែ​ប​ស្លាប់​34​នាក់', 'ពួក​ឧទ្ទាម​ស៊ី​រី​ក្នុង​នោះ​មាន​សមាជិក​អាល់​កៃ​ដា​ផង​ដែរ កាលពី​ថ្ងៃ​ទី​៤ មីនា ធ្វើការ​វាយប្រហារ​យ៉ាង​សាហាវ​មួយ​ប្រឆាំង​ទីបញ្ជាការដ្ឋាន​របស់​ភ្នាក់ងារ​ស៊ើបការណ៍​កងទ័ពអាកាស​នៅ​ក្រុង​អា​ឡែ​ប', 'http://kohsantepheapdaily.com.kh/article/116009.html', 'http://i.kspg.co/2015/3/e612e68e-6a08-4f08-9719-ff21b712535e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('249', 'cl000001', 'u0000003', 'អ៊ុយ​ក្រែ​ន​ចាប់ផ្តើម​ដំណាក់កាល​ទី​2​នៃ​ការ​ដក​អាវុធ​ធុន​ធ្ងន់', 'ផ្នែក​សារព័ត៌មាន​របស់​កងកម្លាំង​ប្រតិបត្តិការ​យោធា​អ៊ុយ​ក្រែ​ន គូសបញ្ជាក់​ថា កងទ័ព​អ៊ុយ​ក្រែ​ន​ចាប់ផ្តើម​កាលពី​ថ្ងៃ​ទី​៤ មីនា នូវ​ដំណាក់កាល​ទី​២ នៃ​ការ​ដក​អាវុធ​ធុន​ធ្ងន់​របស់​ខ្លួន​ចេញពី​', 'http://kohsantepheapdaily.com.kh/article/116005.html', 'http://i.kspg.co/2015/3/e4ca6f68-8905-43a0-9dbc-178f5c7800ff.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('250', 'cl000001', 'u0000003', 'លោកពូ​ទី​ន ថា​ឧក្រិដ្ឋកម្ម​នយោបាយ​ត្រូវ​បញ្ឈប់​នៅ​រុ​ស្ស៊ី', 'ប្រធានាធិបតី​រុ​ស្ស៊ី លោក​វ្ល៉ា​ឌី​មៀរ ពូ​ទី​ន ថ្លែង​កាលពី​ថ្ងៃ​ទី​៤ មីនា ថា បណ្តា​ឧក្រិដ្ឋកម្ម​នយោបាយ​ដូច​ជា​ការ​សម្លាប់​មេដឹកនាំ​ប្រឆាំង​លោក​បូ​រី​ស ណេ​ម​សូវ ជាដើម ត្រូវ​តែ​បញ្ឈប់​។', 'http://kohsantepheapdaily.com.kh/article/116001.html', 'http://i.kspg.co/2015/3/c7520855-5cb8-4578-8da7-0356ee16e009.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('251', 'cl000001', 'u0000003', 'ម៉ិច​ស៊ិក​ចាប់ខ្លួន​ស្តេច​រត់ពន្ធ​គ្រឿងញៀន​ក្រុម Los Zetas', 'អាជ្ញាធរ​ម៉ិច​ស៊ិក​ កាលពី​ថ្ងៃ​ទី​៤ មីនា បាន​ចាប់ខ្លួន​ជន​ឈ្មោះ Omar Trevino Morales ប្រមុខ​ក្រុម​រត់ពន្ធ​គ្រឿងញៀន​ភាគ​ខាងជើង​ប្រទេស គឺ​ក្រុម Los Zetas ។', 'http://kohsantepheapdaily.com.kh/article/115994.html', 'http://i.kspg.co/2015/3/9b969ea0-b17f-465e-b071-038f1808a3b3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('252', 'cl000001', 'u0000003', 'ផ្ទុះ​ក្នុង​អណ្តូងរ៉ែ​មួយ​នៅ​ភាគ​ខាងកើត​អ៊ុយ​ក្រែ​ន​ស្លាប់​33​នាក់', 'អាជ្ញាធរ​មូលដ្ឋាន​ដកស្រង់​សម្តី​ដោយ​ទីភ្នាក់ងារ​ព័ត៌មាន​រុ​ស្ស៊ី INTERFAX រាយការណ៍​ថា កម្មករ​រ៉ែ​៣៣​នាក់​បាន​ត្រូវ​ស្លាប់​កាលពី​ថ្ងៃ​ទី​៤ មីនា ដោយសារ​ការ​ផ្ទុះ​ក្នុង​អណ្តូងរ៉ែ​ធ្យូងថ្ម​មួយ​នៅ​តំបន់​', 'http://kohsantepheapdaily.com.kh/article/115993.html', 'http://i.kspg.co/2015/3/50f02550-29db-4ea0-ae4b-1af81b8c5317.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('253', 'cl000001', 'u0000003', 'ឯកអគ្គរដ្ឋទូត​អា​មេ​រិ​ក​ប្រចាំ​ក្រុង​សេ​អ៊ូ​ល​ត្រូវ​គេ​វាយប្រហារ​ដោយ​ផ្លែ​ឡា​ម', 'តាម​ទីភ្នាក់ងារ​ព័ត៌មាន​កូរ៉េ​ខាងត្បូង YONHAP ថា ឯកអគ្គរដ្ឋទូត​សហរដ្ឋអាមេរិក​ប្រចាំ​ក្រុង​សេ​អ៊ូ​ល លោក MARK LIPPERT ត្រូវ​របួស​កាលពី​ថ្ងៃ​ទី​៥ មីនា ដោយសារ​ការ​វាយប្រហារ​របស់​ជន​ម្នាក់', 'http://kohsantepheapdaily.com.kh/article/115976.html', 'http://i.kspg.co/2015/3/86c947b2-cff0-4576-aad6-ede601b97ebf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('254', 'cl000001', 'u0000003', 'រក​ឃើញ​ខ្មោច​នាវាចម្បាំង​ជ​ប៉ុ​ន​ខ្លាំង​ជាងគេ​វាយប្រហារ​លិច​កាល​សង្គ្រាមលោក​លើក​ទី​2', 'លោក PAUL ALLEN សហ​ស្ថាបនិក MICROSOFT អះអាង​ពី​ការ​រក​ឃើញ​ខ្មោច​នាវាចម្បាំង​ខ្លាំង​ជាងគេ​បំផុត​ជា​ប្រវត្តិសាស្ត្រ ជា​នាវាចម្បាំង​ជ​បុ​៉​ន​មួយ​ដែល​លិច​កាលពី​ឆ្នាំ​១៩៤៤ ', 'http://kohsantepheapdaily.com.kh/article/115954.html', 'http://i.kspg.co/2015/3/f92da954-4b4f-43fd-9462-a32ae47cfc70.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('255', 'cl000001', 'u0000003', 'កម្មករ​រ៉ែ​៣២នាក់ស្លាប់​ដោយ​ការផ្ទុះ​ក្នុង​អណ្តូងរ៉ែ​អ៊ុយក្រែន​មួយ', 'ប្រធានសភា​​អ៊ុយក្រែន​​ លោកវ៉ូឡូឌីមៀរ ហ្គោអ៊ីស្មាន់ ថ្លែង កាលពី​ថ្ងៃទី០៤ មីនា ថា កម្មករ​រ៉ែយ៉ាងតិច​៣២នាក់ស្លាប់​ដោយ​សារ​ការ​​ផ្ទុះ​ក្នុង​អណ្តូង​រ៉ែ​មួយ​នាភាគ​ខាងកើត​អ៊ុយក្រែន ។', 'http://kohsantepheapdaily.com.kh/article/115953.html', 'http://i.kspg.co/2015/3/d36d51ba-d167-41ae-a9ae-dd8e56ccbcaf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('256', 'cl000001', 'u0000003', 'មេដឹកនាំ​លោក​ខាងលិច​គំរាម​បញ្ចេញ​ប្រតិកម្ម​ខ្លាំង​ប្រឆាំង​រុ​ស្ស៊ី​បើ​បទ​ឈប់​បាញ់​គ្នា​ត្រូវ​បំផ្លាញ', 'លោក បា​រ៉ា​ក់ អូ​បា​ម៉ា​, លោក ហ្វ្រ​ង់​ស្វ័​រ ហូ​ឡង់ និង​លោកស្រី អេ​ន​ហ្សឺ​ឡា ម័​រ​កែ​ល កាលពី​ថ្ងៃ​ទី​៣ មីនា បាន​សន្យា​បញ្ចេញ​ប្រតិកម្ម​ខ្លាំង​របស់​លោក​ខាងលិច​ក្នុង​ករណី​បទ​ឈប់​បាញ់​គ្នា​ត្រូវ​បំផ្លាញ ', 'http://kohsantepheapdaily.com.kh/article/115952.html', 'http://i.kspg.co/2015/3/bb23ec15-280b-40ab-b462-7b45bc0f11f1.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('257', 'cl000001', 'u0000003', 'យន្តហោះតួកគី​១គ្រឿង​ធ្លាក់​ផ្លូវ​ពេល​ចុះនៅនេប៉ាល់', 'នៅព្រលានយន្ត​ហោះ​ក្រុង​កាតម៉ាន់ឌូ​ ប្រទេស​នេប៉ាល់​ យន្ត​ហោះ​មួយគ្រឿង​របស់​ក្រុមហ៊ុន​អាកាសចរណ៍​តួកគី​ TURKISH AIRLINES បានរត់​ធ្លាក់ផ្លូវ កាលពី​ថ្ងៃទី០៤ មីនា ក្រោយពេលចុះបន្តិច ។', 'http://kohsantepheapdaily.com.kh/article/115951.html', 'http://i.kspg.co/2015/3/e410375f-48b3-4179-88aa-de2b77df9a83.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('258', 'cl000001', 'u0000003', 'នាយក​រដ្ឋមន្ត្រី​អីុ​ស្រា​អែ​ល​អំពាវនាវ​ឱ្យ​បដិសេធ​កិច្ចព្រមព្រៀង​នុយក្លេអ៊ែរ​អាក្រក់​មួយ​រវាង​អា​មេ​រិ​ក និង​អីុរ៉​ង់', 'នាយក​រដ្ឋមន្ត្រី​អីុ​ស្រា​អែ​ល លោក​បេន​ចា​មីន នេ​តាន់​យ៉ា​ហ៊ូ កាលពី​ថ្ងៃ​ទី​៣ មីនា បាន​អំពាវនាវ​ឱ្យ​បដិសេធ​កិច្ចព្រមព្រៀង​អាក្រក់​មួយ​ស្តី​ពី​នុយក្លេអ៊ែរ​ជាមួយ​អីុរ៉​ង់ ដោយ​គូសបញ្ជាក់​ថា', 'http://kohsantepheapdaily.com.kh/article/115950.html', 'http://i.kspg.co/2015/3/3d8e1bfa-fa9f-498f-9d5d-de30bab6bf96.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('259', 'cl000001', 'u0000003', 'វៀតណាម​នឹង​ដំ​ទ្បើ​ង​ថ្លៃ​អគ្គិសនី​7,5%​ពាក់កណ្តាល​ខែមីនា', 'ហូ​ជី​មិ​ញ ៖ តម្លៃ​អគ្គិសនី​នៅ​ប្រទេស​វៀតណាម​នឹង​ត្រូវ​បាន​ដំ​ទ្បើ​ង​៧,៥%​ស្មើនឹង​តម្លៃ​ជា​មធ្យម​១.៦២២​ដុ​ង (០,០៧៦​ដុល្លារ​) ក្នុង​មួយ​គី​ទ្បូ​វ៉ា​ត់ ចាប់ពី​ថ្ងៃ​ទី​១៦ មីនា តទៅ ក្រោយ​ពី​ទទួល​បាន​អនុម័ត', 'http://kohsantepheapdaily.com.kh/article/116161.html', 'http://i.kspg.co/2015/3/f4fab268-f21f-4bce-8bea-a4d76ddfb51b.jpg?w=285&h=170&mode=carve', '20150308');
