INSERT INTO "tbnews" VALUES ('261', 'cl000001', 'u0000003', 'អឺរ៉ុប​នាំ​ចូល​អង្ករ​ជាង​44​ម៉ឺន​តោន​ពី​ប្រទេស​មិនមែន​ជា​សមាជិក​ខ្លួន', 'តាម​ទិន្នន័យ​បង្ហាញ​ថា សហគមន៍​អឺរ៉ុប​បាន​នាំ​ចូល​អង្ករ​ចំនួន​៤៤២.៤៣៣​តោន ពី​បណ្តា​ប្រជាជាតិ​ពុំ​មែន​ជា​សមាជិក​របស់​សហគមន៍​អឺរ៉ុប​ក្នុង​អំ​ទ្បុ​ង​ថ្ងៃ​ទី​១ កញ្ញា ឆ្នាំ​២០១៤ ដល់​១៧ កុម្ភៈ ២០១៥ កើន​ទ្បើ​ង​២% ', 'http://kohsantepheapdaily.com.kh/article/115997.html', 'http://i.kspg.co/2015/3/e5b15a70-2068-497a-8370-d284578aa43e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('262', 'cl000001', 'u0000003', 'វៀតណាម​ទម្លាក់​តម្លៃ​អង្ករ​នាំ​ចេញ​ចាប់ពី​ខែមីនា​នេះ', 'តាម​សេចក្តី​ថ្លែង​ការ សមាគម​ស្បៀងអាហារ​វៀតណាម​បាន​បញ្ចុះតម្លៃ​អង្ករ​នាំ​ចេញ​ក្នុង​កម្រិត​អប្បបរមា​សម្រាប់​អង្ករ​គុណភាព​អន់​អង្ករ​បាក់​២៥% ក្នុង​តម្លៃ​រង្វង់​៣៥០​ដុល្លារ​ក្នុង​មួយ​តោន​ធ្លាក់​ថ្លៃ​៣% ', 'http://kohsantepheapdaily.com.kh/article/115995.html', 'http://i.kspg.co/2015/3/d4e00893-80a7-4567-ae5c-ed0f1de0566b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('263', 'cl000001', 'u0000003', 'អីុរ៉​ង់​ផលិត​ស៊ី​ម៉​ង់​ត៍​61​លាន​តោន​ក្នុង​រយៈពេល​11​ខែ', 'តេ​ហេ​រ៉​ង់ ៖ ទីភ្នាក់ងារ​ព័ត៌មាន​ILNA បានឱ្យ​ដឹង​ថា ប្រទេស​អីុ​រ៉​ង់​ផលិត​ស៊ី​ម៉​ង់​ត៍​បាន​ចំនួន​៦១​លាន​តោន​ក្នុង​រយៈពេល​១១​ខែ នៃ​ប្រតិទិន​ឆ្នាំ​ថ្មី​របស់​ខ្លួន​ដែល​ចាប់ផ្ដើម​ពី​ថ្ងៃ​ទី​២១ មីនា ២០១៤ មក ។ ', 'http://kohsantepheapdaily.com.kh/article/115940.html', 'http://i.kspg.co/2015/3/54dea546-2cf0-4479-9d71-6b86d5b717ff.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('264', 'cl000001', 'u0000003', 'ផល​លើស​ផ្នែក​ពាណិជ្ជកម្ម​វៀតណាម​សម្រេច​បាន​300​លាន​ដុល្លារ​ខែកុម្ភៈ', 'ហាណូយ ៖ យោង​តាម​ការិយាល័យ​ស្ថិតិ​ទូទៅ ប្រទេស​វៀតណាម​បង្កើន​ផល​លើស​ផ្នែក​ពាណិជ្ជកម្ម​បាន​ចំនួន​៣០០​លាន​ដុល្លារ​ក្នុង​ខែកុម្ភៈ ឆ្នាំ​២០១៥ បន្ទាប់​ពី​ការ​ចុះ​ផ្សាយ​នូវ​ឱ​នភា​ប​៣៦១​', 'http://kohsantepheapdaily.com.kh/article/115934.html', 'http://i.kspg.co/2015/3/f022741a-53c3-4a83-8147-81281dfe7937.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('265', 'cl000001', 'u0000003', 'តម្លៃ​ទំនិញ​នៅ​ថៃ​សម្រាប់​ខែកុម្ភៈ​ធ្លាក់​ចុះ​កម្រិត​ច្រើន​បំផុត', 'បាងកក ៖ កាលពី​ខែកុម្ភៈ​កន្លង​មកនេះ តម្លៃ​ទំនិញប្រើប្រាស់​នៅ​ប្រទេស​ថៃ​បាន​ឡើងថ្លៃ​០,១២% ផ្ទុយ​ពី​ខែ​មុន​ធ្លាក់​ថ្លៃ​០,៥៩% ដូចនេះ​គេ​កត់សម្គាល់​ឃើញ​ថា វា​ជា​ការ​បង្កើន​ល្បឿន​', 'http://kohsantepheapdaily.com.kh/article/115932.html', 'http://i.kspg.co/2015/3/1970c3e3-8d98-45ca-920d-609618881e3d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('266', 'cl000001', 'u0000003', 'ការ​ដំឡើង​ប្រាក់ខែ​កម្មករ​ពី​5​ទៅ​10%​នឹង​សម្លាប់​ឧស្សាហកម្ម​កាត់ដេរ​មី​យ៉ា​ន់​ម៉ា', 'ណៃ​ពិដោ ៖ លោក U Khin Muang Aye សមាជិក​គណៈកម្មាធិការ​ប្រតិបត្តិ​កណ្តាល​របស់​សមាគម​រោងចក្រ​កាត់ដេរ​មី​យ៉ា​ន់​ម៉ា​បាន​ប្រាប់​សារព័ត៌មាន​ក្នុងស្រុក​ថា ការ​ដំឡើង​ប្រាក់ខែ​អប្បបរមា', 'http://kohsantepheapdaily.com.kh/article/115875.html', 'http://i.kspg.co/2015/3/859a6b37-78d7-4968-990d-fbfa5183d984.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('267', 'cl000001', 'u0000003', 'សេដ្ឋកិច្ច​ចិន​នឹង​កើនឡើង​7%​ក្នុង​ត្រីមាស​ទី​1​ឆ្នាំ​នេះ', 'ប៉េ​កាំ​ង ៖ តាម​ការ​វាយតម្លៃ​ដែល​ចេញផ្សាយ​ដោយ​មជ្ឈមណ្ឌល​ព័ត៌មាន​របស់​រដ្ឋ​បង្ហាញ​ថា សេដ្ឋកិច្ច​របស់​ប្រទេស​ចិន​ត្រូវ​បាន​រំពឹង​ថា​នឹង​កើនឡើង​៧% ក្នុង​ត្រីមាស​ដំបូង​ឆ្នាំ​២០១៥​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/115873.html', 'http://i.kspg.co/2015/3/f7a8a9fe-cc4e-4fcd-9cce-6300cc2457db.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('268', 'cl000001', 'u0000003', 'វៀតណាម​ជា​អ្នក​នាំ​ចេញ​ទៅ​ទីផ្សារ​អា​មេ​រិ​ក​ធំ​ជាងគេ​ក្នុង​តំបន់​អាស៊ាន', 'ហាណូយ ៖ តាម​ទិន្នន័យ​ពី​ការិយាល័យ​ស្ថិតិប្រជាជន​សហរដ្ឋអាមេរិក​បង្ហាញ​ថា ប្រទេស​វៀតណាម​បាន​ក្លាយទៅជា​អ្នក​នាំ​ចេញ​ទំនិញ​ទៅ​កាន់​អា​មេ​រិ​ក​ធំ​ជាងគេ​បំផុត​នៅ​ក្នុង​តំបន់​អាស៊ី​អាគ្នេយ៍ ', 'http://kohsantepheapdaily.com.kh/article/115732.html', 'http://i.kspg.co/2015/3/f5ddba3e-19b8-4083-bb6d-7d261326ac09.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('269', 'cl000001', 'u0000003', 'ចិន​នាំ​ចូល​អង្ករ​2,56​លាន​តោន​ក្នុង​ឆ្នាំ​2014', 'ប៉េ​កាំ​ង ៖ ទិន្នន័យ​ពី​រដ្ឋបាល​ពន្ធដារ​ជាតិ​បង្ហាញ​ថា ប្រទេស​ចិន​បាន​នាំ​ចូល​អង្ករ​ពី​បរទេស​ចំនួន​ជាង​២,៥៦​លាន​តោន ក្នុង​ឆ្នាំ​២០១៤ កើន​ទ្បើ​ង​១៣% ពី​បរិមាណ​ជាង​២,២៧​លាន​តោន ', 'http://kohsantepheapdaily.com.kh/article/115731.html', 'http://i.kspg.co/2015/3/d9e1ecae-aabb-476e-802e-da15ecd62982.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('270', 'cl000001', 'u0000003', 'ប្រាក់​ចំណេញ​Airbus​កើន​59%', 'ក្រុមហ៊ុន​ផលិត​យន្តហោះ Airbus Group បានឱ្យ​ដឹង​ថា ប្រាក់​ចំណេញ​សុទ្ធ​របស់​ខ្លួន​ក្នុង​រយៈពេល​ពេញ​មួយ​ឆ្នាំ​២០១៤​កន្លង​មកនេះ ពង្រីក​បាន​ចំនួន​៥៩% នោះ​ក៏​ដោយសារ​តែ​មានការ​ជួយ', 'http://kohsantepheapdaily.com.kh/article/115722.html', 'http://i.kspg.co/2015/3/f481621d-6fee-408b-9167-c94425b956fb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('271', 'cl000001', 'u0000003', 'The World This Week', ' ​សេដ្ឋកិច្ច​តំបន់​អា​ស៊ី បាងកក ៖ ធនាគារ​ថៃ​បាន​ចាប់ផ្ដើម​បង្ហាញ​នូវ​ក្រដាស់ប្រាក់​១០០​បាត​ថ្មី​ជាមួយនឹង​លក្ខណៈ​ជឿនលឿន​នៃ​ការ​ប្រឆាំង​នឹង​ធនបត្រ​ក្លែងក្លាយ ហើយ​វា​នឹង​ត្រូវ​បាន​កំណត់​ឱ្យ​ចរាចរ​ជា​សាធារណៈ​នៅ​ថ្ងៃ​ទី​២៦ កុម្ភៈ នេះ ។  ', 'http://kohsantepheapdaily.com.kh/article/115634.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('272', 'cl000001', 'u0000003', 'វិស័យ​វត្ថុធាតុដើម​ចិន​នឹង​ពង្រីក​បាន​8%​ឆ្នាំ​នេះ', 'ប៉េ​កាំ​ង ៖ ក្រសួង​ឧស្សាហកម្ម​និង​បច្ចេកវិទ្យា​ព័ត៌មាន បាន​បង្ហើប​ឱ្យ​ដឹង​លើ​គេហទំព័រ​របស់​ខ្លួន​ថា ឧស្សាហកម្ម​ផលិត​វត្ថុធាតុដើម​នៅ​ក្នុង​ប្រទេស​ចិន ត្រូវ​បាន​គេ​រំពឹង​ថា​នឹង​កើន​ទ្បើ​ង​៨%​សម្រាប់​ឆ្នាំ​២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/115601.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('273', 'cl000001', 'u0000003', 'ទីផ្សារ​អង្ករ', 'តម្លៃ​អង្ក​រលើ​ទីផ្សារ​អា​ស៊ី​មិន​ប្រែប្រួល អា​ស៊ី ៖ គិត​មក​ដល់​ថ្ងៃ​ទី​២៤ កុម្ភៈ បណ្ដា​អ្នកលក់​អង្ករ​នៅ​តំបន់​អា​ស៊ី​ន​បាន​រក្សា​តម្លៃ​ហាងឆេង​លក់​ចេញ​ក្នុង​អត្រា​ថេរ ដោយឡែក​តែ​វៀតណាម​គឺ​បាន​', 'http://kohsantepheapdaily.com.kh/article/115541.html', 'http://i.kspg.co/2015/2/304a2a28-c29f-4046-8553-e6c957de5a3b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('274', 'cl000001', 'u0000003', 'ថៃ​សម្លឹង​ឃើញ​ចំណូល​3,68​ពាន់​លាន​ដុល្លារ​ពី​ការ​នាំ​ចេញ​អាហារ​ទៅ​អឺរ៉ុប', 'បាងកក ៖ ក្រសួង​សហប្រតិបត្តិការ និង​កសិកម្ម នឹង​ដឹកនាំ​ការ​សិក្សា​ឱ្យ​បាន​ទូលំទូលាយ​អំពី​តម្រូវការ​ទីផ្សារ​អឺរ៉ុប​ចំពោះ​ផលិតផល​អាហារ និង​កសិកម្ម​ថៃ​ដែល​ស្ថិត​ក្នុង​ក្តី​រំពឹង​ថា​នឹង​បង្កើន​ការ​', 'http://kohsantepheapdaily.com.kh/article/115470.html', 'http://i.kspg.co/2015/2/66ea0ae5-686f-44ef-aa8c-85a0176455ed.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('275', 'cl000001', 'u0000003', 'វៀតណាម​ប្រមូល​ពន្ធ​កើន​20,2%​ក្នុង​ខែមករា', 'ហាណូយ ៖ ការ​ប្រមូល​ចំណូល​ពន្ធ​ពី​ការ​នាំ​ចូល​និង​នាំ​ចេញ​តាម​រយៈ​ទីភ្នាក់ងារ​ពន្ធដារ​របស់​វៀតណាម​ក្នុង​រយៈពេល​ពេញ​មួយ​ខែមករា ឆ្នាំ​២០១៥ កន្លង​មកនេះ ត្រូវ​បាន​គេ​វាយតម្លៃ​ថា ', 'http://kohsantepheapdaily.com.kh/article/115469.html', 'http://i.kspg.co/2015/2/9932a957-e724-4614-ae21-fbd3e54d9188.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('276', 'cl000001', 'u0000003', 'ការ​ប្រើប្រាស់​ពេជ្រ​របស់​ចិន​បន្ត​ធ្លាក់​ចុះ​ដដែល​ក្នុង​ខែមករា', 'ប៉េ​កាំ​ង ៖ យោង​តាម​ទីភ្នាក់ងារ​ជួញដូរ​ពេជ្រ​ប្រចាំ​ក្រុង​សៀង​ហៃ​បង្ហាញ​ថា បរិមាណ​ពេជ្រ​កែ​ច្នៃ​រួច​ដែល​បាន​នាំ​ចូល​ប្រទេស​ចិន​បន្ត​ធ្លាក់​ចុះ​បួន​ខែ​ជាប់​ៗ​គ្នា​គិត​មក​ដល់​ខែមករា​ដើមឆ្នាំ​២០១៥​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/115468.html', 'http://i.kspg.co/2015/2/d8e5247f-5c9d-4da1-8533-1e2e85a474b3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('277', 'cl000001', 'u0000003', 'ការ​ប្រើ​ប័ណ្ណឥណទាន​របស់​ពលរដ្ឋ​ចិន​កើនឡើង​ក្នុង​ឆ្នាំ​2014', 'ប៉េ​កាំ​ង ៖ បណ្តាញ​ក្នុងស្រុក​ផ្សាយ​ថា ក្នុងអំឡុងពេល​ពេញ​មួយ​ឆ្នាំ​២០១៤ កន្លង​មកនេះ ប្រជាជន​ចិន​បាន​ប្រើប្រាស់​កាត​ឥណទាន​កាន់តែ​ច្រើន​ឡើង​ដែល​សបញ្ជាក់​ឱ្យ​ឃើញ​យ៉ាង​ច្បាស់​ថា', 'http://kohsantepheapdaily.com.kh/article/115399.html', 'http://i.kspg.co/2015/2/0d8dcd7d-454f-4f5a-be24-e1cf250069e0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('278', 'cl000001', 'u0000003', 'សិង្ហ​បុរី​បង្កើន​ការ​នាំ​ចេញ​ទំនិញ​ក្នុង​ខែមករា', 'សិង្ហ​បុរី ៖ នៅ​ក្នុង​អំឡុង​ខែមករា ការ​នាំ​ចេញ​ផលិតផល​ក្រៅពី​ប្រេង​របស់​ប្រទេស​សិង្ហ​បុរី​បាន​កើនឡើង​៤,៣%​ធៀប​នឹង​ខែមករា​ឆ្នាំ​២០១៤ ។ ការ​បោះ​ជំហាន​នេះ គឺ​ប្រសើរ​ជាង​កំណើន​សរុប​', 'http://kohsantepheapdaily.com.kh/article/115387.html', 'http://i.kspg.co/2015/2/f4cf5d4d-587f-4db2-9494-ba1c0d3321e0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('279', 'cl000001', 'u0000003', 'កំណើន​នៃ​ការ​លក់រាយ​នៅ​អូស្ត្រាលី​ស្ទុះ​ឡើង​តិចតួច', 'កង់​បេ​រ៉ា ៖ ការ​លក់រាយ​នៃ​ទំនិញ​ទូទៅ​នៅ​ប្រទេស​អូស្ត្រាលី​បាន​កើនឡើង​០,២%​ក្នុង​ខែធ្នូ​ធៀប​នឹង​ខែ​មុន​ដែល​គេ​គិត​ទាំង​ការ​កែសម្រួល​តួលេខ​តាម​រដូវ ខណៈ​ខែវិច្ឆិកា​កើន​០,១% ។ ', 'http://kohsantepheapdaily.com.kh/article/115386.html', 'http://i.kspg.co/2015/2/2f49f5c1-1936-43ec-a5b5-8eeab4ea1861.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('280', 'cl000001', 'u0000003', 'TAT ៖ វិស័យ​ទេសចរណ៍​ថៃ​នឹង​រក​ចំណូល​បាន​ជិត​68​ពាន់​លាន​ដុល្លារ​ឆ្នាំ​នេះ', 'បាងកក ៖ អាជ្ញាធរ​ទេសចរណ៍​ថៃ (TAT) ជឿជាក់​ថា ចំណូល​ពី​វិស័យ​ទេសចរណ៍​នឹង​កើនឡើង​ដល់​៦៧,៥៧​ពាន់​លាន​ដុល្លារ​នៅ​ដំណាច់ឆ្នាំ​២០១៥ ដោយសារ​តែ​ស្ថិរភាព​នយោបាយ​ក្នុង​ប្រទេស ។', 'http://kohsantepheapdaily.com.kh/article/115322.html', 'http://i.kspg.co/2015/2/c2279ed5-3fca-49d9-a05b-a44ccb06bf39.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('281', 'cl000001', 'u0000003', 'ជប៉ុន​ជួប​ឱនភាព​ពាណិជ្ជកម្ម​ជិត​10​ពាន់​លាន​ដុល្លារ​ខែមករា', 'តូ​ក្យូ ៖ ទិន្នន័យ​រាជរដ្ឋាភិបាល​បង្ហាញ​ថា ប្រទេស​ជប៉ុន​ជួប​ឱនភាព​ពាណិជ្ជកម្ម​នៃ​ទំនិញ ក្នុង​ទំហំ​ទឹកប្រាក់​១.១៧៧,៥​ពាន់​លាន​យេ​ន (៩,៩២​ពាន់​លាន​ដុល្លារ​) ក្នុង​ខែមករា ដែល​ជា​ខែ​ទី​៣១ ', 'http://kohsantepheapdaily.com.kh/article/115311.html', 'http://i.kspg.co/2015/2/5bf0b14c-0fbf-4b37-b224-c10c0a33ef05.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('282', 'cl000001', 'u0000003', 'ចិន​នឹង​រក្សា​អត្រា​ប្តូរ​ប្រាក់​យន់​របស់​ខ្លួន​ឱ្យ​នៅ​ឋេ​រ', 'ប៉េ​កាំ​ង ៖ យោង​តាម​និយតករ​រូបិយប័ណ្ណ​បរទេស​របស់​ខ្លួន បង្ហាញ​ថា ប្រទេស​ចិន​នឹង​បន្ត​ធ្វើ​ឱ្យ​ប្រសើរ​ឡើង​នូវ​យន្តការ​ព័ត៌មាន​នៃ​អត្រា​ប្តូរ​ប្រាក់​យន់ ដើម្បី​រក្សា​មូលដ្ឋាន​នឹងន​ក្នុង​ឆ្នាំ​២០១៥​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/115310.html', 'http://i.kspg.co/2015/2/434be146-02cf-447e-85db-e52ac262c48d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('283', 'cl000001', 'u0000003', 'The World This Week', '​សេដ្ឋកិច្ច​តំបន់​អា​ស៊ី បាងកក ៖ តាម​ការ​ឱ្យ​ដឹង​និង​ព្យាករណ៍​ពី​អ្នកគ្រប់គ្រង​អាកាសយានដ្ឋាន​ថៃ​នៅ​ក្នុងអំឡុងពេល​ចូល​ឆ្នាំ​ប្រពៃណី​ចិន​ឆ្នាំ​២០១៥ នេះ​បរិមាណ​នៃ​ភ្ញៀវ​', 'http://kohsantepheapdaily.com.kh/article/115228.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('284', 'cl000001', 'u0000003', 'ក្រុមរុករករកឃើញ​សាកសព៦នាក់ហើយ​នៅក្បែរកន្លែងប្រទះឃើញ​បំណែកទ្វារយន្តហោះ Air Asia Flight QZ 8501', 'Indonesia ៖​ មកដល់ម៉ោង៤រសៀលនេះ​ ក្រុមរុករកយន្តហោះ​Air Asia Flight​ QZ 8501ដែលបាត់ដាន​កាលពីថ្ងៃអាទិត្យកន្លងទៅ​ បានប្រទះឃើញ​សាក​សព៦នាក់ ​នៅក្បែរទីតំាងដែលរកឃើញ​បំណែកទ្វារយន្តហោះដែល​មន្រ្តីឥណ្ឌូនេស៊ីជឿជាក់ថា ​៩៥%​ ស្រដៀងទៅនឹងAir Asia Flight ​QZ 8501ដែល​បាន​ដានពីប្រព័ន្ឋរ៉ាដា ។', 'http://kohsantepheapdaily.com.kh/article/112033.html', 'http://i.kspg.co/2014/12/beddc0ac-79bd-4fc8-9fc8-c394ca3b130d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('285', 'cl000001', 'u0000003', 'តុល្យការមនុស្ស​​ស្លាប់​ដោយ​ការធ្លាក់​ឧទ្ឋម្ភាគចក្រ​​យោធា​វៀតណាម​​កើន​ដល់​១៩នាក់', 'ទីក្រុង​ហាណូយ ​៖ គិតមកដល់រសៀលថ្ងៃទី០៨ ​កក្កដា ២០១៤នេះ ចំ​នួនមនុស្សស្លាប់​ដោយសារការធ្លាក់​ឧទ្ឋម្ភាគចក្រយោធា​នៅទីក្រុងហាណូយ​ បានកើនដល់១៩នាក់ហើយ ។  គួរបញ្ជាក់ថា ​មនុស្ស​១៦​នាក់ ​បាន​ស្លាប់ភ្លាមៗ និង០​៥​នាក់​​រង​របួស​​ ដោយ​សារ​ធ្លាក់​​ឧទ្ធម្ភាគ​ចក្រ​យោធា​វៀតណាម​មួយគ្រឿង​ ', 'http://kohsantepheapdaily.com.kh/article/100065.html', 'http://i.kspg.co/2014/7/4dcd5114-eb02-4ba9-9581-c2b9c61e3ec8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('286', 'cl000001', 'u0000003', 'អតីត​នាយករដ្ឋមន្រ្តីអ៊ីស្រាអែល ​លោក អារីយែល សារ៉ូន ​ទទួលមរណភាពក្នុងវ័យ៨៥ឆ្នាំ', 'អ៊ីស្រាអែល​៖ ទីបំផុតទៅ​អតីតនាយករដ្ឋមន្រ្តីអ៊ីស្រាអែល​ លោក អារីយែល សារ៉ូន ​( Ariel Sharon ) ​បានទទួលមរណភាពហើយនៅថ្ងៃសៅរ៍ទី១១​ មករា​ ២០១៤ បន្ទាប់ពីលោកបាន​សម្រាក​នៅលើគ្រែ​អស់រយៈពេល៨ឆ្នាំ​ ដšޙសារជម្ងឺដាច់​សរសៃឈាមក្នុងខួរក្បាល ។   គួរបញ្ជាក់ថា​ លោក អារីយែល សារ៉ូន​ បានឡើងកាន់តំណែង​ជានាយករដ្ឋមន្រ្តីអ៊ីស្រាអែល​នៅថ្ងៃទី០៧ ​មីនា ​២០០១ ​និង បានលាលែងពីតំណែង​បន្ទាប់ពីលោកកើត​មានជម្ងឺនៅក្នុងឆ្នាំ២០០៦ ៕ ', 'http://kohsantepheapdaily.com.kh/article/87927.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('287', 'cl000001', 'u0000003', 'ព្រលានយន្តហោះអន្តរជាតិក្រុង​ ណៃរ៉ូប៊ីនៃ​ប្រទេសកេនយ៉ា ​ត្រូវបានបិទបន្ទាប់ពីភ្លើងឆេះ', 'ព្រលានយន្តហោះអន្តរជាតិ​ក្រុងណៃរ៉ូប៊ីនៃប្រទេសកេនយ៉ា ​ត្រូវបានភ្លើងឆាបឆេះសន្ឋោសន្ឋៅ​ចេញពីស្ថានីយ៏​អ្នកដំណើរ​ បណ្តាលឲ្យអ្នកដំណើរ​មានការភ្ញាក់ផ្អើល​រត់​ចេញ​យ៉ាងប្រញាប់ប្រញាល់​កាលពីថ្ងៃអង្គារ(ន៉ោងនៅប្រទេសកេនយ៉ា) ​ត្រូវនឹងថ្ងៃពុធ​ម៉ោង​នៅក្នុងប្រទេសកម្ពុជា ។  យ៉ាងណាក៏ដោយយោងតាមទូរទស្សន៏ ​CNN បានឲ្យដឹងថា ​មកដល់ពេលនេះ ​ភ្លើងបានថមថយវិញហើយ ​បន្ទាប់ពីក្រុមពន្លត់អគ្គិភ័យ​បានខិតខំអន្តរាគមន៏បាញ់ពន្លត់។ ', 'http://kohsantepheapdaily.com.kh/article/77834.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
