INSERT INTO "tbnews" VALUES ('303', 'cl000001', 'u0000003', 'ព្រឹត្តិ​ការណ៍​ពិភពលោក​សំ​ខាន់​ៗ​កើត​ឡើង​ក្នុង​ថ្ងៃទី​១៧​​ធ្នូ​', '*ឆ្នាំ​១៥៨៦ -ស្តេច​ Go-Yozeiបាន​ក្លាយ​ជា​អធិ​រាជ​របស់​ជប៉ុន ។ *ឆ្នាំ​១៧១៨ -អង់​គ្លេស​ប្រកាស​សង្គ្រាម​ជា​មួយ​អេស្ប៉ាញ។ *ឆ្នាំ​១៧៧៧ -ប្រទេស​បារាំង​ប្រកាស​ផ្លូវ​ការ​ទទួល​ស្គាល់​សហរដ្ឋ​អា​មេរិក​ក្នុង​នាម​ជា​ប្រទេស​មួយ។ *ឆ្នាំ​១៩០៧ -ព្រះ​អង្គ​ម្ចាស់​ Gongsa Ugyen Wangchuck​​​​បាន​ឡើង​គ្រង​រាជ្យ​ក្នុង​នាម​ជា​មហាក្សត្រ​ដំបូង​របស់​ប្រទេស​ប៊ូ​តាន​ ។​', 'http://kohsantepheapdaily.com.kh/article/31229.html', 'http://i.kspg.co/2011/12/0789c0f1-b24c-4745-9148-ce1fe994a327.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('304', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​សំខាន់ៗ​ជុំវិញ​ពិភពលោក​ដែល​កើត​ឡើង​នៅ​​ថ្ងៃទី16 ធ្នូ', '*ឆ្នាំ១៩២០ -​រញ្ជួយ​ដី​​កម្រិត​៨,៥​រ៉ិច​ស្ទ័រ​ ក្នុង​ប្រទេស​ចិន បណ្តាល​ឱ្យ​​មនុស្ស​ជាង​ពីរ​សែន​នាក់​ស្លាប់​បាត់​បង់​ជីវិត ។ *​ឆ្នាំ​១៩៤១ -​សង្គ្រាម​លោក​​លើក​ទី​២ ៖ ​កង​ទ័ព​​ជប៉ុន​​ចូល​កាន់​កាប់​​ក្រុង​មីរី​ក្នុង​រដ្ឋ​សារ៉ា​វាក់ ប្រទេស​​​ម៉ា​ឡេ​ស៊ី​ ។ *ឆ្នាំ១៩៤៦ -​ប្រទេស​ថៃ ចូល​ជា​សមាជិករបស់​អង្គ​ការ​សហ​ប្រជាជាតិ ។ ', 'http://kohsantepheapdaily.com.kh/article/31174.html', 'http://i.kspg.co/2011/12/4daabc09-64e7-4c33-9232-51419dd2c429.jpg?w=285&h=170&mode=carve', '20150308');
