INSERT INTO "tbnews" VALUES ('306', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​សំខាន់ៗ​ជុំវិញ​ពិភពលោក​ដែល​កើត​ឡើង​នៅ​​ថ្ងៃទី14 ធ្នូ', '*ឆ្នាំ១៩១២ -ការ​ឈ្លាន​ពាន​របស់​​ប្រទេស​បារាំង​ទៅលើ​ប្រទេស​​រុស្ស៊ី​​ត្រូវបាន​បញ្ចប់ ។ *​ឆ្នាំ​១៨១៩ -​អាឡា​បាម៉ា ក្លាយ​ទៅជា​រដ្ឋទី​២២ របស់​​សហរដ្ឋ​​អាមេរិក ។ *ឆ្នាំ១៩៣៩ -សហ​ភាព​សូវៀត​ ត្រូវ​បាន​បណ្តេញ​​ចេញពី​សម្ព័ន្ធ​​ជាតិ​ ​ដោយ​សារតែ​ប្រទេស​នេះ​មិន​គោរព​សិទ្ធិ​​សញ្ញា ហើយ​​ឈ្លាន​ពាន​​ទៅលើ​ប្រទេស​ហ្វាំង​ឡង់ ។ ', 'http://kohsantepheapdaily.com.kh/article/30991.html', 'http://i.kspg.co/2011/12/3bd36352-9551-4c9a-aa5f-fd6cfae241cb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('307', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​សំខាន់ៗ​ជុំវិញ​ពិភពលោក​ដែល​កើត​ឡើង​នៅ​​ថ្ងៃទី13 ធ្នូ', '*ឆ្នាំ១៩៣៧  ការ​សម្លាប់​រង្គាល​​ក្នុងទី​ក្រុង​ណានជីង ៖ កង​​ទ័ពជប៉ុន​បានបន្ត​កាន់កាប់​ទី​ក្រុង​ណានជីង ​ជាច្រើនសបា្តហ៍​​​​ក្រោយ​​ពី​វាយ​​ផ្តួលរំលំ​ហើយ​បាន​ប្រព្រឹត្ត​​អំពើ​​​ព្រៃផ្សៃ​​​ចាប់ជនស៊ីវិល​​រំលោភ និងសម្លាប់ ។​ *​ឆ្នាំ​១៩៤១ ​ សង្គ្រាម​លោកលើកទី២​​ ៖ ប្រទេស​​​​ហុងគ្រី និងរូម៉ានី ប្រកាស​សង្គ្រាម​ជាមួយ​សហ​រដ្ឋអាមេ​រិក ។ *ឆ្នាំ១៩៧១ ប្រទេស​ម៉ាល់ត៍​ក្លាយ​ទៅជា​​​ប្រទេស​​ប្រកាន់​របប​​នយោបាយសាធារណរដ្ឋ​​​រួមជាមួយ​នឹងបណ្តា​​ប្រទេស​ជា​ច្រើន​​ទៀតក្នុង Commonweath ។', 'http://kohsantepheapdaily.com.kh/article/30891.html', 'http://i.kspg.co/2011/12/c62aee4e-b5d3-4a67-b291-4ba2e05f7b36.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('308', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​សំខាន់ៗ​ជុំវិញ​ពិភពលោក​ដែល​កើត​ឡើង​នៅ​​ថ្ងៃទី12 ធ្នូ', '*ឆ្នាំ១៩១១ ទី​ក្រុង​​ញូវដែលីត្រូវ​បាន​ដាក់​ជំនួស​ទីក្រុង​ Calcutta របស់​ប្រ​ទេស​​ឥណ្ឌា​។​​ *​ឆ្នាំ​១៩១៥ ​ លោក​ Yuan Shikai ប្រធានា​ធិបតី​សាធារណរដ្ឋប្រជាមានិត​ចិនបានប្រកាស​​​ពីបំណង​របស់ខ្លួន​​ចង់ឡើង​​គ្រង​រាជ្យ​ ហើយ​​បន្ទាប់​មក​បានប្រកាស​​ខ្លួនឯង​ជា​ព្រះចៅ​អធិ​រាជ​​របស់​ប្រទេសចិន ។ *ឆ្នាំ១៩៣៧ យន្ត​ហោះ​ចម្បាំង​​​របស់ជប៉ុន​​​ទម្លាក់​គ្រាប់បែក​​ពន្លិច​នាវា​បារាំង​អាមេរិក Panay នៅ​​ទន្លេ​យ៉ាង​ហ្ស៊ី ប្រទេសចិន។ *​ឆ្នាំ១៩៣៩ កង​ទ័ព​ហ្វាំងឡង់​ផ្តួល​រំលំ​កង​ទ័ព​​សហភាព​សូវៀត​ ។', 'http://kohsantepheapdaily.com.kh/article/30830.html', 'http://i.kspg.co/2011/12/1b6114f6-23d9-4ced-bac0-5cd69e26b692.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('309', 'cl000001', 'u0000003', 'ឡូង សុវណ្ណ​ឌឿន ឈ្នះ​ថៃ​ក្នុង​ទឹក​ទី​៣', 'រាជ​ធានីភ្នំពេញ ៖ គូអន្តរ​ជាតិ​ទី​២ កីឡាករ​ឡុង សុវណ្ណ​ឌឿន ឈ្នះ​កីឡាករ​ថៃ​ឈ្មោះ​ចាក់ គ្រីត ក្នុង​ទឹក​ទី​៣៕', 'http://kohsantepheapdaily.com.kh/article/116177.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('310', 'cl000001', 'u0000003', 'កីឡាករ​កំពែង​ ខ្មៅ ចាញ់​កីឡាករ​ថៃ​ដោយ​ពិន្ទុ​នៅ​សង្វៀន​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​រសៀល​នេះ', 'រាជ​ធានី​ភ្នំពេញ ៖ ​ការ​ប្រកួត​កីឡា​ប្រដាល់អន្តរ​ជាតិ​​ដែល​ធ្វើ​ឡើង​រៀង​រាល់​២​សប្តាហ៍​ម្តង​នៅ​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​នា​រសៀល​ថ្ងៃ​ទី​៨ មីនា នេះ គូអន្តរជាតិ​ទី​១ កីឡាករ កំពែង​ ', 'http://kohsantepheapdaily.com.kh/article/116170.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('311', 'cl000001', 'u0000003', 'លាស់ យី​ន ធ្វើ​ឱ្យ​មាស ច័ន្ទ​មាន បះសក់​ក្នុង​ការ​ប្រកួត​សន្សំ​ពិន្ទុ​ជុំ​ទី​4', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ក្នុង​ការ​ប្រកួត​សន្សំ​ពិន្ទុ​ជុំ​ទី​៤ កីឡាករ​មាស ច័ន្ទ​មាន មិន​ត្រឹមតែ​ភ្លាត់​បរាជ័យ​នោះ​ទេ គឺ​ថែម​ទាំង​ត្រូវ​បះសក់​ក្រោម​ដៃ​កីឡាករ​លាស់ យី​ន ដែល​មាន​ពិន្ទុ​ទាប​ក្នុង​ក្រុម​ខុស', 'http://kohsantepheapdaily.com.kh/article/116158.html', 'http://i.kspg.co/2015/3/0e17dab8-cfdf-4ce5-8050-9a43ee71923a.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('312', 'cl000001', 'u0000003', 'ដក ណា​ម ម៉ូ​រ៉ា​ក់ ដាក់​ទី ព​ន្លែ​ត កណ្ដាល​ទី​ក្នុង​ព្រឹត្តិការណ៍​ដណ្ដើម​ខ្សែក្រវាត់', 'រាជធានី​ភ្នំពេញ ៖ ទើបតែ​មានមុខ​ឡើង​ប្រកួត​សន្សំ​ពិន្ទុ​បាន​៣​ជុំ ស្រាប់តែ​កីឡាករ​ណា​ម ម៉ូ​រ៉ា​ក់ ត្រូវ​គណៈកម្មការ​រៀបចំ​ការ​ប្រកួត​ប្រដាល់​ដណ្ដើម​ខ្សែក្រវាត់​K-Cement (​អាស៊ី​អាគ្នេយ៍​)​ដក​ចេញ', 'http://kohsantepheapdaily.com.kh/article/116154.html', 'http://i.kspg.co/2015/3/6d8a2bf2-3f8e-43ac-bde5-8073e6ec36a8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('313', 'cl000001', 'u0000003', 'ដំណើរ​ឆ្ពោះទៅ​កាន់ការ​ប្រកួត​កីឡា​ស៊ី​ហ្គេ​ម​ឆ្នាំ​2023របស់​កម្ពុជា​ផ្តើម​ចេញពី​ការ​រៀបចំ​ការ​ប្រកួត​កីឡា​ថ្នាក់​ជាតិ', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​កីឡា​ថ្នាក់​ជាតិ (National Game) ដែល​គ្រោង​ធ្វើ​ឡើង​នៅ​ក្នុង​ឆ្នាំ​២០១៦ ខាង​មុខ​នេះ គឺជា​តម្រូវការ​ចាំបាច់​បំផុត​របស់​កម្ពុជា ដែល​ត្រូវ​ទទួល​ធ្វើ​ជា​ម្ចាស់ផ្ទះ​នៃ​ការ​ប្រកួត​កីឡា', 'http://kohsantepheapdaily.com.kh/article/116148.html', 'http://i.kspg.co/2015/3/8c47ea89-99fa-4a9b-9dec-a449092eb57c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('314', 'cl000001', 'u0000003', 'ឡុ​ង សុវណ្ណ​ឌឿ​ន និង​កំពែង ខ្មៅ ប៉ះ​កីឡាករ​ថៃ​នៅ​ថ្ងៃអាទិត្យ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ នៅ​រសៀល​ថ្ងៃអាទិត្យ​ទី​៨ មីនា ២០១៥​នេះ កីឡាករ​វ័យក្មេង​២​រូប គឺ ឡុ​ង សុវណ្ណ​ឌឿ​ន និង កំពែង ខ្មៅ នឹង​សាក​កណ្តាប់ដៃ​ជាមួយ​កីឡាករ​ថៃ​នៅ​លើ​', 'http://kohsantepheapdaily.com.kh/article/116078.html', 'http://i.kspg.co/2015/3/94f8f7b2-d1f9-4492-8e01-2b1db4e3cf54.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('315', 'cl000001', 'u0000003', 'គឿ​ម សុវណ្ណ​នឿ​ត ណាត់ ផា​ន់ ផាត់​យុទ្ធ សាក​កណ្តាប់ដៃ​គ្នា​នៅ​លើ​សង្វៀន​ស៊ី​ធី​អិន​ថ្ងៃអាទិត្យ​ស​ប្តា​ហ៍​នេះ', 'រាជធានី​ភ្នំពេញ ៖ គឿ​ម សុវណ្ណ​នឿ​ត មក​ពី​ក្លិប​ឃ្លាំងមឿង​កីឡា បាន​ណាត់ ផា​ន់ ផាត់​យុទ្ធ មក​ពី​ក្លិប​សាល​វ័​ន្ត​កីឡា​សាក​កណ្តាប់ដៃ​គ្នា​នៅ​លើ​សង្វៀន​ស៊ី​ធី​អិន នា​ថ្ងៃអាទិត្យ ', 'http://kohsantepheapdaily.com.kh/article/116077.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('316', 'cl000001', 'u0000003', 'ក្រុម​សី​ប៉ាក់​តា​ក្រ​កម្ពុជា​និយាយ​ពី​ការ​ហ្វឹកហាត់​នៅ​ថៃ', 'រាជធានី​ភ្នំពេញ ៖ តាំងពី​ថ្ងៃ​ពុធ​ស​ប្ដា​ហ៍​នេះ ក្រុម​ជម្រើសជាតិ​ផ្នែក​សី​ប៉ាក់​តា​ក្រ ឬ​ហៅ​ថា​ក្រុមកីឡា​សីដក់​បាន​ទៅ​ដល់​ប្រទេស​ថៃ​ហើយ ដើម្បី​ហាត់​បំប៉ន​សមត្ថភាព​តាម​ការ​គ្រោងទុក ', 'http://kohsantepheapdaily.com.kh/article/116000.html', 'http://i.kspg.co/2015/3/bea922a3-eb62-4044-8510-e6b58fee9fd8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('317', 'cl000001', 'u0000003', 'សា​រឿ​ន ច័ន្ទ និង​យ៉េន ឌី​ណា ជួប​កីឡាករ​ថៃ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​ប៉ុ​ស្តិ៍​លេខ​3', 'រាជធានី​ភ្នំពេញ ៖ សា​រឿ​ន ច័ន្ទ មក​ពី​ក្លិប​សម្រែក​រាជសីហ៍​បង្ក្រាប​គ្រឿងញៀន និង យ៉េន ឌី​ណា មក​ពី​ក្លិប​អប្សរា​គុន​ខ្មែរ នៅ​ថ្ងៃអាទិត្យ ទី​៨ មីនា ស​ប្តា​ហ៍​នេះ​ត្រូវ​បាន​គណៈកម្មការ​រៀបចំ​ការ', 'http://kohsantepheapdaily.com.kh/article/115998.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('318', 'cl000001', 'u0000003', 'សន្និសីទ​សារព័ត៌មាន​ស្តី​ពី​ការ​ប្រកួត​បាល់ទះ​ដណ្តើម​ពានរង្វាន់​ក្រុមហ៊ុនស្រា​បៀរ​លី​អូ​លើក​ទី​4', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​កីឡាបាល់ទះ​ដណ្តើម​ពានរង្វាន់​ក្រុមហ៊ុនស្រា​បៀរ​លី​អូ​លើក​ទី​៤ ឆ្នាំ​២០១៥ នឹង​ចាប់ផ្តើម​ធ្វើ​នៅ​ថ្ងៃ​ទី​១០ ខែមីនា​ខាង​មុខ​នេះ​ហើយ ។', 'http://kohsantepheapdaily.com.kh/article/115861.html', 'http://i.kspg.co/2015/3/4ba8efdd-94fa-4dad-b1ef-6079b907c162.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('319', 'cl000001', 'u0000003', 'ក្រុម​កង់​មេគង្គ​ជិះ​ជុំវិញ​ភ្នំ​ប្រសិទ្ធិ៍​ដើម្បី​លើកកម្ពស់​សុខុមាលភាព និង​បរិស្ថាន', 'រាជធានី​ភ្នំពេញ ៖ ក្រុម​កង់​មេគង្គ​កាលពី​ថ្ងៃអាទិត្យ ទី​១ មីនា កន្លង​ទៅ​នេះ បាន​ប្រមូលផ្តុំ​គ្នា​ប្រហែល​៧០​នាក់​នាំ​គ្នា​ជិះ​កង់​ដោយ​ចេញដំណើរ​ពី​កាំ​កូ​ស៊ី​ធី ឆ្ពោះទៅ​ភ្នំ​ប្រសិទ្ធិ៍ ។ ', 'http://kohsantepheapdaily.com.kh/article/115859.html', 'http://i.kspg.co/2015/3/bb999492-37e0-421a-be36-0ea45f59cae8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('320', 'cl000001', 'u0000003', 'កីឡាករ​ថៃ Nong Ou លេចធ្លោ​ក្នុង​សង្វៀន​ប្រដាល់​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ក្នុង​ចំណោម​កីឡាករ​ថៃ​ដែល​ឡើង​មក​ប្រកួត​នៅ​ក្នុង​ប្រទេស​កម្ពុ​ជានា​រយៈពេល​នេះ គេ​កត់សម្គាល់​ឃើញ​ថា កីឡាករ​វ័យក្មេង​ឈ្មោះ Nong Ou ទទួល​', 'http://kohsantepheapdaily.com.kh/article/115856.html', 'http://i.kspg.co/2015/3/9e5f3b6d-5853-45a4-898c-8b9a157667ab.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('321', 'cl000001', 'u0000003', 'ជ័យ វណ្ណៈ ធ្វើ​ឱ្យ​ស្រិល ទ្រី ចាញ់​ការ​ប្រកួត​យ៉ាង​អស់ចិត្ត ចំណែក​ចាន់ រតនៈ ត្រូវ​គ្រូពេទ្យ​បញ្ឈប់​ត្រឹម​ទឹក​ទី​4', 'រាជធានី​ភ្នំពេញ ៖ ជ័យ វណ្ណៈ មក​ពី​ក្លិប​ឃ្លាំងមឿង​កីឡា បាន​ធ្វើ​ឱ្យ ស្រិល ទ្រី មក​ពី​ក្លិប​គជសារ​កីឡា ចាញ់​ការ​ប្រកួត​ដោយ​ពិន្ទុ​យ៉ាង​អស់ចិត្ត​បំផុត​សម្រាប់​ការ​ជួប​គ្នា​នៅ​លើ', 'http://kohsantepheapdaily.com.kh/article/115779.html', 'http://i.kspg.co/2015/3/87c45142-9fed-4369-a674-c6bd2954e863.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('322', 'cl000001', 'u0000003', 'លោក Antonio Inoki ជើងឯក​កីឡាកាស​ជ​ប៉ុន​ទទួល​ការ​តែងតាំង​ជា​ឯកអគ្គរាជទូត​សុច្ឆន្ទៈ NOCC', 'រាជធានី​ភ្នំពេញ ៖ លោក Antonio Inoki ដែល​ជា​ជើងឯក​កីឡាកាស​ប្រទេស​ជ​បុ​៉​ន​ត្រូវ​បាន​រាជរដ្ឋាភិបាល​កម្ពុជា ប្រកាស​តែងតាំង​ជា​ឯកអគ្គរាជទូត​សុច្ឆន្ទៈ​របស់​គណៈ​កម្មា​', 'http://kohsantepheapdaily.com.kh/article/115777.html', 'http://i.kspg.co/2015/3/2003d3a1-1190-46e1-8a22-88751f29a624.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('323', 'cl000001', 'u0000003', 'ពេជ្រ ម្ទេសខ្មាំង និង​ថៃ​មិន​បាន​ក្តៅ​សាច់​ក្នុង​ព្រឹត្តិការណ៍​ប្ល៊េ​ក​ខេត', 'រាជធានី​ភ្នំពេញ ៖ កាលពី​យប់​ថ្ងៃ​ទី​១ មីនា កីឡាករ​ជើង​ខ្លាំង​២​រូប​បាន​ប៉ះ​ប្រកួត​ជា មួយ​កីឡាករ​ថៃ នៅ​ក្នុង​ព្រឹត្តិការណ៍ «​កំពូល​អ្នក​ប្រដាល់​ប្ល៊េ​ក​ខេត​» របស់​ស្រា​បៀរ​ប្ល៊េ​ក​ខេត', 'http://kohsantepheapdaily.com.kh/article/115776.html', 'http://i.kspg.co/2015/3/a34eddf7-93df-4a85-9f6c-3632274f9a03.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('324', 'cl000001', 'u0000003', 'ហ៊ុន ឈុ​ន​ហេង និង​ឡៅ ច​ន្ទ្រា សុទ្ធតែ​បាក់មុខ​ក្រោម​ដៃ​កីឡាករ​ថៃ​ដូច​គ្នា', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​ប្រដាល់​ជា​លក្ខណៈ​មិត្តភាព​អន្តរជាតិ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​អប្សរា ក្រោម​ការ​ជួយ​ឧបត្ថម្ភ​ពី​ណា​ហ្គា​វើ​ល​រៀង​រាល់​មួយ​ខែ​ម្តងនោះ គឺ​ទទួល​បានការ​គាំទ្រ​យ៉ាង​ខ្លាំង', 'http://kohsantepheapdaily.com.kh/article/115720.html', 'http://i.kspg.co/2015/3/11bd26f6-9983-4839-bfd6-b0b5d640c8c7.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('325', 'cl000001', 'u0000003', 'មជ្ឈមណ្ឌល​ហ្វឹកហ្វឺន​កីឡា​ជាតិ​ធ្វើ​ពិធី​សំណេះសំណាល​ជាមួយកីឡាករ កីឡាការិនី​មុន​ពេល​ចេញ​ទៅ​ហ្វឹកហាត់​នៅ​មី​យ៉ា​ន់​ម៉ា និង​ថៃ', 'រាជធានី​ភ្នំពេញ ៖ មជ្ឈមណ្ឌល​ហ្វឹកហ្វឺន​កីឡា​ជាតិ កាលពី​ថ្ងៃ​សុក្រ ទី​២៧ កុម្ភៈ កន្លង​មកនេះ​បាន​រៀបចំ​ពិធី​សំណេះសំណាល​មួយ​រវាង​មន្ត្រី​អគ្គនាយកដ្ឋាន​អប់រំ​កាយ និង​កីឡា', 'http://kohsantepheapdaily.com.kh/article/115718.html', 'http://i.kspg.co/2015/3/94c2fab6-9736-4644-b286-5fa05b4c7512.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('326', 'cl000001', 'u0000003', 'សរ រាជសីហ៍ និង​ផុ​ន ផា​ន់​ណា បង្កើន​សន្ទុះ​លើ​សង្វៀន', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ក្នុង​ព្រឹត្តិការណ៍​ប្រដាល់​ដណ្ដើម​ខ្សែក្រវាត់ K-Cement កាលពី​ថ្ងៃ​ទី​២៨ កុម្ភៈ ឆ្នាំ​២០១៥ កីឡាករ​២​រូប គឺ​សរ រាជសីហ៍ និង​កីឡាករ​ផុ​ន ផា​ន់​ណា បាន​ក្លាយជា​គូ​ឯក​ទទួល', 'http://kohsantepheapdaily.com.kh/article/115716.html', 'http://i.kspg.co/2015/3/1ad0ee8d-7fbe-4e82-a19b-b60438cac5e1.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('327', 'cl000001', 'u0000003', 'អ៊ុំ ចំរើន ផ្តួល​កីឡាករ​ថៃ​ក្នុង​ទឹក​ទី​២', 'រាជ​ធានី​ភ្នំពេញ​ ៖ គូអន្តរ​ជាតិ​ចុង​ក្រោយ​ កីឡាករ អ៊ុំ ចំរើន​ ផ្តួល​កីឡាករថៃ​ឈ្មោះ គៀង ឌា​ឡិក ក្នុង​ទឹក​ទី​២។', 'http://kohsantepheapdaily.com.kh/article/115695.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('328', 'cl000001', 'u0000003', 'ផន ផល្លា ចាញ់​ពិន្ទុ​កីឡាករ​ថៃ​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ គូអន្តរ​ជាតិ​ទី​២ កីឡាករ​ផន ផល្លា ចាញ់​ពិន្ទុ​កីឡាករ​ថៃ ឈ្មោះ ណុង អ៊ូ ។', 'http://kohsantepheapdaily.com.kh/article/115692.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('329', 'cl000001', 'u0000003', 'ម៉ន សាម៉េត សន្លប់​ក្រោម​ដៃ​ថៃ​ក្នុង​ទឹក​ទី​៣នៅ​សង្វៀន​អាស៊ី​អាគ្នេយ៍​ព្រឹក​នេះ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ គូអន្តរ​ជាតិ​ទី​១​ក្នុង​ការ​ប្រកួត​ប្រដាល់​អន្តរ​ជាតិ​រៀង​រាល់​២​សប្តាហ៍​ម្តង​នៅ​សង្វៀន​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​នា​ព្រឹក​ថ្ងៃ​ទី​១ មីនា នេះ កីឡាករ ម៉ន សាម៉េត សន្លប់​', 'http://kohsantepheapdaily.com.kh/article/115686.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('330', 'cl000001', 'u0000003', 'ពុធ ឆាយ​រិ​ទ្ធី ណាត់​ប្រកួត​សាក​កម្លាំង​ជាមួយ វ៉ែន សុវណ្ណ នៅ​លើ​សង្វៀន​ស៊ី​ធី​អិន​ថ្ងៃអាទិត្យ​ស​ប្តា​ហ៍​នេះ', 'រាជធានី​ភ្នំពេញ ៖ ពុទ្ធ ឆាយ​រិ​ទ្ធី មក​ពី​ក្លិប​ពុទ្ធ​សេរី​វង្ស​ព្រែក​តា​ទែន បាន​ណាត់ វ៉ែន សុវណ្ណ មក​ពី​ក្លិប​កងកេង​កីឡា​ខេត្តបាត់ដំបង ប្រកួត​សាក​កម្លាំង​គ្នា​នៅ​លើ​សង្វៀន​ទូរ​', 'http://kohsantepheapdaily.com.kh/article/115611.html', 'http://i.kspg.co/2015/2/a86a3ea3-b1e5-4df7-bec6-6f06e3e915fd.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('331', 'cl000001', 'u0000003', 'ឡៅ ច​ន្ទ្រា និង​ហ៊ុន ឈុ​ន​ហេង ស្វាគមន៍​អ្នក​ប្រដាល់​ថៃ​នៅ​លើ​សង្វៀន​អប្សរា​ថ្ងៃ​សៅរ៍​ស​ប្តា​ហ៍​នេះ', 'រាជធានី​ភ្នំពេញ ៖ ព្រឹត្តិការណ៍​នៃ​ការ​ប្រកួត​ប្រដាល់​ក្បាច់គុន​បុរាណ​ខ្មែរ​ជា​លក្ខណៈ​អន្តរជាតិ​ក្រោម​ការ​ជួយ​ឧបត្ថម្ភ​ពី​ណា​ហ្គា​វើ​ល​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​អប្សរា​បុ​៉​ស្តិ៍​លេខ​', 'http://kohsantepheapdaily.com.kh/article/115610.html', 'http://i.kspg.co/2015/2/b3e88445-356c-48e0-ab0c-6265bcb579f0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('332', 'cl000001', 'u0000003', 'ឃី​ម បូ​រ៉ា ខ្លាច​ចាញ់​ជុំ​ទី​3 ខណៈ ម៉​ន វិ​រៈ ចង់​ឈ្នះ​គូប្រកួត', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​នៅ​ចុង​ស​ប្ដា​ហ៍​ថ្ងៃ​សៅរ៍ និង​ថ្ងៃអាទិត្យ​នេះ​មាន​ចំណុច​គួរ​ឲ្យ​ចាប់អារម្មណ៍​ទៀត​ហើយ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍ ដោយសារ​កីឡាករ​', 'http://kohsantepheapdaily.com.kh/article/115608.html', 'http://i.kspg.co/2015/2/9a587f37-490d-452c-908f-4c4e440ed75a.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('333', 'cl000001', 'u0000003', 'ម៉​ន សា​ម៉េ​ត ផ​ន ផល្លា និង​អ៊ុំ ចំរើន ត្រៀម​ស្វាគមន៍​ថៃ​ដោយ​កណ្តាប់ដៃ​លើ​សង្វៀន​តែ​មួយ', 'រាជធានី​ភ្នំពេញ ៖ ចាត់​ទុក​ថា ជា​លើក​ទី​១ ដែល​កីឡាករ​ជើង​ខ្លាំង​ដល់​ទៅ​៣​រូប ដែល​ទើប​ឡើង​សន្ទុះ​ដូច​គ្នា ត្រូវ​គេ​រៀប​ឲ្យ​ជួប​ប្រកួត​ជាមួយ​កីឡាករ​ថៃ​ក្នុង​ថ្ងៃ និង​សង្វៀន​តែ', 'http://kohsantepheapdaily.com.kh/article/115606.html', 'http://i.kspg.co/2015/2/8b055ce9-358b-470e-9978-feb8415a41c4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('334', 'cl000001', 'u0000003', 'បាល់ទាត់​អា​ល្លឺ​ម៉​ង់​ប៊ុ​ន​ដេ​ស្លី​ហ្គា​ជុំ​ទី​24​សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះ​មាន​8​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​អា​ល្លឺ​ម៉​ង់​ប៊ុ​ន​ដេ​ស្លី​ហ្គា​ជុំ​ទី 24 នៅ​ចុង​ស​ប្តា​ហ៍​នេះ​មាន 8 ប្រកួត ។ ក្រុម Bayern, Gladbach, Leverkusen មាន​ប្រៀប​ជាង​គូប្រកួត ។', 'http://kohsantepheapdaily.com.kh/article/116135.html', 'http://i.kspg.co/2015/3/7b7834b6-caeb-44bb-b030-803af1f2c897.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('335', 'cl000001', 'u0000003', 'បាល់ទាត់​អេ​ស្ប៉ា​ញ​ព្រី​មេ​រ៉ា​លី​ហ្គា​ជុំ​ទី​26​សម្រាប់​ថ្ងៃ​សៅរ៍​និង​ថ្ងៃអាទិត្យ', 'ការ​ប្រកួត​បាល់ទាត់​អេ​ស្ប៉ាញ​ព្រី​មេ​រ៉ា​លី​ហ្គា​ជុំ​ទី 26 នៅ​ចុង​ស​ប្តា​ហ៍​នេះ​មាន 8 ប្រកួត ។ ក្រុម Barcelona ប៉ះ​គូប្រកួត​ខ្សោយ ខណៈ Real M, Leverkusen មាន​ប្រៀប​ជាង​ម្ចាស់ផ្ទះ ។', 'http://kohsantepheapdaily.com.kh/article/116133.html', 'http://i.kspg.co/2015/3/63d641a5-cb91-4a3a-8e1c-1ad22d6c9a05.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('336', 'cl000001', 'u0000003', 'ព្យាករណ៍​បាល់ទាត់​បារាំង​ឡឺ​សំ​ផ្ចូ​ណា​ជុំ​ទី​28​សម្រាប់​ថ្ងៃនេះParis SG,St Etienne,Rennes សង្ឃឹម​រកបាន​3​ពិន្ទុ', 'ការ​ប្រកួត​បាល់ទាត់​បារាំង​ឡឺ​សំ​ផ្ចូ​ណា​សម្រាប់​រាត្រី​ចុង​ស​ប្តា​ហ៍​នេះ​មាន 9 ប្រកួត ។ ក្រុម Paris SG, St Etienne, Rennes សង្ឃឹម​រកបាន​បី​ពិន្ទុ​នៅ​ក្នុង​ទឹកដី ។', 'http://kohsantepheapdaily.com.kh/article/116132.html', 'http://i.kspg.co/2015/3/2d69897b-8181-4373-a800-b9f49aa66335.jpg?w=285&h=170&mode=carve', '20150308');
