INSERT INTO "tbnews" VALUES ('338', 'cl000001', 'u0000003', 'នៅ​នី​ហ្សេ​រី​យ៉ា​ក្រុម​បូ​កូ​ហា​រ៉ា​ម​សម្លាប់​មនុស្ស​ដល់​ទៅ​68​នាក់​ជា​ការ​សម្លាប់​រង្គាល​ថ្មី​មួយទៀត', 'មនុស្ស​យ៉ាងតិច​៦៨​នាក់ ក្នុង​នោះ​មាន​ក្មេង​ជំទង់​ជា​ច្រើន​នាក់​ផង ត្រូវ​បាន​សម្លាប់​ក្នុង​អំពើ​សម្លាប់​រង្គាល​ថ្មី​មួយ​របស់​ក្រុម​បូ​កូ​ហា​រ៉ា​ម នៅ​ភាគ​ខាងជើង​នី​ហ្សេ​រី​យ៉ា ខណៈ​ដែល​ក្រុម​អ៊ិ​ស្លា​ម​និយម​នេះ', 'http://kohsantepheapdaily.com.kh/article/116084.html', 'http://i.kspg.co/2015/3/06c7186e-440d-47c0-a1f7-013907a2a694.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('339', 'cl000001', 'u0000003', 'ការ​វិភាគ​បាល់ទាត់​ចម្រុះ​នៅ​អឺរ៉ុប​សម្រាប់​ថ្ងៃ​សុក្រ​មាន​12​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​សុក្រ​នេះ​មាន 12 ប្រកួត ។ ក្រុម Fulham, Marseille និង Hertha ជា​ក្រុម​ល្អ ។  អង់គ្លេស​ឆេ​ម​ពាន​ស៊ី​ប Fulham (20) V Bournemouth (4)', 'http://kohsantepheapdaily.com.kh/article/116053.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('340', 'cl000001', 'u0000003', 'បាល់ទាត់​បារាំង National សម្រាប់​រាត្រី​ថ្ងៃនេះ​មាន​9​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​បារាំង National នៅ​រាត្រី​ថ្ងៃ​សុក្រ​នេះ​មាន 9 ប្រកួត ។ ក្រុម Red Star, Avranches សង្ឃឹម​រកបាន​បី​ពិន្ទុ​នៅ​ក្នុង​ទឹកដី ខណៈ Paris មាន​ប្រៀប​ជាង​ម្ចាស់ផ្ទះ ។', 'http://kohsantepheapdaily.com.kh/article/116052.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
