INSERT INTO "tbnews" VALUES ('347', 'cl000001', 'u0000003', 'ការ​ទស្សន៍​ទាយ​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​នេះ​មាន​9​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​ពុធ​នេះ​មាន 9 ប្រកួត ។ ក្រុម Bayern, Napoli ប៉ះ​គូប្រកួត​ខ្សោយ ខណៈ​ខ្លាំង​ប៉ះ​ខ្លាំង Paris SG ជួប Monaco។', 'http://kohsantepheapdaily.com.kh/article/115909.html', 'http://i.kspg.co/2015/3/17b06fab-6386-47e4-875e-098cb186791a.jpg?w=285&h=170&mode=carve', '20150308');
