INSERT INTO "tbnews" VALUES ('466', 'cl000001', 'u0000003', 'យល់​ច្រឡំ', 'យុវ​សិស្ស​ម្នាក់​ចូល​ហាង​សម្លៀក​បំពាក់​ទិញ​ស្រោមដៃ​មួយគូ​ផ្ញើ​សង្សារ​សម្រាប់​ជា​កាដូ​ថ្ងៃ​ខួបកំណើត​នាង​។ នាយ​ពឹង​នារី​លក់​ខោអាវ​ពាក់​សាក​ឱ្យ​ខ្លួន​មើលឃើញ​ថា​សម​មែន ក៏​យល់ព្រម​ទិញ​យក ដោយ​សុំ​ឱ្យ​វេច​ខ្ចប់​ស្អាតបាត​ខ្ចប់​ជាមួយ​សំបុត្រ​មួយ​ផង​។ ប៉ុន្តែ​ដោយ​ហេតុ​ភ្ញៀវ​ទិញ​របស់​ច្រើន​នាក់​ពេក នារី​លក់​ខោអាវ​ប្រញាប់ប្រញាល់​ក៏​ខ្ចប់​ច្រឡំ​ខោ​ស្លុ​ី​ប​របស់​អ្នក​ផ្សេង​ទៅ​វិញ​។', 'http://kohsantepheapdaily.com.kh/article/106528.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
