INSERT INTO "tbnews" VALUES ('469', 'cl000001', 'u0000003', 'ដើម​ត្នោត​ចម្លែក​​មាន​ដើម​តែ​មួយ​ដុះ​បែកមែក​៩​ដើម​ ​ជំនឿ​ថា​មាន​ឬ​ទ្ធិបារមី', 'ខេត្ត​កំពង់​​ឆ្នាំង ៖ មិន​មែន​ចម្លែក​ពេក​ទេ ព្រោះ​កោះសន្តិភាព​ធ្លាប់​បាន​ចុះ​ផ្សា​យ​ច្រើន​​ណាស់​មក​ហើយពី​អ្វី​ដែល​ប្លែក ស្រ​ដៀង​គ្នា​នឹង​អត្ថបទ​ខាងក្រោម​នេះ តែ​​ដើម្បី​ឱ្យប្រិ​យ​មិត្ត​​​បានដឹង​បន្ថែម​ទៀត​ពីធម្មជាតិ​ដែល​ប្លែក​នោះ គឺ​ដើម​ត្នោត​មួយ​ដើម​មាន​មែក​ដល់​ទៅ​៩ កាល​មុន​ធ្លាប់​ឃើ​មាន​បែក​ជា៤​ដើម​ដែរ ។  ដើម​​ត្នោត​ដ៏​កម្រ​មាន​និងចម្លែក​​​មួយ​ដើម​នេះ​ដុះ​នៅ​ក្នុងភូមិ​ជំទាវ​សុខ ឃុំ​ច្រេស ស្រុក​កំពង់ត្រឡាច​ ​។ ', 'http://kohsantepheapdaily.com.kh/article/105913.html', 'http://i.kspg.co/2014/10/458ce92d-e026-47f0-bdf2-6cb1f8217d8c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('470', 'cl000001', 'u0000003', 'ឪពុក​លើស​កូន​មួយ​ដង', 'ឪពុក​បន្ទោស​កូនប្រុស ៈ -​កូន​ឯង​កាន់តែ​ធំ​កាន់តែ​រឹងក្បាល លែង​ស្តាប់​សម្តី​ពុក​គ្រប់យ៉ាង ។ ពុក​ប្រាប់​ឱ្យ​យប់​ឡើង​ខំ​មើល​មេរៀន តែ​យប់​ណា​ក៏​កូន​ឯងរ​ត់ទៅ​លេង​នៅ​ម្តុំ​ផ្ទះ​ខាងត្បូង​សាលា​។ ពុក​ទៅ​លេងអុក​ដើរ​កាត់​តាម​នោះ​១០​ដង ឃើញ​កូន​ឯង​ដល់​ទៅ​៩​ដង ។ ម៉េច​បានជា​អុ​ី​ចឹ​ង ?', 'http://kohsantepheapdaily.com.kh/article/105887.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('471', 'cl000001', 'u0000003', 'ទួល​អភិរក្ស​សត្វ​ស្លាបពាន់តា​ឡីបឹង​ស្នេ​ហ៌ ​ភ្ញៀវ​ទេសចរ​អាច​ទស្សនាសត្វ​ស្លាប​ជា​ច្រើន​ប្រភេទ នៅ​មុន​ពេល​ថ្ងៃ​លិច', 'ខេត្ត​ព្រៃវែង ៖ ទួល​ពាន់តា​ឡី គឺជា​ទួល​ដ៏​ធំ​មួយ​​ដុះ​នៅ​ចំ​​កណ្តាល​បឹង​ស្នេ​ហ៍ ក្នុង​ឃុំ​ធា​យ ស្រុក​បាភ្នំ ខេត្តព្រៃវែង ដែលកាលពីមុន​ទួល​នេះ​គេ​ឃើញ​មាន​វ៉ូង​សត្វ​​​ស្លាប ចុះ​មក​រក​ចំណី​នៅ​តាម​មាត់ទឹក តែ​នៅ​ពេល​ល្ងាច​មួយ​ភ្លែ​ត​ៗ​តែ​ប៉ុណ្ណោះ រួច​ក៏​នាំ​គ្នា​ហើរ​ចេញ​ទៅ​វិញ​អស់ ដោយសារ​មានការ​គំរាមកំហែង ពី​សំណាក់​​អ្នកស្រុក​ដែល​នៅ​ក្បែ​ៗ​នោះ មិន​អាច​ឱ្យ​ពួក​វា​ធ្វើ​សំបុក​រស់នៅ​លើ​ទួល​នេះ​បាន​។ ', 'http://kohsantepheapdaily.com.kh/article/105837.html', 'http://i.kspg.co/2014/9/61cf7212-e1d2-4094-b38f-3b8dbb3a8e24.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('472', 'cl000001', 'u0000003', 'ដឹង​មុន', 'ក្មេង​ប្រុស​ពីរ​នាក់​និយាយ​គ្នា ៈ -​ពេល​ប្រឡង​បាក់​ឌុ​ប បងស្រី​អញ ដឹង​វិញ្ញាសា​មុន​៣០​នាទី តែ​ទីបំផុត​នៅ​តែ​ធ្លាក់​។ -​ត្រូវ​ធ្លាក់​ទោះ​ដឹង​វិញ្ញាសា​មុន​រាប់​ខែ​ក៏​ធ្លាក់​ដែរ​។ ឧទាហរណ៍ បងស្រី​អញ​តែ​ម្តង​, គាត់​ដឹង​មុន​គ្រប់​វិញ្ញាសា​ទាំងអស់ ហើយ​ដឹង​មុន​', 'http://kohsantepheapdaily.com.kh/article/105731.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
