INSERT INTO "tbnews" VALUES ('475', 'cl000001', 'u0000003', 'នៅ​តែ​រួម​តុ', 'ឪពុក​ដឹក​ដៃ​កូនប្រុស​អាយុ​១០​ឆ្នាំ ចេញពី​ថ្នាក់រៀន​ត្រឡប់​ទៅ​ផ្ទះ​, តាម​ផ្លូវ​ជួប​មិត្ត​ចាស់​ម្នាក់ ។ បុរស​ទាំង​២​ស្រែកហៅ​គ្នា​ទៅ​វិញ​ទៅ​មក​។ ឃើញ​ដូច្នេះ​កូនប្រុស​សួរ​ឪពុក -​ប៉ា និង​គាត់​ត្រូវជា​មិត្តភក្តិ​មែន​ទេ?', 'http://kohsantepheapdaily.com.kh/article/104443.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
