INSERT INTO "tbnews" VALUES ('479', 'cl000001', 'u0000003', 'ព័ត៌មាន​ប្លែក​ៗ​កើតឡើង​លើ​ពិភពលោក', '១-​ដេក​មើល​កុន អតិថិជន​ដេក​ទស្សនា​ភាពយន្ត​ក្នុង​រោង «​គី​ម៉ូ​ស្តា​រ៍ ដឺ​ឡាក់​» នៅ​មជ្ឈមណ្ឌល​ពាណិជ្ជកម្ម​ក្រុង​មូ​ស្គូ ប្រទេស​រុ​ស្ស៊ី និង​ចាត់ទុកជា​រោង​ភាពយន្ត​ដំបូង​នៅ​ក្នុង​ប្រទេស​ដែល​បង្កើត​ជា​គ្រែ​ដេក​បាន ។ ', 'http://kohsantepheapdaily.com.kh/article/115497.html', 'http://i.kspg.co/2015/2/6ff03c23-ab96-498d-b749-e01ead97ef1d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('480', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​ព័ត៌មាន​ចរាចរណ៍​ចម្លែក​នៅ​លើ​ពិភពលោក', 'ឪពុក​ចង​កូន​ភ្ជាប់​ចង្កូតរថយន្ត​បើកបរ​គ្រវី​ទៅ​មក ៖ ទីភ្នាក់ងារ​ព័ត៌មាន Apple Daily ផ្សាយ​ថា បណ្តា​អ្នក​ប្រើ​ប្រព័ន្ធ Facebook នៅ​តៃវ៉ាន់ បាន​រិះគន់​លើ​វី​ដេ​អូ​ឃ្លី​ប ដែល​ឪពុក​ប្រើ​ខ្សែ​ចង កូន​តូច របស់​ខ្លួន​', 'http://kohsantepheapdaily.com.kh/article/115213.html', 'http://i.kspg.co/2015/2/789a7642-e53e-4590-970b-25281d708fec.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('481', 'cl000001', 'u0000003', 'ឆ្នាំមមែ​និយាយ​រឿង​ប្លែក​ៗ​ជុំវិញ​ពពែ', '១-​ពពែ​ឡើង​ចំណោត ពពែ​ភ្នំ​អាល់​ពី​ន (Alpine) ជា​អ្នកមាន​ជំនាញខ្ពស់​ខាង​ឡើង​ចំណោត​ពេល​វា​ចាត់​ទុក​ទំនប់​ទឹក​កម្ពស់​៤៩​ម៉ែត្រ​នៅ​ឧទ្យាន​ជាតិ Gran Paradiso ភាគ​ខាងជើង​ប្រទេស​អីុតា​លី​ជា​ផ្ទះ​របស់​វា​។ ', 'http://kohsantepheapdaily.com.kh/article/115133.html', 'http://i.kspg.co/2015/2/2cb701a0-3021-4b96-8456-e6e093b49e93.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('482', 'cl000001', 'u0000003', 'សកម្មភាព​ប្លែក​និង​កម្រ​មាននៅ​លើ​ពិភពលោក', '១-​សម្រាម​សមុទ្រ សិស្ស​ហ្ស៊​ក​ដា​នី ឈរ​មើល​គំនរ​សម្រាម​ប្លា​ស្ទិ​ក​នៅ​ក្នុង​ពិ​ព​រ័​ណ៍ នៅ​ក្រុង​អាម​មា​៉​ន​រៀបចំ​ឡើង​ដើម្បី​ឱ្យ​ពលរដ្ឋ​ទូទៅ​យល់​ដឹង​ពី​ការ​អភិរក្ស​ធម្មជាតិ និង​ការពារ​បរិស្ថាន​នៅ​ក្នុង​សមុទ្រ ។', 'http://kohsantepheapdaily.com.kh/article/114691.html', 'http://i.kspg.co/2015/2/87e8486f-f81c-4069-9be3-786b4c74a07d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('483', 'cl000001', 'u0000003', 'រូបភាព​ប្លែក​ៗ​មួយ​ចំនួន​គួរ​ឱ្យ​ចាប់អារម្មណ៍', '១-​រូបសំណាក​លោកពូ​ទី​ន រូបសំណាក​លោក «​ពូ​ទី​ន អេ​គ្រី​វ៉េ​ន​»​ឬ «​ពូ​ទី​ន​អ្នកនិពន្ធ​» ស្នាដៃ​របស់​លោក​គ្រី​ស្ទៀ​ន បែ​ល​លី ជាង​ច្នៃ​រូប​ទីងមោង​ជនជាតិ​ស្វ៊ី​ស រួម​ជាមួយ​បទ​ចម្រៀង ក្រោម​ចំណងជើង​ថា «​សន្ធិសញ្ញា​នៃ​ការ​ទទួលយក​សាធារណរដ្ឋ​គ្រី​មេ', 'http://kohsantepheapdaily.com.kh/article/114333.html', 'http://i.kspg.co/2015/2/d889a2ff-6c7c-447b-9913-e794a5216588.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('484', 'cl000001', 'u0000003', 'ព្រឹត្តិការណ៍​ចរាចរណ៍​ប្លែក​ៗ​កើត​មាននៅ​លើ​ពិភពលោក​សម្រាប់​ឆ្នាំ​2015', '*​បើករថយន្ត​ដេញ​បុក​ក្របី​ឡើងក​ដែល​វ័ធ​មនុស្ស​ផ្អើល​នៅ​កណ្ដាល​ក្រុង​ឈើ​ង​ទូ ៖ ទីភ្នាក់ងារ​សៀង​ហៃ​ដេ​លី​របស់​ចិន​កាលពី​ថ្ងៃ​ទី​២ កុម្ភៈ ផ្សាយ​ថា ក្របី​ឡើងក​មួយ​ក្បាលមាន​ទម្ងន់​៧០០​គីឡូក្រាម​បាន​ដេញ​វ័ធ​ពលរដ្ឋ​', 'http://kohsantepheapdaily.com.kh/article/114261.html', 'http://i.kspg.co/2015/2/8f5bfcd8-8094-4dbc-9aaf-1a1382647622.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('485', 'cl000001', 'u0000003', 'រឿង​រ៉ា​វ​កម្រ​មាននៅ​លើ​ពិភពលោក​ក្នុង​ពេល​បច្ចុប្បន្ន', '១-​ក្រុម​និយម​ស្រាត​ខ្លាច​ឆ្លង​ជំងឺ ក្រុម​អ្នក​ស្រាត​ដើរ​លំហែកាយ​នៅ​មាត់សមុទ្រ​ម៉ា​ស់​ស្ប៉ា ឡូ​ម៉ា ប្រទេស​អេ​ស្ប៉ា​ញ ភ្ញាក់ផ្អើល​ដោយ​មាន​ក្រុម​ជន​អាហ្វ្រិក​ជិះ​នាវា​ចត​នៅ​ឆ្នេរ​ខ្សាច់ និង​មាន​អ្នកខ្លះ​កើត​ជំងឺ​ធ្វើ​ឱ្យ​អ្នក​និយម​ស្រាត​ភ័យ​ខ្លាច​ឆ្លង ។', 'http://kohsantepheapdaily.com.kh/article/114169.html', 'http://i.kspg.co/2015/2/5329835e-a33b-4167-9254-bd52175cbdac.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('486', 'cl000001', 'u0000003', 'បុរស​ស៊ូ​ដង់​យក​ពពែ​ធ្វើ​ប្រពន្ធ កើត​បានកូន​មួយ', 'សារព័ត៌​​មាន​បរទេស​កាលពី​ពេល ថ្មក̡ߗ​នេះ  ផ្សាយ​ថា​លោក Tombe ជនជាតិ​ស៊ូ​ដង់​បាន​យក​ពពែ​ធ្វើ​ជា​ប្រពន្ធ ក្រោយ​ពី​អ្នកស្រុក​ប្រទះ​ឃើញ​គេ​លួច​រួមរ័ក​ជាមួយ​សត្វ​ពពែ​នោះ ។', 'http://kohsantepheapdaily.com.kh/article/114135.html', 'http://i.kspg.co/2015/2/ce6f8df5-1bc4-47f5-8150-1e2fdddce708.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('487', 'cl000001', 'u0000003', 'រឿងហេតុ​ប្លែក​ៗ​នៅ​លើ​ពិភពលោក', '១-​រថភ្លើង​តិច​សាស់ រថភ្លើង​ល្បឿន​លឿԢINSERT INTO "tbnews" VALUES ('488', 'cl000001', 'u0000003', 'នារី​អា​មេ​រិ​ក​អាយុ​40​ឆ្នាំ​រក​គូ​មិន​បាន​រៀប​ការ​ម្នាក់ឯងគ្រោង​មក​ក្រេបទឹក​ឃ្មុំ​នៅ​ប្រទេស​កម្ពុជា និង​ឡាវ', 'សារព័ត៌មាន​បរទេស កាលពី​ថ្ងៃ​ទី​២៨ មក​រា ២០១៥ ផ្សាយ​ថា ស្ត្រី​ម្នាក់​បាន​បញ្ចប់​ក្តី​ប្រាថ្នា​រៀប​ការ​ជាមួយ​បុរស ដោយ​សម្រេចចិត្ត​រៀប​ការ «​តែ​ម្នាក់ឯង​» វិញ ព្រោះ​មិន​អាច​រក​បុរស​ធ្វើ​ជា​គូស្រករ​បាន', 'http://kohsantepheapdaily.com.kh/article/113808.html', 'http://i.kspg.co/2015/1/c2e00d70-863a-4667-8fb8-f4037c0dd934.jpg?w=285&h=170&mode=carve', '20150308');
