INSERT INTO "tbnews" VALUES ('492', 'cl000001', 'u0000003', 'ច្បាប់​ចម្លែក​ៗ​ទាក់ទិន​ការ​រួម​ភេទ​នៅ​លើ​ពិភពលោក', 'កោះសន្តិភាព​សូម​បង្ហាញ​ជូន​ច្បាប់​ចម្លែក​ៗ​ទាក់ទិន​ការ​រួម​ភេទ​របស់​ប្រទេស​មួយ​ចំនួន​លើ​សាកលលោក​យោង​តាម​គេហទំព័រ The Richest ។', 'http://kohsantepheapdaily.com.kh/article/112933.html', 'http://i.kspg.co/2015/1/01e8cd89-c39b-430d-8357-357ca84bc41b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('493', 'cl000001', 'u0000003', 'រឿងរ៉ាវ​ប្លែក​ៗ​កើត​មាននៅ​លើ​ពិភពលោក', '១-​ក្មេង​ប្រុស​ខ្សែក្រវាត់​ខ្មៅ​តេ​ក្វា​ន់​ដូ ក្មេង​ប្រុស​អា​ឡិច​ក្រូ​ស ជនជាតិ​អង់គ្លេស អាយុ​៦​ឆ្នាំ ក្លាយជា​អ្នក​ហាត់​វិជ្ជា​គុន​តេ​ក្វា​ន់​ដូ ខ្សែក្រវាត់​ខ្មៅ ខណៈ​ពេល​លោកស្រី​សង់​ដ្រា ត្រូវជា​ម្តាយ​បញ្ជាក់​', 'http://kohsantepheapdaily.com.kh/article/112852.html', 'http://i.kspg.co/2015/1/a8e44fba-204e-4422-b648-57a3a6df32ec.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('494', 'cl000001', 'u0000003', 'សញ្ញា​សម្គាល់​ភ្នែក​ម៉្ញូប​មើល​ជិត​ឃើញច្បាស់​មើល​ឆ្ងាយ​ព្រាល', 'ភ្នែក​ម្ញ៉ូ​ប​មិន​អាច​មើល​ឆ្ងាយ​បាន​ច្បាស់​តែបើ​មើល​ជិត​ឃើញច្បាស់​ដោយសារ​រូបភាព​មក​ផ្ដុំ​គ្នា​នៅ​មុខ​សរសៃ​បាត​ភ្នែក​(​រ៉េ​ទី​ន​) ។ ការ​ចាំង​ពន្លឺ​ក៏​ជា​មូលហេតុ​មួយ​ធ្វើ​ឱ្យ​ភ្នែក​មិន​អាច​ចាប់​ពន្លឺ', 'http://kohsantepheapdaily.com.kh/article/115825.html', 'http://i.kspg.co/2015/3/8f3a57fd-852d-4ea6-85b3-778713adef43.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('495', 'cl000001', 'u0000003', 'អ្នកមាន​ជំងឺថ្លើម​ប្រភេទ​B​ច្រើន​តែ​ហួសពេល​ព្រោះ​មិន​ចេញ​រោគ​សញ្ញា', 'មាន​តែ​ការ​ពិនិត្យ​ឈាម​មួយ​គត់ ដែល​អាច​បញ្ជាក់​ប្រាប់​អ្នក​ថា មាន​ឬ​គ្មាន​មេរោគ​រលាក​ថ្លើម​ប្រភេទ​បេ ។ ក្នុង​ករណី​ខ្លះ​ជំងឺ​រលាក​ថ្លើម​ប្រភេទ​បេ​នេះ ក៏​អាច​លេច​ចេញ​នូវ​រោគ​សញ្ញា​មួយ​', 'http://kohsantepheapdaily.com.kh/article/115824.html', 'http://i.kspg.co/2015/3/901e4794-89a9-4f33-ae9b-2a913b3ddcd5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('496', 'cl000001', 'u0000003', 'ធាត់​លើស​ទម្ងន់​ក្នុង​វ័យ​កុមារភាព​ប្រឈម​នឹង​ជំងឺថ្លើម បេះដូង​និង​លើសឈាម', 'ក្រុម​អ្នក​វិទ្យាសាស្ត្រ​មក​ពី​សាកលវិទ្យាល័យ កាលី​ហ្វ័​រ​ញ៉ា សហរដ្ឋអាមេរិក​បាន​ព្រមាន​ថា សភាព​ធាត់​ខ្លាំង អាច​បណ្តាល​ឱ្យ​ក្មេង​ប្រឈម​ខ្ពស់​នឹង​ជំងឺ​មួយ​ចំនួន​ទៅ​ថ្ងៃ​ខាង​មុខ ក្នុង​នោះ​មាន​ដូច​ជា', 'http://kohsantepheapdaily.com.kh/article/115823.html', 'http://i.kspg.co/2015/3/54125f20-84aa-4b0b-b2e4-1b0e40023425.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('497', 'cl000001', 'u0000003', 'ទទួលទាន​ទឹក​ផ្លែ​ឈើ​ស្រស់​រាល់​ព្រឹក​បង្កើន​ហានិភ័យ​ដល់​សុខភាព', 'បុគ្គល​ដែល​ទទួលទាន​ទឹក​ផ្លែ​ឈើ​ស្រស់​ទៀងទាត់​រៀង​រាល់​ព្រឹក អាច​បណ្តាល​ឱ្យ​សម្ពាធ​ឈាម​ឡើង​ខ្ពស់​ធៀប​នឹង​បុគ្គល​ដែល​ទទួលទាន​ម្តងម្កាល នេះ​បើ​យោង​តាម​របកគំហើញ​ថ្មី​មួយ ។ ', 'http://kohsantepheapdaily.com.kh/article/115822.html', 'http://i.kspg.co/2015/3/eeb8d02d-c466-4555-9138-4db114252ee2.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('498', 'cl000001', 'u0000003', 'ពិត​ទេ​ដែល​ការ​ប្រើ​ថ្នាំ​Niacin​ច្រើន បង្កើន​អត្រា​ស្លាប់​កម្រិត​ខ្ពស់?', 'ការ​សិក្សា​ស្រាវជ្រាវ​ជា​លក្ខណៈ​អន្តរជាតិ​ដ៏​ធំ​មួយ​បាន​បង្ហាញ​ថា បុគ្គល​លើស​ខ្លាញ់​កូ​ឡេ​ស្តេ​រ៉ូ​ល​ដែល​ប្រើ​ឱសថ Niacin​ច្រើន មិន​ត្រឹមតែ​មិន​អាច​ជួយ​កាត់​បន្ថយ​ហានិភ័យ​ស្លាប់​ពី​ជំងឺ​ដាច់​សរសៃ', 'http://kohsantepheapdaily.com.kh/article/115821.html', 'http://i.kspg.co/2015/3/27b4f262-4862-4eca-911f-eb1f5541ba93.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('499', 'cl000001', 'u0000003', 'អត្រា​កុមារ​កើត​ជំងឺ​ទឹកនោមផ្អែម​នៅ​ប្រទេស​ឥណ្ឌា​កំពុង​កើនឡើង', 'អត្រា​អ្នក​កើត​ជំងឺ​ទឹកនោមផ្អែម​ក្នុង​វ័យ​កុមារភាព​កំពុង​ក្លាយទៅជា​បញ្ហា​សុខភាព​ដ៏​ធ្ងន់ធ្ងរ​មួយ​នƢINSERT INTO "tbnews" VALUES ('500', 'cl000001', 'u0000003', 'អត្ថប្រយោជន៍​ខាត់ណា​ខៀវ​ចំពោះ​ស្បែក', 'ការ​សិក្សា​ថ្មី​មួយ​បាន​បង្ហាញ​ថា បន្លែ​ស្ពៃ​ខាត់ណា​ខៀវ​មាន​ប្រសិទ្ធភាព​ជួយ​កាត់​បន្ថយ​មហារីក​ស្បែក​បាន ដោយ​គ្រាន់តែ​យក​វា​ទៅ​ជូត​លើ​ស្បែក​ដោយ​ផ្ទាល់​ប៉ុណ្ណោះ ។ ', 'http://kohsantepheapdaily.com.kh/article/115818.html', 'http://i.kspg.co/2015/3/e7f7573e-ccd4-4df3-b30b-4e423e2d505f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('501', 'cl000001', 'u0000003', 'ទទួលទាន​ត្រសក់​ផ្តល់​អត្ថប្រយោជន៍​ច្រើន​យ៉ាង​ដល់​សុខភាព', 'ត្រសក់​ជា​បន្លែ​ដែល​គេ​និយម​ដាំ​ច្រើន​ជាងគេ​បំផុត​លំដាប់​ទី​៤​ក្នុង​ពិភពលោក និង​ត្រូវ​បាន​ដាក់​បញ្ចូល​ទៅ​ក្នុង​ប្រភេទ​អាហារ​ឈរ​នៅ​លំដាប់​លើ​គេ​ខាង​ការ​ផ្តល់​អត្ថប្រយោជន៍ដល់​សុខភាព​ជា​ទូទៅ ។ ', 'http://kohsantepheapdaily.com.kh/article/115817.html', 'http://i.kspg.co/2015/3/5f897efc-0d3c-4392-8fc3-6e0d455be852.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('502', 'cl000001', 'u0000003', 'ជំងឺ​បេះដូង​ដំណាក់កាល​ដំបូង​អាច​បណ្តាល​ឱ្យ​ងាប់​លិង្គ', 'ការ​សិក្សា​ថ្មី​មួយ​បាន​រក​ឃើញ​ថា បុរស​កើត​ជំងឺ​សរសៃឈាម​បេះដូង​ដំណាក់កាល​ដំបូង អាច​នាំ​ទៅ​រក​ការ​ងាប់​លិង្គ​ពេល̡ހ្រោយ​ក្នុង​ជីវិត ។ លោក David  Feldman​ជំនួយការ​ស្រាវជ្រាវ', 'http://kohsantepheapdaily.com.kh/article/115816.html', 'http://i.kspg.co/2015/3/b94bbb7d-ddcf-403c-952d-ea22a07841b4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('503', 'cl000001', 'u0000003', 'របៀប​ថែរក្សា​ក្រោយ​ការ​វះកាត់​ភ្នែក​ឡើងបាយ គឺជា​រឿង​សំខាន់​ដែល​គួរ​ឱ្យ​យកចិត្តទុកដាក់', 'កាលពី​លេខ​មុន​លោក​អ្នក​បាន​ជ្រាប​ខ្លះ​ៗ​រួច​មក​ហើយ អំពី​ជំងឺ​បាយ​ភ្នែក និង​រោគ​សញ្ញា រួម​ទាំង​ការ​ព្យាបាល តែ​សម្រាប់​លេខ​នេះ កោះសន្តិភាព សូម​លើក​មក​បង្ហាញ​ពី​វិធី​ការពារ​ក្រោយ', 'http://kohsantepheapdaily.com.kh/article/115813.html', 'http://i.kspg.co/2015/3/cd061f74-1d03-4350-a7be-d267515b00ef.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('504', 'cl000001', 'u0000003', 'ប្រើប្រាស់​បណ្តាញ​ព័ត៌មាន​សង្គម​ទប់ស្កាត់ការ​ធ្លាក់​ចុះ​សុខភាព​មនុស្ស​វ័យ​ចំណាស់', 'បុគ្គល​វ័យ​ចំណាស់​គួរតែ​ប្រើប្រាស់ Facebook និង​បណ្តាញ​ព័ត៌មាន​សង្គម​ផ្សេង​ៗ​ទៀត​ខណៈ​ការ​សិក្សា​ស្រាវជ្រាវ​មួយ​បាន​រក​ឃើញ​ថា ទ​ង្វើ​បែប​នេះ​អាច​ជួយ​ទប់ស្កាត់​ការ​ធ្លាក់​ចុះ​ផ្នែក​សុខភាព ។ ', 'http://kohsantepheapdaily.com.kh/article/115811.html', 'http://i.kspg.co/2015/3/db556728-4350-42cf-810d-cfba4a82ffe5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('505', 'cl000001', 'u0000003', 'ឱសថ​ប្រភេទ​ថ្មី​សម្រាប់​ព្យាបាល​ជំងឺ​លើស​សម្ពាធ​ឈាម​ចូល​មក​ទីផ្សារ​កម្ពុជា ដើម្បី​ជួយ​គ្រូពេទ្យ​កម្ពុជា​ក្នុង​ការ​ព្យាបាល​អ្នកជំងឺ', 'រាជធានី​ភ្នំពេញ ៖ ឱសថ​ប្រភេទ​ថ្មី​ដែល​មានឈ្មោះ​ថា NATRIXAM ជា​ឱសថ​សម្រាប់​ព្យាបាល​ជំងឺ​លើស​សម្ពាធ​ឈាម ត្រូវ​បាន​ប្រកាស​លក់​ក្នុង​ប្រទេស​កម្ពុជា ជា​ផ្លូវការ ដើម្បី​ជា​ជំរើស​ក្នុង​ការ​ព្យាបាល', 'http://kohsantepheapdaily.com.kh/article/115713.html', 'http://i.kspg.co/2015/3/92724524-4bd6-42c8-8eae-2fb5ea9d193f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('506', 'cl000001', 'u0000003', 'បុគ្គល​មាន​ឈាម​ប្រភេទ A , B​និង AB​ប្រឈម​ខ្ពស់​នឹង​ជំងឺ​ទឹកនោមផ្អែម​ប្រភេទ​-2', 'បុគ្គល​ដែល​មាន​ឈាម​ប្រភេទ A, B​និង AB​គួរ​ប្រយ័ត្នប្រយែង​ឱ្យ​បាន​ខ្លាំងក្លា​បំផុត​ពី​សុខភាព​របស់​ខ្លួន និង​ឧស្សាហ៍​ពិនិត្យ​ឈាម​រក​មើល​កម្រិត​ជាតិ​ស្ករ​ខណៈ​អំណះអំណាង​វិទ្យាសាស្ត្រ​ថ្មី​មួយ​បាន​លើក​ឡើង​ថា ', 'http://kohsantepheapdaily.com.kh/article/115372.html', 'http://i.kspg.co/2015/2/104001cf-b8fc-4e7d-a43f-e31e6e44e770.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('507', 'cl000001', 'u0000003', 'ទទួលទាន​ត្រប់វែង​ផ្តល់​អត្ថប្រយោជន៍​ចំពោះ​សុខភាព​4​ចំណុច', 'មាន​ដើម​កំណើត​នៅ​ឥណ្ឌា និង​ត្រូវ​បាន​គេ​យក​មក​ដាំ​ដុះ​ជា​ដំណាំ​កសិកម្ម​ដំបូង​បំផុត​នៅ​ប្រទេស​ចិន​នា​ដើម​មជ្ឈិមសម័យ ត្រប់វែង​បច្ចុប្បន្ន​ត្រូវ​គេ​ទទួលស្គាល់​ថា ជា​ប្រភេទ​', 'http://kohsantepheapdaily.com.kh/article/115371.html', 'http://i.kspg.co/2015/2/c1f9544d-fffb-40c4-8c78-ca78bffed6df.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('508', 'cl000001', 'u0000003', 'ទទួលទាន​ប៊័រ 1​ផ្លែ​ក្នុង​មួយ​ថ្ងៃ​ជួយ​បង្ការ​លើស​ខ្លាញ់​កូ​ឡេ​ស្តេ​រ៉ូ​ល', 'អ្នក​ស្រាវជ្រាវ​អា​មេ​រិ​កបាន​អះអាង​ថា ការ​ទទួលទាន​ផ្លែ​ប៊័រ​ចំនួន​មួយ​ផ្លែ​ក្នុង​មួយ​ថ្ងៃ អាច​ជួយ​កែ​លម្អ​កម្រិត​ខ្លាញ់​កូ​ឡេ​ស្តេ​រ៉ូ​ល​អាក្រក់ ។ ពួក​គេ​បន្ត​ថា ការ​ទទួលទាន​ជា​ប្រចាំ ផ្លែ​ឈើ​ប្រភេទ​នេះ', 'http://kohsantepheapdaily.com.kh/article/115370.html', 'http://i.kspg.co/2015/2/9a570e2e-713d-414c-a0bb-625f3db5dab9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('509', 'cl000001', 'u0000003', 'ស្វែង​យល់​ពី​អត្ថប្រយោជន៍​សុខភាព​របស់​ផ្លែ​ទុរេន', 'ទុរេន​មិន​ខុស​ពី​ផ្លែ​ឈើ​ដទៃ​ទៀត​ក្នុង​តំបន់​ត្រូ​ពិ​ក​តួយ៉ាង​ដូច​ជា ចេក ផ្លែ​ប៊័រ និង​ខ្នុរ នោះ​ទេ គឺ​វា​ផ្ទុក​ទៅ​ដោយ​ថាមពល​សារធាតុ​រ៉ែ​និង​វីតាមីន​ក្នុង​កម្រិត​យ៉ាង​ខ្ពស់ ។ ទុរេន​ស្រស់​១០០​ក្រាម​ផ្ទុក', 'http://kohsantepheapdaily.com.kh/article/115369.html', 'http://i.kspg.co/2015/2/40b024b9-17bc-4168-863d-561055834ad3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('510', 'cl000001', 'u0000003', 'ព្យាបាល​ជំងឺរបេង​ទទួល​បាន​លទ្ធផល​ជោគជ័យ​តម្រូវ​ឱ្យ​អ្នកជំងឺ​តស៊ូ​អត់ធ្មត់', 'ដោយ​មានការ​យកចិត្តទុកដាក់ និង​តាមដាន​យ៉ាង​សកម្ម​ក្នុង​ការ​ព្យាបាល​ជំងឺរបេង​របស់​ក្រសួងសុខាភិបាល​ធ្វើ​ឱ្យ​ជំងឺរបេង​នៅ​ប្រទេស​កម្ពុជា​សះស្បើយ​មាន​កម្រិត​ខ្ពស់ រហូត​ដល់​ទៅ​៩០% ។ ការ​ព្យាបាល​ជំងឺរបេង', 'http://kohsantepheapdaily.com.kh/article/115368.html', 'http://i.kspg.co/2015/2/74514680-4c2e-4151-ac82-5a0be0aa9e9f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('511', 'cl000001', 'u0000003', 'កើត​ជំងឺ​ទឹកនោមផ្អែម​ក្នុង​វ័យ​កណ្ដាល​ធ្វើ​ឱ្យ​សមត្ថភាព​ខួរក្បាល​ចុះ​ថយ', 'ក្រុម​អ្នក​ស្រាវជ្រាវ​អា​មេ​រិ​កបាន​និយាយ​ថា ជំងឺ​ទឹកនោមផ្អែម​ធ្វើ​ឱ្យ​ខួរក្បាល​ឆាប់​ចាស់​ជាង​អាយុ​ពិត​ប្រហែលជា​៥​ឆ្នាំ ហើយ​បណ្តាជន​ដែល​កើត​ជំងឺ​នេះ​ក្នុង​វ័យ​ខ្ទង់​៥០​ឆ្នាំ​គឺ​ប្រឈម​នឹង​បញ្ហា​ចុះ​ខ្សោយ', 'http://kohsantepheapdaily.com.kh/article/115367.html', 'http://i.kspg.co/2015/2/5e1230d5-052a-49b2-97a3-1742fb2b8dd1.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('512', 'cl000001', 'u0000003', 'ផល​ប៉ះពាល់​នៃ​ការ​ព្យាបាល​ជំងឺមហារីក​ដោយ​ប្រើ​ថ្នាំ​គីមី', 'មហារីក​មិនមែន​ជា​ជំងឺឆ្លង​ទេ ដូច្នេះ​វា​មិន​រាលដាល​ចម្លង​ពី​មនុស្ស​ម្នាក់​ទៅ​មនុស្ស​ម្នាក់​ទ្បើ​យ តែ​ទោះ​យ៉ាងនេះ​ក្តី ជំងឺ​ដ៏​កាច​សាហាវ​មួយ​នេះ​ធ្វើ​ឱ្យ​មនុស្ស​ផង​ទាំងពួង​ខ្លាចរអា​មិន​ចង់​ជួប​មិន​ចង់​ឃើញ​ជា​ដាច់ខាត', 'http://kohsantepheapdaily.com.kh/article/115366.html', 'http://i.kspg.co/2015/2/60b97204-d49f-4609-91e7-a20d41490fa5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('513', 'cl000001', 'u0000003', '3​ដំណាក់កាល​នៃ​ប្រតិកម្ម​ក្នុង​ការ​ព្យាបាល​ជំងឺឃ្លង់', 'រាជធានី​ភ្នំពេញ ៖ ពេល​ខ្លះ​អ្នកជំងឺ​អាច​មាន​ប្រតិកម្ម​មុន​ពេល​ព្យាបាល កំពុង​ព្យាបាល និង​ក្រោយ​ព្យាបាល ។ សញ្ញា​ប្រតិកម្ម​នេះ​មិនមែន​ជា​ផលវិបាក​ដែល​បណ្ដាល​មក​ពី​ប្រើ​ថ្នាំ​ច្រើន​មុខ​(MDT)​ទេ', 'http://kohsantepheapdaily.com.kh/article/115365.html', 'http://i.kspg.co/2015/2/684d4174-e969-4ecf-b3b0-c0eb024942cb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('514', 'cl000001', 'u0000003', 'តើ​អ្វី​ទៅ​ជា​ជំងឺ​គ្រោះថ្នាក់​សរសៃឈាម​ខួរក្បាល ?', 'រាជធានី​ភ្នំពេញ ៖ តាម​និយម​ន័យ​ជំងឺ​គ្រោះថ្នាក់​សរសៃឈាម​ខួរក្បាល គឺជា​ជំងឺ​មួយ​ដែល​មានការ​ប៉ះពាល់​មុខងារ​របស់​ប្រព័ន្ធ​ប្រសាទ ហើយ​កើតឡើង​ភ្លាម​ៗ​អាច​ស្ទះ​សរសៃឈាម ', 'http://kohsantepheapdaily.com.kh/article/115364.html', 'http://i.kspg.co/2015/2/9ba4d00d-7046-4815-86da-6b345c7d60d0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('515', 'cl000001', 'u0000003', 'ក្បាច់​រួម​ភេទស្ត្រី​នៅ​ពីលើ​បង្ក​ហានិភ័យ​ខ្ពស់​ដល់​ភាគី​បុរស (18+)', 'បើ​ទោះជា​ផ្តល់​ភាព​ស្រណុក​ស្រួល​ច្រើន​គួរសម​ក៏​ដោយ ក្បាច់​រួម​ភេទ​ដែល​ស្ត្រី​នៅ​ពីលើ​ហាក់​មិន​ផ្តល់​សុវត្ថិភាព​ដល់​ភាគី​បុរស​ឡើយ ដ្បិត​អ្នក​វិទ្យាសាស្ត្រ​មួយ​ក្រុម​បាន​រក​ឃើញ​ថា ក្បាច់​នេះ', 'http://kohsantepheapdaily.com.kh/article/115363.html', 'http://i.kspg.co/2015/2/aaed62b3-8777-4ad5-8e1d-01b229f39f62.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('516', 'cl000001', 'u0000003', 'ឱសថ​វៃឆ្លាត​ជា​ក្តី​សង្ឃឹម​ថ្មី​ក្នុង​ការ​ប្រយុទ្ធ​ប្រឆាំង​បំផ្លាញ​កោសិកា​ជំងឺមហារីក', 'ជំងឺមហារីក គឺជា​មូលហេតុ​ចម្បង​មួយ​នៃ​មរណភាព​នៅ​ទូ​ទាំង​ពិភពលោក បើ​ទោះជា​សម្រេច​បានការ​រីក​ចម្រើន​ធំ​ៗ​ក្នុង​ការ​ព្យាបាល​យ៉ាងច្រើន​នៅ​រយៈពេល​ប៉ុន្មាន​ឆ្នាំ​ចុង​ក្រោយ​នេះ​ក្តី ', 'http://kohsantepheapdaily.com.kh/article/114962.html', 'http://i.kspg.co/2015/2/5ff1ab5f-cc20-4ab9-a116-16519bd64e49.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('517', 'cl000001', 'u0000003', 'ជុំវិញ​អត្ថប្រយោជន៍​នៃ​ការ​ទទួលទាន​គ្រាប់​ធញ្ញជាតិ​សណ្តែកដី', 'ក្នុង​នាម​ជា​គ្រាប់​ធុញ្ញ​ជាតិ សណ្តែកដី​ត្រូវ​បាន​ក្រុម​អ្នក​វិទ្យាសាស្ត្រ​សាកលលោក​ចាត់​ចូល​ក្នុង​ជំពូក​អាហារ​ដែល​ផ្តល់​អត្ថប្រយោជន៍​ច្រើន​ដល់​សុខភាព​អ្នក​និយម​ទទួលទាន ។ ', 'http://kohsantepheapdaily.com.kh/article/114961.html', 'http://i.kspg.co/2015/2/4125e6eb-8f86-4e3f-a1f4-8e6cb10c185e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('518', 'cl000001', 'u0000003', 'មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត​ពិនិត្យ​មាត់ស្បូន​ដោយ​ឥត​គិតថ្លៃ​ជូន​ស្ត្រី​រៀប​ការ​រួច​200​នាក់', 'រាជធានី​ភ្នំពេញ ៖ លោកស្រី​វេជ្ជបណ្ឌិត ហាវ ម​នី​រត្ន ប្រធាន​ផ្នែក​វិភាគ​កោសិកា​មហារីក​នៃ​មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត បាន​ប្រាប់​អ្នក​យក​ព័ត៌មាន​កោះសន្តិភាព​ឱ្យ​ដឹង​ថា ប្រទេស​កម្ពុជា​មាន​អត្រា​កើត', 'http://kohsantepheapdaily.com.kh/article/114957.html', 'http://i.kspg.co/2015/2/57333c6a-8dba-4487-b4ab-2d580e14dd38.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('519', 'cl000001', 'u0000003', 'កាកបាទក្រហមកម្ពុជា​ទទួល​អំណោយ​ពី​សមាគម​ស្ត្រី​ថៃ និង​ក្រុមហ៊ុន​ដាប់​ប​ល​ឡាក់ ត្រេ​ត​ឌី​ង', 'រាជធានី​ភ្នំពេញ ៖ សមាគម​ស្ត្រី​ថៃ​ដែល​មាន​ស្នាដៃ​ចំពោះ​ជាតិ និង​ក្រុមហ៊ុន​ដាប់​ប​ល​ឡាក់ ត្រេ​ត​ឌី​ង បាន​ដឹកនាំ​គណៈប្រតិភូ សមាជិក​របស់​ខ្លួន​ចូល​ជួប​សម្តែង​ការ​គួរសម ', 'http://kohsantepheapdaily.com.kh/article/116175.html', 'http://i.kspg.co/2015/3/905fe3fa-e421-4f48-9843-ec56eb3637b4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('520', 'cl000001', 'u0000003', 'លោកស្រី អូ​ដា មុ​សុ​កូ មុន​ពេល​ស្លាប់​ផ្តល់​ថវិកា​សាងសង់​អគារសិក្សា​1​ខ្នង​ដល់​កូន​ខ្មែរ', 'ខេត្ត​ត្បូងឃ្មុំ ៖ មុន​ពេល​ស្លាប់ លោកស្រី អូ​ដា មុ​សុ​កូ សប្បុរសជន​ជប៉ុន​បាន​សាងសង់​អគារសិក្សា​ថ្មី​១​ខ្នង​អំពី​បេតុង មាន​បណ្តោយ​ប្រវែង​៣៥,២​ម៉ែត្រ ទទឹង​៨,២​ម៉ែត្រ កម្ពស់​៨,៥​ម៉ែត្រ', 'http://kohsantepheapdaily.com.kh/article/116153.html', 'http://i.kspg.co/2015/3/36c0a172-02b3-4304-b679-70feac88f5fe.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('521', 'cl000001', 'u0000003', '​អំណោយរបស់សម្តេចតេជោឧបត្ថម្ភ​ដល់គ្រួសារ​សពកុមារ៣នាក់ស្លាប់ដោយ​សារលង់ទឹក​', 'ខេត្តបន្ទាយ​មានជ័យ ៖ ដោយ​ក្តី​អាណិតអាសូរ​ចំពោះ​មរណភាព​កុមា​រា​ចំនួន​៣​នាក់  ដែល​២​នាក់​ជា​បងប្អូន​បង្កើត​និង​គ្នា​និង​ម្នាក់​ទៀត ជា​បងសӡޢូន​ជីដូនមួយ  ដោយ​លង់​ទឹកស្រះ  នៅ​ល្ងាច​ថ្ងៃ​ទី​០៦​ខែ​មិ​នា​ឆ្នាំ​២០១៥ នៅ​ភូមិ​ទ្រ​លោក​ត្បូង ឃុំ​ភ្នំ​លៀប ស្រុក​ព្រះនេត្រព្រះ ខេត្តបន្ទាយមានជ័យ ។​', 'http://kohsantepheapdaily.com.kh/article/116114.html', 'http://i.kspg.co/2015/3/f4685b7a-4849-4dce-87d6-6e3ee7c6f908.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('522', 'cl000001', 'u0000003', 'ប្រជាពលរដ្ឋនៅ​ខេត្តកំពត និង​ខេត្ត​កែប ទទួល​បាន​អណ្តូងទឹក​ស្អាត', 'រាជធានីភ្នំពេញ ៖ ដោយ​មើលឃើញ​ពី​តំរូវការ​ចាំបាច់​ នៃ​ការ​ប្រើប្រាស់​ទឹក ​តាម​តំបន់​ជនបទ​ដាច់​ស្រយាល ក្រុមការងារ​នៃ​មូលនិធិ​អណ្តូងទឹក​ស្អាត​', 'http://kohsantepheapdaily.com.kh/article/115857.html', 'http://i.kspg.co/2015/3/15e02ee6-de14-441f-a41d-2e4e8610ad4f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('523', 'cl000001', 'u0000003', 'កាក​បាត​ក្រហម​ខណ្ឌមានជ័យ នាំ​អំ​ណោ​ច​ចែក​ជូន​ស្ត្រី​មាន​ជម្ងឺមហារីក​សុដន់', 'រាជធានីភ្នំពេញ ៖ អនុ​សាខា​កាក​បាត​ក្រហម​ខណ្ឌមានជ័យ នៅ​ព្រឹក​ថ្ងៃ​ទី​៣ ខែ​មិ​នា ឆ្នាំ​២០១៥ ​នេះ បាន​ចុះ​សួរសុខទុក្ខ និង​ពាំនាំ​យក​អំ​ណោ​ច​ជូន​ដល់​ស្ត្រី​ម្នាក់​ដែល​មាន​ជម្ងឺ​ជា​ទម្ងន់', 'http://kohsantepheapdaily.com.kh/article/115846.html', 'http://i.kspg.co/2015/3/9c881ce3-9a4b-4208-b090-7b4b8cc4751d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('524', 'cl000001', 'u0000003', '​ពលរដ្ឋ​ទីទ័ល​ក្រ​​១៥៥គ្រួសារនៅ​ស្រុក​ស្អាងបាន​ទទួល​អំណោយ​ពី​សាខា​កាកបាទក្រហមកម្ពុជា​ខេត្តកណ្តាល​', 'ខេត្តកណ្ដាល ​៖​ ​ប្រជាពលរដ្ឋ​ទីទ័ល​ក្រ​មាន​ជីវភាព​ខ្វះខាតចំនួន១៥៥គ្រួសារ ស្ថិត​នៅ​ភូមិ​កោះ​រំដួល ហៅ​ភូមិ​កោះគ ឃុំ​រកា​ខ្ពស់ ស្រុក​ស្អាង ខេត្តកណ្តាល បាន​ទទួល​', 'http://kohsantepheapdaily.com.kh/article/115782.html', 'http://i.kspg.co/2015/3/2738117d-2bb9-4003-abf0-6c0454e01bfb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('525', 'cl000001', 'u0000003', '​លោក​ជំទាវ​ ជា ខេ​ង​ ស៊ុ​យ សែ​ម​ រៀបចំ​អាហារ​ជូន​កុមារ​កំព្រា​​ពោធិ៍សាត់​ និង​សន្យា​ទិញ​ម៉ូតូ​មួយ​គ្រឿង​ជូន​អ្នក​​ជាប់​ចំណាត់ថ្នាក់​​លេខ​​១', 'ខេត្តពោធិ៍សាត់​ ៖​ សមាគមនារី​កម្ពុជា ​ដើម្បី​សន្តិភាព​ និង​អភិ​វ​ឌ្ឍន៍​ក្រសួង​រ៉ែ​ និង​ថាមពល​ ដឹកនាំ​ដោយ​លោក​​ជំទាវ ​ជា ខេ​ង​ ស៊ុ​យ សែ​ម ​ជា​ប្រធានកិត្តិយស​ កាលពី​ថ្ងៃ​ទី​០១ ​មីនា​ ២០១៥ មាន​រៀបចំ​អាហារ​មួយ​​ពេល​ជូន​កុមារ​កំព្រា​ចំនួន​៤៧​នាក់ ​ដែល​រស់នៅ​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/115750.html', 'http://i.kspg.co/2015/3/a34664f7-70c3-4186-a15b-814c5f11000e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('526', 'cl000001', 'u0000003', 'អំណោយ​សម្តេច​តេជោ និង​សម្តេច​កិត្តិព្រឹទ្ធបណ្ឌិត​ ប្រគល់​ជូន​កង​អនុសេនា​តូច​លេខ២​តាមរយៈ​លោក​​ហ៊ុន ម៉ាណែត​', 'ខេត្ត​កោះ​កុង ៖ នា​ចុង​ស​ប្តា​​ហ៍​​នេះ ក្រុមការងារ​​​លោក​ហ៊ុន ម៉ា​ណែ​ត បាន​​នាំ​យក​​នូវ​ម៉ាស៊ីនបូមទឹក​​១​គ្រឿង, ស៊ី​វិច​ទ័​រ​​១​គ្រឿង, បន្ទះ​សូ​ឡា១​ផ្ទាំង និង​ប្រេង​​៥០០​លី​ត្រ ដែល​ជា​​អំណោយ​ដ៏​ថ្លៃថ្លា​របស់សម្តេច​តេ​ជោ និង​សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត​ ', 'http://kohsantepheapdaily.com.kh/article/115448.html', 'http://i.kspg.co/2015/2/96f95da0-feac-4e34-a445-6f92b8e355bc.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('527', 'cl000001', 'u0000003', 'លោក ហោ ម៉ៅ និង​អ្នក​នាង​អ៊ុំ គ​ន្ធា ទទួល​អំណោយ​បន្ថែម​ពី​សប្បុរសជន', 'រាជធានី​ភ្នំពេញ ៖ លោក ហោ ម៉ៅ អតីត​អ្នក​សម្រួល​ចរាចរ​ណ៏ ​ស្ម័គ្រចិត្ត​ នៅ​តំបន់​លូ​ប្រាំ និង​អ្នក​នាង ​អ៊ុំ គ​ន្ធា អតីត​អ្នកសិល្បៈ បាន​ទទួល​អំណោយ​ជា​ស្បៀងអាហារ ', 'http://kohsantepheapdaily.com.kh/article/115397.html', 'http://i.kspg.co/2015/2/ff87b563-84ba-43b8-8075-27cdf65e00fd.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('528', 'cl000001', 'u0000003', 'អតិថិជន​កម្ពុជា​តេ​លា​ជួយ​មន្ទីរពេទ្យ​គន្ធ​បុប្ផា', 'រាជធានី​ភ្នំពេញ ៖ ថវិកា​ចំនួន​២៥៣.៣៥៥.៣០០​រៀល បាន​មក​ពី​ប្រជាពលរដ្ឋ​កម្ពុជា​ទូ​ទាំង​ប្រទេស ដែល​បាន​ជាវ​ប្រេង​កម្ពុជា​តេ​លា ត្រូវ​បាន​យក​ទៅ​បរិច្ចាគ​ជូន​ដល់​', 'http://kohsantepheapdaily.com.kh/article/115141.html', 'http://i.kspg.co/2015/2/51d0ce5c-9815-4ec6-86cf-7b75214856c5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('529', 'cl000001', 'u0000003', 'ជន​រង​គ្រោះ​ដោយ​អគ្គិ​ភ័យ នៅ​ភូមិ​តា​អ៊ិ​ន​២ ទទួល​អំណោយ​ពី​អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ស្រុក​មង្គល​បូ​រី', 'ខេត្តបន្ទាយ​មាន​ជ័យ ៖ ​ជន​រង​គ្រោះ​២​នាក់​យាយ តា ដោយសារ​អគ្គិភ័យ​ឆាបឆេះ​ផ្ទះ កាលពី​ព្រឹក​ថ្ងៃ​ទី​១១​ ខែកុម្ភៈ ឆ្នាំ​២០១៥ កន្លង​ទៅ​នេះ នៅ​ភូមិ​តា​អ៊ី​ន​២ ឃុំ​ស្រះ​រាំង', 'http://kohsantepheapdaily.com.kh/article/114857.html', 'http://i.kspg.co/2015/2/08020bb4-126f-4f40-9918-be4014dc26f9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('530', 'cl000001', 'u0000003', 'កាកបាទក្រហម​ខណ្ឌ​មានជ័យ​ចុះ​សួរសុខទុក្ខ និង​នាំ​អំណោយ​ជូន​សន្តិសុខ​ខណ្ឌ​មានជ័យ​ដែល​​ធ្លាក់ខ្លួនឈឺ​ពិការ​​មួយ​ចំហៀង​​ខ្លួន', 'រាជ​ធានីភ្នំពេញ ៖ អនុ​សាខា​កាកបាទក្រហមកម្ពុជាខណ្ឌមានជ័យ ​ដឹកនាំ​ដោយ​លោក​ឌី រ័​ត្ន​ខេ​ម​រុណ អភិបាលរង​​ខណ្ឌ​មាន​ជ័យ និង​​ជា​​ប្រធាន​កាកបាទក្រហម​​ប្រចាំ​ខណ្ឌ កាលពី​រសៀល​​ថ្ងៃ​ទី​១៣ ​កុ​ម្ភៈ ​២០១៥ បានអញ្ជើញ​​​ចុះ​សួរសុខ​ទុក្ខ និង​ពាំនាំ​យក​', 'http://kohsantepheapdaily.com.kh/article/114849.html', 'http://i.kspg.co/2015/2/744f4898-43c9-4c6e-9da3-e66665da8d51.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('531', 'cl000001', 'u0000003', 'រដ្ឋមន្ត្រីក្រសួង​ព័ត៌មាន​លោក​ខៀវ កា​ញ៉ា​រិ​ទ្ធិ «​ជំងឺអេដស៍​មិនមែន​ជា​ជំងឺ​គួរ​ឲ្យ​ខ្លាច​នោះ​ទេ?ជំងឺ​ខ្លាច​ជាងគេ គឺ​ជំងឺឆ្លង​ថ្នល់​»', 'រាជធានី​ភ្នំពេញ ៖ លោក​រដ្ឋមន្ត្រី​ខៀវ កា​ញ៉ា​រិ​ទ្ធិ​រដ្ឋមន្ត្រីក្រសួង​ព័ត៌មាន​បាន​ចុះ​ផ្ទាល់​ជាមួយ​មន្ត្រី​ក្រសួង​យក​អំណោយ​ផ្ទាល់ខ្លួន និង​អំណោយ​សប្បុរ​ជន​មួយ​ចំនួន​ជូន​ដល់​អតីត​តារាចម្រៀង​អ​ភ័​ព្ទ​អ្នក​នាង អ៊ុំ គ​ន្', 'http://kohsantepheapdaily.com.kh/article/114812.html', 'http://i.kspg.co/2015/2/a6acf7ae-7157-4058-8c7d-bf397a4adcfc.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('532', 'cl000001', 'u0000003', 'ស្ត្រី​២​នាក់​ក្រោយ​សំរាលកូន ​ទទួល​បាន​អំណោយ​មនុស្សធម៌​ពីសាខា​កាកបាទក្រហមកម្ពុជា', 'ខេត្ត​រតន​គិរី​ ៖ អនុវត្ត​តាម​អនុ​សាស​ន៍​ដ៏​ខ្ព​ង់​ខ្ពស់​របស់​សម្តេច​កិត្តិព្រឹទ្ធ​បណ្ឌិត​ប៊ុ​ន រ៉ា​នី ហ៊ុន សែន ប្រធាន​កាកបាទក្រហមកម្ពុជាក្នុង​ការ​ជម្រុញ​លើកទឹកចិត្ត​ឲ្យ​ស្ត្រី​ជា​មាតា​គ្រប់​រូប​មក​សម្រាលកូន​នៅ​មណ្ឌល​សុខភាព​', 'http://kohsantepheapdaily.com.kh/article/114556.html', 'http://i.kspg.co/2015/2/c0939364-97ab-4c39-81f4-bccd9bb609f5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('533', 'cl000001', 'u0000003', 'គ្រូពេទ្យ​ស្ម័គ្រ​ចិត្ត​សមាគម​សុខាភិបាល​ខ្មែរ​អា​មេ​រិ​ក​ចុះ​ពិនិត្យ​ព្យាបាល​ជំងឺ​ដោយ​ឥត​គិតថ្លៃ៥៣៨០​នាក់​លើស​ផែនការ', 'ខេត្តស្វាយរៀង ៖ ​មាន​ម្ចាស់​មូលនិធិ​ជំនួយដៃអា​មេ​រិ​ក​កាំ​ង ​សហការ​ជាមួយ​សមាគម​សុខាភិបាល​ខ្មែរ​អា​មេ​រិ​ក ​ដឹកនាំ​ក្រុម​គ្រូពេទ្យ​ស្ម័គ្រ​ចិត្ត​ ចំនួន​៦០​អង្គ​/​នាក់​ មក​ពី​', 'http://kohsantepheapdaily.com.kh/article/114393.html', 'http://i.kspg.co/2015/2/3d5d2dc7-e0b7-4b58-9b79-3f223932faab.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('534', 'cl000001', 'u0000003', 'មូលនិធិ​ជំនួយ​ដៃ​អា​មេ​រិ​ក​កាំ​ង​សហការ​ជាមួយ​សមាគម​សុខាភិបាល​ខ្មែរ​អា​មេ​រិ​ក​ចុះ​ពិនិត្យ​ព្យាបាល​ជំងឺ​ដោយ​ឥត​គិតថ្លៃ', 'ខេត្តស្វាយរៀង  ៖ មាន​ម្ចាស់​មូលនិធិ​ជំនួយ​ដៃ​អា​មេ​រិ​ក​កាំ​ង​សហការ​ជាមួយ​សមាគម​សុខាភិបាល​ខ្មែរ​អា​មេ​រិ​ក​ដឹកនាំ​ក្រុម​គ្រូពេទ្យ​ស្ម័គ្រចិត្ត​ចំនួន​៦០​អង្គ​/​នាក់ មក​ពី​សហរដ្ឋអាមេរិក និង​៣៩​អង្គ​/​នាក់ ', 'http://kohsantepheapdaily.com.kh/article/114203.html', 'http://i.kspg.co/2015/2/41f9a7b0-7c0a-40b7-b886-8e68628f1f27.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('535', 'cl000001', 'u0000003', 'គ្រួសារ​មួយមាន​គ្នា​៤នាក់ តែ​ពិការ​ភ្នែក​៣នាក់ រស់ទាំងវេទនា', 'ខេត្តកំពត ៖ ​គ្រួសារក្រីក្រ​មួយ ដែលមានគ្នា​៤នាក់ តែ​បាន​ពិការ​ភ្នែក​​៣នាក់ទៅហើយ គឺ​​ម្តាយ កូនប្រុស និងកូនស្រីម្នាក់ នៅតែ​ឪពុក​​មិន​​ពិការ​ ។', 'http://kohsantepheapdaily.com.kh/article/114165.html', 'http://i.kspg.co/2015/2/0ee11833-01cb-4b49-b8ff-8900aa0f2294.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('536', 'cl000001', 'u0000003', 'ភ្លើងចង្ក្រាន​ត្របាក់​ផ្ទះ​ឈើ​អស់​១​ខ្នង​ពេល​ម្ចាស់​ចេញ​ទៅ​ចំការ', 'ខេត្តកំពង់ចាម​ ៖ លំនៅ​ឋាន​របស់​ប្រជាពលរដ្ឋ​សាងសង់​អំពើ​ឈើប្រក់​ស័ង្ក​សី​មាន​ទំហំ​៤​គុណ​៥​ម៉ែត្រ ត្រូវ​បាន​អគ្គិ​ភ័យ​លេប​ត្របាក់​ខ្ទេច ដោយ​មាន​អណ្តាតភ្លើង ​និង​ផ្សែង ហុយ​ខ្មួលខ្មាញ់​ទៅ​លើ​អាកាស', 'http://kohsantepheapdaily.com.kh/article/114132.html', 'http://i.kspg.co/2015/2/f56f338a-52a1-4682-827a-ec0bdbb721c8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('537', 'cl000001', 'u0000003', 'ស្ត្រី​អនាថា​ម្នាក់​ដើរ​ដួល​សន្លប់​តាម​ផ្លូវ ទូរស័ព្ទ​ហៅ​រថយន្ត​សង្គ្រោះ មិន​ព្រម​មក', 'រាជធានី​ភ្នំពេញ ៖ ស្ត្រី​អនាថា អាយុ​ជាង​៣០​ឆ្នាំ​ម្នាក់ បាន​ដួល​សន្លប់​នៅ​លើ​ចិញ្ចើម​ផ្លូវ អស់​រយៈពេល​ជា​ច្រើន​ម៉ោង ក្រោយមក​ក៏​មាន​អន្តរាគមន៍​ពី​អាជ្ញាធរ​មូលដ្ឋាន', 'http://kohsantepheapdaily.com.kh/article/114096.html', 'http://i.kspg.co/2015/2/154111c8-a5bf-4c17-a193-8da54303a8fd.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('538', 'cl000001', 'u0000003', 'ស្ត្រីចំណាស់ទីទ័លក្រអំពាវនាវ​រក​សប្បុរសជន​ឱ្យ​ជួយ', 'ខេត្តមណ្ឌលគិរី ៖ ព្រោះតែ​ជីវភាពទីទ័ល​ក្រ​ខ្លាំង​ពេក ស្ត្រី​ចំណាស់​ម្នាក់​បាន​ខិត​ខំ​ត្រដរ​ដាំ​បន្លែ​បន្តិចបន្តួច​ដើម្បី​ហូប​ និង​ទុក​លក់​ផង គ្រាន់​បាន​លុយ​ចិញ្ចឹម​កូន​តូច​ឆ្កួត​ម្នាក់ ឯ​ប្ដី​ស្លាប់​គ្មាន​កូន​នឹង​ទីពឹង មើលទៅ​គួរ​ឲ្យ​សង្វេគ​ណាស់', 'http://kohsantepheapdaily.com.kh/article/114051.html', 'http://i.kspg.co/2015/2/6f3e739e-cc37-4d84-aa27-db2dd21a7a8c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('539', 'cl000001', 'u0000003', 'គ្រួសារ​ពិការ​ក្រីក្រ ក្នុង​ឃុំ​សេដ្ឋី ស្រុក​កំពង់ត្រឡាច ទទួល​អំណោយ​មនុស្ស​ធ​ម៏', 'ខេត្តកំពង់ឆ្នាំង ៖ លោកថោ​ង ចំរើន ប្រធាន​មន្ទីរ​សង្គមកិច្ច អតីត​យុ​ទ្ឋ​ជន និង​យុវ​និ​តិ​សម្បទា ខេត្ត​កំពង់ឆ្នាំង កាលពី​ព្រឹក​ថ្ងៃ​ទី២៩ ខែមក​រា ឆ្នាំ២០១៥ បាន​ដឹកនាំ​មន្ត្រី​ក្រោម​ឱវាទ ', 'http://kohsantepheapdaily.com.kh/article/113934.html', 'http://i.kspg.co/2015/1/0053f8ba-4ba4-4c37-8115-a8a81a920fcb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('540', 'cl000001', 'u0000003', 'មុខមាត់​ថ្មី​ជាមួយ​ក្តី​សង្ឃឹម​បន្ទាប់​ពី ការ​ជួយ​ជ្រោមជ្រែង​ពី​សប្បុរសជន', 'ខេត្តកំពង់ស្ពឺ ៖ ដោយ​បានការ​ជួយ​ជ្រោមជ្រែង​ពី​សំណាក់​សប្បុរសជន​ទាំង​ក្នុង​និង​ក្រៅប្រទេស​តាម​រយៈ​កោះសន្តិភាព​មក ពេល​នេះ​កុមារ រុន ចំរើន បាន​ធ្វើការ​វះកាត់​ស្បែក​ជាប់​កបាន​ដោយ​ជោគជ័យ​ហើយ', 'http://kohsantepheapdaily.com.kh/article/113773.html', 'http://i.kspg.co/2015/1/78f61c82-c825-4176-a39e-17a8ab7b29a3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('541', 'cl000001', 'u0000003', 'លោកអភិបាល​ស្រុក​នាំអំណោយ​ផ្តល់ជូនគ្រួសារ​​រងគ្រោះ​ដោយអគ្គិភ័យ​​', 'ខេត្តកំពង់ឆ្នាំង ៖ លោក​ឃួ​ន ​សៅ​រុំ អភិបាលស្រុក​​បរិបូរ​ណ៍ កាលពី​វេលា​ព្រឹក​ថ្ងៃ​ទី​២៥ ​មករា ២០១៥ បានដឹកនាំ​មន្ត្រី​ក្រោម​ឱវាទ​យក​​', 'http://kohsantepheapdaily.com.kh/article/113607.html', 'http://i.kspg.co/2015/1/6db5af0d-83cc-49d3-842a-053b7dff62f5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('542', 'cl000001', 'u0000003', 'អនុ​សាខា​កាក​បាទ​ក្រហម​ខណ្ឌ​ប្ញស្សី​កែវ និងព្រះ​ចៅ​អធិការ​វត្ត​ញាណ​រង្សី​ចែក​អំណោយ​ជូនពល​រដ្ឋងាយ​រង​គ្រោះ​១០​គ្រួសារ​នៅ​សង្កាត់​ទួល​សង្កែ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ គណៈ​កម្ម​ការ​អនុ​សាខា​កាក​បាទ​ក្រហម​ខណ្ឌ​ប្ញស្សី​កែវ​រួម​ជាមួយ​សហ​គមន៍​មូល​និធិ​នគរ​កាយ​វត្ត​ព្រះ​ពុទ្ធ​សាសនា​កម្ពុជា​ នៅ​ព្រឹក​ថ្ងៃ​ទី​២៣ មករា នេះ​ បាន​នាំ​អំណោយ​ជា​គ្រឿង​ឧប​ភោគ ', 'http://kohsantepheapdaily.com.kh/article/113465.html', 'http://i.kspg.co/2015/1/5d10dbe3-a38d-4f62-867a-8a1cf0282e03.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('543', 'cl000001', 'u0000003', 'HUAWEI HONOR 6 PLUS កំពុង​តែ​មាន​ភាព​ល្បីល្បាញ​ជាមួយនឹង​កាមេរ៉ា​ខាងក្រោយ​ភ្លោះ ថតរូប​ច្បាស់​លើស​ពី​ការ​ស្មាន', 'HUAWEI គឺជា​ក្រុមហ៊ុនទូរស័ព្ទ​មានឈ្មោះ​ល្បី​ជាប់​ចំណាត់ថ្នាក់​លេខ​៣​មួយ​នៅ​លើ​ភិ​ភព​លោក ដែល​ទទួល​បានការ​គាំទ្រ​យ៉ាងច្រើន​ពី​មនុស្ស​ទូរ​ទៅ​និង​កំពុង​តែ​ពេញ​និយម​ក្នុង​ការ​ប្រើប្រាស់​នៅ​ប៉ែក​អ៊ឺ​រ៉ុ​ប', 'http://kohsantepheapdaily.com.kh/article/116140.html', 'http://i.kspg.co/2015/3/5a57eee4-5b11-423d-8a14-651fe81e5788.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('544', 'cl000001', 'u0000003', 'មេ​នូ​ឡាយ​ហ្វ៍ លើកកម្ពស់​តួនាទី​ស្ត្រី​កម្ពុជា​ក្នុង​ការ​កសាង​អនាគត​គ្រួសារ ​នា​ឱកាស​ទិវា​សិទ្ធិ​នារី​អន្តរជាតិ', 'រាជធានី​ភ្នំពេញ ៖ ដើម្បី​ចូលរួម​ជាមួយ​រដ្ឋាភិបាល ក្រុមហ៊ុន​ឯកជន អង្គការ​មិនមែន​រដ្ឋាភិបាល និង​ប្រជាជន​នៅ​ក្នុង​បណ្ដា​ប្រទេស​ទាំងឡាយ​នៅ​លើ​ពិភពលោក', 'http://kohsantepheapdaily.com.kh/article/116116.html', 'http://i.kspg.co/2015/3/e43b20e0-c09f-4658-8f04-95eba82030f1.jpg?w=285&h=170&mode=carve', '20150308');
