INSERT INTO "tbnews" VALUES ('558', 'cl000001', 'u0000003', 'ក្រុមហ៊ុន​Samsung​ធ្វើ​ឲ្យ​ពិភពលោក​ចាប់អារម្មណ៍​ចង់​ដឹង​ជា​ថ្មី​ចំពោះ​ផលិតផល​Samsung Galaxy បន្ទាប់', 'រាជធានី​ភ្នំពេញ​៖ បន្ទាប់​ពី​ធ្វើ​ឲ្យ​ពិភពលោក​ភ្ញាក់ផ្អើល​ដោយ​ការ​ឈ្នះ​បាន​ពានរង្វាន់​រហូត​ដល់​ទៅ 80 រង្វាន់ មិន​បាន​ប៉ុន្មាន​ផង ក្រុមហ៊ុន Samsung ក៏​ធ្វើ​ឲ្យ​បណ្តាញសារព័ត៌មាន​អន្តរជាតិ ', 'http://kohsantepheapdaily.com.kh/article/115598.html', 'http://i.kspg.co/2015/2/78100b4c-bf6a-4ddc-a7bf-d8fa8324e52d.jpg?w=285&h=170&mode=carve', '20150308');
