INSERT INTO "tbnews" VALUES ('560', 'cl000001', 'u0000003', 'ក្រុម​​ហ៊ុន Manulife ប្រគល់​ពាន​កិត្តិ​យស​ជូន​ទី​ប្រឹក្សា​ឆ្នើម', 'រាជធានីភ្នំពេញ ៖ នៅ​ថ្ងៃ​ទី​២១ កុម្ភៈ ២០១៥ នេះ ក្នុង​ឱកាស​ប្រារព្ធ​ពិធី​អប​អរ​ចំពោះ​សមិទ្ធ​ផល​អាជីវ​កម្ម​ដែល​សម្រេច​បាន​នៅ​ក្នុង​ឆ្នាំ​២០១៤ កន្លង​ទៅ ក្រុម​ហ៊ុន Manulife បាន​ប្រគល់​ពាន​កិត្តិ​យស​ចំនួន​១៨​ពាន', 'http://kohsantepheapdaily.com.kh/article/115305.html', 'http://i.kspg.co/2015/2/eb7f9667-9be6-4ee2-96bd-8374d09bfb7d.jpg?w=285&h=170&mode=carve', '20150308');
