INSERT INTO "tbnews" VALUES ('700', 'cl000001', 'u0000003', 'សា​រឿ​ន ច័ន្ទ ផ្តួល​ថៃ​ឱ្យ​សន្លប់​ត្រឹម​ទឹក​ទី​1 ឯ​យ៉េន ឌី​ណា ចាញ់​ពិន្ទុ', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​ប្រដាល់​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​រាជធានី​ភ្នំពេញ​បុ​៉​ស្តិ៍​លេខ​៣ កាលពី​ថ្ងៃអាទិត្យ ទី​៨ មីនា កន្លង​មកនេះ សា​រឿ​ន ច័ន្ទ មក​ពី​ក្លិប​សម្រែក​រាជសីហ៍​', 'http://kohsantepheapdaily.com.kh/article/116228.html', 'http://i.kspg.co/2015/3/4d8a157c-136c-487b-97d5-52894d9bb720.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('701', 'cl000001', 'u0000003', 'ជើង​ខ្លាំង​ថៃ​បំបាក់ កំពែង ខ្មៅ កម្ពុជា​លើ​រេញ SEA TV', 'រាជធានី​ភ្នំពេញ ៖ ខុស​ពី​ការ​គ្រោងទុក កីឡាករ​កម្ពុជា កំពែង ខ្មៅ ត្រូវ​ចាញ់​ក្រោម​ដៃ​កីឡាករ​ថៃ ដែល​ជា​គូប្រកួត​យ៉ាង​គំហុក​តែ​ម្ដង សម្រាប់​ការ​ប្រកួត​កាលពី​ថ្ងៃអាទិត្យ ទី​៨', 'http://kohsantepheapdaily.com.kh/article/116227.html', 'http://i.kspg.co/2015/3/b3185df3-12a2-4337-9f07-b786b5428e4c.jpg?w=285&h=170&mode=carve', '20150314');
