INSERT INTO "tbnews" VALUES ('715', 'cl000001', 'u0000003', 'តារា​សម្តែង​ស្រី​អ៊ុយ​ក្រែ​ន​សាក់​រូប​កីឡាករ​ម៉េ​ស៊ី​លើ​ដើមទ្រូង​របស់​នាង', 'តារា​សម្តែង​អ៊ុយ​ក្រែ​ន នាង​ណា​តា​លី​យ៉ា ឃូ​ហ៊្សេ​ន​ណូ​វ៉ា បាន​ធ្វើ​ឱ្យ​កក្រើក​បណ្តាញ​គេហទំព័រ​នានា​បន្ទាប់​ពី​រូប​នាង​សាក់​រូប​កីឡាករ​សញ្ជាតិ​អា​ហ្ស​ង់​ទី​ន វ័យ​២៧​ឆ្នាំ លី​អូ​ណែ​ល', 'http://kohsantepheapdaily.com.kh/article/116264.html', 'http://i.kspg.co/2015/3/46f7ff50-f95d-4b93-87b2-59ff6af2f6ba.jpg?w=285&h=170&mode=carve', '20150314');
