INSERT INTO "tbnews" VALUES ('717', 'cl000001', 'u0000003', 'បិសាច​ក្រហម Man Utd នឹង​លក់​កីឡាករ​ស្លាប​បម្រើ ឌី ម៉ា​រៀ ទៅ​ឱ្យ​ក្លិប Paris SG', 'ក្លិប​បិសាច​ក្រហម Man Utd ក្នុង​ក្របខ័ណ្ឌ​អង់គ្លេស​ប្រឺ​មី​ញេ​លី​ក នឹង​លក់​កីឡាករ​ស្លាប​បម្រើ​សញ្ជាតិ អា​ហ្ស​ង់​ទី​ន​វ័យ​២៧​ឆ្នាំ ឌី ម៉ា​រៀ ទៅ​ឱ្យ​ក្លិប​ក្របខ័ណ្ឌ​បារាំង Paris SG ក្នុង​ទំហំ​ទឹកប្រាក់​ប្រមាណ', 'http://kohsantepheapdaily.com.kh/article/116245.html', 'http://i.kspg.co/2015/3/1bbda219-7482-4775-8480-4ef0f0cf86d6.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('718', 'cl000001', 'u0000003', 'ព្យាករណ៍​ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​នេះ​ចំនួន​16​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​នេះ​មាន 16 ប្រកួត ។ ក្រុម Juventus ត្រូវការ​ឈ្នះ Sassuolo ដើម្បី​ស្តារ​កិត្តិយស​ឡើង​វិញ ខណៈ Lazio, Man Utd ទទួលភ្ញៀវ​ខ្លាំង​ដូច​គ្នា ។', 'http://kohsantepheapdaily.com.kh/article/116196.html', 'http://i.kspg.co/2015/3/41fef9d9-c112-418e-b409-6ac6bdc6b860.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('719', 'cl000001', 'u0000003', 'ផលិត​កម្ម​ចក្កវាឡ ធ្វើ​ពិធី​ក្រុង​ពាលី​រឿង​ភាគ «ស្ទឹង​ទឹក​ភ្នែក»', 'រាជធានីភ្នំពេញ ៖ ពិធី​ក្រុង​ពាលី​រឿង «ស្ទឹង​ទឹក​ភ្នែក» ធ្វើ​ឡើង​នៅ​ព្រឹក​ថ្ងៃទី​១៣ មីនា ២០១៥ នៅ​លើ​ជួរ​ភ្នំ​ស្រង់ ស្ថិត​ក្នុង​ស្រុក​គង​ពិសី ខេត្ត​កំពង់​ស្ពឺ ។ រឿង​ថ្មី​របស់​ផលិតកម្ម​ចក្ក​វាឡ​មាន​៣០​ភាគ ថត​រយៈ​ពេល​៣​ខែ ។ ', 'http://kohsantepheapdaily.com.kh/article/116539.html', 'http://i.kspg.co/2015/3/090dc573-e6b1-4460-97a0-823452d17f78.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('720', 'cl000001', 'u0000003', 'អតីត​អ្នកដឹកនាំ​សម្តែង​របស់​សាន់​ដេ​និង​SR​ធ្លាក់ខ្លួន​ពិការ​2​ឆ្នាំ​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ លោកស្រី ស៊ី​យ៉ា​រិ​ទ្ធិ​ហៅ​គា អតីត​អ្នកដឹកនាំ​សម្តែង​ល្បីឈ្មោះ​ម្នាក់​នៅ​ផលិតកម្ម​សាន់​ដេ និង​ផលិតកម្ម​ក្រៅ​ស្រុក គឺ SR បាន​ធ្លាក់ខ្លួន​ពិការ​២​ឆ្នាំ​មក​ហើយ ។', 'http://kohsantepheapdaily.com.kh/article/116531.html', 'http://i.kspg.co/2015/3/c349ebc8-910f-4e50-8229-b225ea9364a9.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('721', 'cl000001', 'u0000003', 'តារា​ស្រី​បរទេស​ចំនួន10ដួងលេចធ្លោ​ពី​ភាពធាត់​ទ្រលុកទ្រលន់', 'កោះសន្តិភាព​សូម​បង្ហាញ​ជូន​តារា​ស្រី​បរទេស ១០​រូប​លេចធ្លោ​ពី​ភាពធាត់ យោង​តាម​គេហស�ព័រ​បរទេស TheRichest  ។  ១-​នាង​ឃេ​ត អាប់​ថុ​ន តារា​ម៉ូ​ដែល និង​សម្តែង​អា​មេ​រិ​ក​វ័យ​២២​ឆ្នាំ នាង​ឃេ​ត អាប់​ថុ​ន មាន​កម្ពស់​១,៧៨​ម៉ែត្រនិង​មាន​ទំហំ​ដើមទ្រូង​៣៣ សង្ទីម៉ែត្រ ចង្កេះ​២៥​សង្ទីម៉ែត្រ និង​', 'http://kohsantepheapdaily.com.kh/article/116498.html', 'http://i.kspg.co/2015/3/04d0299e-4f3b-4ca2-9b89-7de128182972.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('722', 'cl000001', 'u0000003', 'ផលិតកម្ម​ថោ​ន​ព្រមាន​ប្តឹង​ទារ​ជាង​50​ម៉ឺ​ន​ដុល្លារចំពោះ​អ្នក​លួច​ចម្លង​វ៉ុល​ចម្រៀង​បុណ្យចូលឆ្នាំ​ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ ផលិតកម្ម Town បាន​ចេញ​សេចក្តី​ប្រកាស​ព័ត៌មាន ព្រមាន​ប្ដឹង​ទាមទារ​សំណង​ជា​ទឹកប្រាក់​មិន​ក្រោម​៥០​ម៉ឺន​ដុល្លារ ចំពោះ​អ្នក​ដែល​លួច​ចម្លង​អាល់​ប៊ុ​ម​ចូល​ឆ្នាំ​ប្រពៃណី​ជាតិ', 'http://kohsantepheapdaily.com.kh/article/116413.html', 'http://i.kspg.co/2015/3/a616897b-82a9-460c-ab11-e9615712c502.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('723', 'cl000001', 'u0000003', 'តារា​ថ្មី​ៗ​ក្នុង​រឿង​«​ហ្វេ​ស​ប៊ុ​ក​ផ្តាច់​វិញ្ញាណ​»​របស់​ផលិតកម្ម​អភិមង្គល​ស្ពឹក​មុខពេល​ផលិតករ​រិះគន់​ការ​មិន​គោរព​ពេលវេលា​សម្តែងនៅថ្ងៃ​បញ្ចាំង​រឿង​ជា​ផ្លូវការ', 'រាជធានី​ភ្នំពេញ ៖ ជា​ការ​មួយ​មិន​នឹក​ស្មាន​ដែល​បាន​កើតឡើង​ក្នុង​ពិធី​ដាក់​បញ្ចាំង​រឿង «​ហ្វេ​ស​ប៊ុ​ក​ផ្តាច់​វិញ្ញាណ​» របស់​ផលិតកម្ម​អភិមង្គល កាលពី​រសៀល​ថ្ងៃ​ទី​១១ មីនា ២០១៥ នៅ​រោងកុន​លុ​ច្ស ', 'http://kohsantepheapdaily.com.kh/article/116411.html', 'http://i.kspg.co/2015/3/890dd35b-014d-47b9-863e-32fc209fa450.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('724', 'cl000001', 'u0000003', 'ប៊ុ​ន ផៃ​លី​ន ថា​មិន​ខ្ចី​អែបអប​សុំ​គេ​ច្រៀង​តាម​ទូរទស្សន៍​ទេ', 'រាជធានី​ភ្នំពេញ ៖ តារាចម្រៀង​សិច​ស៊ី​នាង​ប៊ុ​ន ផៃ​លី​ន មិនសូវ​ឃើញ​បង្ហាញមុខ​តាម​កញ្ចក់​ទូរទស្សន៍​ដូច​តារា​ផ្សេង​ទេ ។ មូលហេតុ​នេះ ប៊ុ​ន ផៃ​លី​ន​អះអាង​ថា មក​ពី​នាង​មិន​យក​ខ្លួន​', 'http://kohsantepheapdaily.com.kh/article/116345.html', 'http://i.kspg.co/2015/3/fe598af6-3ed2-4d22-b30f-0fa89485cefa.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('725', 'cl000001', 'u0000003', 'សង្សារ​ប្រែប្រួល​បែក​បែរចិត្ត​គ្មាន​អាល័យបង្ហូរ​ទឹកខ្មៅ​កវី​ជា​បទ « ស្ពានឈើ​អភ័ព្វ »', 'បទ​ភ្លេង​នៃ​បទ​ចម្រៀង «​ស្ពានឈើ​អភ័ព្វ​» លោក​គ​ង្គ ប៊ុ​ន​ឈឿ​ន តែង​នៅ​ឆ្នាំ​១៩៥៤ ពេល​ដែល​លោក​នៅ​រៀន​វិទ្យាល័យ​ព្រះ​មុនី​វង្ស តែ​បាន​ដាក់​ទំនុកច្រៀង​ផ្សេង​ឈ្មោះ​ថា «​ខ្សែទឹក​កួច​» ។', 'http://kohsantepheapdaily.com.kh/article/116280.html', 'http://i.kspg.co/2015/3/15dd0700-00fa-478e-a4f2-52c8c5a8e7ae.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('726', 'cl000001', 'u0000003', 'សា​រ៉ា​យ ស​ក្ខ​ណា នៅ​អា​មេ​រិ​ក​សម្រាល​បានកូន​ស្រី​មួយ​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ អតីត​តារា​សម្តែង សម្បុរ​ស្រអែម នាង​សា​រ៉ា​យ ស​ក្ខ​ណា សម្រាល​បានកូន​ស្រី​ម្នាក់​នៅ​អា​មេ​រិ​ក​ទី​ដែល​នាង និង​ស្វាមី​កំពុង​រស់នៅ ។  តាម​រយៈ​ទំព័រ​ហ្វេ​ស​ប៊ុ​ក សា​រ៉ា​យ ស​ក្ខ​ណា ', 'http://kohsantepheapdaily.com.kh/article/116277.html', 'http://i.kspg.co/2015/3/5a933784-e7e3-43f6-8305-6a539099fe4a.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('727', 'cl000001', 'u0000003', 'តារា​ថ្មី​លោក​ឆឹ​ម សក្ក​ដា រីករាយ​ចំពោះ​តំណែង​តួឯកលើក​ដំបូង​ក្នុង​ខ្សែភាពយន្ត​រឿង «​ហ្វេ​ស​ប៊ុ​ក​ផ្តាច់​វិញ្ញាណ​»', 'រាជធានី​ភ្នំពេញ ៖ លោក​ឆឹ​ម សក្ក​ដា ជា​តារា​សម្តែង​ថ្មី និង​ជា​អតីត​តារា​បង្ហាញ​ម៉ូ​ដ​ផ​ប​ម៉ូ​ដែល​កំពុង​តែ​ស្រូប​យក​ប្រជាប្រិយភាព​តាម​រយៈ​ការ​សម្តែង​ក្នុង​ខ្សែភាពយន្ត​មួយ​ចំនួន ។ ថ្មី​ៗ​នេះ​លោក​សក្ក​ដា', 'http://kohsantepheapdaily.com.kh/article/116275.html', 'http://i.kspg.co/2015/3/7e11477f-3071-429f-9dd5-39dfc3c325d1.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('728', 'cl000001', 'u0000003', 'អ្នក​កាន់​សាសនា​ហិណ្ឌូ​ប្រារព្ធ​ពិធី​ធម្ម​សាសនា​រឿង «រាមកេរ្តិ៍» នៅខេត្តសៀមរាប​', 'ខេត្តសៀមរាប ៖ ជា​លើក​ទី​១ ​ដែល​អ្នក​កាន់សាសនា​ហិណ្ឌូ​យ៉ាងច្រើន​កុះករ​ដែលមក​ពី​ប្រទេស​ឥណ្ឌា​ បាន​ជួបជុំ​គ្នា​លើ​ទឹកដី​នៃ​ខេត្ត​សៀមរាប​ កាលពី​ថ្ងៃ​ទី​០៧ ​មីនា ​២០១៥​ ដើម្បី​ប្រា​រ​ព្ធ​ពិធី​បើក​ធម្មទេសនា រឿង​រាមកេរ្តិ៍ ​​ក្រោម​វត្តមាន​របស់​លោក​សុខ អាន​ឧបនាយក', 'http://kohsantepheapdaily.com.kh/article/116214.html', 'http://i.kspg.co/2015/3/74a1b4db-e472-4d0b-8e7d-63b2daa0f4b9.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('729', 'cl000001', 'u0000003', 'មង្គលការ​អ្នក​នាង​ស្រី​តូច ចំណាន​និង​អ្នកឧកញ៉ា​គិត ម៉េ​ង ដើម្បី​ឱ្យ​កូន​ទទួល​មរតក​ស្របច្បាប់', 'រាជធានី​ភ្នំពេញ ៖ ពេល​នេះ អ្វី​ៗ ច្បាស់ការ​ណ៍ហើយ គឺ​អតីត​តារាចម្រៀង​អ្នក​នាង​ស្រី​តូច ចំណាន នឹង​ចូល​រោង​ជ័យ​ជាមួយ​អ្នកឧកញ៉ា​គិត ម៉េ​ង នៅ​ថ្ងៃ​ទី​២០ មីនា ឆ្នាំ​២០១៥ ។ មង្គលការ​នេះ', 'http://kohsantepheapdaily.com.kh/article/116200.html', 'http://i.kspg.co/2015/3/9ca33e5b-e31f-4b87-9df6-4355fd76dd07.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('730', 'cl000001', 'u0000003', 'របាំ​បុរាណ​រឿង «​ជីវិត​អសុរា​» សម្តែង​នៅ​ថ្ងៃ​ទី​12 មីនា​អំពាវនាវ​សូម​ឱ្យ​មហាជន​ទូទៅ​ចូលរួម​ទស្សនា', 'រាជធានី​ភ្នំពេញ ៖ ក្រុម​សិល្បៈ​សុ​ភី​លី​ន នឹង​សម្តែង​របាំ​មួយ​មាន​ចំណងជើង​ថា «​ជីវិត​អសុរា​» ពី​ថ្ងៃ​ទី​១២ មីនា ដល់ទី​១៤ មីនា ២០១៥ វេលា​ម៉ោង​៧​យប់ ។ បន្ទាប់​មក​នៅ​ថ្ងៃ​ទី​១៥ មីនា', 'http://kohsantepheapdaily.com.kh/article/116194.html', 'http://i.kspg.co/2015/3/5a27d6d9-50ca-4f45-b3c7-01e3b86a7c2e.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('731', 'cl000001', 'u0000003', 'ប្រាសាទបុរាណ​4​នៅ​រតនគិរី 3​បាត់​រូបរាង 1​នៅ​សល់​40%', 'ខេត្តរតនគិរី ៖ មាន​ប្រាសាទ​ចំនួន​៤ គឺ​ប្រាសាទ​ព្រះពុទ្ធ ស្ថិត​ក្នុងភូមិ​ប៉ុង ឃុំ​ប៉ុង ស្រុក​វ៉ឺ​ន​សៃ​, ប្រាសាទ​យ៉ាក​ណាំង  ស្ថិត​ក�នុងភូមិ​ដ​តូច ឃុំ​យ៉ាង​ទុង  ស្រុក​អូ​រយ៉ា​ដាវ​, ប្រាសាទ​បាក់​យាង', 'http://kohsantepheapdaily.com.kh/article/116415.html', 'http://i.kspg.co/2015/3/86023bcf-7ed0-47d4-8863-4e5719fbd69e.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('732', 'cl000001', 'u0000003', 'បើ​ទៅ​ខេត្តបន្ទាយមានជ័យ​កុំ​ភ្លេច​ឆៀង​ចូល​ងូតទឹក​លេងនិង​ទស្សនា​សត្វ​ស្លាប​កម្រ​នៅ​រមណីយដ្ឋាន​អាង​ទឹក​ត្រពាំង​ថ្ម', 'ខេត្តបន្ទាយមានជ័យ ៖ រមណីយដ្ឋាន​អាង​ទឹក​ត្រពាំង​ថ្ម ស្ថិត​នៅ​ទិសឦសាន​នៃ​ខេត្តបន្ទាយមានជ័យ គ្របដណ្តប់​ដោយ​ភូមិ​ចំនួន​៣ គឺ​ភូមិ​ត្រពាំង​ថ្ម​ខាងជើង ភូមិ​សំបូរ និង​ភូមិ​ពង្រ ក្នុង​ឃុំ​ប៉ោយ​ចារ ', 'http://kohsantepheapdaily.com.kh/article/116495.html', 'http://i.kspg.co/2015/3/3f06d832-615d-4275-a753-1f0ed9a0cf88.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('733', 'cl000001', 'u0000003', 'រឿងរ៉ាវ​ប្លែក​ៗ​កើតឡើង​នៅ​លើ​ពិភពលោក', '១-​បណ្តើរ​គោ​ទៅ​បង្អួត​ប្រធានាធិបតី​ចិន អាជ្ញាធរ​នូ​វ៉ែល​សេ​ឡង់ បណ្តើរ​គោ​យក​ទៅ​បង្ហាញ​ប្រធានាធិបតី​ចិន លោក​ស៊ី ជី​ន​ពីង ខណៈ​ពេល​មេដឹកនាំ​ចិន​រូប​នេះ ចូល​ទស្សនា​កន្លែង​បសុសត្វ ក្នុង​ក្រុង​អូ​ក្លែ​ន ប្រទេស​នូ​វ៉ែ​ល​សេ​ឡង់ ។', 'http://kohsantepheapdaily.com.kh/article/116344.html', 'http://i.kspg.co/2015/3/54c0dc33-33c6-4f25-be05-429fffe50d07.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('734', 'cl000001', 'u0000003', 'សម្បកឈើ​Cinnamon​ជួយ​ប្រឆាំង​ជំងឺ​បេះដូង​អ្នក​កើត​ទឹកនោមផ្អែម', 'បណ្តា​អ្នកជំនាញ​អាហារូបត្ថម្ភ​រាង​កាយ​របស់​អា​មេ​រិ​កបាន​រក​ឃើញ​ថា សម្បកឈើ Cinnamon​ដែល​គេ​យក​ទៅ​ច្នៃ​ជា​គ្រឿងទេស​និង​ទទួល​បានការ​ពេញ​និយម​ពី​ប្រទេស​ជា​ច្រើន​គឺ', 'http://kohsantepheapdaily.com.kh/article/116211.html', 'http://i.kspg.co/2015/3/3ad05da2-fa38-4ca2-aea5-54e0af7cd13f.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('735', 'cl000001', 'u0000003', 'ការ៉ុត​ជួយ​កែ​លម្អ​សមត្ថភាព​ផ្តល់​កំណើត​របស់​បុរស', 'ការ៉ុត​ជា​ប្រភេទ​បន្លែ​ដែល​គេ​ដឹង​ថា ផ្តល់​អត្ថប្រយោជន៍​ដល់​ភ្នែក ។ ប៉ុន្តែ​យោង​តាម​ការ​រក​ឃើញ​កាលពី​ពេល​ថ្មី​ៗ​ដោយ​ក្រុម​អ្នក​វិទ្យាសាស្ត្រ​អា​មេ​រិ​ក ការ៉ុត​មាន​អត្ថប្រយោជន៍​មួយ​ផ្សេង', 'http://kohsantepheapdaily.com.kh/article/116210.html', 'http://i.kspg.co/2015/3/ac0133d1-e897-4ff7-afe3-82e2311fe5c5.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('736', 'cl000001', 'u0000003', 'ការ​រំជួលចិត្ត​ដោយ​កំហឹង ផ្តល់​ភាព​គ្រោះថ្នាក់​ចំពោះ​បេះដូង​ស្មើនឹង​ការ​ជក់បារី', 'កំហឹង​គឺជា​ភាព​រំជួល​ក្នុង​អារម្មណ៍​ដែល​មនុស្ស​គ្រប់​គ្នា​មាននៅ​គ្រប់ពេល​គ្រប់​វេលា ។ វា​ជា​បទ​ពិសោធន៍​សាមញ្ញ​មួយ​នៃ​ជីវិត​រស់នៅ​ក្នុង​សម័យ​ទំនើប ។ យ៉ាងណា​ក៏​ដោយ ការ​កើត​មាន​កំហឹង​គ្រប់', 'http://kohsantepheapdaily.com.kh/article/116209.html', 'http://i.kspg.co/2015/3/259d62bb-0d7f-401d-b202-b3cec0d3872a.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('737', 'cl000001', 'u0000003', 'ដេក​ពុំ​លក់ ជា​កត្តា​ហានិភ័យ​ចម្បង​នាំ​ឱ្យ​កើត​ជំងឺលើសឈាម', 'របាយការណ៍​ស្រាវជ្រាវ​មួយ​របស់​ចិន​បាន​បង្ហាញ​ថា បណ្តា​បុគ្គល​ដែល​មាន​បញ្ហា​ពិបាក​ដេក​ឱ្យ​លក់​ឬ​ដេក​ពុំ​លក់​ជា​ប្រចាំ ប្រឈម​យ៉ាង​ខ្លាំង​នឹង​បញ្ហា​សម្ពាធ​ឈាម​ឡើង​ខ្ពស់ ។ ', 'http://kohsantepheapdaily.com.kh/article/116208.html', 'http://i.kspg.co/2015/3/f8ed4c2a-cba5-475f-91eb-065b1aabcf91.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('738', 'cl000001', 'u0000003', 'ផឹក​«​តែ​»​ផ្តល់​អត្ថប្រយោជន៍​ច្រើន​យ៉ាង​ដល់​សុខភាព', 'រហូត​មក​ទល់​ពេល​នេះ នៅ​មិន​ទាន់​មាន​ប្រភព​ច្បាស់លាស់​មួយ​ណា​អះអាង​ថា «​តែ​»​ពេល​ប្រើ​ច្រើន​ផ្តល់​ផល​អាក្រក់​ចំពោះ​សុខភាព​នោះ​ទេ ពោល​គឺ​មិន​ដូច​ជា​កាហ្វេ​ឡើយ​ដែល​មានការ​អះអាង​ថា', 'http://kohsantepheapdaily.com.kh/article/116207.html', 'http://i.kspg.co/2015/3/62427af7-83f8-4907-b6ec-3fe4a9de1aa0.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('739', 'cl000001', 'u0000003', 'ការ​ដើរ​ទី​ជិត​ៗ​រយៈពេល​ខ្លី​ជួយ​បញ្ចៀស​ពិការ​ភាព​ចំពោះ​មនុស្ស​វ័យ​ចំណាស់', 'ការ​ស្រាវជ្រាវ​ថ្មី​មួយ​បាន​រក​ឃើញ​ថា ការ​ដើរ​ក្នុង​សួនឧទ្យាន​ឬ​ទី​សាធារណៈ​រយៈពេល​២០​នាទី​រៀងរាល់ថ្ងៃ អាច​ជួយ​រក្សា​សកម្មភាព​រាង​កាយ​មនុស្ស​ចាស់ និង​ឈាន​ទៅ​ទប់ស្កាត់​បានការ​ពិការ​ភាព ។ ', 'http://kohsantepheapdaily.com.kh/article/116206.html', 'http://i.kspg.co/2015/3/3173ee71-a755-4059-8a69-5f839b8422fe.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('740', 'cl000001', 'u0000003', 'តើ​ការ​ទទួលទាន​ផ្សិត​ពិតជា​អាច​ទប់ស្កាត់​មហារីក​មាត់ស្បូន​បាន​មែន​ទេ ?', 'គ្រប់​ៗ​គ្នា​ធ្លាប់តែ​ដឹង​ថា ផ្សិត​ជា​អាហារ​ដែល​សម្បូរ​ដោយ​សារធាតុចិញ្ចឹម​និង�មាន​កាឡូរី​ទាប  ប៉ុន្តែ​ក្រុម​អ្នក​ស្រាវជ្រាវ​អា​មេ​រិ​កបាន​ពិសោធ​ឃើញ​កាលពី​ពេល​ថ្មី​ៗ​នេះ​ថា វា​ក៏​អាច​ប្រើ​ប្រឆាំង​នឹង​ជំងឺ', 'http://kohsantepheapdaily.com.kh/article/116205.html', 'http://i.kspg.co/2015/3/0af104ee-8fa8-4424-8a6f-02dcbe3b9475.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('741', 'cl000001', 'u0000003', 'រមៀត​ជួយ​បន្ធូរ​ការ​រលាក បង្កើន​សុខភាព', 'ការ​ស្រាវជ្រាវ​បែប​វិទ្យាសាស្ត្រ​ថ្មី​មួយ​បាន​រក​ឃើញ​ថា មើម​រមៀត​ដែល​ជា​គ្រឿងផ្សំ​សំខាន់​មួយ​ក្នុង​សម្លការី មាន​សក្តានុពល​ប្រឆាំង​នឹង​ការ​រលាក​ក្នុង​រាង​កាយ និង​ជួយ​បង្កើន​សុខភាព​', 'http://kohsantepheapdaily.com.kh/article/116204.html', 'http://i.kspg.co/2015/3/251c491e-217e-4aad-aed5-29b6df61068c.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('742', 'cl000001', 'u0000003', 'រោគ​សញ្ញា​មាត់​ធ្មេញ​5​ចំណុច​ដែល​មិន​គួរ​មើល​រំលង !', 'សុខភាព​មាត់​ធ្មេញ​គឺជា​ផ្នែក​មួយ​សំខាន់​ដែល​គេ​មិន​គួរ​មើល​រំលង ។ ចូរ​ប្រញាប់​ទៅ​ពិគ្រោះ​ជាមួយ​គ្រូពេទ្យ​ជំនាញ នៅ​ពេល​អស់លោក​អ្នក​កើត​ចេញ​រោគ​សញ្ញា​៥​ចំណុច​ដូច​ខាងក្រោម ៖', 'http://kohsantepheapdaily.com.kh/article/116203.html', 'http://i.kspg.co/2015/3/72908c62-e32f-4986-bdf9-4b542480a6b5.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('743', 'cl000001', 'u0000003', 'អាហារ​ដែល​មាន​ប្រយោជន៍​ចំពោះ​សុខភាព​បេះដូង​ជួយ​កុំឱ្យ​មាន​ខ្លាញ់​ក្នុង​សរសៃឈាម', 'ការ​ជ្រើសរើស​ចំណីអាហារ​យក​មក​បរិភោគ គឺជា​កត្តា​សំខាន់ ព្រោះ​អាហារ​មួយ​ចំនួន​អាច​បង្ក​ឲ្យ​កើត​មាន​ជាតិ​ខ្លាញ់​ក្នុង​សរសៃឈាម​បេះដូង បង្ក​ទៅ​ជា​ធាត់​ដុះពោះ​កំប៉ោង ទៅ​ជា​ជំងឺលើសឈាម', 'http://kohsantepheapdaily.com.kh/article/116199.html', 'http://i.kspg.co/2015/3/0c173ee4-c30e-4918-b003-4f39defcea3c.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('744', 'cl000001', 'u0000003', 'ប្រើ​ថ្នាំ​ក្រពះ​ច្រើន​អាច​នាំ​ឱ្យ​ខ្វះ​វីតាមីន​B12​ដែល​ជា​តម្រូវការ​ចាំបាច់​របស់​រាង​កាយ', 'ក្រុម​អ្នកជំនាញ​អា​មេ​រិ​កបាន​និយាយ​ថា ការ​ប្រើ​ថ្នាំ​ព្យាបាល​រលាក​និង​ដំបៅ​ក្រពះ​រយៈពេល​យូរ​អាច​បណ្តាល​ឱ្យ​គ្រោះថ្នាក់​ដល់​កម្រិត​វីតាមីន​B12​ក្នុង​រាង​កាយ ។ ', 'http://kohsantepheapdaily.com.kh/article/116198.html', 'http://i.kspg.co/2015/3/1eacfdec-d729-4401-b66c-6905562bb3cb.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('745', 'cl000001', 'u0000003', 'សម្តេច​​កិត្តិ​ព្រឹ​ទ្ធ​​បណ្ឌិត​​ផ្តល់​ប្រាក់​​៣០០០​ដុល្លារ​​ដល់​ស្ត្រី​រង​គ្រោះ​​ដោយ​ទឹក​​អាស៊ីត', 'រាជធានី​ភ្នំពេញ ៖ បន្ទាប់​ពី​បាន​ទទួល​ព័ត៌​មាន​ថា មាន​យុវតី​សន្តិសុខ​រោងចក្រ​ម្នាក់​បាន​រង​គ្រោះ​ដោយ​ទឹក​អាស៊ីត សម្តេច​កិត្តិ​ព្រឹ​ទ្ឋ​បណ្ឌិត​ ប៊ុ​ន រ៉ា​នី ​ហ៊ុន សែន ​បាន​ផ្តល់​ទឹកប្រាក់​ចំនួន​៣០០០​ដុល្លារ​អា​មេ​រិ​ក ​ដល់​យុវតី​រង​គ្រោះ តាម​រយៈ​លោក​សាស្ត្រាចារ្យ​ឈាង រ៉ា​', 'http://kohsantepheapdaily.com.kh/article/116405.html', 'http://i.kspg.co/2015/3/2bb6b719-16d1-4086-a0fd-05b1d4bc9563.jpg?w=285&h=170&mode=carve', '20150314');
INSERT INTO "tbnews" VALUES ('746', 'cl000001', 'u0000003', 'កិច្ច​សហប្រតិបត្តិការ ក្រុមហ៊ុន Huawei ជាមួយ​និង​ក្រសួង​ប្រៃសណីយ៍ និង​ទូរគមនាគមន៍​កម្ពុជា ឈាន​ដល់​;​ដំណាក់កាល​ល្អ​ប្រសើរ', 'រដ្ឋមន្ត្រីក្រសួង​ប្រៃសណីយ៍ និង​ទូរគមនាគមន៍​នៃ​ប្រទេស​កម្ពុជា ឯកឧត្តម ប្រាក់ សុ​ខុន បាន​ធ្វើ​ដំណើរ​ទស្សនកិច្ច​ទៅ​កាន់​ទីក្រុង​បា​សេ​ឡូ​ណា តាម​ការ​អញ្ជើញ​របស់​មហាសន្និបាត​ទូរស័ព្ទ​ពិភពលោក', 'http://kohsantepheapdaily.com.kh/article/116389.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150314');
