INSERT INTO "tbnews" VALUES ('789', 'cl000001', 'u0000003', 'នាយក​រដ្ឋមន្ត្រី​ជប៉ុន​សង្�&ឹមថា CPP & CNRP បន្ត​រក្សា​វប្បធម៌​សន្ទនា', 'ទីក្រុង​សេន​ដាយ ប្រទេស​ជប៉ុន ថ្ងៃ​ទី​១៥ មីនា ឆ្នាំ​២០១៥ ៖ នៅ​ក្នុង​ជំនួប​ទេ្វ​ភាគី​ជាមួយ​សម្តេច​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា នៅ​ឯ​សណ្ឋាគារ​រ៉ូ​យ៉ា​ល់ ផា​កក', 'http://kohsantepheapdaily.com.kh/article/116621.html', 'http://i.kspg.co/2015/3/2f520fad-c2a3-4cd7-b16f-6c8d3c62ad39.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('790', 'cl000001', 'u0000003', 'សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត​ប៊ុន រ៉ានី ហ៊ុន សែន ផ្តល់​ផ្ទះឈើ​មួយ​ខ្នង​ជូន​ស្ត្រី​មេម៉ាយ​កូន​ប្រាំ​រង​គ្រោះ​ដោយ​អគ្គិ​ភ័យ​ឆេះ​ផ្ទះ', 'ខេត្ត​ព្រះ​វិហារ​ ៖ ផ្ទះ​ឈើ​មួយ​ខ្នង​ធ្វើ​អំពី​ឈើ​ប្រក់​ស័ង្គ​សី​ជា​អំណោយ​របស់​សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត​ ប៊ុន រ៉ានី ហ៊ុន សែន  ប្រធា​ន​កាក​បាទ​ក្រហម​កម្ពុជា​តាម​រយៈ​សាខា​កាក​បាទ​', 'http://kohsantepheapdaily.com.kh/article/116620.html', 'http://i.kspg.co/2015/3/78139d9d-b0f3-4632-9fc8-26dc36837bec.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('791', 'cl000001', 'u0000003', 'អ្នក​យក​ព័ត៌មាន​ឲ្យ​ទូរទស្សន៍ គេហទំព័រ និង​កាសែត​ក្នុងស្រុក​៥​អង្គភាព ត្រូវ​គេ​គំរាម​បាញ់​សម្លាប់ ជេរ​ប្រមាថ និង​បោក​កំទេច​កាមេរ៉ា ទូរស័ព្ទ', 'ខេត្តបន្ទាយមានជ័យ ៖ អ្នក​យក​ព័ត៌មាន​ឲ្យ​ទូរទស្សន៍ គេហទំព័រ និង​កាសែត​ក្នុងស្រុក​ចំនួន​៤​នាក់ មក​ពី​៥​អង្គភាព រួម​មាន​ទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍​, គេហទំព័រ កាសែត​នគរវត្ត​, គេហទំព័រ​កាសែត​ដើម​អំពិល​,', 'http://kohsantepheapdaily.com.kh/article/116617.html', 'http://i.kspg.co/2015/3/c4cc27ec-07c1-4154-96c7-034d7669ac4d.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('792', 'cl000001', 'u0000003', 'អតីត​អនុ​ប្រធាន​តុលាការ​ខេត្ត​ព្រះ​វិហារ​ធ្លាក់​ខ្លួន​ឈឺដេក​លើ​កន្ទេល​ដង្ហោយ​រក​សប្បុរស​ជន​ជួយ', 'ខេត្ត​ព្រះ​វិហារ​ ៖ លោក​នូ វ៉េង អតីត​អនុ​ប្រធាន​តុលាការ​ខេត្ត​ព្រះ​វិហារ​បាន​ធ្លាក់​ខ្លួន​ឈឺពិការដេក​នៅ​លើ​កន្ទេល​អស់​រយៈ​ពេល​៤​ឆ្នាំ​មក​ហើយ​។ ខណៈ​ជំងឺ​របស់​លោក​កាន់​តែ', 'http://kohsantepheapdaily.com.kh/article/116616.html', 'http://i.kspg.co/2015/3/b8cfb303-d0f6-4e3a-a43c-e5a67bbc098a.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('793', 'cl000001', 'u0000003', 'ថា​កុំឱ្យ​គេ​ឮ', '* អ្នក​ជិះ​រថយន្ត​ម៊ែ​រ​សឺ​ដេ​ស​លឿន​ពេក ខឹង​នគរបាល​ចរាចរណ៍​នៅ​ខេត្តកំពង់ស្ពឺ​ឃាត់​ផាកពិន័យ បាន​ដក​កាំភ្លើងខ្លី​បាញ់​អស់​មួយ​បង់ ទោះបី​ភ័យ​ខ្លួន​ចូល​សារភាព​ក៏​ដោយ ក៏​ត្រូវ​បាន​ចាប់ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/116613.html', 'http://i.kspg.co/2015/3/a6da3754-d1c6-4cfa-a7ef-6a71f6f4f1c2.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('794', 'cl000001', 'u0000003', 'អ្នក​បំពុល​ត្រី​ងាប់​រាប់រយ​គីឡូក្រាម​ ៥​ថ្ងៃ​ហើយ​មិន​ទាន់​ត្រូវ​បាន​ចាប់ខ្លួន​នៅឡើយ', 'ខេត្តកំពង់ចាម ​៖ ត្រី​ជា​ច្រើន​ប្រភេទ​តូច​ធំ ត្រូវ​បាន​គេ​សង្ស័យ​មាន​ជន​មិន​ស្គាល់​មុខលួច​បំពុល​បណ្តាល​ឲ្យ​ងាប់​អណ្តែត​ហូរ​តាម​ទឹក​ព្រែក​គៀន​ជ្រៃ ស្ថិត​នៅ​ភូមិ​ឥន្ទនេល ឃុំ​', 'http://kohsantepheapdaily.com.kh/article/116611.html', 'http://i.kspg.co/2015/3/d31e45e2-f812-420f-a692-ec19326cd31f.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('795', 'cl000001', 'u0000003', 'ចាត់វិធានការ​​​ក្ដៅ​ផ្អាក​អាជីវកម្ម​បូម​ខ្សាច់​គ្មាន​ច្បាប់​ និង​លុប​បំបាត់​ឈ្មួញកណ្ដាល​ម៉ៅ​ការ​ពី​អ្នក​បូម', 'ខេត្តពោធិ៍សាត់ ​៖ ​ដោយសារ​ការ​​បូម​ខ្សាច់​គ្មាន​ច្បាប់​អនុញ្ញាត​អនាធិបតេយ្យ​ក្នុង​ខេត្តពោធិ៍សាត់​នៅ​តែ​ធ្វើ​សកម្មភាព​រាល់ថ្ងៃ​ ហើយ​អាជ្ញាធរ​ដែនដី​នៅ​តែ​ឈរ​មើល​មិន​ព្រម​ចាត់​', 'http://kohsantepheapdaily.com.kh/article/116607.html', 'http://i.kspg.co/2015/3/9dcc7bbe-0ec9-493c-8540-68435fe34dfe.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('796', 'cl000001', 'u0000003', 'អាវុធហត្ថ​ខេត្ត​ចាប់ខ្លួន​ជនសង្ស័យ​ពី​បទ​រំលោភ​សេព​ស​ន្ទ​វៈ​បញ្ជូន​ទៅ​តុលាការ', 'ខេត្តសៀមរាប​ ៖ ជនសង្ស័យ​ម្នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​ធ្វើការ​ឃាត់ខ្លួន​ពី​បទ​រំលោភ​សេព​ស​ន្ទ​វៈ​​លើ​ក្មេង​ស្រី​អាយុ​១៦​ឆ្នាំ​ ដែល​ត្រូវជា​សង្សារ​ទើបតែ​ស្គាល់គ្នា​រយៈពេល​៤-​៥​ខែ​តាម​ទូរស័ព្ទ ប៉ុន្តែ​ត្រូវ​ម្តាយ​ជន​រង​គ្រោះ​​ប្តឹង​ប៉េ​អឹ​ម​ខេត្ត​ឲ្យ​ជួយ​អន្តរាគមន៍​ ។', 'http://kohsantepheapdaily.com.kh/article/116603.html', 'http://i.kspg.co/2015/3/bff0f4bd-cc7a-4629-ad11-53727d383e65.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('797', 'cl000001', 'u0000003', 'អតីត​អ្នក​យក​ព័ត៌​មាន​វិទ្យុ​អាស៊ី​សេរី​ទទួល​មរណ​ភាព​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តាម​សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា អតីត​អ្នក​យក​ព័ត៌​មាន​វិទ្យុ​អាស៊ី​សេរី​ លោក​សូ សេរី ហៅ​សុខ សេរី បាន​ទទួល​មរណ​ភាព​ហើយ​កាល​ពី​ព្រឹក​មិញ​នៅ​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/116601.html', 'http://i.kspg.co/2015/3/b2a157e4-84d5-4a79-bbc8-b287d5a37830.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('798', 'cl000001', 'u0000003', 'សមត្ថកិច្ច​​ខេត្តកំពង់ឆ្នាំង​ចាប់​ជន​ចែកចាយ​ថ្នាំ​ញៀន​ដ៏​សកម្ម​ម្នាក់​បញ្ជូន​ទៅ​តុលាការ', 'ខេត្តកំពង់ឆ្នាំង ៖ ជន​ចែកចាយ​ថ្នាំ​ញៀន​ដ៏​សកម្ម​ម្នាក់​ត្រូវ​បាន​កម្លាំង​នគរបាល​ក្រុង​កំពង់ឆ្នាំង​ចាប់​ឃាត់​នៅ​វេលា​ម៉ោង៨​ និង​៣០​នាទី​ព្រឹក​ថ្ងៃ​ទី​១៥ ខែ​មីនា ឆ្នាំ​២០១៥ នៅផ្ទះ​ជនសង្ស័យ​ខាងលើ​ ស្ថិត​ក្នុង​ភូមិ​កណ្តាល', 'http://kohsantepheapdaily.com.kh/article/116600.html', 'http://i.kspg.co/2015/3/db8900fb-7a75-498f-8ff9-298370c2dd6c.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('799', 'cl000001', 'u0000003', '​ស្ទាក់​ចាប់​បាន​រថយន្ត​អង្គការ​អភិរក្ស​ព្រៃឈើ​ដែល​ជំរិត​ទារ​ប្រាក់​ហើយ​ប្លន់​យក​ឈើ​ម្ចាស់​សិប្បកម្ម​មួយ​កន្លែង​នៅ​ស្រុក​សំឡូត', 'ខេត្តបាត់ដំបង​ ៖ មន្ត្រី​រដ្ឋបាល​ព្រៃឈើ​បាន​អះអាង​ថា ស្ថាន​ប័ន​ខ្លួន និង​មន្ត្រី​បរិស្ថាន​បាន​ស្ទា​ក់​ចាប់​រថយន្ត​ម៉ា​កកូ​រ៉ូ​ឡា​១​គ្រឿង​របស់​អង្គការ​អភិរក្ស​ព្រៃឈើ ដែល​សំបូរ​ទៅ​ដោយ​រឿងអាស្រូវ​មួយ​ប្រចាំ​ខេត្តបាត់ដំបង', 'http://kohsantepheapdaily.com.kh/article/116598.html', 'http://i.kspg.co/2015/3/f55bed28-c056-432f-b0c0-e69085e0534c.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('800', 'cl000001', 'u0000003', 'សង្ស័យ​បែក​ថ្នាំ​យក​ពន្លាក​អា​រ.ក​ប៉ុនប៉ង​ធ្វើ​អត្តឃាត', 'ខេត្ត​មណ្ឌលគិរី ៖ បុរស​វ័យ​ជាង​៣០​ឆ្នាំ​ម្នាក់​បាន​យក​ពន្លាក​អា​រ.ក​ខ្លួនឯង​ប៉ុនប៉ង​ធ្វើ​អត្តឃាត​ ​រួច​ស្រែ​កថារ​មាស់ក​ណាស់ ហេតុ​ការ​ណ៏​ដ៏​ភ្ញាក់ផ្អើល​មួយ​នេះ​បាន​កើតឡើង​នៅ​ម៉ោង​ប្រមាណ​ជា​២​រសៀល​ថ្ងៃ​ទី​១៤​ ខែមីនា', 'http://kohsantepheapdaily.com.kh/article/116597.html', 'http://i.kspg.co/2015/3/09ad0a2d-9990-4189-adf8-b0d3f6d6d4c6.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('801', 'cl000001', 'u0000003', 'នាយ​រង​កង​ដឹក​ជញ្ជូ​ន​កងពល​តូច​លេខ​៧០​ម្នាក់​ស្រវឹង​ស្រា​ដក​កាំ​ភ្លើងបាញ់​ភ្ញុច​ដី​ផ្លា​តគ្រាប់​ត្រូវ​ក្មេង​ប្រុស​ម្នាក់​​របួសធ្ងន់', 'រាជ​ធានី​​ភ្នំពេញ​៖ ក្មេង​ប្រុស​ម្នាក់​បាន​រង​របួស​​ធ្ងន់​ដោយសារ​​មន្ត្រី​យោធា​ម្នាក់ដែល​មាន​ឋានៈ​ជា​នាយ​រង​កង​ដឹក​ជញ្ជូនកងពលតូច​លេខ​៧០​ផឹកស្រា​ស្រវឹង ហើយ​បាន​ដក​', 'http://kohsantepheapdaily.com.kh/article/116595.html', 'http://i.kspg.co/2015/3/cab0f1c9-7c48-458f-94de-ef26f47b7ff9.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('802', 'cl000001', 'u0000003', 'ខណ្ឌដូនពេញ​វាយ​កម្ទេច​​ទូ​ហេ្គ​ម​ប្រភេទ​ល្បែង​ស៊ីសងចំនួន​៨៩​ទូ​ជា​​លើក​ទី​២', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នេះ​ជា​លើក​ទី​២​ហើយ​ដែល​សាលា​ខណ្ឌដូនពេញ​បាន​វាយ​កម្ទេច​ទូ​ហ្គេ​ម​ប្រភេទ​ល្បែង​ស៊ីសង​ចំនួន​៨៩​ទូ​ ក្នុង​នោះ​មាន​ហ្គេ​ម​ប្រភេទ​បាញ់​ត្រី​ចំនួន​១៤​ទូ​ ', 'http://kohsantepheapdaily.com.kh/article/116591.html', 'http://i.kspg.co/2015/3/3b46a2ba-95ff-435b-8c7e-7cb7bf06ea92.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('803', 'cl000001', 'u0000003', 'ប្រជាពលរដ្ឋ​មាន​ជំនឿ​ថា​ពស់ថ្លាន់​ចូល​ផ្ទះ​នាំ​សេចក្តីសុខ', 'រាជ​ធានីភ្នំពេញ ៖ ពស់ថ្លាន់​មួយ​ក្បាល​លូន​ចូល​ផ្ទះ​ម្តង​រួច​ហើយ​បាន​លូន​ចេញ​ទៅ​វិញ ពេល​ដែលម្ចាស់ផ្ទះ​អុជ​ធូប​បន់ស្រន់​បាន​មួយ​ម៉ោង​ពស់ថ្លាន់​បាន​លូន​មក​វិញ បង្ក​ឲ្យ​', 'http://kohsantepheapdaily.com.kh/article/116589.html', 'http://i.kspg.co/2015/3/66e059fd-bcdd-414e-8cf0-2a9829d5a591.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('804', 'cl000001', 'u0000003', 'សម្តេច​តេ​ជោ ហ៊ុន សែន ៖ កម្ពុជា​ប្តេជ្ញា​សហការ​ជាមួយ​ដៃគូ​ពាក់ព័ន្ធ ដើម្បី​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ', 'ទីក្រុង​សេន​ដាយ ប្រទេស​ជប៉ុន ថ្ងៃ​ទី​១៤ មីនា ២០១៥ ៖ ក្នុង​ឱកាស​អញ្ជើញ​ចូលរួម​ក្នុង​សន្និសីទ​ពិភពលោក​លើក​ទី​៣ របស់​អង្គការសហប្រជាជាតិ ស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ​នៅ​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/116575.html', 'http://i.kspg.co/2015/3/eabacc49-2619-4240-9437-b113a1bbcacf.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('805', 'cl000001', 'u0000003', 'អ្នក​ផ្ទុកមេរោគ​អេ​ដស៍ នឹង​ចាស់ជរា​ប្រមាណ​ជាង​៣០០​នាក់ ទទួល​អំណោយអនុ​សាខា​កាកបាទក្រហម​ក​កម្ពុជា​ខណ្ឌ​៧​មក​រា', 'រាជធានី​ភ្នំពេញ​៖ អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ខណ្ឌ​៧​មក​រា កាលពី​នៅ​ព្រឹក ថ្ងៃ​ទី​១៤ ខែ​មិ​នា ឆ្នាំ​២០១៥​បាន​នាំ​យក​នូវ​អំណោយ ដ៏​ថ្លៃថ្លា​របស់​សម្តេច​គត្តិ​ព្រឹទ្ធ​បណ្ឌិត ប៊ុ​ន រ៉ា​នី ហ៊ុន សែន​ប្រធាន​កាកបាទក្រហមកម្ពុជា ចែក​ជូន​ប្រជាពលរដ្ឋ​ចាស់ជរា នឹង​មាន​ជីវភាព​ខ្វះខាត', 'http://kohsantepheapdaily.com.kh/article/116564.html', 'http://i.kspg.co/2015/3/faca536e-bf1b-4869-b022-13e69413a962.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('806', 'cl000001', 'u0000003', 'លបលួចចូលកាប់​​​​​ស្រ្តី និង​កូនតូច​​អាយុ​៣​ឆ្នាំ ​ខណៈ​កំពុង​​ដេក​​​​លក់​​​ក្នុង​ផ្ទះ  ​', 'ខេត្ត​​ប៉ៃ​លិន ៖ មានហេតុការណ៍កាប់​ប្រហារ​​មួយ បណ្តាល​ឱ្យ​ស្ត្រី​វ័យ​ក្មេង​ម្នាក់ និង​កូនស្រី​តូច​ម្នាក់​ទៀត​​អាយុ​៣​ឆ្នាំ រង​របួសធ្ងន់ហើយ​​ត្រូវ​​​សាច់ញាតិ និង​អ្នក​ជិត​ខាងជួយ​​បញ្ជូន​ទៅ​សង្គ្រោះ​នៅ​មន្ទីរពេទ្យ​បង្អែក​ខេត្ត ខណៈ​​កំពុង​តែ​នាំ​គ្នា​​ដេក​លង់លក់​សុខ​ៗ​ក្នុងផ្ទះ ​ក៏​ត្រូវ​', 'http://kohsantepheapdaily.com.kh/article/116562.html', 'http://i.kspg.co/2015/3/2abc6552-c7dc-40fd-a6b5-3d6815820e20.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('807', 'cl000001', 'u0000003', 'ទណ្ឌិត៥នាក់​​ចាប់​មន្ត្រី​អនុរក្ស​ពន្ធនាគារ​ធ្វើជា​ចំណាប់​ខ្មាំង', 'ខេត្ត​ឧត្តរមានជ័យ ៖ កាលពី​វេលា​​ម៉ោង០​៤​រសៀល​​ថ្ងៃ​ទី​១៣ មីនា ​២០១៥​ ស្ថិត​នៅ​​​ពន្ធ​ធនាគា​រខេត្ត​ក្នុងភូមិ​ឆ្អើ​ប ឃុំ​ពង្រ ស្រុក​ចុង​កាល់ ខេត្ត​ឧត្តរមានជ័យ​ មានទ​ណ្ឌិត​៥​នាក់​​ដែល​ជាប់​ទោស​ពី​បទ​ប្លន់​​ប្រដាប់​អាវុធ​ហើយ​​ត្រូវ​​តុលាការ​សម្រេច​ឃុំខ្លួន​​នៅ​ក្នុង​ពន្ធ​ធនា​', 'http://kohsantepheapdaily.com.kh/article/116560.html', 'http://i.kspg.co/2015/3/44040047-31c1-4136-8219-fc862a549dbd.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('808', 'cl000001', 'u0000003', 'ក្រុម​យុវជននាំ​គ្នា​ជិះ​កង់ ដើម្បី​ផ្សព្វផ្សាយ​អំពី​ព្រឹត្តិការណ៍​អង្គរ​សង្ក្រាន្ដ​ឆ្នាំ​២០១៥', 'ខេត្តសៀមរាប​ ៖ ​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៤ ​មីនា​ ក្រុម​យុវជន នៃ​សហភាព​សហព័ន្ធ​យុវជន​កម្ពុជា ប្រមាណ​ជាង​១០០​នាក់ បាន​នាំ​គ្នា​ជិះ​កង់​ចម្ងាយ​ជាង​១០​គីឡូម៉ែត្រ ​ចេញពី​ទីរួមខេត្ត​សៀមរាប', 'http://kohsantepheapdaily.com.kh/article/116559.html', 'http://i.kspg.co/2015/3/918b23a4-eed0-4db3-839f-de6346e7735e.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('809', 'cl000001', 'u0000003', '​បុគ្គលិកអង្គការការពារ​សត្វ​ និងព្រៃ​ឈើ​ប្រចាំខេត្តបាត់ដំបងម្នាក់ជាប់​ចោទពីបទ​​ជម្រិតទារ​​​ប្រាក់​', 'ខេត្តបាត់ដំបង ៖ កាលពីវេលាម៉ោង០៣ និង​១០នាទីរសៀល​ថ្ងៃទី​១៣ មីនា ២០១៥ ស្ថិត​នៅ​ចំណុច​ភូមិអូរទាស ឃុំតាតោក ស្រុក​សំឡូត ​កម្លាំង​មន្ត្រី​ព្រៃឈើ​ និង​មន្ត្រីបរិស្ថាន​ស្រុកសំឡូត បាន​ធ្វើ​ការ​ស្ទាក់​ចាប់​រថយន្តម៉ាកកូរ៉ូឡា​មួយគ្រឿង​ ពណ៌ឈាមជ្រូក ពាក់​', 'http://kohsantepheapdaily.com.kh/article/116558.html', 'http://i.kspg.co/2015/3/3bfd2517-7b35-4928-891e-04b46f28e164.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('810', 'cl000001', 'u0000003', 'ឧត្តមសេនីយ៍​សេង ផ​ល្លី ៖ នគរបាល​ជាតិ​បម្រើ​ការងារ​តែ៧០ភាគរយ ៣០​ភាគរយទៀត​ភ្ជួរ​ឈ្មោះ', 'រាជធានីភ្នំពេញ ៖ លោកនាយ​ឧត្តមសេនីយ៍ ​សេង ផ​ល្លី ប្រធាន​បណ្ឌិត្យសភា​នគរបាល​កម្ពុជា ថ្លែង​ក្នុង​ពិធី​ផ្សព្វផ្សាយ​ផែនការ​យុទ្ធសាស្ត្រ ​រយៈពេល​៥​ឆ្នាំ​២០១៥-២០១៩', 'http://kohsantepheapdaily.com.kh/article/116557.html', 'http://i.kspg.co/2015/3/e9fb6bd9-ebed-4000-a99e-bac35a6760e5.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('811', 'cl000001', 'u0000003', 'ក្រុមចោរ ​AK ​បាញ់គំរាមប្លន់​អ្នកភូមិជីខលើ យកមាស១៥តម្លឹង ​និងលុយដុល្លារជិត៨ពាន់', 'ខេត្តកោះកុង ​៖ ចោរប្រដាប់អាវុធមួយ​ក្រុមបានប្រើបា្រស់កំាភ្លើង​ AK ​ធ្វើសកម្មភាពបាញ់១៦គ្រាប់ ​គំរាមប្រជាពលរដ្ឋ​មួយគ្រួសារយក​មាសអស់១៥តម្លឹង ', 'http://kohsantepheapdaily.com.kh/article/116622.html', 'http://i.kspg.co/2015/3/81116394-92ae-4af2-87b8-b5f19a23dc03.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('812', 'cl000001', 'u0000003', 'កូនមេគយម្នាក់​និងបក្ខពួកបាញ់បោះ​កន្លែង​កម្សាន្តបង្កការ​ភ្ញាក់ផ្អើល​កណ្តាល​ក្រុងភ្្នំពេញ', 'រាជធានីភ្នំពេញ ​៖ សំឡេងផ្ទុះជាច្រើនគ្រាប់ ​បានត្រូវគេឮសូរនៅ​មុខមណ្ឌល​កម្សាន្តមួយកន្លែង​ស្ថិតនៅសង្កាត់បឹងត្របែក​ ខណ្ឌចំការមន​ បង្កឲ្យមានការ​', 'http://kohsantepheapdaily.com.kh/article/116596.html', 'http://i.kspg.co/2015/3/8a4c3aeb-fb82-49fe-a67d-b127f51d5a12.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('813', 'cl000001', 'u0000003', 'ចេញពី​ផឹក​ទឹកត្នោតជូ​រចោរ​កាប់​ប្លន់​យក​ម៉ូតូទៅ​បាត់', 'រាជ​ធានី​ភ្នំពេញ ៖ បុរស​២​នាក់​ចេញពី​ផឹក​ទឹកត្នោតជូរ​ត្រូវ​ជនសង្ស័យ​កាប់​​​ប្លន់​យក​ម៉ូតូ​នៅ​ក្បែរកន្លែង​លក់​ទឹកត្នោតជូរ តាម​ផ្លូវ​៦០​ម៉ែត្រ សង្កាត់​ចាក់​អង្រែ​លើ ខណ្ឌមានជ័យ', 'http://kohsantepheapdaily.com.kh/article/116573.html', 'http://i.kspg.co/2015/3/855ccdb0-7f00-4f01-add7-0d28239b777f.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('814', 'cl000001', 'u0000003', 'តារា​ប្រឡូក​ក្នុង​សង្គម​ហ្វេ​ស​ប៊ុ​ក​ម្នាក់ ត្រូវ​បាន​សមត្ត​កិច្ច​នគរបាល​ឃាត់ខ្លួន ប់ ពាក់ព័ន្ធ​ថា​ប៉ុនប៉ង​រំលោភ​នារី​ម៉ាស្សា', 'រាជធានី​ភ្នំពេញ​៖ យុវជន​ម្នាក់​ដែល​ត្រូវ​គេ​បាន​ស្គាល់​តាម​រយៈ​ការ​លេង​ក្នុង​សង�គម​ហ្វេ​ស​ប៊ុ​ក  កាលពី​វេលា​ម៉ោង​៦​នឹង​៣០​នាទី​ល្ងាច ថ្ងៃ​ទី​១៤ មិ​នា ២០១៥ ត្រូវ​បាន​សមត្ត​កិច្ច​នគរបាល​ព្រហ្មទណ្ឌ​រាជធានី​ភ្នំពេញ សហការ​ជាមួយ​នគរបាល​ប៉ុស្តិ៍​រដ្ឋបាល​សង្កាត់​មិត្តភាព ខណ្ឌ​៧​មក​រា ឃាត់ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/116568.html', 'http://i.kspg.co/2015/3/7097aa6f-6a41-4287-82d3-c9a71eee5b90.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('815', 'cl000001', 'u0000003', 'ប្រធាន​មន្ទីរ​ធម្មការ​ខេត្តព្រៃវែង​ក្រោយ​ពី​ធ្លាយ​រឿងអាស្រូវ បង្ក​ព្យុះ​ភ្លៀង​កាន់តែ​ខ្លាំង', 'ខេត្តព្រៃវែង​៖ មន្ត្រី​ជា​ច្រើន​នៅ​ក្នុង​មន្ទីរ​ធម្មការ​និង​សាសនា​ខេត្តព្រៃវែង​បាន​ទំលាយ​ឲ្យ​ដឹង​ថា​ក្រោយ​ពី​បណ្តាញ​ព័ត៌មាន​ក្នុងស្រុក​មួយ​ចំនួន​បាន​ចុះ​ផ្សាយ​អំពី​រឿងអាស្រូវ​ពុក​រលួយ​របស់​លោក​ប្រធាន​មន្ទីរ មុំ ច័ន្ទ​តារា និង​ប្អូនថ្លៃ​ឈ្មោះ ប៊ុត សុផល ជា​ប្រធាន​ការិយាល័យ​រដ្ឋបាល', 'http://kohsantepheapdaily.com.kh/article/116567.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('816', 'cl000001', 'u0000003', 'ក្មេង​ប្រុស​ស្រី​២​នាក់​បាន​លង់ទឹក​រណ្តៅ​ស្លាប់ ខណៈ​ដើរ​បេះ​បន្លែ​ឲ្យ​ម្តាយ', 'ខេត្ត​ព្រះ​សីហ​នុ​៖ តាម​សេចក្តីរាយការណ៍​ពី​ភ្ញាក់​ងារ​កោះសន្តិភាព​បាន​ឲ្យ​ដឹង​ថា​ក្មេង​ប្រុស​ស្រី​២​នាក់​បាន​លង់ទឹក​រណ្តៅ​ស្លាប់ ខណៈ​ពេល​ដើរទៅ​បះ​បន្លែ​ប្រ​ម៉ាត់​ដី​ឲ្យ​ម្តាយ​យក​មក​ស្ល​កកូរ នៅ​ពេល​ថ្ងៃ​ក្តៅ​ពេក ក៏​ចុះ​ងូតទឹក​នៅ​រណ្តៅ​ដី​មួយ​កន្លែង បណ្តាល​ឲ្យ​លង់​ស្លាប់​តែ​ម្តង', 'http://kohsantepheapdaily.com.kh/article/116565.html', 'http://i.kspg.co/2015/3/38603732-7fa5-498b-82a7-b3f3b671c277.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('817', 'cl000001', 'u0000003', 'ស្ត្រី​ខ្មែរ​រង​គ្រោះ​ចំនួន​៤​នាក់ បាន​មក​ពី​ប្រទេស​ចិន', 'រាជធានី​ភ្នំពេញ​៖​ក្រសួងការបរទេស និង​សហប្រតិបត្តិការ​អន្តរជាតិ​កម្ពុជា តាម​រយៈ ស្ថាន​អគ្គ​កុងស៊ុល​កម្ពុជា​ប្រចាំ​ទីក្រុង​ក្វា​ង​ចូ​វ ប្រទេស​ចិន បាន​សហការ​ជាមួយ​អាជ្ញាធរ​ចិន ជួយ​សង្គ្រោះ​ស្ត្រី​ខ្មែរ​ចំនួន​៤​នាក់​ដែល​រង​គ្រោះ​ដោយ​មេខ្យល់​នាំ​យក​ទៅ​រៀប​ការ​ជាមួយ​បុរស​ជនជាតិ​ចិន', 'http://kohsantepheapdaily.com.kh/article/116563.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('818', 'cl000001', 'u0000003', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃស​ប្តា​ហ៍​ទី​2​នៃ​ខែមីនា 2015', 'រាជធានី​ភ្នំពេញ ៖  តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​២​ដើមខែ មីនា ២០១៥ នេះ ឃើ​ញា​ថា មុខទំនិញ​ដូច​ជា​សាច់​គោ និង​បន្លែ​មានការ​ប្រែប្រួល​តម្លៃ​តិច​តិច តែ​ដោយឡែក គ្រឿង​សមុទ្រ ', 'http://kohsantepheapdaily.com.kh/article/116590.html', 'http://i.kspg.co/2015/3/82aab5c4-3c92-4d14-a84f-b4aff7b8371c.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('819', 'cl000001', 'u0000003', 'តម្រូវការ​អគារ​ការិយាល័យ​កើនឡើង​តិចតួច​នៅ​ដើមឆ្នាំ​2015​នេះ', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ដើមឆ្នាំ​២០១៥​នេះ តម្រូវការ​ជួល​អគារ​ការិយាល័យ​សម្រាប់​ក្រុមហ៊ុន ឬ​អង្គការ​ស្ថាប័ន​ផ្សេង​ៗ​ត្រូវ​បាន​គេសង្កេត​ឃើញ​ថា មាន​កំណើន​តិចតួច​បើ​ធៀប​នឹង​ឆ្នាំ​២០១៤​កន្លង​មក', 'http://kohsantepheapdaily.com.kh/article/116588.html', 'http://i.kspg.co/2015/3/da804e17-f259-4ce5-a49c-eeeda47fad0b.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('820', 'cl000001', 'u0000003', 'បើក​ទូលាយ​សិទ្ធិ​លំនៅ​ឋាន ជនបរទេស​ទិញ និង​ជួល​ខុន​ដូ​នៅ​កម្ពុជា​កាន់តែ​កើនឡើង', 'រាជធានី​ភ្នំពេញ ៖ ចាប់តាំងពី​រាជរដ្ឋាភិបាល​កម្ពុជា​បាន​អនុញ្ញាត​ឱ្យ​ជនបរទេស​មាន​សិទ្ធិ​ទិញ​ខុន​ដូ​ចាប់ពី​ជាន់​ទី​១​ឡើង​ទៅ ចំនួន​ជនបរទេស​ទិញ​ខុន​ដូ និង​ជួល​ខុន​ដូ​ស្នាក់​នៅ​ប្រទេស​កម្ពុជា ', 'http://kohsantepheapdaily.com.kh/article/116586.html', 'http://i.kspg.co/2015/3/153e91d3-9f7d-41eb-97f2-fdc7ccb1052b.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('821', 'cl000001', 'u0000003', 'យល់​ដឹង​ពី​ការ​ជួញដូរ​នៅ​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា', 'ថ្ងៃ​ជួញដូរ​ដំបូង​របស់​ក្រុមហ៊ុន​ចុះបញ្ជី​ថ្មី នៅ​ពេល​ក្រុមហ៊ុន​មួយ​បាន​បោះផ្សាយ​លក់​ភាគហ៊ុន​របស់​ខ្លួន​រួចរាល់​ហើយ​នៅ​ទីផ្សារ​ដំបូង បាន​សេចក្តី​ថា បាន​បញ្ចប់​ដំណើរការ​បរិវិសកម្ម​(​ដំណើរការ​ចុះ​', 'http://kohsantepheapdaily.com.kh/article/116584.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('822', 'cl000001', 'u0000003', 'ឡិច​ស៊ី​ស​ពន្លះម៉ូតូ​ឌ្រីម​​ប៉ើង​ជាង​១០​ម៉ែត្រ ​របួសធ្ងន់​២​នាក់​នៅ​ក្រុង​ស្វាយ​រៀង', 'ខេត្ត​ស្វាយ​រៀង ៖ យ៉ាង​ហោច​ណាស់​​​មនុស្ស​ស្រី​២​នាក់​រង​របួស​បែកក្បាល​ បាក់ជើង​សន្លប់​នៅ​នឹង​កន្លែង​ក្នុង​ហេតុ​ការណ៍គ្រោះថ្នាក់​ចរាចរណ៍​ដ៏​រន្ធត់​រវាង​រថយន្ត​ឡិច​ស៊ីស ', 'http://kohsantepheapdaily.com.kh/article/116615.html', 'http://i.kspg.co/2015/3/de226103-c0a1-4c1f-bd1b-7aabbe6f3baa.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('823', 'cl000001', 'u0000003', 'រថយន្ត​ដឹក​អាចម៍​រណា​បើក​ឡើង​ចំណោទ​វាក់​រថយន្ត​បើក​ពីមុខ​រអិល​ថយ​ក្រោយ​បុក​ចូល​ផ្ទះ​២ខ្នង​រង​ការ​ខូច​ខាត', 'រាជ​ធានី​ភ្នំពេញ​ ៖​ រថ​យន្ត​មួយ​គ្រឿង​ម៉ាក​ហ៊ី​ណូ​ ចំណុះ៥តោន ពណ៌​ស ពាក់​ស្លាក​លេខ​ភ្នំពេញ​៣A៨៩២៣ ​ដឹក​អាចម៍​រណាបានបើក​ឡើង​ចំណោទ​វាក់​នឹង​រថយន្ត​មួយ​គ្រឿង​', 'http://kohsantepheapdaily.com.kh/article/116599.html', 'http://i.kspg.co/2015/3/8e622174-ee2e-4fb8-8a38-07a518832876.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('824', 'cl000001', 'u0000003', 'ជិះ​ម៉ូតូ​ចាំង​ភ្លើង​​រថ​យន្ត​​មើល​ផ្លូវ​អត់​ឃើញ​បុក​រថយន្ត​អនាម័យ​ខេត្តចត​ស្ងៀម​ របួសធ្ងន់​២​នាក់', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖ យុវជន​​២​នាក់​បាន​រង​របួសធ្ងន់ ខណៈ​ពេល​បើក​ម៉ូតូ​ទៅ​បុក​ប៉ះ​នឹង​រថយន្ត​អនាម័យ​ខេត្ត​ឈប់​ចត​នៅ​លើ​ចិញ្ចើមថ្នល់​បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​​កាល​ពី​', 'http://kohsantepheapdaily.com.kh/article/116594.html', 'http://i.kspg.co/2015/3/e1c3f4b8-720a-42b0-a73c-1f3f1e12f8ad.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('825', 'cl000001', 'u0000003', 'ចេញ​ពី​​ហូបការ​គេ​ ​បើក​រថយន្ត​លឿនជ្រុល​បុក​រះ​ម៉ូតូ​៣​គ្រឿង​រង​ការ​ខូច​ខាត​', 'រាជ​ធានី​ភ្នំពេញ ៖ យុវ​ជន​ម្នាក់បើក​រថយន្ត​​ត្រឡប់​មក​ពី​ស៊ី​ការ​​​គេទៅ​ផ្ទះ​​ដល់​​​ចំណុច​ផ្លូវ​កោង​​ជ្រុល​ទៅ​បុក​រះ​ម៉ូតូ​៣​គ្រឿង​ដែល​ម្ចាក់​ចត​ស្ងៀម​​មុខ​ផ្ទះ​បណ្តាល​ឱ្យខួច​ខាត​ ', 'http://kohsantepheapdaily.com.kh/article/116593.html', 'http://i.kspg.co/2015/3/3554b5c7-a5e8-4a3a-99a5-af76a15cdaa8.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('826', 'cl000001', 'u0000003', 'គ្រោះថ្នាក់​ចរាចរណ៍​ម្សិលមិញ​ស្លាប់០៦ របួស០៨នាក់', 'រាជធានីភ្នំពេញ ៖​ យ៉ាងហោចណាស់​មនុសុ្ស០៦នាក់បានស្លាប់​ និង០៨នាក់រងរបួស​ ក្នុងនោះរបួសធ្ងន់០៥នាក់ ​ដោយសារគ្រោះថ្នាក់​ចរាចរណ៍០៨លើក​', 'http://kohsantepheapdaily.com.kh/article/116592.html', 'http://i.kspg.co/2015/3/099fee2f-fe43-4536-9a1f-c531994a4cc5.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('827', 'cl000001', 'u0000003', 'រថយន្ត​៣​គ្រឿង​បុក​បន្ត​កន្ទុយ​គ្នា​ដោយ​សារ​គោ​ដើរ​ឆ្លង​ផ្លូវ​នៅ​ស្រុក​សំរោង​ទង', 'ខេត្ត​កំពង់​ស្ពឺ​ ៖ កាល​ពី​វេលា​ម៉ោង​៨ និង​១៥​នាទី​ព្រឹក​ ថ្ងៃ​ទី​១៥ មីនា នេះនៅ​ចន្លោះ​គីឡូ​ម៉ែត្រ​លេខ​៣៤-៣៥ ស្ថិត​ក្នុង​ភូមិ​ត្រពាំង​សាំងចៀក ឃុំ​ត្រពាំង​គង ស្រុក​សំរោង​ទង', 'http://kohsantepheapdaily.com.kh/article/116581.html', 'http://i.kspg.co/2015/3/9127db13-a26d-4dc5-a289-0621af0f5724.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('828', 'cl000001', 'u0000003', 'រថយន្ត​ឡង់​គ្រី​ស្ស័របុក​ក្មេង​ប្រុស​ម្នាក់កំពុង​ជិះ​កង់​​ស្លាប់​យ៉ាង​អាណោ​ច​អាធ័ម', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖​ ក្មេង​ប្រុស​ជំទង់​ម្នាក់​បានស្លាប់​ភ្លាម​ៗ​នៅ​កន្លែង​កើតហេតុ​ដោយសារ​រថយន្ត​ឡង់​គ្រី​ស្ស័របុក​ពេល​កំពុង​ជិះ​កង់តាម​ផ្លូវ​​។​ ហេតុការណ៍នេះ​បាន​​កើតឡើង​', 'http://kohsantepheapdaily.com.kh/article/116572.html', 'http://i.kspg.co/2015/3/2504e1ad-c2ce-4357-a6a7-ea24e79a310b.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('829', 'cl000001', 'u0000003', 'បើករថយន្ត​ត្រឡប់​មក​ពី​ហូប​ការ​គេ​វិញ​ ក្រឡាប់​ស្លាប់​ម្នាក់​ របួស​៣​នាក់', 'ខេត្ត​ត្បូងឃ្មុំ ​៖ ប្រជាពលរដ្ឋ​ប្រុស​ស្រី​មាន​គ្នា​ចំនួន​៦​នាក់បាន​មក​ចូលរួម​ពិធី​មង្គលការ​បងប្អូន​នៅភូមិ​គង​ជ័យ ឃុំ​គង​ជ័យ ស្រុក​អូរ​រាំង​ឪ កាលពី​ល្ងាច​ថ្ងៃ​ទី១៣ មីនា ២០១៥ ​ ', 'http://kohsantepheapdaily.com.kh/article/116570.html', 'http://i.kspg.co/2015/3/f746c9be-9ebd-4839-bcf3-3dfd83caca2e.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('830', 'cl000001', 'u0000003', 'រថយន្ត​កូរ៉េ​បុក​ម៉ូតូ​របួសធ្ងន់​បងប្អូន​២​នាក់', 'ខេត្ត​ព្រះ​សីហ​នុ​៖ ក្មេង​ប្រុស​អាយុ​៣​ឆ្នាំ​ម្នាក់​បាន​រង​របួស ចំណែក​ក្មេង​ស្រី​អាយុ​១៧​ឆ្នាំ​ម្នាក់​ទៀត​ត្រូវជា​បង ក៏​រង​របួសធ្ងន់​ដូច​គ្នា ខណៈ​ពេល​កំពុង​ជិះ​ម៉ូតូ​ក្នុង​ទិស​ដៅ​បញ្ច្រាស់ទិស​គ្នា ត្រូវ​រថយន្ត​កូរ៉េ​បុក​ចំ​ពីមុខ​ពេញទំហឹង កាល​វេលា​ម៉ោង​៥​ល្ងាច ថ្ងៃ​ទី​១៤', 'http://kohsantepheapdaily.com.kh/article/116566.html', 'http://i.kspg.co/2015/3/3a455176-a519-45fe-9121-01ecf33dc7dc.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('831', 'cl000001', 'u0000003', 'របួសធ្ងន់ ខណៈបើកម៉ូតូដល់ផ្លូវបំបែកមិនបន្ថយល្បឿន​ បុករថយន្តពេញទំហឹង', 'បន្ទាយ​មាន​ជ័យ ​៖ ​បុរស​ម្នាក់​រងរបួសធ្ងន់​ក្នុង​ហេតុការណ៍​គ្រោះថ្នាក់​ចរាចរ​ណ៍មួយ​ បន្ទាប់ពី​ជិះ​ម៉ូតូ​ទៅ​បុក​រថយន្ត​កាម​រី​ហា​យ​ប្រ៊ីតមួយគ្រឿង ស្ថិតនៅ​​ចំណុចផ្លូវបំបែក​ជា​៤​​ព្រំប្រទល់​', 'http://kohsantepheapdaily.com.kh/article/116561.html', 'http://i.kspg.co/2015/3/8b425913-8123-4494-aa5b-810807bdc23a.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('832', 'cl000001', 'u0000003', 'បុរស​ម្នាក់​ស្លាប់​ដោយ​សារ​ខ្យល់​គ​ពេល​កំពុង​ពិសារអាហារ​​ពិធី​មង្គល​ការ​', 'ខេត្តកំពង់ឆ្នាំង ៖ បុរស​ម្នាក់​បាន​ស្លាប់​ក្នុង​ឥរិយា​បទ​អង្គុយ​ក្រាប​លើ​តុ ​បណ្តាល​ឱ្យ​​មានការ​ភ្ញាក់ផ្អើល​ពេញ​រោងការ ខណៈ​ដែល​បុរស​រូប​នោះ​ទៅ​ចូលរួម និង​ពិសា​រការ​បងប្អូន​ក្នុងភូមិ​ជាមួយ​គ្នា ។', 'http://kohsantepheapdaily.com.kh/article/116619.html', 'http://i.kspg.co/2015/3/28c02847-a26f-46dc-a6aa-018bb4c2982f.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('833', 'cl000001', 'u0000003', 'ព្យុះ​វាយប្រហារ​ក្រុង​ឡូ​ប៊ី​តូ​ប្រទេស​អង់​ហ្គោ​ឡា​ស្លាប់​ជិត​70​នាក់​និង​ផ្ទះ​120​ខ្នង​ខូចខាត', 'ប្រភព​ព័ត៌មាន​ពី​បរទេស​កាលពី​ថ្ងៃ​ទី​១៥ មីនា​ផ្សាយ​ថា ចំនួន​អ្នក​ស្លាប់​បាត់បង់​ជីវិត​ពី​ហេតុការណ៍​ព្យុះ​វាយប្រហារ​នៅ​ក្រុង​ឡូ​ប៊ី​តូ ប្រទេស​អង់​ហ្គោ​ឡា បាន​កើនឡើង​យ៉ាងតិច​ដល់​៦៩​នាក់ ', 'http://kohsantepheapdaily.com.kh/article/116618.html', 'http://i.kspg.co/2015/3/f9ec1a53-9861-41e4-81d4-f725439e5fcf.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('834', 'cl000001', 'u0000003', 'សោកនាដកម្ម​ព្យុះ Pam វាយប្រហារ​លើ​ប្រជុំ​កោះ​វ៉ា​នូ​អា​ទូ​បង្ក​ការ​ខូចខាត​ជាង​90%', 'បណ្តាញ​ព័ត៌មាន BBC របស់​អង់គ្លេស​ផ្សាយ​ថា យ៉ាង​ហោច​ណាស់​មាន​អ្នក​ស្លាប់​៨​នាក់ ប៉ុ​ន្តែ​ក៏​នឹង​កើនឡើង​ខ្ពស់​ជាង​នេះ ចំណែក​ផ្ទះសម្បែង​ទទួល​រង​ការ​ខូចខាត​យ៉ាងច្រើន​ដោយសារ​ឥទ្ធិពល', 'http://kohsantepheapdaily.com.kh/article/116612.html', 'http://i.kspg.co/2015/3/09815c8f-c89e-4091-b4b9-93967aca1b12.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('835', 'cl000001', 'u0000003', 'ស៊ី​លី​ជម្លៀស​ពលរដ្ឋ​ជា​ច្រើន​ពាន់​នាក់​គេច​ពី​ភ្លើង​ឆេះ​ព្រៃ', 'ទីភ្នាក់ងារ​ព័ត៌មាន​រ៉​យ​ទ័​រ​ផ្សាយ​ថា ប្រជាជន​ជា​ច្រើន​ពាន់​នាក់​ត្រូវ​បាន​ជម្លៀស​ចេញពី​ក្រុង​កំពង់​ផែ​បាល់​ប៉ា​រ៉ា​អីុសូ ប្រទេស​ស៊ី​លី  កាលពី​ថ្ងៃ​ទី​១៣ មីនា កន្លង​មក ដោយសារ​ភ្លើង​ឆេះ​ព្រៃ​ដែល​', 'http://kohsantepheapdaily.com.kh/article/116610.html', 'http://i.kspg.co/2015/3/0bc83e0a-9e6a-47c1-a35a-b01a577e0540.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('836', 'cl000001', 'u0000003', 'រញ្ជួយ​ដី​នៅ​ខេត្ត​អាន​ហ៊ុយ​ប្រទេស​ចិន​ខូចខាត​លំនៅ​ឋាន​ជាង​10.000​ខ្នង', 'ទីភ្នាក់ងារ​ព័ត៌មាន​បរទេស កាលពី​ថ្ងៃ​ទី​១៥ មីនា ផ្សាយ​ថា មានការ​រញ្ជួយ​ដី​ដ៏​ខ្លាំង​មួយ​នៅ​ក្នុង​ខេត្ត​អាន​ហ៊ុយ​ភាគ​ខាងកើត​ប្រទេស​ចិន កាលពី​រសៀល​ថ្ងៃ​ទី​១៤ មីនា បណ្តាល​ឱ្យ​ផ្ទះសម្បែង​ខូចខាត', 'http://kohsantepheapdaily.com.kh/article/116606.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('837', 'cl000001', 'u0000003', 'ជនជាតិ​អា​មេ​រិ​ក​10​នាក់​សង្ស័យ​ឆ្លង​វីរុស​អេ​បូ​ឡា​នៅ​សៀ​រ៉ាឡេ​អូន', 'ទីភ្នាក់ងារ​ព័ត៌មាន​បរទេស​ផ្សាយ​ថា ជនជាតិ​អា​មេ​រិ​ក​យ៉ាង​តិច​១០​នាក់ ដែល​ជាប់​សង្ស័យ​ថា​អាច​ឆ្លង​វីរុស​អេ​បូ​ឡា ត្រូវ​បាន​បញ្ជូន​ចេញពី​ប្រទេស​សៀ​រ៉ា​ឡេ​អូន នៅ​អាហ្វ្រិក​ខាងលិច ', 'http://kohsantepheapdaily.com.kh/article/116605.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('838', 'cl000001', 'u0000003', 'ទិន្នផល​កៅស៊ូ​ម៉ា​ទ្បេ​ស៊ី​កើន​53,8%​ខែមករា', 'ក្រុង​គូ​ទ្បា​ទ្បាំ​ពួរ ៖ នាយកដ្ឋាន​ស្ថិតិ​បានឱ្យ​ដឹង​ថា ផលិតកម្ម​កៅស៊ូ​ធម្មជាតិ​របស់​ប្រទេស​ម៉ា​ទ្បេ​ស៊ី​កាលពី​ខែមករា​ដើមឆ្នាំ​នេះ បាន​កើន​ទ្បើ​ង​៥៣,៨% ឬ​ស្មើនឹង​២៨.៦០០​តោន​ដូច', 'http://kohsantepheapdaily.com.kh/article/116609.html', 'http://i.kspg.co/2015/3/28e77f3a-f0b8-41e0-9bbb-0d4d85b68436.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('839', 'cl000001', 'u0000003', 'ប៉ា​គី​ស្ថាន​អាច​មិន​សម្រេច​ទិស​ដៅ​នាំ​ចេញ​អង្ករ​ជិត​4​លាន​តោន​នា​ឆ្នាំ​ថ្មី​នេះ', 'តាម​សារព័ត៌មាន Bloomberg ផ្សាយ​ថា ប្រធាន​សមាគម​អ្នក​នាំ​ចេញ​អង្ករ​ប៉ា​គី​ស្ថាន​ព្យាករ​ថា ប្រទេស​នេះ​នឹង​មិន​អាច​សម្រេច​បាន​ទិស​ដៅ​នាំ​ចេញ​អង្ករ​របស់​ខ្លួន​ក្នុង​រង្វង់​៣,៨​លាន​តោន​ទ្បើ​យ', 'http://kohsantepheapdaily.com.kh/article/116608.html', 'http://i.kspg.co/2015/3/03c2cb05-50b3-4720-9ff4-95ccd9f4d25f.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('840', 'cl000001', 'u0000003', 'ចាន់ រតនា ត្រូវ​អ្នកលេង​ហ្វេ​ស​ប៊ុ​ក​ចាត់ទុកជា​វីរបុរស​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ជ័យជម្នះ​ដ៏​ត្រចះត្រចង់​របស់​កីឡាករ​ចាន់ រតនា នៅ​ក្នុង​ព្រឹត្តិការណ៍​ប្រដាល់​ដាក់​ទ្រុង One FC កាលពី​ថ្ងៃ​សុក្រ កន្លង​ទៅ​ថ្មី​ៗ​នេះ បាន​ជួយ​ឲ្យ​កីឡាករ​កម្ពុជា​រូប​នេះ​ត្រូវ​គេ​ចាត់ទុក', 'http://kohsantepheapdaily.com.kh/article/116604.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('841', 'cl000001', 'u0000003', 'មាស ច័ន្ទ​មាន ស៊ី​3​ពិន្ទុ​ផុយ ខណៈ​គូប្រជែង​ឈ្នះ​គូប្រកួត​ដោយ​លំបាក', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​សន្សំ​ពិន្ទុ​ក្នុង​ជុំ​ទី​៥ កាលពី​ថ្ងៃ​ទី​១៤ មីនា ម្សិលម្ង៉ៃ​មាន​កីឡាករ​តែ​៣​រូប​ប៉ុណ្ណោះ​ស្ថិត​ក្នុង​ការ​ប្រកួតប្រជែង​យ៉ាង​ស្វិតស្វាញ​បំផុត ដោយសារ​ពួក​គេ​មាន​ពិន្ទុ​ប្រកៀក​ប្រកិត​គ្នា ។', 'http://kohsantepheapdaily.com.kh/article/116602.html', 'http://i.kspg.co/2015/3/220a1a4b-c5f1-4b18-aea9-ad3c22d27266.jpg?w=285&h=170&mode=carve', '20150315');
