INSERT INTO "tbnews" VALUES ('844', 'cl000001', 'u0000003', 'ព្រឹកនេះ​ ក្រុមកីឡាករបាល់ទាត់​ជម្រើសជាតិកម្ពុជា​ចេញដំណើរ​ទៅម៉ាកាវហើយ​ដើម្បីប្រកួតជម្រុះជើងទី២នៅថ្ងៃទី១៧មីនា', 'រាជធានីភ្នំពេញ​ ៖ ក្រុមកីឡាករបាល់ទាត់​ជម្រើសជាតិកម្ពុជា​បានចាកចេញពីរាជធានីភ្នំពេញ​ធ្វើដំណើរទៅកាន់ទឹកដីម៉ាកាវហើយ ​នៅព្រឹកថ្ងៃទី១៥ មីនា ', 'http://kohsantepheapdaily.com.kh/article/116577.html', 'http://i.kspg.co/2015/3/af47b913-63fe-46a9-9886-4210b9e1f28e.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('845', 'cl000001', 'u0000003', 'សៀវ ង៉ុយ ឈ្នះ​ថៃ​ដោយ​ពិន្ទុ​នៅ​សង្វៀន​អាស៊ី​អាគ្នេយ៍​ព្រឹក​នេះ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ ការ​ប្រកួត​ប្រដាល់​អន្តរ​ជាតិ​រៀង​រាល់​២​សប្តាហ៍​ម្តង​នៅ​សង្វៀន​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៥ មីនា នេះ គូ​អន្តរ​ជាតិ​ទី​១ កីឡាករ សៀវ ង៉ុយ ឈ្នះ​', 'http://kohsantepheapdaily.com.kh/article/116574.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('846', 'cl000001', 'u0000003', 'ព្យាករណ៍​បាល់ទាត់​បារាំង​ឡឺ​សំ​ផ្ចូ​ណា​ជុំ​ទី​29​សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះ​មាន​6​ប្រកួត Metz ត្រូវការ​ឈ្នះ St Etienne', 'ការ​ប្រកួត​បាល់ទាត់​បារាំង​ឡឺ​សំ​ផ្ចូ​ណា​ជុំ​ទី 29 សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះ​មាន 8 ប្រកួត ។ Metz ត្រូវការ​ឈ្នះ St.Etienne ខណៈ Paris SG សង្ឃឹម​រកបាន​បី​ពិន្ទុ​បន្ថែម​ពេល​ជួប​ក្រុម Bordeaux ។', 'http://kohsantepheapdaily.com.kh/article/116583.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150315');
