INSERT INTO "tbnews" VALUES ('848', 'cl000001', 'u0000003', 'ក្រុម Chelsea,Everton,Man Utd និង Arsenal ជា​ក្រុម​រឹង​មាំ​ក្នុង​ទឹកដី​ខណៈ Man City មាន​ប្រៀប​ជាង​ម្ចាស់ផ្ទះ', 'ការ​ប្រកួត​បាល់ទាត់​ក្រប​ខណ្ឌ​អង់គ្លេស​ប្រឺ​មី​ញេ​លី​ក​ជុំ​ទី 28 សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះ មាន 9 ប្រកួត ។ Chelsea, Everton, Man Utd និង Arsenal ជា​ក្រុម​រឹង​មាំ​ក្នុង​ទឹកដី ខណៈ Man City ', 'http://kohsantepheapdaily.com.kh/article/116580.html', 'http://i.kspg.co/2015/3/8be49b20-5e3f-42f4-954d-8d42f4376755.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('849', 'cl000001', 'u0000003', 'ការ​ប្រកួត​បាល់ទាត់​អេ​ស្ប៉ា​ញ​ព្រី​មេ​រ៉ា​លី​ហ្គា​ជុំ​ទី​27​សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះមាន​8​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​អេ​ស្ប៉ា​ញ​ព្រី​មេ​រ៉ា​លី​ហ្គា ជុំ​ទី 27 នៅ​រាត្រី​ថ្ងៃ​សៅរ៍​-​អាទិត្យ​នេះ មាន 8 ប្រកួត ។ ក្រុម Barcelona ត្រូវការ​ឈ្នះ​គូប្រកួត​ដើម្បី​គ្រប់គ្រង​តំណែង​លេខ 1 ក្នុង​តារាង ', 'http://kohsantepheapdaily.com.kh/article/116579.html', 'http://i.kspg.co/2015/3/090bbd64-b048-4f5e-b43d-06a5595dec65.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('850', 'cl000001', 'u0000003', 'បាល់ទាត់​អា​ល្លឺ​ម៉​ង់​ប៊ុ​ន​ដេ​ស្លី​ហ្គា​ជុំ​ទី​25 ៖ Wolfsburg និង Gladbach សង្ឃឹម​រកបាន​បី​ពិន្ទុ​ក្នុង​ទឹកដី', 'ការ​ប្រកួត​បាល់ទាត់​អា​ល្លឺ​ម៉​ង់​ប៊ុ​ន​ដេ​ស្លី​ហ្គា​ជុំ​ទី 25 សម្រាប់​រាត្រី​ចុង​ស​ប្តា​ហ៍​នេះ​មាន 8 ប្រកួត ។ ក្រុម Wolfsburg, Gladbach សង្ឃឹម​រកបាន​បី​ពិន្ទុ​ក្នុង​ទឹកដី​ថ្ងៃនេះ ខណៈ Bayern និង Schalke', 'http://kohsantepheapdaily.com.kh/article/116578.html', 'http://i.kspg.co/2015/3/4a225f2d-26cd-4774-aa3d-81ee0cf7c36b.jpg?w=285&h=170&mode=carve', '20150315');
INSERT INTO "tbnews" VALUES ('851', 'cl000001', 'u0000003', 'តារា​រះ​ថ្មី​កញ្ញា វិ​រី​យ៉ា ចាប់ផ្តើម​មាន​ទីផ្សារ​សិល្បៈ​ក្នុង​តំណែង​តារា​សិច​ស៊ី​ស្រស់​ឆើតឆាយ', 'រាជធានី​ភ្នំពេញ ៖ ប្រឡូក​សិល្បៈ​ទើបតែ​បាន​រយៈពេល​ជិត​២​ឆ្នាំ​តារា​រលក​ក្រោយ​ដែល​កំពុង​មាន​ជំនឿ​ចិត្ត​ថា នឹង​អាច​ទទួល​បាន​ប្រជាប្រិយភាព​ពី​ទស្សនិកជន​កញ្ញា វិ​រី​យ៉ា ដែល​អំឡុង​ពេល​នេះ', 'http://kohsantepheapdaily.com.kh/article/116571.html', 'http://i.kspg.co/2015/3/cb389836-1900-46f4-b803-a46e71978f10.jpg?w=285&h=170&mode=carve', '20150315');
