INSERT INTO "tbnews" VALUES ('878', 'cl000001', 'u0000003', 'ម៉ោងជិត៥ល្ងាចនេះ​ សម្តេចតេជោ ហ៊ុន សែន​ វិលនិវត្តន៍មកដល់កម្ពុជាវិញហើយ', 'រាជធានីភ្នំពេញ ៖ ​សម្តេច​អគ្គមហាសេនាបតីតេជោ ​ហ៊ុន សែន ​និងគណៈប្រតិភូអមដំណើរ​បានវិលនិវត្តន៍មកដល់​កម្ពុជាវិញ​ហើយនៅម៉ោងជិត៥​ល្ងាច​', 'http://kohsantepheapdaily.com.kh/article/116692.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('879', 'cl000001', 'u0000003', 'ប្រទេស​ទួរគី​សន្យា​ពង្រឹង​ទំនាក់​ទំនង​ផ្នែក​ពាណិជ្ជ​កម្ម​ជាមួយ​កម្ពុជា​ឱ្យ​ដល់​៥០០​លាន​ដុល្លារ​ឆ្នាំ​២០២០', 'រាជ​ធានី​ភ្នំពេញ​ ៖ ប្រទេស​ទួរគី​បាន​សន្យា​ពង្រឹង​ទំនាក់​ទំនង​​ផ្នែក​ពាណិជ្ជ​កម្ម​ជាមួយ​កម្ពុជា​ឱ្យ​ដល់​៥០០​លាន​ដុល្លារ​ឆ្នាំ​២០២០ និង​ធ្វើ​ការ​សិក្សា​ពីការ​ហោះ​ហើរ​ដោយ​ត្រង់​មក​កម្ពុជា​ ក្នុង​នោះ​ប្រទេស​ទូរគី​ក៏​បាន​', 'http://kohsantepheapdaily.com.kh/article/116690.html', 'http://i.kspg.co/2015/3/555b5be4-366c-4438-a61f-b8ca2664f9b2.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('880', 'cl000001', 'u0000003', 'កម្លាំង​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល​ខេត្តចុះបង្ក្រាប​ទីតាំង​កត់កន្ទុយ​៤កន្លែង ​ក្នុងសង្កាត់​ស្វាយប៉ោ ក្រុងបាត់​ដំបង​', 'ខេត្តបាត់ដំបង ៖ កម្លាំង​​នគរ​បាល​ផ្នែក​បង្ការបង្ក្រាប​​របស់​ការិយាល័យ​ព្រហ្ម​ទណ្ឌ​កម្រិត​ស្រាល​ខេត្ត កាលពីវេលាម៉ោង​២រសៀល​ថ្ងៃទី​១៦ មីនា ២០១៥ នេះ បានធ្វើការ​ចុះបង្ក្រាប​ទីតាំង​កត់កន្ទុយ​លេខ៤​កន្លែង ដែល​ស្ថិត​ក្នុង​សង្កាត់​ស្វាយប៉ោ ក្រុង​បាត់ដំបង និង​ឃាត់​', 'http://kohsantepheapdaily.com.kh/article/116688.html', 'http://i.kspg.co/2015/3/0e0b4ab7-e07f-4a7b-92b3-9c004deb4db8.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('881', 'cl000001', 'u0000003', 'ជម្លោះ​នៅ​មន្ទីរពេទ្យ​បង្អែក​ចំការ​ចេក ខេត្ត​ព្រះ​សីហ​នុ ពេល​ណា​ទើប​ចប់?', 'ខេត្ដ​ព្រះ​សីហ​នុ ៖ ជំងឺ​រ៉ាំរ៉ៃ​បក្សពួក​និយម​នៅ​ក្នុង​មន្ទីរពេទ្យ​បង្អែក​ចំការ​ចេក ខេត្ត​ព្រះ​សីហ​នុ នៅ​តែ​អូស​បន្លាយ បើ​ទោះបីជា​មក​ដល់​ពេល​នេះ​មាន​រយៈពេល​ជា​ច្រើន​ខែ​មក​ហើយ', 'http://kohsantepheapdaily.com.kh/article/116680.html', 'http://i.kspg.co/2015/3/8ecdbb54-b928-4197-aaa3-efb909763a9f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('882', 'cl000001', 'u0000003', 'អាជ្ញាធរ​សង្កាត់​ភ្នំ​ពេញ​ថ្មី​​ចុះ​ប្រតិ​បត្តិ​ការ​ចាប់​ទូ​ហ្គេម​បាញ់​ត្រី​ចំនួន​២កន្លែង​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នៅ​វេលា​ម៉ោង២ និង​៤០​នាទី​រសៀល ថ្ងៃ​ទី​១៦ មីនា នេះ ​លោក​ជុំ សារ៉ាយ ចៅ​សង្កាត់​ភ្នំពេញ​ថ្មី​បាន​បើក​ប្រតិ​បត្តិ​ការ​ថ្មី​មួយ​ទៀត​ហើយ​ ដោយ​មានកង​កម្លាំង​ការ​ពារ​ និង​ប៉ូលិស​ប៉ុស្តិ៍​ចុះប្រតិ​បត្តិ​', 'http://kohsantepheapdaily.com.kh/article/116679.html', 'http://i.kspg.co/2015/3/971e0951-6ed7-4638-8ce8-13b2a1c851fe.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('883', 'cl000001', 'u0000003', 'យប់ម្សិល​កូន​មេ​គយ​ម្នាក់​បាញ់បោះ​កណ្តាល​ក្រុង ឯ​ថ្ងៃនេះ​ធ្លាយ​រឿង​ប្អូនប្រុស​មេ​គយ​ខេត្ត​ត្បូងឃ្មុំ​សេព​គ្រឿង​ញឿ​ន', 'ខេត្តកំពង់ចាម ៖  កងកម្លាំង​នគរបាល​ខេត្ត​បាន​ចុះ​បង្ក្រាប​មុខសញ្ញា​ជួញដូរ និង​ប្រើប្រាស់​គ្រឿងញៀន​មួយ​កន្លែង​នៅ​បន្ទប់​ផ្ទះ​ជួល​មួយ​កន្លែង ស្ថិត​នៅ​ភូមិ​បឹង​កុក​ទី​២ សង្កាត់​បឹង​កុក ក្រុង​កំពង់ចាម', 'http://kohsantepheapdaily.com.kh/article/116677.html', 'http://i.kspg.co/2015/3/32e3b23d-2f5d-426c-b599-31f0dabcd9ba.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('884', 'cl000001', 'u0000003', 'ត្រី​ប៉ា​សេ​អីុពី​មួយ​ឆ្នាំទៅ​មួយ​ឆ្នាំ​​មានការ​ថយ​ចុះ​​បន្ត​បន្ទាប់', 'ខេត្តស្ទឹងត្រែង ៖ អ្នកនេសាទ​ និង​អាជីវករ​ទទួល​ទិញ​-លក់​ត្រី​ប៉ា​សេ​អ៊ី​និយាយ​ថា រយៈពេល​ប៉ុន្មាន​ឆ្នាំ​ចុង​ក្រោយ​នេះ​ ត្រូវ​បាន​គេ​មើល​ឃើញ​ថា​ ត្រី​ប៉ា​សេ​អ៊ី​មានការ​ថយ​ចុះ​ជា​បន្តបន្ទាប់​ខុស​ពី​ទស​វត្ស​ឆ្នាំ​៩០ ​និង​ឆ្នាំ​២០០០ ។ ដែល​ក្នុង​ទស​វត្ស​នោះ ត្រូវ​បាន​គេ​មើល​', 'http://kohsantepheapdaily.com.kh/article/116675.html', 'http://i.kspg.co/2015/3/9021bf1c-4073-474f-a75d-f1facfa8769c.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('885', 'cl000001', 'u0000003', '​លោក​ឧត្តម​សេនី​យ៍ឯក ទូច ណា​រ៉ុ​ថ បំពាក់​ឋានន្ត​រស័​ក្កិ​ដល់​នគរបាល​ខេត្តក្រចេះ​ចំនួន​៤៩​រូប', 'ខេត្តក្រចេះ ​៖ ជំនួស​មុខ ​និង​តំណាង​ឲ្យ​លោកនាយ​ឧត្តម​សេនី​យ៏​អគ្គស្នងការ​នគរបាល​ជាតិ លោក​ឧត្តម​សេនី​យ៍​ឯក ទូច ណា​រ៉ុ​ថ អគ្គស្នងការ​រង​ និង​ជា​ប្រធាន​នាយកដ្ឋាន​នគរបាលអង្គរក្ស​ដែល​បាន​អញ្ជើញ​ជា​', 'http://kohsantepheapdaily.com.kh/article/116674.html', 'http://i.kspg.co/2015/3/32daeb4e-9942-4562-8579-9d96dd9e182f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('886', 'cl000001', 'u0000003', 'លោកអភិបាលស្រុកព្រៃនប់​ដឹកនាំ​ក្រុមការងារ​ចុះ​ត្រួតពិនិត្យ​ផ្លូវបេតុង​នៅ​ផ្សារ​វាល​រេញ​', 'ខេត្តស្វាយរៀង ៖ កាលពីវេលាព្រឹកថ្ងៃទី​១៦ មីនា ២០១៥ លោកទិត​ វុទ្ធី អភិបាល​ស្រុកព្រៃនប់ អម​ដោយយ​សហភាព​សហព័ន្ធ​យុវ​ជន​​កម្ពុជាស្រុកព្រៃនប់, លោកមេឃុំ, មេភូមិ បានចុះទៅ​ត្រួតពិនិត្យ​ផ្លូវចាក់​បេតុង​ក្នុង​ផ្សារ​​វាលរេញ ដែល​ទើប​តែ​ស្ថាបនា​រួច​រាល់​កាល​', 'http://kohsantepheapdaily.com.kh/article/116672.html', 'http://i.kspg.co/2015/3/3eacae6a-3d94-4325-8512-faf505c4e94e.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('887', 'cl000001', 'u0000003', 'សមត្ថកិច្ច​ចម្រុះ​ចាប់​ឈើ​ធ្នង់​៤១​ដុំ ជា​របស់​ក្មួយបង្កើត​អភិបាលខេត្ត​កំពង់ធំ', 'ខេត្តកំពង់ធំ ៖ ឈើ​ធ្នង់​មុខ​២០.២៥ ចំនួន​៤១​ដុំ ប្រវែង​ជាង​១​ម៉ែត្រ និង​រថយន្ត​ម៉ា​កតា​កូ​ម៉ា​២​គ្រឿង ត្រូវ​បាន​កម្លាំង​នគរបាល​ប្រឆាំង​បទល្មើស​សេដ្ឋកិច្ច​នៃ​ស្នងការដ្ឋាន​នគរបាល​ខេត្តកំពង់់ធំ ', 'http://kohsantepheapdaily.com.kh/article/116663.html', 'http://i.kspg.co/2015/3/e5aa1bca-93cf-4aa4-a589-0c53d8a5bd86.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('888', 'cl000001', 'u0000003', 'ចាប់ជនសង្ស័យប្រើប្រាស់គ្រឿងញៀន៣នាក់ ខណៈកំពុងធ្វើ​សកម្មភាព​', 'ខេត្តបាត់ដំបង ​៖ ជន​សង្ស័យ​ប្រើប្រាស់គ្រឿងញៀន​៣នាក់ ត្រូវ​កម្លាំង​ការិយាល័យ​នគរបាល​ប្រឆាំងគ្រឿងញៀន សហការ​ជាមួយ​ក្រុម​បង្ការ​បង្ក្រាប​នៃស្នងការដ្ឋាន​នគរបាលខេត្ត​ធ្វើការចុះបង្រ្កាប ខណៈ​កំពុង​ធ្វើ​សកម្មភាព កាលពីម៉ោង០៧ និង៣០នាទីយប់ថ្ងៃទី​', 'http://kohsantepheapdaily.com.kh/article/116660.html', 'http://i.kspg.co/2015/3/3ceb10c8-5ab4-43e1-8c09-1ae4890ccb1b.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('889', 'cl000001', 'u0000003', 'ប្រជាពលរដ្ឋ​ភ្ញាក់រលឹក​យក​អាវុធ​មក​ប្រគល់​ជូន​សមត្ថកិច្ច​ដើម្បី​កាត់​បន្ថយ​បទល្មើស', 'ខេត្ត​ត្បូងឃ្មុំ​ ៖  ក្រោយ​ពី​បាន​សា្ត​ប់​ការ​ផ្ស​ព្វផ្សាយ​តាម​រយៈ​វេទិការ​សាធារណៈលើ​គោល​ការ​ណ៍ទាំង៩​ចំណុច​របស់​ក្រសួងមហាផ្ទៃបាន​ធ្វើ​ឲ្យ​ប្រជាពលរដ្ឋ​នៅ​តាម​មូលដ្ឋាន​នីមួយ​ៗមានការ​ភ្ញាក់រលឹក​ខ្ពស់', 'http://kohsantepheapdaily.com.kh/article/116659.html', 'http://i.kspg.co/2015/3/4cb9f2c0-4397-4af5-bcd2-9a6b23da54d6.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('890', 'cl000001', 'u0000003', 'ម៉ោង៤រសៀលនេះ​ សម្តេច ហេង​ សំរិន​ ទទួលជួបរដ្ឋមន្រ្តី​ការបរទេស​ទួរគី', 'រាជធានីភ្នំពេញ ៖​ បើគ្មានអ្វីប្រែប្រួលនោះទេ​ សម្តេច​អគ្គមហាពញាចក្រី ​ហេង សំរិន​ ប្រធានរដ្ឋសភា​នឹងអនុញ្ញាតឲ្យ​លោក Mevlut Cavusoglu រដ្ឋមន្រ្តី​', 'http://kohsantepheapdaily.com.kh/article/116658.html', 'http://i.kspg.co/2015/3/311dc436-32d0-4fd0-ac4e-ccc1b2daef62.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('891', 'cl000001', 'u0000003', 'អាជ្ញាធរ​សង្កាត់ភ្នំពេញ​ថ្មី ដឹកនាំកម្លាំង​ប្រើត្រាក់ទ័រ​ឈូស​ចិញ្ចើម​​ផ្ទះ​មួយ​កន្លែង ​ដែលរំលោភលើ​ផ្លូវសា​ធារណៈ', 'រាជធានីភ្នំពេញ ៖ ​ផ្ទះ​លក់​គ្រឿង​សំណង់​មួយកន្លែង ​ត្រូវបាន​អាជ្ញាធរ​សង្កាត់ភ្នំពេញ​ថ្មី ​ដឹកនាំដោយលោក​ជុំ សារ៉ាយ​ ចៅសង្កាត់​ភ្នំពេញ​ថ្មី​ នាំ​​​កម្លាំង​ប្រជាការពារ​​ រួមទាំង​អាជ្ញាធរដោយយ​​ប្រើ​ត្រាក់ទ័រ​យក​មក​ឈូសឆាយ​ចិញ្ចើម​ផ្ទះ​ខាងលើ​​ ដែល​ម្ចាស់​បានធ្វើ​', 'http://kohsantepheapdaily.com.kh/article/116656.html', 'http://i.kspg.co/2015/3/69bca19d-5ab7-44fb-a69d-b43be580bbd6.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('892', 'cl000001', 'u0000003', 'លោកស៊ុយ សែម ជួប​សំណេះសំណាល​​ជាមួយ​យុវជន​២.៥១៦នាក់​​នៅស្រុក​បាកាន', 'ខេត្តពោធិ៍សាត់ ​៖ ​កាលពី​ថ្ងៃ​ទី​១៤​ ​មីនា ​​២០១៥ ​លោក​ស៊ុ​យ សែ​ម ​រដ្ឋមន្ត្រីក្រសួង​រ៉ែ​ និង​ថាមពល​ និង​លោក​ខូ​យ សុខា​ ប្រធាន​ក្រុម​ប្រឹក្សា​​ខេត្តពោធិ៍សាត់ ​បាន​ជួប​សំណេះសំណាល​​ជាមួយ​យុវជន​ចំនូ​ន​២.៥១៦​នាក់​ មក​ពី​០៦​ឃុំ ​គឺ​ឃុំ​តា​លោ ឃុំ​បឹង​បត់​​កណ្ដោល ឃុំ​', 'http://kohsantepheapdaily.com.kh/article/116643.html', 'http://i.kspg.co/2015/3/f4fc6561-84b8-49c6-aace-998941d4c3cc.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('893', 'cl000001', 'u0000003', 'ក្រសួងយុត្តិធម៌បើក​កិច្ច​ប្រជុំ​ស្តី​ពី​​​ការ​​លើក​លែង និង​បន្ធូរ​បន្ធយ​ទោស​របស់​ទណ្ឌិត​​នៅថ្ងៃ​បុណ្យ​​ចូល​ឆ្នាំ​ខ្មែរខាងមុខ​​', 'រាជធានីភ្នំពេញ ៖ ក្រសួងយុត្តិធម៌ ​នាវេលាម៉ោងព្រឹកថ្ងៃទី​១៦ មីនា ២០១៥ នេះ នៅទីស្តីការ​ក្រសួង មានរៀបចំ​បើក​កិច្ច​ប្រជុំពិភាក្សា​ស្តី​ពី​​​ការ​​លើក​លែង និង​បន្ធូរ​បន្ធយ​ទោស​របស់​ទណ្ឌិត​នៅថ្ងៃ​បុណ្យ​​ចូល​ឆ្នាំប្រពៃណី​ខ្មែរ​ខាងមុខនេះ ​ក្រោម​អធិបតីភាពលោក​អង្គ វង្ស​វឌ្ឍនា', 'http://kohsantepheapdaily.com.kh/article/116640.html', 'http://i.kspg.co/2015/3/3fa514a4-36d7-47a5-b5b5-a6053c6b0a1b.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('894', 'cl000001', 'u0000003', 'សមត្ថកិច្ច​បញ្ជូន​ក្រុម​ជើង​ខ្លាំង​ប្រចាំ​ស្រុក​រតន​មណ្ឌល​៤​នាក់​ទៅ​តុលាការ', 'ខេត្តបាត់ដំបង​ ៖ ក្មេង​ទំនើង​៤​នាក់​ក្នុង​ចំណោម​៥​នាក់ ដែល​សមត្ថកិច្ច​ចាត់​ទុក​ថា ជា​ក្រុម​ជើង​ខ្លាំង​ប្រចាំ​ស្រុក​រតន​ណ្ឌ​ល ត្រូវ​បាន​បញ្ជូន​ទៅ​តុលាការ​ហើយ​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៦​ មីនា បន្ទាប់​ពី​ត្រូវ​ឃាត់ខ្លួន​ក្នុង​អំពើ​ហិ​ង្សា​', 'http://kohsantepheapdaily.com.kh/article/116638.html', 'http://i.kspg.co/2015/3/e1b290f8-5083-4891-aa72-28070e5815d0.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('895', 'cl000001', 'u0000003', 'កម្លាំង​អាវុធហត្ថ​រាជធានី​បង្ក្រាប​ក្រុម​ជួញដូរ​គ្រឿងញៀន​មួយ​សំបុក​នៅ​សង្កាត់​ស្ទឹង​មាន​ជ័យ', 'រាជធានី​ភ្នំពេញ ៖  កាលពី​វេលា​ម៉ោង​០៥​ល្ងាច​ថ្ងៃ​ទ​១៥ មីនា ​​២០១៥​ នេះ កម្លាំង​កងរាជអាវុធហត្ថ​រាជ​ធានី​ភ្នំពេញ សហការ​ជាមួយ​កម្លាំង​​កងរាជអាវុធហត្ថ​ខណ្ឌមានជ័យ បាន​​បើក​ប្រតិបត្តិការ​បង្ក្រាប​សំបុក​ក្រុម​ជួញដូរ និង​ចែកចាយ​គ្រឿងញៀន​យ៉ាង​សកម្ម នៅ​', 'http://kohsantepheapdaily.com.kh/article/116634.html', 'http://i.kspg.co/2015/3/273a9ce4-f078-4c47-9c94-db8d909e29d7.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('896', 'cl000001', 'u0000003', 'ទុ​ស្សេ​​ចរន្ត​អគ្គិសនីឆេះ​បង្គោល​ភ្លើង​នៅ​ខណ្ឌ​ចំការ​មន​', 'រាជ​ធានី​ភ្នំពេញ ៖ បង្គោល​ភ្លើង​មួយ​កន្លែង​ស្ថិត​នៅ​មុខ​ហាង​បាយ​និង​កាហ្វេ ណា​ជឺ ផ្ទះ​លេខ​៥៧​សេ ផ្លូវ​លេខ​៣៧៦​កែង​៩៥ សង្កាត់​បឹង​កេងកង​ទី​៣ ខណ្ឌចំការមនត្រូវ​អគ្គិ​ភ័យ', 'http://kohsantepheapdaily.com.kh/article/116633.html', 'http://i.kspg.co/2015/3/97cc8570-4374-49a2-9285-19e87e1032c5.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('897', 'cl000001', 'u0000003', 'យុវជន​វៀតណាម​២​នាក់​ផឹក​ស្រវឹង​កើត​ជម្លោះវាយ​គ្នា ម្នាក់​របួសធ្ងន់​ដឹក​ទៅ​ពេទ្យ ម្នាក់​ទៀត​ត្រូវ​ចាប់ខ្លួន', 'ខេត្តកំពង់ឆ្នាំង ៖ ជនសង្ស័យ​សញ្ជាតិ​វៀតណាម​ម្នាក់ ត្រូវ​បាន​កម្លាំង​សមត្ថកិច្ច​នគរបាលក្រុង​កំពង់ឆ្នាំង​​ចាប់​ឃាត់ខ្លួនកាល​ពី​វេលា​ម៉ោង​៩​ និង​៣០​នាទី​ព្រឹក​ ថ្ងៃ​ទី​១៥ ​​មីនា ​', 'http://kohsantepheapdaily.com.kh/article/116630.html', 'http://i.kspg.co/2015/3/f99ff45d-aa8e-4df8-b9b7-37077e48b667.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('898', 'cl000001', 'u0000003', 'ក្រុមអ្នករត់ម៉ូតូ​ឌុប​ជាង៦០នាក់​ផ្តិតមេដៃតវ៉ា​​នៅមុខ​សង្កាត់ប៉ោយប៉ែត មិន​ឱ្យ​រថយន្ត​ក្រុង​ទម្លាក់​ម៉ូយ​​នៅក្រៅ​ចំណត​', 'ក្រុងប៉ោយប៉ែត ៖ សេចក្តីរាយការណ៍នាវេលាម៉ោង​០៨ និង​៣៥នាទីព្រឹកថ្ងៃទី​១៦ មីនា ២០១៥ នេះថា ​មាន​​អ្នកក្រុម​​រត់ម៉ូតូ​ឌុប​ចំនួន​ជាង​​៦០​នាក់​ បាន​នាំគ្នា​​ផ្តិត​មេដៃលើ​ក្រដាស​យកមក​​មុខ​ប៉ុស្តិ៍​រដ្ឋបាល​សង្កាត់​ប៉ោយប៉ែត ស្ថិត​ក្នុងភូមិ​បាលេលិយ្យ១ ដើម្បី​ធ្វើការ​តវ៉ា​', 'http://kohsantepheapdaily.com.kh/article/116629.html', 'http://i.kspg.co/2015/3/05972d6d-c8b6-43e6-82ec-44f85cbed97c.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('899', 'cl000001', 'u0000003', 'ក្រុម​ឈ្មួញ​ទិញ​ដំឡូងមីរងការ​ចោទប្រកាន់​ពីប្រជា​ពលរដ្ឋ​ថា ទិញដំឡូងមិនឱ្យ​លុយ​', 'ខេត្តឧត្តរ​មានជ័យ ៖ ក្រុម​ឈ្មួញ​តូច​-ធំ​រង​ការ​ចោទប្រកាន់​ពី​ប្រជាពលរដ្ឋ​ថា​ ដើរ​ប្រមូល​ទិញ​ដំឡូង​មី​ពី​ប្រជាពលរដ្ឋ​ជា​ច្រើន​ភូមិ​ហើយ​មិន​ព្រម​គឹ​ត​លុយ​ឱ្យ​ពួក​គាត់​ ហើយត្រូវ​បានពួកគាត់មក​ដាក់​ពាក្យ​ប្តឹង​ចូល​ដល់​អាជ្ញាធរ​មូលរ​ដ្ឋា​ន និង​សមត្ថ​កិច្ច​ឱ្យជួយ​ធ្វើការ​ដោះ​', 'http://kohsantepheapdaily.com.kh/article/116628.html', 'http://i.kspg.co/2015/3/78181fd7-61dc-4f88-8f1b-1d0fee0bb0f2.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('900', 'cl000001', 'u0000003', 'ប្រជាពលរដ្ឋ​ដាំដំឡូង​មី​បាន​១៧៥​ហិ​កតា​និង​ដំណាំ​រួម​ផ្សំ​៨១​ហិ​កតា​ក្រោយ​ពីរ​ច្រូតកាត់​ស្រូវ​វស្សា​រួច​នៅ​ស្រុក​រំដួល', 'ខេត្តស្វាយរៀង​៖ ប្រជាពលរដ្ឋ​ដែល​រស់នៅ​តាម​បន្ទាត់​ព្រំដែន​កម្ពុជា វៀតណាម​ក្នុងស្រុក​រំដួល​បាន​នាំ​គ្នា​ដាំដំឡូង​មី​និង​ដំណាំ​រួម​ផ្សំ​បន្លែ​និង​ឪឡឹក​ជា​លក្ខណៈ​គ្រួសារ​ក្រោយ​ពេល​ច្រូតកាត់​ស្រូវ​វស្សា​ដើម្បី​បង្កើន​ជីវភាព​រស់នៅ​ឲ្យ​បាន​ល្អ​ប្រសើរ​', 'http://kohsantepheapdaily.com.kh/article/116625.html', 'http://i.kspg.co/2015/3/5292cf78-9820-4355-bb8a-68734cffda22.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('901', 'cl000001', 'u0000003', 'ពិធីបុណ្យ​ទន្លេ​លើក​ទី​១ បាន​ប្រារព្ធ​ធ្វើ​ឡើង​នៅ​ទីក្រុង​ក្រចេះ ក្រោម​ប្រធានបទ ទេសចរណ៍ តាម​ដង​ទន្លេ​សម្រាប់​ការ​អភិវឌ្ឍ​សហគមន៍', 'ខេត្តក្រចេះ​៖ ប្រជាពលរដ្ឋ ភ្ញៀវ​ជាតិ អន្តរជាតិ ជា​ច្រើន​ម៉ឺន​នាក់​មក​ពី​តាម​បណ្តា​ខេត្ត ក្រុង រាជធានី និង​មន្ត្រី​អ្នកមុខអ្នកការ បាន​មក​ជួបជុំ​គ្នា ប�រារព្ធ​ពិធីបុណ្យ​ទន្លេ​លើក​ទី​១  ប្រព្រឹត្តិ​ទៅ​ចំនួន​៣​ថ្ងៃ ចាប់ពី​ថ្ងៃ​ទី​១៣ ដល់​ថ្ងៃ​ទី​១៥ ខែ​មិ​នា ឆ្នាំ​២០១៥ ', 'http://kohsantepheapdaily.com.kh/article/116624.html', 'http://i.kspg.co/2015/3/ad382cee-cc44-403f-a6e6-02624a7ae526.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('902', 'cl000001', 'u0000003', 'បំផ្ទុះ​គ្រាប់បែក​ចាប់​ត្រី​នៅ​ដែន​អន្លុង​ការពារ​ត្រូវ​ចាប់ខ្លួន', 'ខេត្តស្ទឹងត្រែង​៖​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៧​មីនា​២០១៥​នេះ​កម្លាំង​ជំនាញ​រដ្ឋបាល​ជលផល​បាន​បញ្ជូន​យុវជន​ម្នាក់​ទៅ​សាលាដំបូង​បន្ទាប់​ពី​ត្រូ​ឃាត់ខ្លួន​ខណៈ​ធ្វើ​សកម្មភាព​បំផ្ទុះ​គ្រាប់បែក​ចាប់​ត្រី​កាលពី​វេលា​ម៉ោងជាង​១០​ព្រឹក ស្ថិត​ក្នុងភូមិ​សាស្ត្រ​ន៍​អន្លុង​ស្វាយ', 'http://kohsantepheapdaily.com.kh/article/116694.html', 'http://i.kspg.co/2015/3/f2168de9-aeb8-47f0-b386-b2b6a6c6b7e6.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('903', 'cl000001', 'u0000003', 'ឪពុក​រំលោភ​កូនស្រី​ចុង​នៅ​ទឹកដី​ថៃ​ចាប់​បាន​នៅ​ទឹកដី​ខ្មែរ', 'ខេត្តបាត់ដំបង​ឪពុកចុង​ចិត្ត​តិរច្ឆាន​ម្នាក់​ត្រូវ​សមត្ថកិច្ច​ឃាត់ខ្លួន​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៦ ខែ​មិ​នា បន្ទាប់​ពី​រង​ការ​ចោទប្រកាន់​ថា​ចាប់រំលោភ​កូនស្រី​ចុង​របស់​ខ្លួន​កាលពី​ចុង​ស​ប្ដា​ហ៍​មុន នៅ​ឯ​ទឹកដី នៃ​ប្រទេស​ថៃ រួច​គេចខ្លួន​ត្រឡប់​មក​ផ្ទះ​វិញ​នៅ​ភូមិ​អូរ​ដា​លើ', 'http://kohsantepheapdaily.com.kh/article/116693.html', 'http://i.kspg.co/2015/3/9ee6b8ca-66c8-40ec-940e-b7f4e7e9512f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('904', 'cl000001', 'u0000003', 'កូនប្រុស​មេគយដែលបាញ់​បោះសេរីត្រូវឃាត់ខ្លួនហើយ ​តែសមត្ថកិច្ច​ហាក់លាក់លៀម', 'រាជធានីភ្នំពេញ ៖​ ទីបំផុតទៅជនសង្ស័យម្នាក់​​ក្នុងចំណោមបក្ខពួកដែលបាញ់​បោះសេរីបង្កការភ្ញាក់ផ្អើល​​កណ្តាលក្រុងភ្នំពេញ​នៅមុខ​មណ្ឌល​កម្សាន្តរ៉ក់​', 'http://kohsantepheapdaily.com.kh/article/116687.html', 'http://i.kspg.co/2015/3/8c0500ae-46c1-41ee-8b1e-8ee8dc0b27cb.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('905', 'cl000001', 'u0000003', 'បុរស​ម្នាក់​ចង.កធ្វើ​អត្ត​ឃាត​​ខ្លួនឯងព្រោះ​តូចចិត្ត​មាន​ជំងឺ​ប្រចាំ​កាយ​', 'ខេត្តកណ្តាល​ ៖ បុរស​ម្នាក់​ដោយសារ​មាន​ជំងឺ​ប្រចាំ​កាយ​យូ​រឆ្នាំ​មក​ហើយ បាន​ផឹកស្រា​ស្រវឹង​រួច​សម្រេចចិត្ត​ធ្វើ​អត្ត​ឃាត​ខ្លួនឯង​ដោយ​ចង.ក​សម្លាប់​ខ្លួន​កាលពី​វេលា​ម៉ោង​ប្រហែល​៤​ ថ្ងៃ​ទី​១៥ ខែ​មីនា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116665.html', 'http://i.kspg.co/2015/3/06206895-9112-4fb3-b8fc-cd41ec9501d7.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('906', 'cl000001', 'u0000003', 'អ្នក​ភូមិ​ចាប់ចោ​រលួច​ម៉ូតូបាន​វាយ​ទ្រមខ្លួនរួចប្រគល់​ឲ្យ​នគរបាល', 'ខេត្តកណ្ដាល​ ៖​ ជនល្មើស​ម្នាក់​បាន​ធ្វើ​សកម្មភាព​លួច​ម៉ូតូ​មួយ​គ្រឿង​ដែល​ម្ចាស់​ចត​ទុក​នៅ​ក្បែរ​ផ្ទះ​ត្រូវ​អ្នក​ភូមិ​នាំ​គ្នា​ព​ទ្ធ័​ចាប់​បាន​វាយទ្រម​ខ្លួន​ទើប​ប្រគល់​ឲ្យ​នគរបាល កាលពី​ម៉ោង​៣​និ​ង៥០​នាទី​រសៀល ថ្ងៃ​ទី​១៥', 'http://kohsantepheapdaily.com.kh/article/116662.html', 'http://i.kspg.co/2015/3/efca0a92-939e-464b-8091-4ea04efd0b7f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('907', 'cl000001', 'u0000003', 'ប្រឆាំង​ម៉ាហ្វីយ៉ា​បរទេស អគ្គស្នងការ​នគរបាល​ជាតិ ៖ នៅ​ខេត្ត​ព្រះ​សីហ​នុ​ជនបរទេស​បង្ក​បទល្មើស', 'រាជធានី​ភ្នំពេញ ៖ ក្នុង​ពិធី​ផ្សព្វផ្សាយ​ការ​រៀបចំសេចក្តី​ព្រាង​ផែនការ​ការពារ​សន្តិសុខ សុវត្ថិភាព ជូន​វិនិយោគិន និង​អ្នកទេសចរ​បរទេស ជូន​តំណាង​ស្ថានទូត ដែល​មាន​និវេសនដ្ឋាន​របស់​ខ្លួន​ប្រចាំ​នៅ​កម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/116644.html', 'http://i.kspg.co/2015/3/314e94c0-4244-4e97-b7b8-d7c4521dc25d.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('908', 'cl000001', 'u0000003', 'ជន​ស្ទាវ​៣នាក់​ខ្លាំង​ដូច​តោ​ បាញ់​រះ​ដោយ​កាំភ្លើង AK-47 ចូល​មជ្ឈមណ្ឌល​កម្សាន្ត​រ៉ក់', 'រាជធានី​ភ្នំពេញ ៖ ស្នូរ​បាញ់​រះ​យ៉ាង​សាហាវ​ប្រមាណ​ជិត​២០​គ្រាប់​ដោយ​កាំភ្លើង​អាកា ដូច​ក្នុង​ខ្សែភាពយន្ត បាន​ធ្វើ​ឱ្យ​មជ្ឈមណ្ឌល​កម្សាន្ត​រ៉ក់ ដែល​ស្ថិត​នៅ​កណ្តាល​រាជធានី​ភ្នំពេញ ញ័រ​កក្រើក​កណ្តាលរាត្រី', 'http://kohsantepheapdaily.com.kh/article/116642.html', 'http://i.kspg.co/2015/3/92c59548-a19b-46d4-9a8a-788d5f9c5932.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('909', 'cl000001', 'u0000003', 'ចូល​ផឹកស៊ី​បង្ក​ការ​អុកឡុក​ប្រុង​វាយ​ថៅកែ​ស្រី ថៅកែ​ប្រុស​បាញ់​គំរាម​១​គ្រាប់​ផ្អើល​សមត្ថកិច្ច', 'ខេត្ត​ព្រះ​សីហនុ​​៖​ស្នូរ​ផ្ទុះអាវុធ​១​គ្រាប់​នៅ​កណ្តាល​ខេត្ត​ព្រះ​សហ​នុ ខណៈ​ភ្ញៀវ​ចូល​ផឹកស៊ី​នៅ​ក្នុង​ហាង​មួយ​កន្លែង ហើយ​បង្ក​ការ​អុកឡុក​រក​វាយ​ថៅកែ​ស្រី ភ្លាម​ៗ​នោះ​ថៅកែ​ប្រុស​ជា​ប្តី​បាន​ដក​កាំភ្លើងខ្លី ម៉ាក​K54 បាញ់​ឡើង​លើ​គំរាម​តែ​ម្តង ', 'http://kohsantepheapdaily.com.kh/article/116627.html', 'http://i.kspg.co/2015/3/94fa177b-515a-4e33-8a7b-49aa2b378347.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('910', 'cl000001', 'u0000003', 'ជនបរទេស​ម្នាក់​ដួល​ផ្ងា​ក្រោយ​ស្លាប់ ខណៈ​ពេល​កំពុង​អង្គុយ​ញ៉ាំ​អាហារ', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ជនបរទេស​ម្នាក់​បាន​ស្លាប់​ភ្លាម​ៗ ខណៈ​កំពុង​អ្គុ​យ​ញ៉ាំ​អាហារ​ដួល​ផ្ងា​ក្រោយ​បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​ដល់​អ្នក​ភូមិ​ដែល​នៅ​ក្បែរ​ប្តឹង​ទៅ​សមត្ថកិច្ច​ចុះ​មក​ដល់​កន្លែង​កើតហេតុ កាល​វេលា​ម៉ោង​៧​និង​១០​នាទី​យប់ ថ្ងៃ​ទី​១៥ ខែ​មិ​នា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116623.html', 'http://i.kspg.co/2015/3/6268696e-34a3-41d1-ad8c-0d2db78dbca9.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('911', 'cl000001', 'u0000003', 'ចាប់​រថយន្ត​ដឹក​ដី​ហៀរ​ទ្រុង​គ្មាន​គ្រប​៤​គ្រឿង​ផាកពិន័យ', 'ខេត្តកំពង់ឆ្នាំង​ ៖ ​រថយន្ត​បែន​ដឹក​ដី​៤​គ្រឿង​ដែល​បាន​ដឹក​ដី​កំពប់​ពេញ​ផ្លូវ​ និង​ខ្លះ​អត់​ពាក់​ផ្លា​ក​លេខ ត្រូវ​បាន​នគរបាល​រៀបចំ​សណ្តាប់ធ្នាប់​ក្រុង​កំពង់ឆ្នាំង', 'http://kohsantepheapdaily.com.kh/article/116683.html', 'http://i.kspg.co/2015/3/60a9094b-fad1-4092-9c9c-aef47bae0616.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('912', 'cl000001', 'u0000003', 'ជិះ​ម៉ូតូ​វ៉ា​គ្នា​​ទាក់​ដៃចង្កូត​ជ្រុល​ទៅ​បុក​​​រថយន្ត​​​​ស្លាប់​ម្នាក់', 'ខេត្តកំពង់ឆ្នាំង ៖ យុវជន​ម្នាក់​បាន​ស្លាប់​ភ្លាម​ៗ​នៅ​នឹង​កន្លែង​កើតហេតុ ខណៈ​ពេល​ជិះ​ម៉ូតូ​​ប៉ះ​ដៃចង្កូតជាមួយ​​ម៉ូតូ​មួយ​គ្រឿង​ទៀត​ដែល​ជិះទន្ទឹម​គ្នា បណ្តាល​ឱ្យជ្រុល​ចង្កូត​ទៅ​ខាងឆ្វេង​ដៃបុក​ជាមួយរថយន្ត​មួយ​គ្រឿង​', 'http://kohsantepheapdaily.com.kh/article/116671.html', 'http://i.kspg.co/2015/3/d035af76-0a35-40a7-8a1c-3990cd22fe6a.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('913', 'cl000001', 'u0000003', 'ជិះ​ម៉ូតូ​ដឹក​សង្សារ​ទៅ​បុក​រថយន្ត​រង​របួសធ្ងន់​ទាំង​២​នាក់', 'រាជ​ធានីភ្នំពេញ ៖ គូសង្សារ​មួយគូបាន​រង​របួសធ្ងន់​ទាំង​២​នាក់ ​ខណៈ​ពេល​ជិះ​ម៉ូតូ​មក​ដល់​ផ្លូវបែក​ជា​៤​​ជ្រុល​បុក​រថយន្ត​ដួលរ​ដូក​រណែល​ពេញ​ថ្នល់ ចំណែក​រថយន្ត​បើករ​ត់​គេច​', 'http://kohsantepheapdaily.com.kh/article/116636.html', 'http://i.kspg.co/2015/3/287fff40-0004-4f61-9999-cf98bcc21c5d.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('914', 'cl000001', 'u0000003', 'ជិះ​ម៉ូតូ​វ៉ា​រថយន្ត​ទាក់​ដៃចង្កូត​ជាមួយ​រនាំង​ដែក​ដួល​ចូល​ក្រោម​រថយន្ត​កិន​ស្លាប់', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​បាន​ស្លាប់ ក្រោយ​ពី​ជិះ​ម៉ូតូ​វ៉ា​រថយន្ត​ជ្រុល​ទាក់​ដៃចង្កូត​ជាមួយ​រនាំង​ដែក បណ្តាល​ឲ្យ​ដួល​ខ្ទាត​ចូល​ក្រោម​រថយន្ត​កិន​ចំ​ពីលើ​តែ​ម្តង កាល​វេលា​ម៉ោង​១០​និង​៣០​នាទី​យប់ ថ្ងៃ​ទី​១៥ ខែ​មិ​នា ឆ្នាំ​២០១៥ ស្ថិត​តាម​បណ្តោយ​មហាវិថី​ព្រះ​មុនី​វង្ស', 'http://kohsantepheapdaily.com.kh/article/116626.html', 'http://i.kspg.co/2015/3/f57b11bb-a29b-4ce0-94d0-c4417dfe50a1.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('915', 'cl000001', 'u0000003', 'ឃុបឃិតជាមួយ​​ជល​ផល​ខេត្ត លួចនាំត្រីចូលពីស្រុកយួនរាល់​យប់​', 'ជា​រៀង​រាល់​យប់​មាន​ឈ្មួញ​ចំនួន​៣​នាក់​កំពុង​នាំ​ត្រី​ពី​ស្រុក​យួន​ ចូល​មក​លក់​ចែកចាយ​នៅ​ទីផ្សារ​នានា​តាម​ខេត្ត រហូត​ដល់ទី​ផ្សារ​ភ្នំពេញ ។ ការ​ដែរ​នាំ​ចូល​ត្រី​មក​ពី​ស្រុក​យួន​នេះ ', 'http://kohsantepheapdaily.com.kh/article/116676.html', 'http://i.kspg.co/2015/3/b34dfeaf-f7f4-4654-85b2-da77ea459397.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('916', 'cl000001', 'u0000003', 'ឧត្តម​សេនីយ៍ចិនម្នាក់​ជាប់ចោទ​ពុក​រលួយ​ស្លាប់​ដោយ​មហារីក​', 'មានអាយុ​៧១​ឆ្នាំ ឧត្តមសេនីយ៍​ XU CAIHU បាន​ទទួលមរណភាព​ហើយ កាលពីថ្ងៃទី​១៥ មីនា ដោយជំងឺ​មហារីករន្ធគូទ ។', 'http://kohsantepheapdaily.com.kh/article/116689.html', 'http://i.kspg.co/2015/3/26890481-7c22-44c1-894b-d61b482f4aad.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('917', 'cl000001', 'u0000003', 'អន្តរាគមន៍​បរទេស​ជា​មូលហេតុ​សំខាន់​នៃ​ការ​អូស​បន្លាយ​វិបត្តិ​នៅ​ស៊ី​រី​ដែល​ឈាន​ចូល​ឆ្នាំ​ទី​5', 'ខណៈ​ដែល​វិបត្តិ​នៅ​ប្រទេស​ស៊ី​រី​ឈាន​ចូល​ដល់​ឆ្នាំ​ទី​៥ ដោយ​គ្មាន​ដំណោះស្រាយ​នយោបាយ​ជាក់ស្តែង​ណាមួយ​នោះ បណ្តា​អ្នកនយោបាយ​ស៊ី​រី​បាន​ចោទ​ថា អន្តរាគមន៍​របស់​ពួក​បរទេស​ជា​មូល', 'http://kohsantepheapdaily.com.kh/article/116684.html', 'http://i.kspg.co/2015/3/c017065f-3004-4c2f-b3ff-904718d2c2cd.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('918', 'cl000001', 'u0000003', 'ប្រធានាធិបតី​វេ​ណេ​ស៊ុ​យ​អេ​ឡា​ទទួល​បាន​អំណាច​ពិសេស', 'ប្រធានាធិបតី​វេ​ណេ​ស៊ុ​យ​អេ​ឡា លោក​នី​កូ​ឡាស់ ម៉ា​ឌូ​រ៉ូ កាលពី​ថ្ងៃ​ទី​១៥ មីនា ទទួល​បាន​អំណាច​ពិសេស​ពី​សភា ដែល​អំណាច​ពិសេស​នេះ​អាច​ឱ្យ​លោក​ដឹកនាំ​ប្រទេស​តាម​រយៈ​ក្រិ​ត្យរ​ហូត', 'http://kohsantepheapdaily.com.kh/article/116668.html', 'http://i.kspg.co/2015/3/dd069415-ff51-4ff7-bf25-9812d6c39e4c.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('919', 'cl000001', 'u0000003', 'រញ្ជួយ​ដី​ខ្លាំង​មួយ​នៅ​ភាគ​កណ្តាល​ឥណ្ឌូ​ណេ​ស៊ី', 'ទីភ្នាក់ងារ​ឧតុនិយម និង​ភូ​កុម្ភ​សាស្ត្រ​ឥណ្ឌូ​ណេ​ស៊ី​រាយការណ៍​ថា ការ​រញ្ជួយ​ដី​ក្រោម​សមុទ្រ​យ៉ាង​ខ្លាំង​មួយ​ក្នុង​ជម្រៅ​រាក់​កើត​មាន​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៦ មីនា នៅ​កោះ​ស៊ូ​ឡា​វេ​ស៊ី ភាគ​កណ្តាល​ឥណ្ឌូ​ណេ​ស៊ី ', 'http://kohsantepheapdaily.com.kh/article/116667.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('920', 'cl000001', 'u0000003', 'ចិន​នឹង​បញ្ចេញ​ប្រតិកម្ម​តប បើ​មានការ​វាយប្រហារ​ចូល​ដែនដី​របស់​ខ្លួន​ទៀត​', 'លោក ហ្វា​ន ចាង​ឡុ​ង អនុប្រធាន​គណៈកម្មាធិការ​យោធា​កណ្តាល​របស់​ចិន ថ្លែង​កាលពី​ថ្ងៃ​ទី​១៤ មីនា កន្លង​មកនេះ​ថា ប្រទេស​ចិន​នឹង​មិន​អាច​ទទួលយក​បាន​ឡើយ ចំពោះ​ឧប្បត្តិហេតុ', 'http://kohsantepheapdaily.com.kh/article/116666.html', 'http://i.kspg.co/2015/3/ac67ba88-53a6-4bfb-9819-752a857af3ee.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('921', 'cl000001', 'u0000003', 'យន្តហោះ​ឥណ្ឌូ​ណេ​ស៊ី​ពីរ​គ្រឿង​ធ្លាក់​ពេល​តាំងពិព័រណ៍​យន្តហោះ​នៅ​ម៉ា​ឡេ​ស៊ី', 'សារព័ត៌មាន​ក្នុងស្រុក​រាយការណ៍​ថា យន្តហោះ​ពីរ​គ្រឿង​របស់​ឥណ្ឌូ​ណេ​ស៊ី បាន​ធ្លាក់​កាលពី​ថ្ងៃ​ទី​១៥ មីនា ពេល​ហោះ​សាកល្បង​បង្ហាញខ្លួន​ក្នុង​ពិព័រណ៍​សមុទ្រ និង​យន្តហោះ​អន្តរជាតិ ', 'http://kohsantepheapdaily.com.kh/article/116664.html', 'http://i.kspg.co/2015/3/06e4963b-c734-4f4d-9d3e-0c9814e1088b.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('922', 'cl000001', 'u0000003', 'ក្រុម​អង្គរក្ស​ដណ្តើម​បាន​ចំណាត់ថ្នាក់​លេខ​1 ប្រចាំ​មណ្ឌល​រាជធានី​ភ្នំពេញ', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​កីឡាបាល់ទះ​ដណ្តើម​ពានរង្វាន់​ក្រុមហ៊ុនស្រា​បៀរ​លី​អូ​ឆ្នាំ​២០១៥ វគ្គ​ជម្រុះ​ដណ្តើម​ចំណាត់ថ្នាក់​លេខ​១ ប្រចាំ​មណ្ឌល​រាជធានី​ភ្នំពេញ បាន​បញ្ចប់​ជា​ស្ថាពរ​ហើយ​កាលពី​រសៀល​', 'http://kohsantepheapdaily.com.kh/article/116682.html', 'http://i.kspg.co/2015/3/98330800-c12c-486e-86ba-aa7020e90376.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('923', 'cl000001', 'u0000003', 'ពុទ្ធ ហ៊ុ​ច រួច​ផុត​ពី​ការ​ចោទប្រកាន់​របស់​កីឡាករ​ថៃ', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​ប្រដាល់​ជា​លក្ខណៈ​អន្តរជាតិ​កាលពី​យប់​ថ្ងៃអាទិត្យ ទី​១៥ មីនា នៅ​លើ​សង្វៀន «​កំពូល​អ្នក​ប្រដាល់​ប្លេ​ក​ខេត​» របស់​ស្ថានីយទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍ កីឡាករ​កម្ពុជា ពុទ្ធ ហ៊ុ​ច បាន​រួច​', 'http://kohsantepheapdaily.com.kh/article/116681.html', 'http://i.kspg.co/2015/3/c487176c-d6e6-4b60-bb31-cc1c17cbe24c.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('924', 'cl000001', 'u0000003', 'បាល់ទាត់​ហូ​ឡង់ D2 ជុំ​ទី​29 សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​ នេះ​មាន 10​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ហូ​ឡង់ D2 ជុំ​ទី 29 សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​នេះ មាន 10 ប្រកួត ។ ក្រុម Rotterdam, Nijmegen និង Roda សង្ឃឹម​ឈ្នះ​ក្នុង​ទឹកដី ។', 'http://kohsantepheapdaily.com.kh/article/116641.html', 'http://i.kspg.co/2015/3/e89879c5-07ad-406c-8f70-a8a5e86a7da2.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('925', 'cl000001', 'u0000003', 'បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទនេះ​មាន​13​ប្រកួត Swansea ស្វាគមន៍​ក្រុម​ខ្លាំង Liverpool', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​មាន 13 ប្រកួត ។ Swansea ស្វាគមន៍​ក្រុម​ខ្លាំង Liverpool ខណៈ Roma ជា​ក្រុម​រឹង​មាំ​ក្នុង​ទឹកដី ។ ', 'http://kohsantepheapdaily.com.kh/article/116639.html', 'http://i.kspg.co/2015/3/d5e29b9a-b04e-47cb-b3b6-ad29df647efc.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('926', 'cl000001', 'u0000003', 'បុណ្យ​រំឭក​ព្រះ​ឧបការគុណ​សម្តេចព្រះសង្ឃរាជ​ជោ​ត​ញ្ញា​ណោ ជួន ណាត', 'ខេត្តសៀមរាប ៖ នៅ​ថៃ្ង​ទី​១៤ មីនា នៅ​វត្ត​រាជ​បូព៌ សង្កាត់​សាលា​កំរើក ក្រុង​សៀមរាប មាន​ប្រារព្ធ​ធ្វើ​ពិធីបុណ្យ​រំឭក​ព្រះ​ឧបការគុណ​សម្តេចព្រះសង្ឃរាជ​ជោ​ត​ញ្ញា​ណោ ជួន ណាត និង ក្រុម​ជំនុំ​ព្រះ​ត្រៃ​បិដក​ខែ្ម​រ ', 'http://kohsantepheapdaily.com.kh/article/116635.html', 'http://i.kspg.co/2015/3/0de42412-4b3d-4ff7-a2f8-ac1071ed86aa.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('927', 'cl000001', 'u0000003', 'បុណ្យ​រំឭក​ព្រះ​ឧបការគុណ​សម្តេចព្រះសង្ឃរាជ​ជោ​ត​ញ្ញា​ណោ ជួន ណាត', 'ខេត្តសៀមរាប ៖ នៅ​ថៃ្ង​ទី​១៤ មីនា នៅ​វត្ត​រាជ​បូព៌ សង្កាត់​សាលា​កំរើក ក្រុង​សៀមរាប មាន​ប្រារព្ធ​ធ្វើ​ពិធីបុណ្យ​រំឭក​ព្រះ​ឧបការគុណ​សម្តេចព្រះសង្ឃរាជ​ជោ​ត​ញ្ញា​ណោ ជួន ណាត និង ក្រុម​ជំនុំ​ព្រះ​ត្រៃ​បិដក​ខែ្ម​រ ', 'http://kohsantepheapdaily.com.kh/article/116631.html', 'http://i.kspg.co/2015/3/0de42412-4b3d-4ff7-a2f8-ac1071ed86aa.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('928', 'cl000001', 'u0000003', 'រឿងរ៉ាវ​ប្លែក​ៗ​កើត​មាននៅ​លើ​ពិភពលោក', '១-​កំប្រុក​លួច​ស៊ី​សណ្តែក​ពេល​ម្ចាស់​មិន​នៅ លោក​អេ​ម​ម៉ា រស់នៅ​ក្រុង​ប៊ី​យូ​លី ចក្រភព​អង់គ្លេស​បាន​ច្នៃ​ឧបករណ៍​ដាក់​សណ្តែក​មាន​រាង​ដូច​ម៉ាស់​មុខ​ព្យួរ​នៅ​ក្នុង​ឧទ្យាន​ធ្វើ​ឱ្យ​កំប្រុក​មួយ​ក្បាល​', 'http://kohsantepheapdaily.com.kh/article/116637.html', 'http://i.kspg.co/2015/3/23f5ace7-4741-4725-9795-1dfe17693c0d.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('929', 'cl000001', 'u0000003', 'ស្រាក្រហម​និង​សូកូឡា​មិន​ជាប់​ពាក់ព័ន្ធ​នឹង​ការ​ជួយ​ឱ្យ​អាយុ​វែង', 'ការ​ពិសោធ​លើ​អ្នក​ស្ម័គ្រចិត្ត​ប្រមាណ​៧៨៣​នាក់​ពី​សំណាក់​ក្រុម​អ្នក​ស្រាវជ្រាវ​សាកលវិទ្យាល័យ Johns Hopkins University សហរដ្ឋអាមេរិក​បាន​រក​ឃើញ​ថា Resveratrol​ជា​សមាសធាតុ', 'http://kohsantepheapdaily.com.kh/article/116657.html', 'http://i.kspg.co/2015/3/e27306a8-5f5d-482e-a715-24f3a9341b8f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('930', 'cl000001', 'u0000003', 'ទទួលទាន​ត្រី​ជួយ​ស្ត្រី​ទប់ស្កាត់​ការ​ខូច​សោតវិញ្ញាណ', 'ក្រុម​អ្នក​ស្រាវជ្រាវ​អា​មេ​រិ​កបាន​អះអាង​កាលពី​ពេល​ថ្មី​ៗ​នេះ​ថា ស្ត្រី​បរិភោគ​ត្រី​២-៣​ដង​ក្នុង​១​ស​ប្តា​ហ៍​អាច​កាត់​បន្ថយ​ហានិភ័យ​បាត់បង់​សោតវិញ្ញាណ (​ត្រចៀក​ថ្លង់​) ។', 'http://kohsantepheapdaily.com.kh/article/116655.html', 'http://i.kspg.co/2015/3/414645ac-b5af-4749-bd79-6bb53be55d8d.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('931', 'cl000001', 'u0000003', 'ទទួលទាន​ផ្លែ​ឈើ​កំប៉ុង​ជា​រឿយ​ៗ​បង្កើន​ហានិភ័យ​ស្លាប់​មុន​អាយុ', 'លទ្ធផល​នៃ​ការ​សិក្សា​ស្រាវជ្រាវ​ថ្មី​មួយ​ចុះ​ផ្សាយ​ក្នុង​ព្រឹត្តិ&��ត្រ Journal of Epidemiology & Community Health បាន​បង្ហាញ​ថា ការ​ទទួលទាន​ផ្លែ​ឈើ​កំប៉ុង​ជា​រឿយ​ៗ​ជាប់​ទាក់ទង', 'http://kohsantepheapdaily.com.kh/article/116654.html', 'http://i.kspg.co/2015/3/17563fc0-6d81-495b-b2cd-7324235cff26.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('932', 'cl000001', 'u0000003', 'តើ​ផ្លែ​ពី​ស្តា​ស៊ី​អូស (Pistachios) ពិតជា​អាច​ទប់ស្កាត់​ជំងឺ​ទឹកនោមផ្អែម​មែន​ទេ ?', 'ពី​ស្តា​ស៊ី​អូស (Pistachios) ជា​ផ្លែ​ឈើ​មួយ​ប្រភេទ​ដែល​មាន​ដាំ​នៅ​តាម​បណ្តា​ប្រទេស​ក្នុង​តំបន់​អាស៊ី​កណ្តាល និង​មជ្ឈិមបូព៌ា ។ ផ្លែ​ឈើ​ប្រភេទ​នេះ ត្រូវ​បាន​ក្រុម​អ្នក​វិទ្យាសាស្ត្រ​ប្រទេស​អេ​ស្ប៉ា​ញ', 'http://kohsantepheapdaily.com.kh/article/116653.html', 'http://i.kspg.co/2015/3/1e1ff287-b278-449f-af12-f9e3987476ad.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('933', 'cl000001', 'u0000003', 'អាហារ​ផ្ទុក​ខ្លាញ់​ច្រើន​បង្កើន​ហានិភ័យ​មហារីក​ដោះ​20​ភាគរយ', 'ការ​ពិនិត្យ​មើល​លើ​ទិន្នន័យ​របស់​អ្នកជំងឺ ១​ម៉ឺន​នាក់​គឺ​បាន​បង្ហាញ​ពី​ការ​ជាប់​ទាក់ទង​គ្នា​រវាង​ការ​ទទួលទាន​ខ្លាញ់​និង​ជំងឺមហារីក​ដោះ ។ ការ​សិក្សា​ស្រាវជ្រាវ​ធ្វើ​ឡើង', 'http://kohsantepheapdaily.com.kh/article/116652.html', 'http://i.kspg.co/2015/3/650fdc5a-1749-493d-b2b8-3b071ee03376.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('934', 'cl000001', 'u0000003', 'ឈ្វេងយល់​ពី​អាការ​បញ្ហា​ស្លាក់​ខ្យល់​និង​របៀប​ជួយ​សង្គ្រោះ​បឋម', 'អាការ​ស្លាក់​ខ្យល់​កើត​ពី​ការ​ស្ទះ​ណែន​នៃ​បំពង់ខ្យល់ អាច​ជា​អាហារ​ឬ​កម្អួត​ដែល​ធ្លាក់​ចូល​ខុសផ្លូវ ឬ​ជា​វត្ថុ​មាន​ធ្មេញ​ដែល​បាន​បាក់ ឬ​ក៏​គ្រូពេទ្យ​ធ្វើ​ធ្មេញ​ឱ្យ​ធ្លាក់​ចូល​ទៅ​ក្នុង​បំពង់ក​ជាដើម', 'http://kohsantepheapdaily.com.kh/article/116650.html', 'http://i.kspg.co/2015/3/68e23537-ff0b-4567-bf22-e9986986a079.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('935', 'cl000001', 'u0000003', 'ចង្វាក់បេះដូង​ដើរ​ខុសធម្មតា​ជីវិត​អ្នក​ប្រឈម​នឹង​ហានិភ័យ​អ្វីខ្លះ?', 'តើ​លោក​អ្នក​ធ្លាប់​មាន​អារម្មណ៍​ថា បេះដូង​របស់​អ្នក​លោត​ខុស​ប្រក្រតី​ដែរ​ឬទេ ? លោក​អ្នកមួយ​ចំនួន​តែងតែ​មានការ​ធ្វេសប្រហែស​ក្នុង​ការ​ចាប់អារម្មណ៍​អំពី​ស្ថានភាព​បេះដូង​របស់​ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/116649.html', 'http://i.kspg.co/2015/3/3fabd6f3-4927-4bb8-96ac-fed21bf2c620.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('936', 'cl000001', 'u0000003', 'ឧស្សាហ៍​រមាស់​ក្បាល​ពេល​ពាក់មួកសុវត្ថិភាព​ធ្វើ​យ៉ាងម៉េច​ទើប​បាត់?', 'អាការ​រមាស់​នេះ​អាច​មិន​មាន​គ្រោះថ្នាក់​អ្វី​ទ្បើ​យ តែ​ផ្ទុយ​ទៅ​វិញ ប្រសិនជា​អ្នក​ប្រើ​ដៃ​អេះ​ត្រង់​កន្លែង​រមាស់​ក្រោម​មួក​សុវត្ថិភាព​ពេល​បើកបរ​នោះ​ទើប​ជា​គ្រោះថ្នាក់ ព្រោះ​អាច​បាត់បង់​ការ​បញ្ជា​ដៃចង្កូត', 'http://kohsantepheapdaily.com.kh/article/116648.html', 'http://i.kspg.co/2015/3/34ecbfbc-be0d-4c7a-8c6e-dd3337ec9631.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('937', 'cl000001', 'u0000003', 'ទទួលទាន​អាហារ​ឆ្អែត​ត្រឹម​2​ពេល​ក្នុង​1​ថ្ងៃ​ផ្តល់​អត្ថប្រយោជន៍​ដល់​អ្នក​កើត​ជំងឺ​ទឹកនោមផ្អែម', 'ក្រុម​អ្នក​វិទ្យាសាស្ត្រ​ប្រទេស​ឆែក​បាន​អះអាង​ថា ការ​ទទួលទាន​អាហារ​ឆ្លែ​ត​ៗ​ត្រឹមតែ​២​ពេល​ក្នុង​មួយ​ថ្ងៃ​គឺ​ពេល​ព្រឹក​និង​ពេល​ថ្ងៃត្រង់​ផ្តល់​ផល​ល្អ​ដល់​អ្នក​កើត​ជំងឺ​ទឹកនោមផ្អែម​ប្រភេទ​-២', 'http://kohsantepheapdaily.com.kh/article/116647.html', 'http://i.kspg.co/2015/3/71c22678-1bd0-491e-a6ec-ae1719ad785d.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('938', 'cl000001', 'u0000003', 'ស្ត្រី​មាន​ផ្ទៃពោះ​គួរ​ប្រតិបត្តិ​កិច្ចការ​អ្វីខ្លះ​ទើប​មាន​សុវត្ថិភាព​ពេញលេញ', 'សកម្មភាព​ក្នុង​ការងារ​ប្រចាំ​ថ្ងៃ​របស់​ស្ត្រី​ដែល​កំពុង​ពពោះ​ឬ​មានគភ៌ គឺជា​កិច្ចការ​ដ៏​សំខាន់​បំផុត ដែល​សាមីខ្លួន​ផ្ទាល់​ក៏​ដូច​ជា​ស្វាមី​គ្រប់​រូប​គួរ​យកចិត្តទុកដាក់​បន្តិច ។', 'http://kohsantepheapdaily.com.kh/article/116646.html', 'http://i.kspg.co/2015/3/a9920341-17c3-4c4c-bee2-86ea13750c9b.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('939', 'cl000001', 'u0000003', 'មាន​ជំងឺ​ក្លន​កុំ​ភ្លេច​មក​ព្យាបាល​នៅ​មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត', 'រាជធានី​ភ្នំពេញ ៖ អ្នក​កើត​ជំងឺ​ក្លន​បើសិនជា​មាន​រោគ​សញ្ញា​ចុក​ខ្លាំង ក្អួត​រួច​សង្កត់​ពោះវៀន​ឲ្យ​ចូល​ទៅ​កន្លែង​ដើម​វិញ​មិន​បាន​ទេ​នោះ​មិន​ត្រូវ​ទុក​យូរ​លើស​ពី​៦​ម៉ោង​ដាច់ខាត​ត្រូវ​តែ​នាំ​អ្នកជំងឺ​មក​មន្ទីរ', 'http://kohsantepheapdaily.com.kh/article/116645.html', 'http://i.kspg.co/2015/3/2e19551f-d007-4692-9a22-0af25ec8f4fb.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('940', 'cl000001', 'u0000003', 'អភិ​បាលខណ្ឌសែនសុខ​​យកអំណោយ​សាខាកាកបាទក្រហម​កម្ពុជា​​ចែកជូនប្រជាពលរដ្ឋ​​ក្រីក្រ​១០​ភូមិក្នុងសង្កាត់ភ្នំពេញថ្មី​', 'រាជធានីភ្នំពេញ ៖ លោកលី សាវ៉េត ​ប្រធានកិត្តិយសអនុ​ប្រធាន​សាខា​កាកបាទក្រហម​កម្ពុជា​ខណ្ឌ​សែនសុខ និង​ជាអភិបាល​ខណ្ឌ​ ​នា​វេលាម៉ោងរសៀលថ្ងៃទី​១៦ មីនា ២០១៥ នេះ បានដឹកនាំ​​មន្ត្រី​ក្រោមឱវាទ ​នាំយកអំណោយ​​របស់​​សម្តេច​កិត្តិព្រឹទ្ធ​បណ្ឌិត​ ប៊ុន​ រ៉ានី​ ', 'http://kohsantepheapdaily.com.kh/article/116686.html', 'http://i.kspg.co/2015/3/21060dc8-bacc-4c44-8fac-782145b3a0ae.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('941', 'cl000001', 'u0000003', 'ក្រុមហ៊ុន HUAWEI និង​កញ្ញា មាស សុខ​សោភា កំពុង​តែ​បន្ត​ដឹក​ដៃ​គ្នា​ឈាន​ទៅ​រក​ភាព​ល្បីល្បាញ', 'កញ្ញា​មាស សុខ​សោភា ជា​តារាចម្រៀង​មួយ​រូប​ដែល​មានកេរ្តិ៍ឈ្មោះ​ដ៏​ល្បីល្បាញ​នៅ​ក្នុង​ព្រះរាជ​ជា​ណា​ចក្រ​កម្ពុជា​នោះ បាន​នឹង​កំពុង​តែ​បន្ត​តួនាទី​ដ៏​សំខាន់​របស់​ខ្លួន​ជា​ឯកអគ្គរាជទូត​សុច្ឆន្ទៈ​របស់​ក្រុមហ៊ុន ', 'http://kohsantepheapdaily.com.kh/article/116685.html', 'http://i.kspg.co/2015/3/468b59fe-1d88-4a89-a0ce-43d20cda7e18.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('942', 'cl000001', 'u0000003', 'ម្សៅ​សាប៊ូ PAO ប្រគល់​រង្វាន់​ជូន​អតិថិជន​ដែល​ឈ្នះ​ក្នុង​ពី​វិធី «ឈ្នះ​រាល់​ស្នាម​ប្រឡាក់ ឈ្នះ​លុយ​ភ្លាម​ៗ» ជា​លើក​ទី​ពីរ', 'រាជធានី​ភ្នំពេញ​៖ កាលពី​ថ្ងៃ​ទី ១៤ ខែមីនា ឆ្នាំ ២០១៥ កន្លង​ទៅ​នេះ ម្សៅ​សាប៊ូ PAO Nano Tech បាន ប្រគល់​រង្វាន់​ជូន​ដល់​អតិថិជន​ដែល​បាន​ឈ្នះ​ពី​កម្មវិធី «​ឈ្នះ​រាល់​ស្នាម​ប្រឡាក់ ឈ្នះ​លុយ​ភ្លាម​ៗ» ', 'http://kohsantepheapdaily.com.kh/article/116678.html', 'http://i.kspg.co/2015/3/0cd3d7c7-5b42-48a2-b29f-2646fac2037f.jpg?w=285&h=170&mode=carve', '20150316');
INSERT INTO "tbnews" VALUES ('943', 'cl000001', 'u0000003', 'Samsung Galaxy S6 & S6 Edge មាន​វត្ត​មាននៅ​លើ​ទីផ្សារ​កម្ពុជា​មុន​ថ្ងៃ​ចូល​ឆ្នាំ​ថ្មី​ប្រពៃណី​ខ្មែរ', 'រាជធានី​ភ្នំពេញ​៖ ក្រុមហ៊�ន Samsung  ក្រោយ​ពី​ធ្វើ​ឲ្យ​ពិភពលោក​ចាប់អារម្មណ៍​ទៅ​លើ​ទូរស័ព្ទ​ដៃ​ទាំង​ពីរ​ម៉ូ​ដែល​រ&��ស់​ខ្លួន​គឺ Samsung Galaxy S6 & S6 Edge​ពេល​នេះ ថែម​ទាំង​នៅ​ទទួល', 'http://kohsantepheapdaily.com.kh/article/116673.html', 'http://i.kspg.co/2015/3/d9ccb8ef-e6c6-4ff7-b04c-0f2d8a5651e6.jpg?w=285&h=170&mode=carve', '20150316');
