INSERT INTO "tbnews" VALUES ('982', 'cl000001', 'u0000003', 'អគ្គិភ័យ​កំពុងបំផ្លាញ​ផ្សារក្រោលគោនៅស្វាយជ្រំ', 'ខេត្តស្វាយរៀង​ ៖ អគ្គិភ័យ​បាននិងកំពុងឆេះ​ផ្សារក្រោលគោ​ ស្ថិតនៅតាមបណ្តោយផ្លូវជាតិលេខ១ ​ក្នុងភូមិ-​ឃុំក្រោលគោ ​ស្រុកស្វាយជំ ។ ​', 'http://kohsantepheapdaily.com.kh/article/116764.html', 'http://i.kspg.co/2015/3/c0dd3c88-a389-4b3c-a2c4-595e7ed5746d.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('983', 'cl000001', 'u0000003', 'ចាប់ខ្លួនជន​សង្ស័យ​ម្នាក់​ បន្ទាប់​ពី​លួច​ម៉ូតូ​២​គ្រឿង​​យក​ទៅ​លក់', 'ខេត្តកណ្ដាល ​៖​ នគរបាល​ព្រហ្មទណ្ឌ​ស្រុក​ស្អាង​ បានធ្វើការ​ចាប់ខ្លួនជន​សង្ស័យ​​ម្នាក់​​កាលពី​​វេលា​ម៉ោង​០៨​យប់​ថ្ងៃទី​១៦ មីនា ២០១៥ បន្ទាប់​​​ពី​យុវជន​នោះ បាន​លួច​ម៉ូតូ​គេ​​ចំនួន​២​គ្រឿង​​បន្តបន្ទាប់​គ្នានៅ​ក្នុង​​សិប្ប​កម្ម​ផលិត​កែវ​ជ័រ ​ស្ថិត​នៅ​ភូមិ​ព្រែក​ថ្មី ​ឃុំ​ទឹក​វិល ​ស្រុក​', 'http://kohsantepheapdaily.com.kh/article/116763.html', 'http://i.kspg.co/2015/3/c0d2107c-727b-41ef-8ef7-57e83f16a533.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('984', 'cl000001', 'u0000003', 'បុគ្គលិក​អង្គការ​​ CMAC ​ម្នាក់ ភ្លាត់ស្នៀត​រាវ​ប៉ះ​ចំ​គ្រាប់​មីន​ផ្ទុះ​ស្លាប់នៅ​នឹង​កន្លែង', 'ខេត្ត​ប៉ៃលិន ៖ បុរស​ម្នាក់​ត្រូវ​គេ​ស្គាល់​ថា ជា​មន្ត្រី​អង្គការ​កំចាត់​មីន​កម្ពុជា(CMAC) បាន​ស្លាប់​ភ្លាម​ៗ នៅ​នឹង​កន្លែង ខណៈ​កំពុងធ្វើការ​រាវរក​គ្រាប់​មីន សេសសល់​ពី​សម័យ​ស​ង្គ្រាម ', 'http://kohsantepheapdaily.com.kh/article/116760.html', 'http://i.kspg.co/2015/3/4f3f3761-39f3-4ee5-a26c-cac51b50fef0.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('985', 'cl000001', 'u0000003', 'កម្លាំង​​សមត្ថកិច្ច​​ចម្រុះ​ខណ្ឌ​ពោធិ៍​សែន​ជ័យ​ចុះ​បង្ក្រាប​ទីតាំង​​ល្បែង​ហ្គេ​ម​បាញ់​ត្រី​មួយ​កន្លែង ​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ កម្លាំង​សមត្ថកិច្ចច​ម្រុះ​ខណ្ឌ​ពោធិ៍​សែន​ជ័យ ដឹកនាំ​ដោយ​លោក​អភិបាលរង​​ខណ្ឌ សេង គន្ធ បាន​ចុះ​​ទៅ​បង្ក្រាប​ទី​តាំង​ល្បែង​ហ្គេ​ម​​បាញ់​ត្រី​មួយ​​កន្លែង ដែល​​បាន​កំពុង​ពង្វក់​មនុស្ស​ឱ្យ​ភ្លេច​អស់​ការ​រក​ស៊ី​ ជា​ពិសេស​ធ្វើ​ឱ្យ​សិស្សភ្លេច​ការ​រៀនសូត្រ​គេច​សាលា​ថែ​ម​ទៀត​ផង​។', 'http://kohsantepheapdaily.com.kh/article/116757.html', 'http://i.kspg.co/2015/3/5ca0c255-8ed4-4b4e-b907-9546cc33d47c.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('986', 'cl000001', 'u0000003', 'ចរន្តអគ្គិសនី​ក្នុង​ក្រុង​ស្វាយ​រៀង​​រយៈ​ពេល​១​ម៉ោង​​ដាច់​​១០​ដង ​ធ្វើឱ្យ​ក្រុម​អាជីវករ​​ ​និងប្រជាពលរដ្ឋ​​ក្ដៅ​ក្រហាយ', 'ខេត្តស្វាយរៀង​៖ ​ក្រុម​អាជីវករ​លក់ដូរ​ ក៏​ដូច​ជា​ប្រជាពលរដ្ឋ​ក្នុង​ក្រុង​ស្វាយ​រៀង​ បាន​ធ្វើការ​ត្អូញត្អែរ​ និង​រិះគន់​យ៉ាង​ចាស់​ដៃដល់​អង្គ​ភាព​​អគ្គិសនី​ខេត្តស្វាយរៀង ចំពោះ​បញ្ហា​ការ​ដាច់​ភ្លើង​ញឹកញាប់​ ដោយ​រយៈ​ពេល​១​ម៉ោង​ដាច់​​១០​ដង​ទៅ​ហើយ ធ្វើ​ឱ្យ​សម្ភារ​​ដូច​ជា​', 'http://kohsantepheapdaily.com.kh/article/116755.html', 'http://i.kspg.co/2015/3/fd7ec21f-6b6a-45cb-9e66-2a70b78c1d45.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('987', 'cl000001', 'u0000003', 'ព័ទ្ធចាប់​ជន​សង្ស័យ​ប្រើប្រាស់​គ្រឿងញៀន២នាក់​នៅ​​ភូមិ​ត្រពាំងឈូក តែត្រូវគេច​ខ្លួនបាត់​', 'រាជធានីភ្នំពេញ ៖ សេចក្តី​រាយ​ការណ៍នាវេលាម៉ោង​​០៣ និង​០៥នាទីរសៀលថ្ងៃទី​១៧ មីនា ២០១៥ នេះថា កម្លាំង​ប៉ូលិស​ការិយាល័យ​​ប្រឆាំង​គ្រឿងញៀន​នៃមន្ទីរ​​ប្រឆាំង​គ្រឿងញៀន​រាជធានីភ្នំពេញ សហការជាមួយ​កម្លាំង​ប៉ូលិស​ព្រហ្មទណ្ឌ​ បាននិង​កំពុង​ធ្វើការ​ឡោម', 'http://kohsantepheapdaily.com.kh/article/116754.html', 'http://i.kspg.co/2015/3/8f59f8b5-0d48-4071-af99-4b9df9dd1f9e.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('988', 'cl000001', 'u0000003', 'មនុស្ស​ប្រុស ស្រី​៣នាក់ត្រូវ​​ប៉េ​អឹម​ខេត្តព្រះ​សីហនុចាប់ខ្លួន​ពាក់ព័ន្ធ​ជួញដូរ​គ្រឿងញៀន', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ កំលាំង​សមត្ថកិច្ច​កងរាជអាវុធហត្ថ​ខេត្តបាន​​ឃាត់ខ្លួន​ជនជាប់សង្ស័យ​ជួញដូរ​ថ្នាំ​ញៀនប្រុស ស្រីចំនួន៣នាក់ជា​បន្តបន្ទាប់​ កាល​ពី​ថ្ងៃ​ទី ១៦ ​មីនា ​២០១៥ នៅ​ត្រង់​ចំណុច​ផ្ទះ​ជួល​មួយ​កន្លែង​ស្ថិត​នៅ​', 'http://kohsantepheapdaily.com.kh/article/116753.html', 'http://i.kspg.co/2015/3/73837dad-7baa-4cd1-87c6-e43d7d7ee814.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('989', 'cl000001', 'u0000003', 'យុវជន​ម្នាក់តូច​ចិត្ត​​សុំ​លុយ​ឪពុក​ទិញ​ទឹកត្នោតជូរ​ផឹក​បន្ត​​មិន​បាន​ចង​.ក​សម្លាប់​ខ្លួន​', 'ខេត្តកំពង់ឆ្នាំង ​៖ ​យុវជន​ម្នាក់​ដោយសារ​តែ​ខឹង​សុំ​លុយ​ឪពុក​៥​ពាន់​រៀលទិញ​ទឹកត្នោតជូរ​បន្ត​ផឹក​ទៀតមិន​បាន ​គិត​ខ្លី​យក​ក្រ​មាចង.ក​សម្លាប់​ខ្លួនបណ្តាល​ឱ្យស្លាប់ និង​ធ្វើ​​ឱ្យ​មានការ​ភ្ញាក់ផ្អើល​កាល​ពីព្រឹក​ព្រលឹម​', 'http://kohsantepheapdaily.com.kh/article/116750.html', 'http://i.kspg.co/2015/3/d26c455c-57f1-4b38-8f48-f52c728c9954.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('990', 'cl000001', 'u0000003', 'ពិធី​ប្រកាស​ទទួលស្គាល់​ជា​ផ្លូវការ​សមាសភាព​គណៈកម្មាធិការ​អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ស្រុក និងក្រុម​កាកបាទក្រហមកម្ពុជា​ឃុំ​ទាំង៨​ទូ​ទាំង​ស្រុក​កៀនស្វាយ', 'ខេត្តកណ្ដាល​៖​ កាល​ពីព្រឹក​ថ្ងៃ​ទី១៧ ​មីនា នៅ​សាលប្រជុំ​សាលាស្រុក​កៀនស្វាយមាន​រៀប​ចំ​ពិធី​ប្រកាស​ទទួលស្គាល់​ជា​ផ្លូវការ​សមាសភាព​គណៈកម្មាធិការ​អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ស្រុក​កៀនស្វាយ ', 'http://kohsantepheapdaily.com.kh/article/116746.html', 'http://i.kspg.co/2015/3/02bdfdb6-17e3-4705-a178-86d5fc2b5127.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('991', 'cl000001', 'u0000003', 'សាលា​ក្រុង​នឹង​បិទ​ផ្សាយ​លទ្ធ​ផល​បឋម​នៃ​ការ​វាយ​តម្លៃ​ពល​រដ្ឋ​បុរី​កីឡា​ដែល​មិន​ទាន់​ដោះ​ស្រាយ​នៅ​ថ្ងៃ​ទី​២០ មីនា', 'សាលា​ក្រុង​នឹង​បិទ​ផ្សាយ​លទ្ធ​ផល​បឋម​នៃ​ការ​វាយ​តម្លៃ​ពល​រដ្ឋ​បុរី​កីឡា​ដែល​មិន​ទាន់​ដោះ​ស្រាយ​នៅ​ថ្ងៃ​ទី​២០ មីនា', 'http://kohsantepheapdaily.com.kh/article/116744.html', 'http://i.kspg.co/2015/3/a60dbff0-8284-4a0b-8f4d-c574bc819eb2.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('992', 'cl000001', 'u0000003', 'ប្រជាពលរដ្ឋ​ក្រីក្រ​២៤៨​គ្រួសារ ទទួល​អំណោយ​ពីអនុ​សាខា​កា​ក​បាទ​ក្រហម​ខណ្ឌមានជ័យ', 'រាជ​ធានីភ្នំពេញ ៖ កា​ក​បាទ​ក្រហម​ខណ្ឌមានជ័យ នៅ​ព្រឹក​ថ្ងៃ​ទី​១៧ ​​មីនា ​២០១៥ បាន​នាំ​យកអំណោយ​សម្តេច​កិត្តិព្រឹទ្ធ​បណ្ឌិត ប៊ុ​ន រ៉ា​នី ហ៊ុន សែន ចែក​ជូន​ប្រជាពលរដ្ឋ​ក្រីក្រ​ចំនួន​២៤៨​គ្រួសារ ដែល​រស់នៅ​ក្នុងភូមិ​', 'http://kohsantepheapdaily.com.kh/article/116742.html', 'http://i.kspg.co/2015/3/6ceeb119-ba71-4368-b360-872d958f3387.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('993', 'cl000001', 'u0000003', 'អាវុធហត្ថ​រាជធានី​ភ្នំពេញ​បង្ក្រាប​ជនសង្ស័យ​ជួញដូរ និង​ប្រើប្រាស់គ្រឿងញៀន​បាន​​​៧​នាក់', 'រាជធានី​ភ្នំពេញ​ ៖ ជនសង្ស័យ​ពាក់ព័ន្ធ​ការ​ជួញដូរ​ និង​ប្រើប្រាស់​គ្រឿងញៀន ត្រូវ​បាន​បង្ក្រាប​ជា​បន្ត​បន្ទា​ប់​ដូចភ្លៀងរលឹម​បាន​ចំនួន​៧​នាក់ ដោយ​កម្លាំង​កងរាជអាវុធហត្ថ​រាជធានី​ភ្នំពេញ ក្រោម​ការ​ដឹកនាំចង្អុល​', 'http://kohsantepheapdaily.com.kh/article/116741.html', 'http://i.kspg.co/2015/3/090a02d4-a248-46d8-a3a1-78d83c22ba20.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('994', 'cl000001', 'u0000003', 'កុមារ​តូច​ៗ​​នាំ​គ្នា​ឈរ​មុខ​ស្ថានទូត​​អា​មេ​រិ​ក​ ដើម្បី​សុំ​កិច្ច​អន្តរាគមន៍​ពី​ Mchelle Obama', 'រាជ​ភ្នំពេញ​ ៖ តំណាង​សហគមន៍​ថ្មគោល បឹង​កក់ បុរី​កីឡា​ និង​ផ្លូវ​រថភ្លើង​ដែល​ភាគច្រើន​​ជា​កុមារ​តូច​ៗ​ប្រមាណ​ជា​៨០​នាក់​ បា​នាំ​គ្នា​ឈរ​កាន់​បដា​ពាក្យ​ស្លោក​ប្រមូល​ម្តុំ​នៅ​មុខ​ស្ថានទូត​អា​មេ​រិ​ក​លើ​កំណាត់​ផ្លូវ​លេខ​៩៦​ សង្កាត់​វត្ត​ភ្នំ​ ខណ្ឌដូនពេញ​ កាលពី​វេលា​ម៉ោង​', 'http://kohsantepheapdaily.com.kh/article/116740.html', 'http://i.kspg.co/2015/3/3e12d6eb-7759-4610-9a6a-66b5881930a6.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('995', 'cl000001', 'u0000003', 'លោកមិន ឃិ​ន ៖ ​​អាចារ្យ-អាចារិនី​ទាំងអស់​​ត្រូវ​ចៀសវាង​​ការ​យក​ពិធីបុណ្យ​​នានា​ធ្វើ​អាជី​វ​កម្ម', 'រាជធានីភ្នំពេញ ៖ រដ្ឋមន្ត្រីក្រសួង​​ធម្មការ និង​សាសនា​ លោក មិន ឃិ​ន បាន​ធ្វើការ​ព្រមាន​ដល់​អាចារ្យ-អាចារិនី​ទាំងអស់ ​ត្រូវ​ចៀស​វាង​​ការ​យក​ពិធីបុណ្យ​នានា​ធ្វើ​អាជីវកម្ម​ដោយ​កំណត់​ និង​បង្គាប់បញ្ជា​ហួស​ព្រំដែន​សមត្ថភាព​ម្ចាស់​​ទាន កុំ​ប្រកាន់​បក្សពួក និន្នាការ​', 'http://kohsantepheapdaily.com.kh/article/116735.html', 'http://i.kspg.co/2015/3/55f6c579-69ae-4a89-a3af-eeae7efaff0e.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('996', 'cl000001', 'u0000003', '​កងរាជអាវុធហត្ថ​ខេត្តប៉ៃលិន​ឃាត់ខ្លួន​អ្នក​កត់​កន្ទុយ​លេខ​ពីរ​នាក់​ប្តី ប្រពន្ធ បញ្ជូន​ទៅ​តុលាការ ខណៈ​មេ​ឆ្នោត​ធំ​​គេច​បាត់​', 'ខេត្ត​ប៉ៃលិន ៖ កូន​កា​តុង​កត់​កន្ទុយលេខ​ឆ្នោត​ពីរ​នាក់​ប្តី​ប្រពន្ធ ត្រូវ​សមត្ថកិច្ច​កងរាជអាវុធហត្ថ​ខេត្តចាប់​ឃាត់ខ្លួន ខណៈ​ស្ត្រី​ជា​ប្រពន្ធ​កំពុង​រៀបចំ​ឡើង​លុយ​ថ្លៃ​កន្ទុយលេខ​ឆ្នោត ដែល​អ្នក​ញៀន​ល្បែង​ចាក់​(​លេង​) ', 'http://kohsantepheapdaily.com.kh/article/116732.html', 'http://i.kspg.co/2015/3/14cb4bc4-44c2-40df-9fbf-2c134bc02b52.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('997', 'cl000001', 'u0000003', 'សមត្ថកិច្ច​ចំរុះ​ព័ទ្ធ​ចាប់​អ្នក​ចែកចាយ​និង​ប្រើប្រាស់​ថ្នាំ​ញៀន​ម្នាក់ដកហូត​​ថ្នាំ​ញៀន​១​កញ្ចប់​', 'ខេត្តកំពង់ឆ្នាំង ៖ យុវជន​ជា​មុខសញ្ញា​ចែកចាយ​ និង​ប្រើប្រាស់​ថ្នាំ​ញៀន​ម្នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​ចំរុះឡោម​ព័ទ្ធ​ចាប់ខ្លួន​បាន កាល​ពីវេលា​ម៉ោង​៤ ​និង​៣០​នាទី​រសៀល ​ថ្ងៃ​ទី១៦ ​មីនា ​​២០១៥ នៅ​ភូមិ​ទី​៣ សង្កាត់ខ្សា​ម ', 'http://kohsantepheapdaily.com.kh/article/116729.html', 'http://i.kspg.co/2015/3/9dd4f8b2-8d97-4ef8-8dbb-1133faa885bb.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('998', 'cl000001', 'u0000003', 'រដ្ឋាករ​ទឹក​ស្វយ័ត​រាជធានីភ្នំពេញផ្សព្វផ្សាយ ​និង​បំពេញ​កិច្ចសន្យាជូន​អតិថិជនបាន៥៥,៥៩%', 'រាជធានី​ភ្នំពេញ ៖ យោង​តាម​របាយការណ៍​របស់​មន្ត្រី​រដ្ឋាករ​ទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​បានឱ្យ​ដឹង​ថា «​សរុប​អតិថិជន​ម្ចាស់ផ្ទះ​ជួល រហូត​​មក​ដល់​ថ្ងៃ​ទី​១៦ ​មីនា ​២០១៥ នេះ មាន​ចំនួន​២៤៥២​ម្ចាស់​ទីតាំង ស្មើនឹង​៥០៦៥០​​បន្ទប់ ស្មើនឹង​៥៥,៩៨% នៃ​ចំនួន​បន្ទប់​ជួល​', 'http://kohsantepheapdaily.com.kh/article/116719.html', 'http://i.kspg.co/2015/3/31b67fd5-982f-47fb-9c4a-234527f5f9f5.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('999', 'cl000001', 'u0000003', 'ខណ្ឌ​រដ្ឋបាល​ព្រៃឈើ​ប៉ៃលិនស្ទាក់​ចាប់​រថយន្ត​ដឹក​បង្គោលរបង​ និង​បាច់​ឫស្សី​អត់​ច្បាប់​យក​ទៅ​រក្សា​ទុក​', 'ខេត្ត​ប៉ៃលិន ៖ ស្ត្រី​ម្នាក់​បាន​ជួល​រថយន្ត​មួយ​គ្រឿងដឹក​បាច់​ឫស្សី​ចំនួន​៩០​បាច់ និង​បង្គោលរបង​ចំនួន​៥០​ដើម ដោយ​ទិញ​ចេញពី​ភូមិតា​គ្រក់ ខេត្តបាត់ដំបង​មាន​ព្រំប្រទល់ជាប់​នឹង​ខេត្ត​ប៉ៃលិន លុះ​ដឹក​ចូល​មក​ដល់​ក្នុង', 'http://kohsantepheapdaily.com.kh/article/116718.html', 'http://i.kspg.co/2015/3/7d67beb0-7bdc-4087-af9f-59e508958f63.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1000', 'cl000001', 'u0000003', 'ផ្លូវជាតិលេខ៥៨​​ប្រវែងជាង១៧០Km ​តភ្ជាប់ពីក្រុងប៉ោយប៉ែត​ទៅក្រុងសំរោង​នឹងត្រូវសម្ពោធ​បើកការដ្ឋានសាងសង់​នៅព្រឹកស្អែក​ក្រោមអធិបតីភាព​សម្តេចតេជោ​ ហ៊ុន សែន', 'ខេត្តបន្ទាយមាន-​ឧត្តរមានជ័យ​ ៖ បើគ្មានអ្វីប្រែប្រួលនោះទេ​ សម្តេច​អគ្គមហាសេនាបតីតេជោ​ ហ៊ុន សែន ​នាយករដ្ឋមន្រ្តីនៃ​ព្រះរាជាណាចក្រ​កម្ពុជានឹង', 'http://kohsantepheapdaily.com.kh/article/116716.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1001', 'cl000001', 'u0000003', 'បុរស​បរទេសម្នាក់​ស្រវឹងស្រា​ចុះមុជទឹកលង់ស្លាប់​នៅឆ្នេរអូរត្រេះ១​', 'ខេត្ត​​ព្រះ​សីហ​នុ ៖ ​ជន​បរទេស​​ម្នាក់បន្ទាប់​ពី​​​ផឹកស្រា​ស្រវឹង​​ហើយ​​ដើរចូល​ទឹក​លង់ស្លាប់ ​បង្កឱ្យ​​ភ្ញាក់ផ្អើល​​​ កាលពីវេលា​ម៉ោង​០៣​រសៀល​ថ្ងៃទី​១៦ មីនា ២០១៥ ស្ថិតតាម​​បណ្តោយ​ឆ្នេរ​អូ​រត្រេះ​១ ត្រង់​ចំណុច​ហាង វ៉ា​ន់ នី ​ក្នុង​ក្រុម​៨ ​ភូមិ​៦ សង្កាត់​លេខ​៤ ក្រុង​ព្រះ​សី', 'http://kohsantepheapdaily.com.kh/article/116713.html', 'http://i.kspg.co/2015/3/3e726622-18da-4f3d-87e3-3349ab521f1d.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1002', 'cl000001', 'u0000003', 'គោ​យន្ត​បុក​បាក់​បង្គោល​ភ្លើង​ដាច់​ចរន្ត​ងងឹត​ខេត្តសៀមរាប​១យប់', 'ខេត្តសៀមរាប ៖ កាលពី​យប់​ថ្ងៃអាទិត្យ ទី​១៥ មីនា នៅ​ក្នុង​ខេត្តសៀមរាប​ទាំងមូល​បាន​ងងឹត​សូន្យឈឹង​បន្ទាប់​ពី​មាន​គោ​យន្ត​ដឹក​ដី​មួយ​គ្រឿង​បើក​ឡើង​ទួល​លើ​ផ្លូវជាតិ​លេខ​៦​មិន​រួច ', 'http://kohsantepheapdaily.com.kh/article/116711.html', 'http://i.kspg.co/2015/3/5503a91e-fe06-4dc7-bd10-794c851a5c62.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1003', 'cl000001', 'u0000003', 'លោក​ឧបនាយករដ្ឋមន្ត្រី​ កែ គឹ​ម​យ៉ា​ន សំណេះសំណាល​ជាមួយ​សមាជិក​បក្ស​នៅ​ស្រុក​ភ្នំស្រុក', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖ លោក​ឧបនាយករដ្ឋមន្ត្រី​ កែ គឹ​ម​យ៉ា​ន សមាជិក​អចិន្ត្រៃយ៍នៃ​គណៈកម្មាធិការ​កណ្តាល​ និង​ជាប្រធាន​ក្រុមការងារ​ចុះ​ជួយ​ខេត្តបន្ទាយមានជ័យ មាន​ប្រសាសន៍​ឲ្យ​សមាជិក សមាជិកា​បក្ស​', 'http://kohsantepheapdaily.com.kh/article/116709.html', 'http://i.kspg.co/2015/3/3f7bdb38-0762-45be-abaf-cab128a3cb8b.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1004', 'cl000001', 'u0000003', 'នគរបាល​ក្រុង​ប៉ោយ​ប៉ែ​ត​ត្រួតពិនិត្យ​អាវុធ​ជាតិ​ផ្ទុះ​ពង្រឹង​សន្តិសុខ​ជូន​ប្រជាពលរដ្ឋ', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖​ កាល​ពី​យប់​ថ្ងៃ​ទី ១៦ មីនា ២០១៥ នគរបាល​ក្រុង​ប៉ោយ​ប៉ែ​ត​បាន​ធ្វើការ​ត្រួតពិនិត្យ​អាវុធ​ជាតិ​ផ្ទុះ​ និង​ឆែកឆេរ​រក​បទល្មើស​ផ្សេង​ៗ​តាម​គោលដៅ​មួយ​ចំនួន​ក្នុង​ក្រុង​ដើម្បី​ពង្រឹង​សន្តិសុខ​ជូន​', 'http://kohsantepheapdaily.com.kh/article/116708.html', 'http://i.kspg.co/2015/3/6a78fb55-aeca-4e1a-a3dd-4bde2d794e88.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1005', 'cl000001', 'u0000003', 'បង្ក្រាប​រថយន្ត​បិទ​ស្លាក​​​អក្សរ M ដឹកជញ្ជូន​ឈើ​ខុសច្បាប់​', 'ខេត្ត​កំពង់​ស្ពឺ​ ៖​ ជា​ទូទៅ​គេ​តែង​សង្កេត​ឃើញ​ថា រថយន្ត​ដែល​មាន​បិទ​អក្សរ M ​តែងតែ​ដឹកឈើ​គ្រប់​ប្រភេទ​​ មិន​ថា​ឈើ​ប្រណីត​​ ឬ​ឈើ​អារ​​ឡើយ​។', 'http://kohsantepheapdaily.com.kh/article/116707.html', 'http://i.kspg.co/2015/3/faefffc8-de0a-4019-a031-7a6973b45679.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1006', 'cl000001', 'u0000003', 'ម៉ោង៤រសៀលនេះ​ កូនមេគយបាញ់​បោះសេរី​ត្រូវបានបញ្ជូន​ទៅឃុំ​ក្នុងពន្ឋនាគារ PJ', 'រាជធានីភ្នំពេញ ​៖​ ​ ទីបំផុតទៅជនសង្ស័យ​​ឈ្មោះ​ កែវ វិទូ ​ដែលបានបាញ់រះ​​​ជាច្រើនគ្រាប់​នៅ​មុខ​មជ្ឈមណ្ឌល​​កម្សាន្តរ៉ក់​​កាលពី​រំលង​អធ្រាត្រ​​ឈាន​ចូល', 'http://kohsantepheapdaily.com.kh/article/116759.html', 'http://i.kspg.co/2015/3/f02fb182-5262-4f55-bb5e-c316fd0758b9.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1007', 'cl000001', 'u0000003', 'យក​កណ្តៀវ​អារ.ក​ស្ត្រី​ម្នាក់​ប្លន់​យក​ក្រវិល​កាឡៃ១គូ និង​លុយ​២ពាន់​រៀល', 'ខេត្ត​តាកែវ​ ៖ ​ស្ត្រី​ម្នាក់​​ត្រូវ​ក្មេង​ស្ទាវ​ជិះ​កង់​មកបន្លំ​ធ្វើ​ជាជួយ​ច្រូត​ស្រូវ​ឆ្លៀត​ឱកាស​ធ្វើ​សកម្ម​ភាព​យក​ផ្លែ​កណ្តៀវ​អារ.ក និង​ចាក់​លើ​ទ្រូងបណ្តាល​ឱ្យ​របួស​ធ្ងន់​​​ប្លន់​យក​​ក្រវិល​កាឡៃ​១​គូ និង​លុយ​២.០០​០​រៀល ​​រួច​រត់​គេច', 'http://kohsantepheapdaily.com.kh/article/116756.html', 'http://i.kspg.co/2015/3/c7e97030-b70c-4a0c-97ac-310723be99a5.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1008', 'cl000001', 'u0000003', 'វាសនា​កូនប្រុស​មេគយ​ដែលបាញ់បោះសេរី​​ត្រូវជាប់ឃុំ ឬមិនជាប់ឃុំនឹងដឹង​ក្នុងពេលបន្តិចទៀតនេះ ​បន្ទាប់ពី​តំណាងអយ្យការចោទប្រកាន់ពីរបទល្មើស​', 'រាជធានីភ្នំពេញ ​៖​ ទីបំផុតទៅជនសង្ស័យ​​ដែលក្រុមគ្រួសារ​នំាខ្លួន​ប្រគល់មកឲ្យ​សមត្ថកិច្ច​ក្រោយការបាញ់រះ​​ជាច្រើនគ្រាប់​នៅមុខ​មជ្ឈមណ្ឌល​​កម្សាន្តរ៉ក់​', 'http://kohsantepheapdaily.com.kh/article/116748.html', 'http://i.kspg.co/2015/3/a5cafcf6-c14c-4844-bcf3-3a1775ea723a.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1009', 'cl000001', 'u0000003', 'ប៉ូលិស​ព្រហ្មទណ្ឌ​វែកមុខ​ក្រុម​ក្លែងខ្លួន​ធ្វើ​ប៉ូលិសខ្លាហោះ​ប្លន់​ពេល​យប់', 'រាជធានី​ភ្នំពេញ ៖ លោក​វរសេនីយ៍ទោ​អេ​ង សោភា នាយការិយាល័យ​នគរបាល​ព្រហ្មទណ្ឌ​កម្រិត​ធ្ងន់​រាជធានី​ភ្នំពេញ កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៦ មីនា បាន​បង្ហាញមុខ​ក្រុម​ជន​ក្លែងបន្លំ​ធ្វើ​ជា​ប៉ូលិស​ខ្លាហោះ', 'http://kohsantepheapdaily.com.kh/article/116715.html', 'http://i.kspg.co/2015/3/11105ac0-7377-4230-ab80-a35b1c2b99a3.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1010', 'cl000001', 'u0000003', 'សមត្ថ​កិច្ច​នាំ​ខ្លួន​កូន​មេ​គយ​បាញ់​បោះ​សេរី​មក​តុលាការ​ដើម្បីសាក​សួរ​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នៅ​ព្រឹក​ថ្ងៃ​ទី​១៧ មីនា ២០១៥ នេះ ​​កូន​មេ​គយ​បាញ់​បោះ​សេរី​ត្រូវ​បាន​សមត្ថ​កិច្ច​នាំ​ខ្លួន​មក​តុលាការ​ដើម្បី​សាក​សួរ​។', 'http://kohsantepheapdaily.com.kh/article/116705.html', 'http://i.kspg.co/2015/3/98f43678-9f39-40ae-939c-b9c809e96c6f.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1011', 'cl000001', 'u0000003', 'ព្រឹក​នេះ​​អនុរក្ស​ពន្ធ​នាគារនាំ​ខ្លួន​ជន​ដៃដល់​​បាញ់​គំរាមប៉ូលិស​ចរាចរណ៍​ជាង​៣០​គ្រាប់​មក​តុលាការ​ដើម្បីសាក​សួរ​បន្ថែម', 'ខេត្ត​កំពង់​ស្ពឺ ៖ តាម​សេចក្តី​រាយការណ៍​ពី​អ្នក​ឆ្លើយ​ឆ្លង​ព័ត៌​មាន​បាន​ឱ្យ​ដឹង​ថា នៅ​ព្រឹក​ថ្ងៃ​ទី ១៧ មីនា ២០១៥ នេះ ស្ថាប័ន​តុលាការ​បាន​អញ្ជើញ​សមត្ថ​កិច្ច​ចរាចរណ៍​ដែល​រង​ការ​បាញ់​គំរាម​មក​ធ្វើ​ការ​បំភ្លឺ និង​​បញ្ជា​ឱ្យ​', 'http://kohsantepheapdaily.com.kh/article/116703.html', 'http://i.kspg.co/2015/3/4edd1d91-dcae-4ee8-8ac7-2ee40ab32204.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1012', 'cl000001', 'u0000003', 'វាយ​ប្លន់​មន្ត្រី​ប៉ូលិស​ក្រ​សួ​មហាផ្ទៃ​ម្នាក់​យក​កាបូប​លុយ​ខ្សែ​រក​និង​ទូរស័ព្ទ​ទៀត​ហើយ​នៅ​ក្នុង​ខណ្ឌចំការមន', 'រាជធានី​ភ្នំពេញ ៖ មន្ត្រី​ប៉ូលិស​ប្រឆាំង​ភេរវកម្ម​ក្រសួងមហាផ្ទៃ​ម្នាក់​ចេញពី​ខារ៉ាអូខេ​ស្រវឹង​ដើរ​ត្រឡប់​ទៅ​ផ្ទះ​នៅ​ក្បែរ​នោះ ក៏​ត្រូវ​ក្រុម​ចោរ​មួយ​ក្រុម​ស្ទាក់​វាយ​ប្លន់​យក​កាបូប​លុយ ខ្សែ​រក​និង​ទូរស័ព្ទ​បាន​ហើយ​គេចខ្លួន​ដោយ​សុ​វត្តិភាព', 'http://kohsantepheapdaily.com.kh/article/116702.html', 'http://i.kspg.co/2015/3/0f4d7089-c68a-49cf-9f7e-276f9ac0503b.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1013', 'cl000001', 'u0000003', 'ចោរ​ថ្លើមធំ​ប្លន់​ម៉ូតូ​២​គ្រឿង​នៅ​ក្បែរ​ប៉ុស្តិ៍​បឹង​ព្រលឹត (មានវីដេអូ)', 'រាជធានី​ភ្នំពេញ​៖ ចោរប្លន់​ប្រដាប់​អាវុធ​៤​នាក់​បាន​ធ្វើ​សកម្មភាព​ភ្ជង់​ស្ត្រី​រង​គ្រោះ រួច​ប្លន់​យក​ម៉ូតូ​ស្គុ​ពី​ចំនួន​២​គ្រឿងក្នុង​ពេល​តែ​មួយ​បាន​ដោយ​មិន​រំខាន​សមត្ថកិច្ច នៅ​ពេល​ដែល​ជន​រង​គ្រោះ​ចេញពី​ជប់លៀង រួច​ជូន​មិត្តភក្តិ​ទៅ​ផ្ទះ​ជិត​កន្លែង​កើតហេតុ ', 'http://kohsantepheapdaily.com.kh/article/116701.html', 'http://i.kspg.co/2015/3/abe8603c-7dbc-4491-9e28-8941cf8d9e6f.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1014', 'cl000001', 'u0000003', 'គូរ​ស្នេហ៍​ជនបរទេស​ឈ្លោះ​ប្រតាយ​គ្នា​ជ្រុះ​ទូរស័ព្ទ អ្នក​ភូមិ​រើស​បាន​ចង់​សុំ​សា​គុណ​ខ្លះ​ជជែក​គ្នា​បែកផ្សែង', 'រាជធានី​ភ្នំពេញ​៖ ជនបរទេស​ជា​គូស្នេហ៍​បាន​ចូល​ទៅ​ផឹកស៊ី​ក្នុង​ក្លឹ​ប​មួយ ខណៈ​ចេញ​មក​វិញ ក៏​ឈ្លោះ​ប្រតាយប្រតប់​គ្នា ជា​ហេតុ​ធ្វើ​ឲ្យ​ជ្រុះ​ទូរស័ព្ទ​ដៃ​ស៊េរី​ទំនើប​១​គ្រឿង ។ ប៉ុន្តែ​ទូរស័ព្ទ​ដៃ​នោះ​ត្រូវ​បាន​អ្នក​ភូមិ​រើស​បាន តែ​គាត់​មិន​ចង់បាន​នោះ​ទេ ', 'http://kohsantepheapdaily.com.kh/article/116700.html', 'http://i.kspg.co/2015/3/c6cb5a04-1763-4196-9161-64affc089fb1.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1015', 'cl000001', 'u0000003', 'ល្បែង​អន​ឡាញ​ភ្នាល់​បាល់ និង កន្ទុយលេខ​(​ឆ្នោត​)​រីក​ដូច​ផ្សិត​ប្រជាពលរដ្ឋ​ថា​អាជ្ញាធរ​គ្មាន​វិធានការ', 'ខេត្តបន្ទាយមានជ័យ​៖ ប្រភព​ព័ត៌មាន​ពី​ប្រជាពលរដ្ឋ​បាន​ឲ្យ​ដឹង​ថា ល្បែងស៊ីសង​ប្រភេទ​អ៊ិ​ន​ធឺណិ​ត អន​ឡាញ និង ល្បែង​កន្ទុយលេខ​(​ឆ្នោត​)​កើត​មាន​យ៉ាង​អនាធិបតេយ្យ រីក​ដូច​ផ្សិត​ត្រូវ​ទឹកភ្លៀង ចំណែក​ល្បែងស៊ីសង​ប្រភេទ​កន្ទុយលេខ​វិញ​នោះ​បាន​ដាក់​តុ​កត់​ជា​ចំហរ', 'http://kohsantepheapdaily.com.kh/article/116695.html', 'http://i.kspg.co/2015/3/1f98f173-625b-4505-a617-2f9326f49be5.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1016', 'cl000001', 'u0000003', 'រថយន្ត​រាប់រយ​គ្រឿង​ដឹក​ដី​និង​ដឹក​ទំនិញ​សំពីងសំពោង​ចូល​ក្រុងសៀមរាប​យ៉ាង​រំភើយ', 'ខណៈ​ពេល​ដែល​អាជ្ញាធរ​ និង​សមត្ថកិច្ច​ដែល​ឈរជើង​នៅ​តាម​ដងផ្លូវ​នានា ​ទទួល​លុយ​ពី​រថយន្ត​ដឹក​ល្មើសច្បាប់​ទាំងនោះ​ ជា​ថ្នូរ​នឹង​ការ​មិន​បង្ក្រាប ​។ ​ដូច្នេះ​ហើយ​ពាក្យ​ថា ទីក្រុង​ស្អាត រមណីយដ្ឋាន​ល្អ នេះ វា​ហាក់ដូចជា​ពាក្យបញ្ជោរ​មួយ​ប៉ុណ្ណោះ', 'http://kohsantepheapdaily.com.kh/article/116743.html', 'http://i.kspg.co/2015/3/9ed9fafb-1582-483a-8827-a3c50e388c92.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1017', 'cl000001', 'u0000003', '​រថយន្ត​កាមរី​២គ្រឿងបុកគ្នាពេញទំហឹងស្លាប់១ របួសធ្ងន់-ស្រាល៤', 'ខេត្តបន្ទាយ​មាន​ជ័យ​ ៖​ មានហេតុការណ៍​គ្រោះថ្នាក់​ចរាចរ​ដ៏​​រន្ធត់​មួយ ​រវាង​រថយន្ត​កាម​រី​​ឆ្លាម ​បុក​ជាមួយ​រថយន្ត​កាម​​រី​ដឹក​ផ្លែ​ឈើ​ខណៈ​បើក​បញ្ច្រាសទិស​គ្នា បណ្តាល​ឱ្យ​​ស្ត្រី​ម្នាក់​ស្លាប់​ និង​៤​នាក់​ទៀត​​រង​របួសធ្ងន់-​ស្រាល ​កាលពី​វេលា​ម៉ោងជាង​៧​យប់​​ថ្ងៃ​ទី​១៦ ​', 'http://kohsantepheapdaily.com.kh/article/116712.html', 'http://i.kspg.co/2015/3/00adcbe5-63fe-4a45-aa95-ff82899f583e.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1018', 'cl000001', 'u0000003', 'បើករថយន្ត​ឡា​គួរ​របៀប​អ្នក​ប្រណាំង​ឡាន​ជ្រុល​ទៅ​បុក​ស្លាក​ប៉ាណូ​វិល​១​ជុំ​ទៅ​បុក​ដើមឈើ​រថយន្ត​ខូចខាត​យ៉ាងដំណំ', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​បាន​បើករថយន្ត​ក្នុង​ល្បឿន​លឿន ជាន់​ហ្វ្រាំង​ភ្លាម​ចង់​ឡា​គួរ​ដូច​ឡាន​ប្រណាំង ជា​ហេតុ​ធ្វើ​ឲ្យ​គូទ​រថយន្ត​ជ្រុល​ទៅ​បុក​ស្លាក​ប៉ាណូ​ផ្សាយពាណិជ្ជកម្ម វិល​១​ជុំ​ទៅ​បុក​ជាមួយ​ដើម​លឿង​រា​ជ្រ បណ្តាល​ឲ្យ​រថយន្ត​រង​ការ​ខូចខាត​យ៉ាងដំណំ', 'http://kohsantepheapdaily.com.kh/article/116699.html', 'http://i.kspg.co/2015/3/9f1a211b-9bd6-48db-89f1-94e3346fd951.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1019', 'cl000001', 'u0000003', 'រថយន្ត​កុង​ទី​ន័​រ​វ៉ា​ម៉ូតូ​សណ្ដោង​រទេះ​គៀ​ប៉ះ​បណ្ដាល​ឲ្យ​ជនជាតិ​វៀត​ណា​ម្នាក់​រង​របួស', 'រាជធានី​ភ្នំពេញ ៖ បុរស​ជនជាតិ​វៀតណាម​ម្នាក់​បាន​រង​របួស ខណៈ​បើក​ម៉ូតូ​សណ្ដោង​រទេះ​ត្រូវ​រថយន្ត កុង​ទី​ន័​រ ធ្វើ​ដំណើរ​មក​ពីក្រោយ​វ៉ា​គៀ​ប៉ះ​បណ្ដាល​អោយ​ក្រឡាប់​២​ទៅ​៣​ជុំ​តែ​ជា​សំណាង​ល​គ្រាន់តែ​របួសស្រាល ចំណែក​រថយន្ត​កុង​ទី​ន័​របើក​គេច​បាត់​តែ​ម្តង', 'http://kohsantepheapdaily.com.kh/article/116698.html', 'http://i.kspg.co/2015/3/97f2c96e-e624-4296-940a-5b496dd7f94f.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1020', 'cl000001', 'u0000003', 'ងោក​មួយភាំង​បើករថយន្ត​ដឹក​ជ្រូក​ឡើង​ពុំនូក​ថ្ម​ក្រឡាប់​ផ្ងា​ជើង', 'រាជធានី​ភ្នំពេញ ៖ រថយន្ត​ចំណុះ​២​តោន​កន្លះ​ដឹក​សត្វ​ជ្រូក ដោយសារ​តៃកុង​រថយន្ត​ងងុយដេក​ខ្លាំង​ពេក​ងោក​មួយភាំង​បើក​ឡើង​ពុំនូក​ថ្ម​ដែល​គេ​ចាក់​គរ​សំរាប់​ជួសជុល​ផ្លូវ​ក្រឡាប់​ផ្ងា​ជើង​នៅ​កណ្ដាលផ្លូវ​តែ​ម្តង ', 'http://kohsantepheapdaily.com.kh/article/116697.html', 'http://i.kspg.co/2015/3/65c6c566-3cd0-4e4e-89cc-bd7daf06e9ec.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1021', 'cl000001', 'u0000003', 'ក្មេង​ស្រី​អាយុ​៣​ឆ្នាំ​រត់​តាម​ពីក្រោយ​ឪពុក​ឆ្លងផ្លូវ​ត្រូវ​រថយន្ត​កុង​ទី​ន័​របុក​កិន​ចំ​ជើង​ទាំង​២​រង​របួសធ្ងន់', 'រាជធានី​ភ្នំពេញ ៖ ក្មេង​ស្រី​អាយុ​៣​ឆ្នាំ​ម្នាក់ ត្រូវ​រថយន្ត​កុង​ទី​ន័​រដឹក​ដែក​រាប់​តោន​បុក​កិន​ចំ​ជើង​ទាំង​២​អូស​ជាង​១០​ម៉ែត្រ​រង​របួសធ្ងន់ បន្ទាប់​ពី​រត់​តាម​ឪពុក​ពីក្រោយ​ឆ្លងផ្លូវ​ពេល​ឪពុក​យួរ​សំរាម​យក​ទៅ​ចោម​មិន​បាន​មើល​កូន ', 'http://kohsantepheapdaily.com.kh/article/116696.html', 'http://i.kspg.co/2015/3/4e2360c2-4d9d-4dfe-8632-e78ef7a7cbbd.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1022', 'cl000001', 'u0000003', 'សាលា សឹ​ង្ហ​បុរី ថូ​ម៉ាស់ ជំរុញ​ឲ្យ​សិស្ស​របស់​ខ្លួន​ស្រឡាញ់​ចេះ​ស្រឡាញ់​បរិស្ថាន​និង​ព្រៃឈើ​ធម្មជាតិ', 'នេះ​ជា​លើ​ទី​១​ហើយ​ដែល សាលា​សឹ​ង្ហ​បុរី ថូ​ម៉ាស បាន​នាំ​សិស្ស​ជាង​២០០​នាក់​ចុះ​ទស្សនកិច្ច​សិក្សា​យល់​ដឹង​អំពី បរិស្ថាន​ព្រៃ​ភ្នំ​ធម្មជាតិ ជា​ពិសេស​ជីវិត​សត្វព្រៃ​ជា​ច្រើន​ប្រភេទ នៅ​រមណីយ​ដ្ឋាន', 'http://kohsantepheapdaily.com.kh/article/116762.html', 'http://i.kspg.co/2015/3/3cb65b9c-29cb-4a6f-a542-d64642a2c0d1.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1023', 'cl000001', 'u0000003', 'ច្រក​ទន្លេ​ចាម និង​ច្រក​ចាន់​មូល ជាច្រកដ៏​អស្ចារ្យ​សម្រាប់​ឈ្មួញ​នាំ​ឈើ', 'ច្រក​ទ្វារ​ព្រំដែន​វៀតណាម​និង​កម្ពុជា ក្នុង​ខេត្ត​ត្បូងឃ្មុំ​ហៅ​ច្រក​ទន្លេ​ចាម និង​ច្រក​ចាន់​មូល ពិតជា​ច្រក​ទ្វារ​ដ៏​អស្ចារ្យ​សម្រាប់​ឈ្មួញ​នាំ​ឈើ​គ្រប់​ប្រភេទ ជា​ពិសេស​ឈើ​ប្រណី​តទៅ​ស្រុក​យួន​រាល់​យប់​-​ថ្ងៃនេះ', 'http://kohsantepheapdaily.com.kh/article/116725.html', 'http://i.kspg.co/2015/3/14e16420-5f84-4449-b5b3-455ace7ba586.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1024', 'cl000001', 'u0000003', 'ប៉ា​គី​ស្ថាន​ព្យួរ​.​ក​អ្នកទោស​ដល់​ទៅ​10​នាក់', 'អាជ្ញាធរ​ប៉ា​គី​ស្ថាន កាលពី​ថ្ងៃ​ទី​១៧ មីនា បាន​ព្យួរ​.​ក​សម្លាប់​អ្នកទោស​ប្រហារជីវិត​១០​នាក់ ដែល​នេះ​ជា​ការ​ប្រហារជីវិត​អ្នកទោស​ក្នុង​ចំនួន​ច្រើន​តែ​មួយ​ថ្ងៃ ក្រោយ​ពេល​លុប​ចោលការ​ពន្យារ', 'http://kohsantepheapdaily.com.kh/article/116758.html', 'http://i.kspg.co/2015/3/6bdf8a66-8f66-4d5a-89ae-4d72c7b72ecc.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1025', 'cl000001', 'u0000003', 'បោះ​ឆ្នោត​សភា​អីុ​ស្រា​អែ​ល​លោក​នេ​តាន់​យ៉ា​ហ៊ូ​ថា បើ​ជាប់​ឆ្នោត​ទៀត​គ្មាន​រដ្ឋ​ប៉ា​ឡេ​ស្ទី​ន', 'ពលរដ្ឋ​អីុ​ស្រា​អែ​ល ចាប់ពី​ព្រឹក​ថ្ងៃ​ទី​១៧ មីនា​ទៅ បាន​នាំ​គ្នា​ទៅ​បោះ​ឆ្នោត​សភា​ដែល​នឹង​កំណត់​ថា តើ​គណបក្ស LIKOUD ដឹកនាំ​ដោយ​លោកនាយក​រដ្ឋមន្ត្រី បេន​ចា​មីន នេ​ចាន់​យ៉ា​ហ៊ូ ', 'http://kohsantepheapdaily.com.kh/article/116752.html', 'http://i.kspg.co/2015/3/6b8547e1-6114-4a69-b100-591cdd13f945.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1026', 'cl000001', 'u0000003', 'លោក ពូ​ទី​ន ឆ្លើយ​តប​ពាក្យ​ចចាមអារ៉ាម​ពី​សុខភាព​របស់​លោក', 'ប្រធានាធិបតី​រុ​ស្ស៊ី លោក វ្ល៉ាឌី​មៀរ ពូ​ទី​ន កាលពី​ថ្ងៃ​ទី​១៦ មីនា បាន​បដិសេធ​ពាក្យ​ចចាមអារ៉ាម​នានា​ទាក់ទិន​នឹង​បញ្ហា​សុខភាព​របស់​លោក ពេល​លោក​ជួប​ជាមួយ​ប្រធានាធិបតី​កៀ​ហ្ស៊ី​ស៊ីស្ថាន', 'http://kohsantepheapdaily.com.kh/article/116745.html', 'http://i.kspg.co/2015/3/1d00c3d6-2fc2-428b-8de7-5327066b5afd.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1027', 'cl000001', 'u0000003', 'កងទ័ព​រុ​ស្ស៊ី​ដាក់​ពង្រាយ​ទាហាន​38.000​នាក់​នៅ​តំបន់​អាក់​ទិ​ក', 'ទាហាន​រុ​ស្ស៊ី​ជាង​៣៨.០០០​នាក់ ចាប់ផ្តើម​កាលពី​ថ្ងៃ​ទី​១៦ មីនា នូវ​សមយុទ្ធ​ទ្រង់ទ្រាយ​ធំ ក្នុង​តំបន់​អាក់​ទិ​ក ដែល​នេះ​ជា​ការ​បង្ហាញ​កម្លាំង​ដ៏​ធំ​មួយ​របស់​កងទ័ព​រុ​ស្ស៊ី ដែល​បង្កើន​នា​ពេល​', 'http://kohsantepheapdaily.com.kh/article/116739.html', 'http://i.kspg.co/2015/3/1844cdd5-70ab-4c60-8420-03cb114b3587.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1028', 'cl000001', 'u0000003', 'រុ​ស្ស៊ី​បើក​ធ្វើ​សមយុទ្ធ​យោធា​ក្នុង​តំបន់​ជា​ច្រើន​របស់​ប្រទេស​ពិសេស​នៅ​គ្រី​មេ', 'ក្រសួង​ការពារ​ប្រទេស​រុ​ស្ស៊ី​រាយការណ៍​កាលពី​ថ្ងៃ​ទី​១៦ មីនា​ថា កងទ័ព​រុ​ស្ស៊ី​បើក​ធ្វើ​សមយុទ្ធ​យោធា​ទ្រង់ទ្រាយ​ធំ​នៅ​ក្នុង​តំបន់​ជា​ច្រើន​របស់​ប្រទេស ពិសេស​នៅ​តំបន់​គ្រី​មេ​ជាប់​អ៊ុយ​ក្រែ​ន', 'http://kohsantepheapdaily.com.kh/article/116736.html', 'http://i.kspg.co/2015/3/93124764-f55f-47d9-9013-9e3de0a440ae.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1029', 'cl000001', 'u0000003', 'អា​មេ​រិ​ក​ប្រគល់​ឱ្យ​អីុ​រ៉ា​ក់​វិញ​បណ្តា​វត្ថុ​បុរាណ​លួច​យក​ពេល​កាន់កាប់​ប្រទេស​នេះ', 'សហរដ្ឋអាមេរិក កាលពី​ថ្ងៃ​ទី​១៦ មីនា បាន​ប្រគល់​ឱ្យ​​អីុ​រ៉ា​ក់​វិញ​នូវ​វត្ថុ​បុរាណ​ចំនួន​៦០ ដែល​ភាគច្រើន​លួច​យក​ទៅ​ពេល​អា​មេ​រិ​ក​កាន់កាប់​ប្រទេស​នេះ​ពី​ឆ្នាំ​២០០៣ ទៅ ២០១១ ។ វត្ថុ​បុរាណ​ទាំងនេះ', 'http://kohsantepheapdaily.com.kh/article/116734.html', 'http://i.kspg.co/2015/3/9ec0e4fe-7769-4719-b144-594dc91e5bf4.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1030', 'cl000001', 'u0000003', 'មេដឹកនាំ​ក្រុម​បងប្អូន​អ៊ិ​ស្លា​ម​22​នាក់​ត្រូវ​កាត់ទោស​ប្រហារជីវិត', 'យោង​តាម​សារព័ត៌មាន​ផ្លូវការ និង​បណ្តា​មេធាវី​ថា តុលាការ​អេ​ហ្ស៊ី​ប​មួយ​កាលពី​ថ្ងៃ​ទី​១៦ មីនា បាន​កាត់ទោស​ប្រហារជីវិត​មេដឹកនាំ​ក្រុម​បងប្អូន​អ៊ិ​ស្លា​ម​២២​នាក់ ក្នុង​នោះ​រួម​មាន​មេដឹកនាំ​កំពូល', 'http://kohsantepheapdaily.com.kh/article/116733.html', 'http://i.kspg.co/2015/3/9e64a3af-2ba2-458a-935c-62d276a45bb3.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1031', 'cl000001', 'u0000003', 'ពួក​ប្រឆាំង​ស៊ី​រី​បរិហារ​ពី​អំពើ​ក្បត់​ថ្មី​របស់​អា​មេ​រិ​ក', 'រដ្ឋមន្ត្រី​ការ​បរទេស​អា​មេ​រិ​ក លោក ច​ន ឃ័​រ​រី អះអាង​កាលពី​ថ្ងៃ​ទី​១៥ មីនា ថា ពិត​ណាស់ ទីបំផុត​ត្រូវ​ចរចា​ជាមួយ​លោក បា​ចា អាល់​អាសា​ដ ដើម្បី​បញ្ចប់សង្គ្រាម​នៅ​ស៊ី​រី ដែល​ឈាន', 'http://kohsantepheapdaily.com.kh/article/116731.html', 'http://i.kspg.co/2015/3/26affc4b-5ccd-43c9-a0b5-6ec2033a94d3.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1032', 'cl000001', 'u0000003', 'ទំហំ​ពាណិជ្ជកម្ម​ម៉ា​ឡេ​ស៊ី​-​ជប៉ុន​សម្រេច​បាន​42​ពាន់​លាន​ដុល្លារ​ឆ្នាំ​2014', 'ក្រុង​តូ​ក្យូ​-​ជប៉ុន ៖ ទំហំ​ពាណិជ្ជកម្ម​សរុប​រវាង​ប្រទេស​ម៉ា​ឡេ​ស៊ី និង​ជប៉ុន​ក្នុងអំឡុងពេល​ពេញ​មួយ​ឆ្នាំ​២០១៤ សម្រេច​បាន​៤២​ពាន់​លាន​ដុល្លារ​កើនឡើង​១,៤%​ធៀប​នឹង​ឆ្នាំ​២០១៣ ក្នុង​នោះ ', 'http://kohsantepheapdaily.com.kh/article/116751.html', 'http://i.kspg.co/2015/3/9a302791-1a04-4b02-8dfb-b12b79c6ecd5.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1033', 'cl000001', 'u0000003', 'ការ​នាំ​ចេញ​ផលិតផល​កសិកម្ម​កូរ៉េ​ខាងត្បូង​ធ្លាក់​ចុះ​8% ខែកុម្ភៈ', 'ក្រុង​សេ​អ៊ូ​ល ៖ រដ្ឋាភិបាល​បានឱ្យ​ដឹង​កាលពី​ថ្ងៃ​ចន្ទ ទី​១៦ មីនា ថា ការ​នាំ​ចេញ​ផលិតផល​កសិកម្ម​របស់​ប្រទេស​កូរ៉េ​ខាងត្បូង​បាន​ធ្លាក់​ចុះ​៨% ក្នុង​ខែកុម្ភៈ ធៀប​នឹង​អំឡុង​ពេល​ដូច​គ្នា​កាលពី​ឆ្នាំ​មុន ', 'http://kohsantepheapdaily.com.kh/article/116747.html', 'http://i.kspg.co/2015/3/9d5811fa-f5d4-407d-9d9a-f4606b11ebc1.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1034', 'cl000001', 'u0000003', 'ការ​ប្រើប្រាស់​អគ្គិសនី​របស់​ប្រទេស​ចិន​កើន​2,5% ពីរ​ខែ​ដំបូង', 'ក្រុង​ប៉េ​កាំ​ង ៖ រដ្ឋបាល​ថាមពល​ជាតិ​បាន​បង្ហើប​ក្នុង​គេហទំព័រ​របស់​ខ្លួន​កាលពី​ថ្ងៃ​ចន្ទ ទី​១៦ មីនា​ថា ការ​ប្រើប្រាស់​ថាមពល​អគ្គិសនី​របស់​ប្រទេស​ចិន ក្នុង​រយៈពេល​២​ខែ​ដំបូង​នៃ​ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116737.html', 'http://i.kspg.co/2015/3/b23a7c1a-1afb-4fa6-8080-3105ac03f363.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1035', 'cl000001', 'u0000003', 'ការ​ទស្សន៍​ទាយ​បាល់ទាត់​អង់គ្លេស​ឆេ​ម​ពាន​ស៊ី​ប​ជុំ​ទី​38Cardiff ស្វាគមន៍​ក្រ�ម​កំពូល​តារាងBournemouth', 'ការ​ប្រកួត​បាល់ទាត់​ក្របខ័ណ្ឌ​អង់គ្លេស​ឆេ​ម​ពាន​ស៊ីប ជុំ​ទី 38 សម្រាប់​រាត្រី​ថ្ងៃ​អង្គារ​នេះ មាន 9 ប្រកួត ។ ក្រុម Cardiff ស្វាគមន៍​ក្រុម​កំពូល​តារាង Bournemouth ខណៈ Derby, Ipswich', 'http://kohsantepheapdaily.com.kh/article/116728.html', 'http://i.kspg.co/2015/3/aeeb2f34-f357-4840-bcec-878fcec7a54f.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1036', 'cl000001', 'u0000003', 'បាល់ទាត់​អង់គ្លេស​លី​ក​វ័ន​ជុំ​ទី​38​សម្រាប់​ថ្ងៃ​អង្គារ​នេះ​មាន​11​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​អង់គ្លេស​លី​ក​វ័ន�ជុំ​ទី 38  សម្រាប់​ថ្ងៃ​អង្គារ​នេះ​មាន 11 ប្រកួត ។ ក្រុម Bristol City និង Preston មិន​ពិបាក​ឈ្នះ​ក្រុម​ភ្ញៀវ​ថ្ងៃនេះ ខណៈ Colchester ត្រូវការ​ឈ្នះ Yeovil ', 'http://kohsantepheapdaily.com.kh/article/116727.html', 'http://i.kspg.co/2015/3/450c9b64-049c-42e9-bbe7-13544e513061.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1037', 'cl000001', 'u0000003', 'ព្យាករណ៍​បាល់ទាត់​យូ​ហ្វា​ឆេ​ម​ពាន​លី​ក​វគ្គ​16​ក្រុម​ជើង​ទី​2​សម្រាប់​ថ្ងៃនេះMonaco ទទួលភ្ញៀវ Arsenal', 'ការ​ប្រកួត​បាល់ទាត់​យូ​ហ្វា​ឆេ​ម​ពាន​លី​ក​វគ្គ 16 ក្រុម​ចុង​ក្រោយ​ជើង​ទី 2 សម្រាប់​រាត្រី​ថ្ងៃ​អង្គារ​នេះ​មាន 2 ប្រកួត ។ Monaco ស្វាគមន៍​ភ្ញៀវ Arsenal ខណៈ Atletico ប៉ះ​ក្រុម​ខ្លាំង Leverkusen ។', 'http://kohsantepheapdaily.com.kh/article/116726.html', 'http://i.kspg.co/2015/3/ef58e0ab-ae7c-4a80-873a-7719daf224a5.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1038', 'cl000001', 'u0000003', 'បាល់ទាត់​អង់គ្លេស​លី​ក​ធូ​ជុំ​ទី 37 សម្រាប់​រាត្រី​ថ្ងៃ​អង្គារ​នេះ​មាន 11 ប្រកួត Wycombe និង Portsmouth សង្ឃឹម​ឈ្នះ​ក្រុម​ភ្ញៀវ', 'ការ​ប្រកួត​បាល់ទាត់​អង់គ្លេស​លី​ក​ធូ​ជុំ​ទី 37 នៅ​រាត្រី​ថ្ងៃ​អង្គារ​នេះ​មាន 11 ប្រកួត ។ ក្រុម​ដែល​សង្ឃឹម​ឈ្នះ​នៅ​ថ្ងៃនេះ​មាន Wycombe, Portsmouth ខណៈ Oxford ជួប​ក្រុម​បាត​តារាង ។', 'http://kohsantepheapdaily.com.kh/article/116724.html', 'http://i.kspg.co/2015/3/e86dc151-53bd-4ca5-b8e6-925e4183f71c.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1039', 'cl000001', 'u0000003', 'លោក​ស៊ី​ន ស៊ី​សា​មុត ជា​អ្នក​ខេត្តស្ទឹងត្រែង​ហេតុ​អ្វី​មិន​តែង​ចម្រៀង​ពី​ស្រុក​កំណើត​ក្រៅពី​មាន​តែ​បទ « រាត្រី​សេ​កុង » មួយ', 'បទ​ចម្រៀង «​រាត្រី​សេ​កុង​» គឺជា​បទ ដែល​លោក​ស៊ី​ន ស៊ី​សា​មុត សរសេរ​ពី​ខេត្តស្ទឹងត្រែង ហើយ​ក៏​ជា​បទ​ដំបូង​គេ​របស់​អ្នកស្រី​ហួយ មាស ដែល​ល្បី​បំផុត​នា​ឆ្នាំ​១៩៦៨ ។ ', 'http://kohsantepheapdaily.com.kh/article/116720.html', 'http://i.kspg.co/2015/3/e72cd909-1ec9-45e7-8999-098e950f4059.jpg?w=285&h=170&mode=carve', '20150317');
INSERT INTO "tbnews" VALUES ('1040', 'cl000001', 'u0000003', 'យូ​រី រង​ការ​រិះគន់​រឿង​ចម្លង​ក្បាច់​រាំ​និង​បទ​ភ្លេង​ពី​វៀតណាម', 'រាជធានី​ភ្នំពេញ ៖ តារាចម្រៀង​វ័យក្មេង​របស់​ផលិតកម្ម​សាន់​ដេ នាង​យូ​រី បាន​រង​ការ​រិះគន់​ពី​បណ្តា​អ្នកលេង​ហ្វេ​ស​ប៊ុ​ក បន្ទាប់​ពី​រូបភាព​វី​ដេ​អូ​ឃ្លី​ប​ដែល​ជន​មិន​ស្គាល់​មុខ​រៀបចំ​ប្រៀបធៀប​ពី​បទ​ចម្រៀង​មួយ​បទ', 'http://kohsantepheapdaily.com.kh/article/116714.html', 'http://i.kspg.co/2015/3/f3de8093-f21d-4e59-8c73-1127cd6e23e6.jpg?w=285&h=170&mode=carve', '20150317');
