INSERT INTO "tbnews" VALUES ('1070', 'cl000001', 'u0000002', 'បណ្ឌិត​ គួច ម៉េងលី​ ថា​ជា​​​​រឿង​គួរ​ឲ្យ​សោក​ស្ដាយ​ដែល​យុវជន​​ប្រឹង​តែ​​​រៀន​ហួស​ហេតុ​ពេក​ ​', 'លោក គួច ម៉េងលី ​ដែល​បាន​បញ្ចប់​​ថ្នាក់​វេជ្ជបណ្ឌិត​​ជំនាញ​វេជ្ជសាស្ត្រ​​ទូទៅ ​ពី​​​​សាកល​វិទ្យាល័យ វិទ្យាសាស្រ្ត សុខាភិបាល ស្ពាតតាន សហរដ្ឋ​អាមេរិក និង​បញ្ចប់​ថ្នាក់​អនុបណ្ឌិត​ផ្នែក​សុខភាព​សាធារណៈ ​នៅ​​សាកល​វិទ្យាល័យ ម៉ាសាឈូសេត នៃ​ទីក្រុង អេមហឺស សហរដ្ឋ​អាមេរិក ហើយ​បច្ចុប្បន្ន​​ជា​យក​​វិទ្យាស្ថាន​អន្តរទ្វីប​អាមេរិក​កាំង ​​​​​បែរ​ជា​​ថ្លែង​​ថា​លោក​​សោក�&ស្&��ា�&�​�&ែល​យុវជន​មួយ​ចំនួន​​​ប្រឹង​ប្រែង​សិក្សា​​ ច្រើន​ហួស​ហេតុ​ពេក​។ &nbsp; &nbsp; &nbsp; &&bsp; អាន​អត្ថបទ៖ ឧកញ៉ា​​ គួច ម៉េងលី ​វេជ្ជបណ្ឌិត​', 'http://news.sabay.com.kh/article/308313', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/03/Untitled-1107-285x170.jpg?0bb167', '20150317');
