INSERT INTO "tbnews" VALUES ('1083', 'cl000001', 'u0000003', 'បុរសចំណាស់ម្នាក់ស្លាប់នៅផ្ទះសំណាក់​សង្ស័យប្រើថ្នាំជំនួយផ្លូវភេទ​លើស​កម្រិត', 'ខេត្តកំពង់ស្ពឺ ៖ មានការភ្ញាក់ផ្អើល​​មួយ​កើត​ឡើង ខណៈ​បុរសម្នាក់បាន​ស្លាប់​នៅក្នុងផ្ទះសំណាក់​យីហោ «ជំនោរាត្រី» ក្នុង​បន្ទប់​លេខ​១០២​​ បន្ទាប់ពី​រួម​រ័កជាមួយ​នារីជាដៃគូរួចមក កាលពីវេលាម៉ោង​០៤ ​​និង​១៥នាទីរសៀល​ថ្ងៃទី១៩ មីនា ២០១៥ នេះ ស្ថិតនៅចំណុច​', 'http://kohsantepheapdaily.com.kh/article/116908.html', 'http://i.kspg.co/2015/3/7347f119-0cfc-49c1-80ba-2bd294655e84.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1084', 'cl000001', 'u0000003', 'សពបុរស​មិន​ស្គាល់​អត្តសញ្ញាណ​លោត​ពីលើ​ស្ពាន​គី​ហ៊្សុណា​ផុស​អណ្តែត', 'ខេត្តកំពង់ចាម ​៖ បុរស​មិន​ស្គាល់​អត្តសញ្ញាណ​ម្នាក់អាយុ​ប្រហែល​ជាង​២០​ឆ្នាំ ស្លៀក​ខោខ្លី​ និង​ពាក់អាវដៃខ្លី​យឺត​ពណ៌​លឿងបាន​ជិះ​កង់​១​គ្រឿង​មក​លោតទឹក​ធ្វើ​អត្តឃាតពីលើ​ស្ពាន់​គី​ហ្ស៊ុ​ណា​ត្រង់​ចន្លោះសរសរ​ប៉េ​​៧', 'http://kohsantepheapdaily.com.kh/article/116907.html', 'http://i.kspg.co/2015/3/8141b419-bc95-4af2-97be-0205d1123559.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1085', 'cl000001', 'u0000003', 'ស្ត្រីចងការប្រាក់ម្នាក់ ​ត្រូវ​ជន​មិន​ស្គាល់​មុខ​​បាញ់ប្រហារ​១​គ្រាប់​រងរបួសធ្ងន់​ ​​សំណាងល្អ​​មិន​ស្លាប់', 'ខេត្តឧត្តរមានជ័យ​ ៖ ស្ត្រី​ម្នាក់​ដែល​ជា​អ្នក​​​ចងការ​ប្រាក់ ត្រូវ​​ជន​មិន​ស្គាល់​មុខ​បាញ់ប្រហារ​ បណ្តាលឱ្យ​រង​រ​បួស​ធ្ងន់​ ខណៈ​កំពុង​​នៅ​ក្រោមផ្ទះ​​ បង្ក​ឱ្យភ្ញាក់​ផ្អើលកាលពី​វេលា​ម៉ោង៥​យប់​​ថ្ងៃ​ទី​១៧ មីនា ​២០១៥ ស្ថិត​នៅ​ក្នុងភូមិ​ទួល​​ពង្រ ឃុំ​ទំនប់​​ដាច់ ស្រុក​ត្រពាំង​ប្រា​', 'http://kohsantepheapdaily.com.kh/article/116906.html', 'http://i.kspg.co/2015/3/7f0c71f9-87bf-4571-beda-b800725bf9ed.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1086', 'cl000001', 'u0000003', 'ក្មេង​ស្រី​បំរើ​តាម​ផ្ទះ​ម្នាក់​ចង​.​កសម្លាប់​ខ្លួន​ដោយមិនដឹង​មូលហេតុ', 'ខេត្ដសៀមរាប ៖ សាកសព​ក្មេង​ស្រី​ម្នាក់ត្រូវ​បាន​គេ​ប្រទះ​ឃើញ​ស្លាប់​ដោយ​ចង​.​កនឹង​ខ្សែ​នីឡុង​ខ្មៅ​ទៅ​នឹង​ដើម​ស្វាយ​មុខ​ផ្ទះ​​ដែល​នាង​ស្នាក់​នៅ​​។ ហេតុការណ៍​នេះ​បង្ក​ឱ្យ​មានការ​ភ្ញាក់​ផ្អើល​កាល​ពី​​វេលា​ម៉ោង​៤ ', 'http://kohsantepheapdaily.com.kh/article/116905.html', 'http://i.kspg.co/2015/3/e5a51946-ab63-4f01-94f6-be065bb37bdd.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1087', 'cl000001', 'u0000003', 'អនុ​ក្រឹត្យ​ស្តីពីការ​កែ​សម្រួល​ប្រាក់​បំណាច់​មុខ​ងារ​មន្ត្រី​រាជ​ការ ស៊ីវិល', 'អនុ​ក្រឹត្យ​ស្តីពីការ​កែ​សម្រួល​ប្រាក់​បំណាច់​មុខ​ងារ​មន្ត្រី​រាជ​ការ ស៊ីវិល', 'http://kohsantepheapdaily.com.kh/article/116904.html', 'http://i.kspg.co/2015/3/ed5b6197-c9bb-4905-8f7f-2aca439eeceb.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1088', 'cl000001', 'u0000003', 'គណៈ​កម្មការ​ផ្សារ​អូប្ញស្សី​និង​សាខា​ពន្ធ​ដារ​ខណ្ឌ​៧​មករាអញ្ជើញ​តំណាង​អាជីវករ​ផ្សារ​អូប្ញស្សី​មក​ប្រជុំ​ផ្សព្វ​ផ្សាយ​សេចក្តី​ជូន​ដំណឹង​ស្តី​ពីកាតព្វ​កិច្ច​បង់​ពន្ធ​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នៅ​រសៀល​ថ្ងៃ​ទី១៩ ​មីនា ​២០១៥ ​នេះ គ​ណៈកម្ម​ការ​ផ្សារ​អូរ​ឫស្សីសហការ​ជាមួយ​សាខា​ព​ន្ធដារ​ខណ្ឌ​៧​មក​រា បាន​អញ្ជើញ​តំណាង​អាជីវករលក់ដូរ​នៅ​ក្នុង​ផ្សារ​អូរ​ឫស្សីមក​ស្នាក់ការ​គ​ណៈកម្ម​ការ​ផ្សារ​', 'http://kohsantepheapdaily.com.kh/article/116903.html', 'http://i.kspg.co/2015/3/fb7ec379-3bb3-44a5-bfad-bedade3a30da.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1089', 'cl000001', 'u0000003', 'ពលករ​​ខ្មែរ​ម្នាក់​​ស្លាប់​ដោយសារ​ត្រាក់​ទ​រ័​កិន​បែកក្បាល​នៅ​​​ប្រទេស​​ថៃ​', 'ខេត្តបាត់ដំបង ៖ បុរស​ជា​កម្មករ​ម្នាក់​ស្លាប់​ បន្ទាប់​ពី​មិត្ត​ភក្តិ​ដឹក​ទៅ​សង្គ្រោះ​បន្ទាន់នៅ​ក្នុង​មន្ទី​ពេទ្យមួយ​នៅ​ក្នុង​ប្រទេស​ថៃ ខណៈ​ដែល​ជន​រង​គ្រោះកំពុង​បើក​ត្រាក់ទ័រភ្ជូ​រ​ដី ហើយ​ជ្រុះ​ស្បែក​ជើងក៏​​អោន​រើស​​ មិន​ប្រយ័ត្នក៏​ត្រូវ​ត្រាក់ទ័រកិន​បែកលលាដ៍​ក្បាល កាលពី​វេលា', 'http://kohsantepheapdaily.com.kh/article/116902.html', 'http://i.kspg.co/2015/3/d87ba649-606d-449f-8575-ba7a390ed842.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1090', 'cl000001', 'u0000003', 'រាំធុងបាស់ត្រូវក្រុម​ខាង​កូនក្រមុំ​ ​យកដែកឆាបចាក់​ក្រុម​​ខាង​កូន​ក​ម្លោះ​​ម្នាក់​​​របួស​ធ្ងន់​ ​', 'ខេត្តពោធិ៍សាត់ ​៖​ ជនសង្ស័យ២​នាក់​ ត្រូវ​សមត្ថកិច្ច​ឃាត់ខ្លួន​ពី​បទ​ប៉ុនប៉ង​​មនុស្សឃាត ​កាលពី​យប់​ថ្ងៃ​ទី​១៦ មីនា ​២០១៥​ ស្ថិតក្នុង​ពិធី​មង្គល​ការ​មួយកន្លែង ​ក្នុង​ភូមិ​ស្នាម​ព្រះ ​ឃុំ​ស្នាម​ព្រះ​ ស្រុក​បាកាន​ ខេត្តពោធិ៍សាត់​ ។', 'http://kohsantepheapdaily.com.kh/article/116901.html', 'http://i.kspg.co/2015/3/cfaacb88-39db-4200-8003-8fb422809eef.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1091', 'cl000001', 'u0000003', 'ក្មេង​​ប្រុស​អាយុ​១៦​ឆ្នាំ​​ប៉ង​ចាប់​រំលោភ​ក្មេង​ស្រី​អាយុ​៦​ឆ្នាំ​ត្រូវឃាត់ខ្លួន', 'ខេត្តកំពត ៖ សង្គម​យើង​កំពុង​មានការអភិវឌ្ឍន៍​រីក​ចម្រើន​ទៅ​មុខ​ជាលំដាប់​លើ​គ្រប់វិស័យ​ ធ្វើ​ឱ្យ​ប្រជាពលរដ្ឋ​​គ្រប់ស្រទាប់វណ្ណៈ​ មាន​ជីវ​ភាព​រស់នៅ​​យ៉ាង​សុខសាន្ត​ ។ ប៉ុន្តែទន្ទឹម​នឹងនេះ គេសង្កេតឃើញ​​វិស័យវប្បធម៌​ប្រពៃណី​ ហាក់​ដូចជាធ្លាក់​ចុះ​មួយកម្រិត ដោយ​', 'http://kohsantepheapdaily.com.kh/article/116898.html', 'http://i.kspg.co/2015/3/8963b2dc-3cc0-4db7-b2ae-b73dd2e70b89.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1092', 'cl000001', 'u0000003', 'នគរបាល​ប៉ុស្តិ៍​ផ្សារ​ថ្មី​ទី​២​ឃាត់ខ្លួន​ជនសង្ស័យ​ជួញដូរ​ថ្នាំ​ញៀន​ម្នាក់', 'រាជ​ធានី​ភ្នំពេញ​ ៖ កម្លាំង​នគរបាល​ប៉ុស្តិ៍​រដ្ឋបាល​ផ្សារ​ថ្មី​ទី​២​ឃាត់ខ្លួន​ជនសង្ស័យ​ម្នាក់​ ខណៈ​ពេល​ឃើញ​ជនសង្ស័យ​រូប​នោះកំពុង​តែ​ឈ្លោះប្រកែក​ជាមួយ​ជនជាតិ​ចិន​ ហើយ​ធ្វើការ​ឆែកឆេរ​ឃើញ​មាន​លាក់​ទុក​​គ្រឿង', 'http://kohsantepheapdaily.com.kh/article/116897.html', 'http://i.kspg.co/2015/3/3686043b-329e-48cb-adcc-f8e08c919245.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1093', 'cl000001', 'u0000003', 'រូបភាព​ពិធី​សូត្រ​មន្ត​អាពាហ៍​ពិពាហ៍លោក​ឧកញ៉ា​ គិត ម៉េង នា​រសៀល​ថ្ងៃទី​១៩ មីនា ២០១៥', 'រូបភាព​ពិធី​សូត្រ​មន្ត​អាពាហ៍​ពិពាហ៍លោក​ឧកញ៉ា​ គិត ម៉េង នា​រសៀល​ថ្ងៃទី​១៩ មីនា ២០១៥', 'http://kohsantepheapdaily.com.kh/article/116891.html', 'http://i.kspg.co/2015/3/af44d81f-0a4d-4f88-af22-86e1c94889b0.jpg?w=285&h=170&mode=carve', '20150319');
