INSERT INTO "tbnews" VALUES ('1095', 'cl000001', 'u0000003', 'ម៉ោង២និង៤៥នាទីរសៀលនេះ ​រដ្ឋសភាអនុម័តសេចក្តី​ស្នើវិសោធនកម្មច្បាប់​បោះឆ្នោតក្នុងសំឡេងគំាទ្រ​១០១លើ១០១', 'រាជធានីភ្នំពេញ​ ៖ បន្ទាប់ពីបាន​ពិភាក្សាយ៉ាងផុលផុស​អស់រយៈពេលជាង​មួយព្រឹកនោះរួចមក ​មកដល់ម៉ោង២និង៤៥នាទី​រសៀលថ្ងៃទី១៩ ​មីនា ២០១៥', 'http://kohsantepheapdaily.com.kh/article/116879.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1096', 'cl000001', 'u0000003', 'CNRP ៖ ពាក្យ​ថា ការ​ផ្លាស់​ប្តូរ​តាម​រយៈ​ការ​បោះ​ឆ្នោត​មិន​មែន​ជា​ការ​ផ្តួល​រំលំ​នោះ​ទេ', 'CNRP ៖ ពាក្យ​ថា ការ​ផ្លាស់​ប្តូរ​តាម​រយៈ​ការ​បោះ​ឆ្នោត​មិន​មែន​ជា​ការ​ផ្តួល​រំលំ​នោះ​ទេ', 'http://kohsantepheapdaily.com.kh/article/116866.html', 'http://i.kspg.co/2015/3/2d52d8da-2a09-46af-937c-4953e627267e.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1097', 'cl000001', 'u0000003', 'បង្ក្រាប​រថយន្ត​ឡង់គ្រីស្ស័រ​ដឹក​ឈើ​គ្រញូងចំនួន​២៧៧ដុំនៅ​ស្រុកជាំក្សាន្ត​', 'ខេត្តព្រះវិហារ ៖ សមត្ថ​កិច្ចរដ្ឋបាល​ព្រៃឈើ​កន្ទួត សហការជាមួយ​​យោធា​សឹក​រ​ងស្រុក​ជាំក្សាន្ត បានធ្វើការស្ទាក់​ឃាត់​រថយន្ត​ឡង់គ្រី​ស្ស័រ​​លួចដឹកឈើ​ប្រណីត​ប្រភេទគ្រញូង​ជាច្រើនដុំ កាលពីវេលាម៉ោង​០៤រសៀល​ថ្ងៃទី​១៧ មីនា ២០១៥ ស្ថិត​នៅត្រង់​ផ្លូវបំបែក​', 'http://kohsantepheapdaily.com.kh/article/116865.html', 'http://i.kspg.co/2015/3/6828851b-dfcb-40c2-bce0-478a8dfa6ccd.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1098', 'cl000001', 'u0000003', 'ម៉ោង១និង៤៨នាទី​រសៀលនេះ ​រដ្ឋសភាជាតិ​បន្តពិភាក្សានិង​អនុម័តសេចក្តី​ស្នើវិសោធនកម្មច្បាប់​បោះឆ្នោត', 'រាជធានីភ្នំពេញ ​៖ បន្ទាប់ពីបាន​​សម្រាកទទួលទានអាហារថ្ងៃត្រង់​នោះរួចមក​ សមាជិក​សមាជិការដ្ឋភា១០៣រូប​ក្នុងនោះក៍មានវត្តមាន​សម្តេច​', 'http://kohsantepheapdaily.com.kh/article/116863.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1099', 'cl000001', 'u0000003', 'នគ​របាល​ម្នាក់​ដេក​ស្លាប់​នៅ​លើ​ប៉ៅ​អ៊ី​​​ ដោយ​សារគាំង​បេះដូង', 'ខេត្តស្វាយរៀង ៖ មានការ​ភ្ញាក់ផ្អើល​ទាំង​ព្រឹក​ព្រលឹម​ដោយ​សារ​លោក​អនុសេនីយ៍ទោ​ យស់​ ឡា​ ជា​មន្ត្រី​នគ​បាល​ខេត្ត​​​ដេក​ស្លាប់​នៅ​លើ​ប៉ៅ​អ៊ីក្នុង​ទីស្នាក់ការ​សាខា​គយ ​និង​រដ្ឋាករ​ខេត្ត​​ ​​។​', 'http://kohsantepheapdaily.com.kh/article/116858.html', 'http://i.kspg.co/2015/3/5948e1ce-a4fc-4702-9ba9-28f599382ebf.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1100', 'cl000001', 'u0000003', 'លោក ប៉ា សុជាតិវង្ស​ ​នឹងធ្វើសន្និសីទ​កាសែតស្តីពី​ការបញ្ចប់វិវាទ​អ្នកបូរីកីឡា​នៅព្រឹកស្អែក', 'រាជធានីភ្នំពេញ ​៖ ប្រភពពីមន្រ្តីសាលា​រាជធានីភ្នំពេញបាន​បង្ហើបឲ្យដឹងថា ​បើគ្មានអ្វីប្រែប្រួលនោះទេ ​អភិបាលនៃ​គណៈអភិបាលរាជធានី​ភ្នំពេញ ', 'http://kohsantepheapdaily.com.kh/article/116856.html', 'http://i.kspg.co/2015/3/2aa8b342-21b1-47f1-99b1-01bf6e48be0f.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1101', 'cl000001', 'u0000003', 'ម៉ោងជិត១០ព្រឹកនេះ ​រដ្ឋសភាអនុម័ត​ជាឯកច្ឆន្ទជំពូកទី១​​និងទី២នៃ​​សេចក្តីស្នើ​វិសោធនកម្ម​ច្បាប់បោះឆ្នោត', 'រាជធានីភ្នំពេញ ៖ ​សមាជិកសមាជិកា​១០៣រូបលើ១០៣រូប ​បានលើកដៃជា​ឯកច្ឆន្ទអនុម័តជំពូកទី១​និងទី២នៃ​សេចក្តីស្នើវិសោធនកម្ម​ច្បាប់បោះឆ្នោត​', 'http://kohsantepheapdaily.com.kh/article/116854.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1102', 'cl000001', 'u0000003', 'ជុំវិញ​ទស្សនកិច្ច​ជំទាវ​ទី​១សហរដ្ឋអាមេរិក​នៅ​កម្ពុជា សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត​ក្នុង​នាម​ម្ចាស់ផ្ទះ​ស្វាគមន៍​នៅ​សៀមរាប', 'រាជធានី​ភ្នំពេញ ៖ ក្នុង​ឱកាស​ដំណើរ​ទស្សនកិច្ច​នៅ​ព្រះរាជាណាចក្រ​កម្ពុជា នៅ​ថ្ងៃ​ទី​២០ មីនា លោក​ជំទាវ មី​ស្ហែ​ល អូ​បា​ម៉ា (Michelle OBAMA) ជំទាវ​ទី​១​សហរដ្ឋអាមេរិក នឹង​ត្រូវ​ទទួល​ស្វាគមន៍​', 'http://kohsantepheapdaily.com.kh/article/116850.html', 'http://i.kspg.co/2015/3/4d43fc2f-d293-41df-989a-35409d9dd685.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1103', 'cl000001', 'u0000003', 'ខ​ណ្ឌរដ្ឋបាល​ជលផល​ទទួល​អាំងវើរទ​រ័​ឆក់​ត្រី​៥០១​គ្រឿង​បន្ទាប់​ពី​ផ្សព្វផ្សាយ​សេចក្តីណែនាំ​រយៈពេល​២​ខែ', 'ខេត្តកំពង់ឆ្នាំ� ​៖  រយៈពេល​២​ខែ​កន្លង​មកនេះ​ប្រជាពលរដ្ឋ​ក្នុង​ខេត្ត​បាន​ភ្ញាក់រលឹក​ប្រគល់​ឧក​រណ៍​ឆក់ត្រី​មក​ឱ្យ​អាជ្ញាធរ​ឃុំ ស្រុក និង​រដ្ឋបាល​ជលផល​បាន​ចំនួន​៥០១​គ្រឿង​ហើយ ក្រោយ​ពី​ខ​ណ្ឌរដ្ឋបាលជលផល​​​​​', 'http://kohsantepheapdaily.com.kh/article/116849.html', 'http://i.kspg.co/2015/3/338bf85e-26d7-4ea5-9170-dcbe99223512.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1104', 'cl000001', 'u0000003', 'សម្ពោធ​ដាក់​ឱ្យ​ប្រើ​ប្រាស់​ជា​ផ្លូវ​ការ​រង្វង់​មូល​មិត្ត​ភាព​កម្ពុជា​-កូរ៉េ​', 'ខេត្ត​កំពត​ ៖ រង្វង់​មូល​នៅ​ចំណុច​ផ្លូវ​ត​ភ្ជាប់​ផ្លូវ​ជាតិ​លេខ​៤៨ និង​ផ្លូវ​ជាតិ​លេខ​៣​កំពត​ ខេត្ត​ព្រះ​សីហនុ​ត្រូវ​បាន​បើក​សម្ពោធ​ឱ្យ​ប្រើ​ប្រាស់​ជា​ផ្លូវ​ការ​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៩ មីនា ២០១៥ នេះ​ ក្រោម​អធិបតី​ភាព​លោក​ត្រាំ អ៊ីវ​តឹក', 'http://kohsantepheapdaily.com.kh/article/116845.html', 'http://i.kspg.co/2015/3/4c0321d0-d1f9-48e0-b885-08babd6bdaee.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1105', 'cl000001', 'u0000003', 'បើកកិច្ចប្រជុំស្តីពីសណ្តាប់​ធ្នាប់ជុំវិញ​ផ្សារ​ដើមគរ', 'រាជធានីភ្នំពេញ ៖ នាវេលាព្រឹកថ្ងៃទី​១៩ មីនា ២០១៥ នេះ លោកប្រាក់​ ថៃសៀង អភិបាល​​រង​រាជធានីភ្នំពេញ, លោក​ប៉ាង​ លីដា អភិ​បាល​រង​ខណ្ឌ​ទួលគោក , មន្ត្រី​មន្ទីរ​សេដ្ឋ​កិច្ច​ និងហិរញ្ញវត្ថុរាជធានី, មន្ត្រីមន្ទីរសាធារណការរាជធានី និង​មន្ត្រី​ពាក់​ព័ន្ធមួយចំនួនទៀត​ ', 'http://kohsantepheapdaily.com.kh/article/116844.html', 'http://i.kspg.co/2015/3/4a394f66-734c-4772-b46a-2a191bfc2463.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1106', 'cl000001', 'u0000003', 'ម៉ោងជាង​៩ព្រឹកនេះ ​រដ្ឋសភាអនុម័ត​វិសោធនកម្មច្បាប់​ស្តីពីការរៀបចំ​និងការប្រព្រឹត្ត​ទៅនៃគ.ជ.បក្នុងសំឡេងគំាទ្រ១០៣', 'រាជធានីភ្នំពេញ ​៖ ទីបំផុតទៅ​សេចក្តីព្រាងវិសោធនកម្ម​ច្បាប់ស្តីពីការរៀបចំនិង​ការប្រព្រឹត្តទៅនៃ​គណៈកម្មាធិការជាតិរៀបចំការ​បោះឆ្នោតបានត្រូវ​', 'http://kohsantepheapdaily.com.kh/article/116843.html', 'http://i.kspg.co/2015/3/5e490a3b-c66c-44ed-a515-6cd7d21eab7a.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1107', 'cl000001', 'u0000003', 'សម្តី​ឯនាយ​សមុទ្រ សម្តេច​តេ​ជោ​ថ្លែង​ប្រតិកម្ម​តប​លើ​អ្នក​មិន​ពេញចិត្ត​កិច្ចព្រមព្រៀង', 'ខេត្តបន្ទាយមានជ័យ ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា​មាន​ប្រសាសន៍​ថា ឥឡូវនេះ​មាន​អ្នក​ចេញ​សារភាព​ហើយ ពី​ការ​ប៉ុនប៉ង​ផ្តួលរំលំ​រាជរដ្ឋាភិបាល​បន្ទាប់​ពី​ការ​បោះ​ឆ្នោត', 'http://kohsantepheapdaily.com.kh/article/116837.html', 'http://i.kspg.co/2015/3/7f94bae1-b5e3-4e53-8692-efeab82a751b.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1108', 'cl000001', 'u0000003', 'បុរស​ខ្មែរ​ម្នាក់​ត្រូវ​តម្រួត​ថៃ​បាញ់​របួស​ដោយ​​ចោទ​ប្រកាន់​ពី​បទ​ប៉ុន​ប៉ង​លួច', 'បន្ទាយ​មាន​ជ័យ​ ៖​ ប្រភព​ព័​ត៌​មាន​ពី​ស្រុក​គោក​ធ្យូង​ ខេត្ត​ស្រះ​កែវ​ ប្រទេស​ថៃ​បាន​ឲ្យ​ដឹង​ថា​ កាលពី​វេលា​ម៉ោង​១​ និង​១០​នាទី​រំលង​អ​ធ្រា​ត្រ​ឈាន​ចូល​ព្រឹក​ថ្ងៃ​ទី​១៩​ ខែមីនា​ ឆ្នាំ​២០១៥​ បុរស​ខ្មែរ​ម្នាក់​ត្រូវ​សមត្ថកិច្ច​ថៃ​បាញ់​របួសធ្ងន់​ដោយ​​ចោទ​ប្រកាន់​ពី​បទ​ប៉ុនប៉ង​លួច​ ។', 'http://kohsantepheapdaily.com.kh/article/116887.html', 'http://i.kspg.co/2015/3/c4cf1dfc-c2d6-492f-9867-839a81f37606.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1109', 'cl000001', 'u0000003', 'ចាប់ខ្លួនបានហើយជនសង្ស័យដែល​រំលោភ​ក្មេងស្រី៦នាក់​វិលជុំ​ម្នាក់ម្តងៗ', 'ខេត្តស្ទឹងត្រែង ​៖ កម្លាំង​ប៉ូលិស​​បាន​ចុះ​ធ្វើការចាប់ខ្លួន​ជនសង្ស័យ​ល្បួង​កុមារី​រំលោភ​ជា​ច្រើន​នាក់​បាន កាលពី​រសៀល​ម៉ោង​ជាង​៥ ​ថ្ងៃ​ទី​១៨ ​មីនា ​២០១៥ នៅ​ឃុំ​កាំ​ង​ចាម​ ​នាំ​មក​ស្នងការដ្ឋាន​ហើយ​ ', 'http://kohsantepheapdaily.com.kh/article/116880.html', 'http://i.kspg.co/2015/3/b2e98c30-2673-4d17-8c9d-9286a4ff7bf7.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1110', 'cl000001', 'u0000003', '​ជនដៃដល់​ម្នាក់ទៀត​ដែល​បាញ់រះនៅមជ្ឈមណ្ឌល​​កម្សាន្តរ៉ក់​បង្កការភ្ញាក់ផ្អើល​កណ្តាល​ក្រុងត្រូវប៉ូលិសឃាត់ខ្លួន', 'រាជធានីភ្នំពេញ ៖ ​ជនដៃដល់​ដែលប្រើកំាភ្លើង​ AK បាញ់រះ​នៅមជ្ឈមណ្ឌល​កម្សាន្តរ៉ក់ជាមួយកូន​ប្រុសមេគយល្បីឈ្មោះ ​លោក កែវ សុខឃាង​ បង្កឲ្យមាន​', 'http://kohsantepheapdaily.com.kh/article/116868.html', 'http://i.kspg.co/2015/3/af3b4ba2-a9eb-4b19-883a-e3d1865ff1cd.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1111', 'cl000001', 'u0000003', 'ប្រើដាវ​សាំមួរ៉ៃ​កាប់​ចំ​កញ្ចឹង​.ក ស្លាប់​ក្នុង​ថ្លុក​ឈាម សង្ស័យគំនុំស្នេហា', 'រាជ​ធានី​ភ្នំពេញ​​៖ យុវ​ជន​ខ្មែរ​អ៊ី​ស្លាម​ម្នាក់​ត្រូវ​ជន​មិន​ស្គាល់​មុខ​៣​នាក់​ជិះ​ម៉ូតូ​មួយគ្រឿង​តាម​កាប់​នឹង​ដាវ​សាំមួរ៉ៃ​ចំ​កញ្ចឹង​.កបណ្តាល​ឱ្យ​​ធ្លាក់​ពី​លើ​ម៉ូតូ​ស្លាប់​ក្នុង​ថ្លុក​ឈាម​លើ​ដង​វិថី​សហ​ព័ន្ធ​រុស្ស៊ី​ ', 'http://kohsantepheapdaily.com.kh/article/116864.html', 'http://i.kspg.co/2015/3/f69acc2c-880e-4fa7-9b79-009c77f332ba.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1112', 'cl000001', 'u0000003', 'អ្នក​យក​ព័ត៌មាន​ឱ្យ​ទូរទស្សន៍ គេហទំព័រ និង​កាសែត​រង​ការ​គំរាម', 'ខេត្តបន្ទាយមានជ័យ​ ៖ ស​ប្តា​ហ៍​ទី​2 នៃ​ខែមីនា ឆ្នាំ​2015 អ្នក​យក​ព័ត៌មាន​ឲ្យ​ទូរទស្សន៍ គេហទំព័រ និង​កាសែត​ក្នុងស្រុក​ចំនួន​៤​នាក់​មក​ពី​៥​អង្គភាព​រួម​មាន​ទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍ គេហទំព័រ​កាសែត​នគរវត្ត គេហទំព័រ​កាសែត​ដើម​អំពិល កាសែត​តា​ព្រហ្ម និង​កាសែត​រាជសីហ៍', 'http://kohsantepheapdaily.com.kh/article/116861.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1113', 'cl000001', 'u0000003', 'លួច​កាបូប​អ្នក​ជិតខាង​ពេល​ចាប់​បាន​ព្យាយាម​រត់គេច​ខ្លួន​ទាំង​ដៃ​ជាប់​ខ្នោះ​ត្រូវ​ប្រជាពលរដ្ឋ​ដេញចាប់​បាន​មក​វិញ​វាយ​រហូត​ដល់​ស្លាប់', 'រាជធានី​ភ្នំពេញ​ ៖ បុរស​ម្នាក់​បាន​ធ្វើ​សកម្មភាព​លួច​យក​កាបូប​លុយ និង​ទូរស័ព្ទ​របស់​អ្នក​ជិត​ខាងក្នុង​ពេល​ផឹកស៊ី​ជាមួយ​គ្នា​ ហើយ​ត្រូវ​បាន​ម្ចាស់​ចាប់ខ្លួន​ប្រគល់​ទៅ​ឲ្យ​ប៉ូ​លិស​ប៉ុស្ដិ៍​ចោម​ចៅ​ដើម្បី​សួរនាំ ហើយ​ប៉ូ​លិសក៏​បាន​ដាក់​ខ្នោះដៃ', 'http://kohsantepheapdaily.com.kh/article/116842.html', 'http://i.kspg.co/2015/3/71063fe7-0c2e-4904-8c01-5fb8331d6d1d.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1114', 'cl000001', 'u0000003', 'គោ​ឈ្មោល​មួយក្បាល​ត្រូវ​ចោរ​លួច​យក​ទៅ​ពន្លះ​សាច់​អស់​សល់​តែ​ក្បាល និង​គ្រោង​ឆ្អឹង', 'ខេត្ត​កំពង់​ស្ពឺ​ ៖ គោ​ឈ្មោល​មួយ​ក្បាល​ត្រូវ​ចោរ​លួច​យក​ទៅ​ពន្លះ​សាច់​អស់​សល់​តែ​ក្បាល និង​គ្រោង​ឆ្អឹង​​​ត្រូវ​បាន​ម្ចាស់​ប្រទះ​ឃើញនៅ​ចំណុច​ភូមិ​ក្រាំង​ធ្នង់​ ឃុំ​វល្លិ៍ស ស្រុក​សំរោង​ បង្ក​ឱ្យ​មានការ​ភ្ញាក់​ផ្អើល​​កាល​ពី​', 'http://kohsantepheapdaily.com.kh/article/116834.html', 'http://i.kspg.co/2015/3/cc0c7f8a-a4b8-40fb-8133-bf8bb925ed59.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1115', 'cl000001', 'u0000003', 'ជំរិត​ទារប្រាក់​៨​ម៉ឺន​ដុល្លារ​ថ្នូរ​មិន​បង្ហោះវីដេអូ​រួមរ័ក​តាម​ហ្វេ​ស​ប៊ុ​ក', 'រាជធានី​ភ្នំពេញ ៖ អ្នកបើកបរ​រថយន្ត​ទេសចរណ៍​ឱ្យ​ក្រុមហ៊ុន​មួយ ត្រូវ​បាន​កងរាជអាវុធហត្ថ​រាជធានី​ភ្នំពេញ​ ចាប់ខ្លួន​កាលពី​វេលា​ម៉ោង​ប្រមាណ​៩​និង​៣០​នាទី​យប់ ថ្ងៃ​ទី​១៧ មីនា នៅ​មុខ​ផ្ទះ​', 'http://kohsantepheapdaily.com.kh/article/116833.html', 'http://i.kspg.co/2015/3/a4607495-ae94-432d-9950-7b6a97f0b7b0.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1116', 'cl000001', 'u0000003', 'ក្រុមហ៊ុន​វៀតណាម ដែល​យក​គ្រឿងចក្រ​ទៅ​ស្តារ​ស្ទឹង​ប​យ៉ា​ខា នៅ​ពេល​នេះ​ផ្អាក​ដំណើរការ​ហើយ សង្ស័យ​ខាតចុងខាតដើម​លែង​ត្រឡប់​មក​វិញ', 'ខេត្ត​ប៉ៃលិន ៖ មាន​ក្រុមហ៊ុន​វៀតណាម​មួយ ដែល​បាន​ចូល​មក​ធ្វើការ​អភិវឌ្ឍ​ស្តារ​ស្ទឹង​ប​យ៉ា​ខា ដោយ​យក​គ្រឿងចក្រ​ជីក​កកាយ​ដុំ​ថ្ម​នៅ​បាត​ស្ទឹង​ទៅ​ដាក់​នៅ​តាម​មាត់​ស្ទឹង ដើម្បី​ឱ្យ​ទឹក​ហូរ​លឿន កុំឱ្យ​ស្ទះ​លិចលង់​ដល់​ភូមិ​ប្រជាពលរដ្ឋ​នា​រដូវវស្សា', 'http://kohsantepheapdaily.com.kh/article/116830.html', 'http://i.kspg.co/2015/3/8d22123f-053a-4310-802e-7dcd68e114ed.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1117', 'cl000001', 'u0000003', 'ចូល​ផឹកស៊ី​ខឹង​គេ​មាត់​ឡូឡា​ឮ​ៗ ស្រែក​ឡូ​ក​មាត់​បង្កជម្លោះ​បែកក្បាល​ម្នាក់', 'រាជធានី​ភ្នំពេញ​៖ បុរស​ម្នាក់​បាន​រង​របួស​បែកក្បាល​ហូរ​ឈាម ខណៈ​ពេល​ចូល​ផឹកស៊ី​នៅ​ក្នុង​ហាង​ឌ្រី​ង​ម៉ាត​២​នាក់​មិត្តភក្តិ ស្រាប់តែ​ផឹក​មួយសន្ទុះ​ស្រវឹង​ស្រឿង​ៗ ក៏​ស្រែ​កថា​ឲ្យ​ក្រុម​ភ្ញៀវ​នៅ​តុ​ជាប់​នោះ​ថា​និយាយ​ឮ​ៗ​ថ្លង់​ណាស់ មិន​ឲ្យ​ចាញ់​នោះ​ទេ ', 'http://kohsantepheapdaily.com.kh/article/116828.html', 'http://i.kspg.co/2015/3/8078b4fa-0093-4a46-9023-650b13ef0705.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1118', 'cl000001', 'u0000003', 'សិស្ស​វិទ្យាល័យ​ព្រះបាទ​សុរា​ម្រឹតកាប់​ប្រហារ​សិស្ស​វិទ្យាល័យ​ក្រុង​កំពង់ឆ្នាំង​រយះក្បាល​​​ម្នាក់​​ជា​ការ​សងសឹក', 'ខេត្តកំពង់ឆ្នាំង ៖ យុវ​សិស្ស​ម្នាក់ក្នុង​ចំណោម​សិស្ស​៣​នាក់ ត្រូវ​បាន​សិស្ស​មួយ​ក្រុម​ផ្សេង​ទៀត​កាប់​ប្រហារ​នឹង​ដាវ​សាំម៉ូ​រៃ បណ្តាលឱ្យរង​របួស​បែកក្បាល​ហូរ​ឈាម​ស្រោច​ខ្លួន ហើយ​ត្រូវ​បានដឹក​មក​សង្គ្រោះ​នៅ​មន្ទីរ​', 'http://kohsantepheapdaily.com.kh/article/116798.html', 'http://i.kspg.co/2015/3/afcc259a-66dc-4adf-a160-a656b41d131c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1119', 'cl000001', 'u0000003', 'ជាប់គុក​៣​ដង​ហើយ​មិន​រាង​ ចេញ​មក​វិញ​ប្រើ​កាំបិត​ប័ុងតោ​ប្លន់​យក​ទូរស័ព្ទ​ត្រូវ​ចាប់​ចូល​គុក​វិញ', 'ខេត្តស្ទឹងត្រែង​ ៖ មុខសញ្ញា​សកម្ម​ក្នុង​ករណី​លួច​-​ប្លន់​ និង​ករណី​រំលោភ​បួ​ក​ ​ជា​ច្រើន​ករណី​មិន​ចេះ​កែ​ច​រិ​ត​ត្រូវ​បាន​កម្លាំង​នគរបាល​ព្រហ្ម​ទណ្ឌ​កម្រិត​ស្រាល​ចាប់ខ្លួន​ជា​ថ្មី​ម្តងទៀត​ក្នុង​ករណី​គំរាម​កំហែង​យកទូរស័ព្ទ', 'http://kohsantepheapdaily.com.kh/article/116771.html', 'http://i.kspg.co/2015/3/ec4d4942-d815-4160-908e-96344950c24d.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1120', 'cl000001', 'u0000003', 'ស្រែក​ឆោឡោ​ថា​ចោរប្លន់​ម៉ូតូ​ផ្អើល​ប៉ូលិស​មួយ​ខណ្ឌ​សែន​សុខ កា​ពិត​ស្រវឹង​វង្វេង​ម៉ូតូ​ទុក​នៅផ្ទះ​ថា​ម៉ូតូ​បាត់', 'រាជធានី​ភ្នំពេញ​៖ បុរស​ចំណាស់​ក្នុង​ស្ថានភាព​ស្រវឹង​បាន​ដើរ​ស្រែក​ឆោឡោ​ថា​មាន​ចោរ​វាយ​ប្លន់​យក​ម៉ូតូ​របស់​គាត់ បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​ដល់​ប៉ូលិស​មួយ​ខណ្ឌ​សែន​សុខ​ចេញ​ជួយ​អន្តរាគមន៍ ប៉ុន្តែ​ធាតុ​ពិត​គាត់​ស្រវឹង​ដល់​ថ្នាក់​វង្វេង​ថា​ចោរប្លន់​ម៉ូតូ', 'http://kohsantepheapdaily.com.kh/article/116769.html', 'http://i.kspg.co/2015/3/9f913e29-4271-4b30-8d9d-dc919c4f5c5c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1121', 'cl000001', 'u0000003', 'ផ្ទុះអាវុធ​២​គ្រាប់​នៅផ្ទះ​មេ​ប៉ុស្តិ៍​ផ្សារ​ចាស់​ផ្អើល​សមត្ថកិច្ច​ទាំង​យប់', 'រាជធានី​ភ្នំពេញ​៖​ស្នូរ​ផ្ទុះអាវុធ​២​គ្រាប់​ចេញពីផ្ទះ​លោក​មេ​ប៉ុស្តិ៍​ផ្សារ​ចាស់​បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​ដល់​សមត្ថកិច្ច​ទាំង​យប់ ខណៈ​មាន​ជម្លោះ​ពាក្យ​សម្តី​តិចតួច​ជាមួយ​ប្រ​ព​ន្ឋ កាល​វេលា​ម៉ោង​១១​និង​២០​នាទី​យប់ ថ្ងៃ​ទី​១៧ ខែ​មិ​នា ឆ្នាំ​២០១៥ នៅ​តាម​បណ្តោយ​ផ្លូវ​K5 ', 'http://kohsantepheapdaily.com.kh/article/116767.html', 'http://i.kspg.co/2015/3/a31feba7-c55b-4f6d-8300-129d5b04ffeb.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1122', 'cl000001', 'u0000003', 'កីឡា​ប្រដាល់​ម្នាក់​ត្រូវ​ចោរ​អារ ក​សម្លាប់​ប្លន់​យក​ម៉ូតូ', 'រាជធានី​ភ្នំពេញ​៖ ជនសង្ស័យ​២​នាក់​បាន​ធ្វើ​សកម្មភាព​អារ ក​សម្លាប់​ជន​រង​គ្រោះ រួច​ប្លន់​យក​ម៉ូតូ​ដោយ​សុវត្ថិភាព ខណៈ​ពេល​ជន​រង​គ្រោះជា​អ្នក​រត់​ម៉ូតូឌុប ត្រូវ​បាន​ជនសង្ស័យ​ហៅ​ឲ្យ​ឌុ​ប​មក​កន្លែង​កើតហេតុ ហើយ​ក៏​ធ្វើ​សកម្មភាព​តែ​ម្តង', 'http://kohsantepheapdaily.com.kh/article/116766.html', 'http://i.kspg.co/2015/3/52405a79-190f-4038-bf76-4f980619476f.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1123', 'cl000001', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី១៩ មីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី១៩ មីនា ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/116862.html', 'http://i.kspg.co/2015/3/e2995712-3893-475b-8a84-09d7aaaf177c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1124', 'cl000001', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី១៨ មីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី១៨ មីនា ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/116795.html', 'http://i.kspg.co/2015/3/b76edc71-2c8d-4384-a636-fbf8d3288446.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1125', 'cl000001', 'u0000003', 'សម្តេចតេជោ​ ហ៊ុន សែន​ ព្រមានដាក់ខ្នោះ​អ្នកចង់ធ្វើបាតុកម្ម​ទាមទារ​ដោះលែង​លោកមាជ សុវណ្ណរ៉ា', 'រាជធានីភ្នំពេញ ៖ ​សម្តេចអគ្គមហាសេនាបតីតេជោ​ ហ៊ុន សែន ​បានមានប្រសាសន៍​ក្នុងន័យព្រមានទុកជាមុនថា ​បានត្រៀមខ្នោះ​រួចជាស្រេច ដើម្បី​', 'http://kohsantepheapdaily.com.kh/article/116852.html', 'http://i.kspg.co/2015/3/11abc3e5-0272-4a5c-9555-f04be56e3926.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1126', 'cl000001', 'u0000003', 'កាដូ​ថ្ងៃ​បុណ្យចូលឆ្នាំ​ថ្មី​សម្រាប់​ពលរដ្ឋ​ខ្មែរ ថ្ងៃ​១៣មេ​សា សភា​បោះ​ឆ្នោត​ជ្រើសរើស គ​.​ជ​.​ប ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ ទោះបីជា​ឈ្មោះ​សមាសភាព​បេក្ខភាព​គណៈកម្មាធិការ​ជាតិ​រៀបចំ​ការ​បោះ​ឆ្នោត (​គ​.​ជ​.​ប​) ថ្មី មិន​ទាន់​ត្រូវ​បាន​បង្ហាញ​ជា​សាធារណៈ​ក្តី ក៏​សមាភាព គ​.​ជ​.​ប ថ្មី​នេះ​នឹង​ត្រូវ​', 'http://kohsantepheapdaily.com.kh/article/116847.html', 'http://i.kspg.co/2015/3/8290c9b7-ac4f-425f-9eef-30c1419c874c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1127', 'cl000001', 'u0000003', 'រថយន្តសំាយ៉ុង​​បុកម៉ូតូសិស្ស​សាលាអូស​ឡើងសួនច្បារ​ សំណាងរបួសស្រាល', 'រាជធានីភ្នំពេញ ​៖ សិស្សសាលាម្នាក់​បានរងរបួស​ស្រាលក្នុង​ឧបទ្ទវហេតុគ្រោះថ្នាក់​ចរាចរណ៍​រថយន្តឈ្នួលបុកម៉ូតូ ។ ', 'http://kohsantepheapdaily.com.kh/article/116909.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1128', 'cl000001', 'u0000003', 'អ្នកលក់​ពោត​ស្ងោរ​ម្នាក់​ត្រូវ​រថយន្ត​យី​ឌុ​ប​បើក​បុក​និង​កិន​បែកក្បាល​ធ្លាយ​ខួរ​ស្លាប់', 'រាជធានី​ភ្នំពេញ ៖ បុរស​អ្នកលក់​ពោត​ស្ងោរ​ម្នាក់​ត្រូវ​បាន​រថយន្ត​យី​ឌុ​ប​មួយ​គ្រឿង​បើក​ក្នុង​ល្បឿន​យ៉ាង​លឿន​ជ្រុល​ទៅ​បុក និង​កិន បណ្ដាល​ឲ្យ​បែកក្បាល​ធ្លាយ​ខួរ​ស្លាប់​បាត់បង់​ជីវិត​ភ្លាម​ៗ​នៅ​នឹង​កន្លែង', 'http://kohsantepheapdaily.com.kh/article/116900.html', 'http://i.kspg.co/2015/3/19a58553-cfb9-4498-a085-a25f11d978aa.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1129', 'cl000001', 'u0000003', 'រថយន្តក្រុង​វៀតណាមបុកជាមួយ​យី​ឌុ​ប​ដឹក​ដំឡូង​ខ្មែរ ស្លាប់​ម្នាក់របួសធ្ងន់​ស្រាល​១១​នាក់ ​', 'ខេត្តក្រចេះ​ ៖ បុរស​ជា​ព្រូ​រថយន្តក្រុង​វៀតណាម​ម្នាក់​បាន​ស្លាប់​ដោយ​ដាច់​ដៃ​ខាងស្តាំម្ខាង និង​អ្នកបើកបរ​រថយន្ត​ក៏​ត្រូវ​រង​របួសធ្ងន់ នៅ​ក្នុង​ហេតុ​ការ​ណ៏​នៃ​គ្រោះថ្នាក់​ចរាចរ​ណ៏ ដែល​បង្ក​ឡើងដោយ​', 'http://kohsantepheapdaily.com.kh/article/116899.html', 'http://i.kspg.co/2015/3/bb2a44fb-087f-4c3f-a786-98a6c5bd26e0.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1130', 'cl000001', 'u0000003', 'ម៉ូតូ​ស្គូពី​បុក​រ៉ឺ​ម៉ក ​បាក់​.ក​ស្លាប់១ ​របួសធ្ងន់-ស្រាល​ ៣', 'ខេត្ត​កណ្ដា​ល ៖ ​ម៉ូតូ​ស្គូ​ពី​ និង​ម៉ូតូ​សណ្តោង​រ៉ឺ​ម៉ក​​បុក​គ្នា​ចំ​ពីមុខ​ពេញ​​ទំ​ហឹង ​បណ្ដាល​ឱ្យ​អ្នក​បើក​​ម៉ូតូ​ស្លាប់​​ភ្លាម​ៗ​​នៅ​នឹងកន្លែង​ និង​អ្នក​ជិះ​​រ៉ឺ​ម៉ក៣​នាក់​​ទៀត​​រង​របួស​ធ្ងន់-ស្រាល ​កាលពី​វេលា​ម៉ោង​១១​យប់​​ថ្ងៃ​ទី​១៨ មីនា ស្ថិតនៅ​លើ​ផ្លូវជាតិ​លេខ​21​B ​​ក្នុងភូមិ​ទួល​​ក្រសាំង ', 'http://kohsantepheapdaily.com.kh/article/116888.html', 'http://i.kspg.co/2015/3/b0773fce-38a2-4b03-be8b-e10b2cbcca42.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1131', 'cl000001', 'u0000003', 'រថយន្ត​បើក​បុក​ម៉ូតូ​នារីម្នាក់​សន្លប់ស្តូកស្តឹង​', 'រាជធានីភ្នំពេញ ៖ បើកបរ​ខ្វះ​ការ​ប្រុង​ប្រយ័ត្ន​​ លុះមក​ដល់​ចំណុច​ផ្លូវកែង​​​​រថយន្ត​ក៏​បុក​ម៉ូតូ​ បណ្តាល​ឱ្យ​សន្លប់​នារី​ម្នាក់ កាលពី​វេលា​ម៉ោង​​១​​២ ​និង​២៥​នាទី​រំលង​អធ្រាត្រ​​ឈាន​ចូល​ថ្ងៃ​ទី​១៩ មីនា ២០១៥ ស្ថិតនៅ​ចំណុច​ផ្លូវ​លេខ​៣៦០​ កែង​១០៥ ក្នុងសង្កាត់​បឹង​កេង​', 'http://kohsantepheapdaily.com.kh/article/116855.html', 'http://i.kspg.co/2015/3/3090e112-5dc8-4ef0-a78e-a8ac02b7ee29.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1132', 'cl000001', 'u0000003', 'មក​ពី​ជប់លៀងស្រវឹង​ជិះ​ម៉ូតូ​បុក​ម៉ូតូ​គូសង្សារ​បាក់ជើង​ស្ត្រី​ម្នាក់', 'រាជ​ធានី​ភ្នំពេញ ៖ បុរស​ម្នាក់​មក​ពី​ជប់លៀង​ក្នុង​ស្ថានភាព​ស្រវឹងស្រា​ជិះ​ម៉ូតូ​ទៅ​បុក​ម៉ូតូ​គូសង្សារ​មួយគូ​ផ្លោង​ទៅ​បោកផ្ទប់​នឹង​ហាង​គុយទាវ​ព្រឹក​ថ្មី​ល្ងាច​ស្រស់​បណ្តាល​ឲ្យ​ស្ត្រី​ម្នាក់​បាក់ជើង និង​អ្នក​បង្ក​បែក​ក្តោងច្រមុះ', 'http://kohsantepheapdaily.com.kh/article/116853.html', 'http://i.kspg.co/2015/3/68ef1153-46f9-48ac-932f-d740e85b1924.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1133', 'cl000001', 'u0000003', '​​សង្ស័យ​ស្រវឹង​ធ្លាក់​ភ្នែក​បើក​​រ៉ឺ​ម៉ក​ម៉ូតូបុក​ពីក្រោយ​ម៉ូតូ​ស្មា​សរី​វ៉ូ​​របួសធ្ងន់​-​ស្រាល​៤​នាក់', 'រាជ​ធានីភ្នំពេញ​ ៖ យ៉ាង​ហោច​ណាស់​​មនុស្ស​ម្នាក់បាន​រង​របួស​ធ្ងន់ និង​៣នាក់​ទៀត​រង​របួសស្រាល​​នៅ​ក្នុង​ឧប្បត្តិហេតុ​គ្រោះថ្នាក់​ចរាចរ​​រវាង​​រ៉ឺ​ម៉ក​ម៉ូតូ​មួយ​គ្រឿងដែល​បើកបរ​បុរស​ម្នាក់សង្ស័យ​ស្រវឹង​ធ្លាក់​ភ្នែក​​បើក​ទៅ​​​​', 'http://kohsantepheapdaily.com.kh/article/116840.html', 'http://i.kspg.co/2015/3/c258fb98-fc37-44de-bd1e-e2e9d142b1ec.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1134', 'cl000001', 'u0000003', 'ស្ត្រី​២​នាក់​ជិះ​ម៉ូតូ​បុក​បង្គោល​ភ្លើង​សន្លប់​ម្នាក់', 'រាជ​ធានីភ្នំពេញ ៖ ស្ត្រី​២​នាក់​ជិះ​ម៉ូតូ​ងោក​មួយភាំង​រេ​ចង្កូត​ទៅ​បុក​បង្គោល​ភ្លើង​បំភ្លឺ​ផ្លូវ​បណ្តាល​ឲ្យ​ដួល​សន្លប់​ម្នាក់ និង​ម្នាក់​ទៀត​បែក​មុខមាត់ ត្រង់​ចំណុច​មហាវិថី​ព្រះ​នរោត្តម​កែង​១៧៤ សង្កាត់​ផ្សារ ថ្មី​ទី​៣ ខណ្ឌ​', 'http://kohsantepheapdaily.com.kh/article/116838.html', 'http://i.kspg.co/2015/3/d5ce5d94-be08-4085-ac6f-dd26c205cce4.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1135', 'cl000001', 'u0000003', 'ស្លាប់​ម្នាក់​របួសធ្ងន់​ម្នាក់​ដោយ​គ្រោះថ្នាក់​ចរាចរ​មិនដឹង​មូលហេតុ​ព្រោះ​រថយន្ត​បង្ករ​គេច​បាត់', 'ខេត្តបន្ទាយមានជ័យ​៖ បុរស​ម្នាក់​ស្លាប់​និង​ម្នាក់​ទៀត​រង​របួសធ្ងន់​ដោយ​ឧប្បតិហេតុ​គ្រោះថ្នាក់​ចរាចរ​លើ​ដងផ្លូវ​ជាតិ​លេខ​៥ ក្នុងភូមិ​ស្ទឹង​បត់ សង្កាត់​ផ្សារ​កណ្ដាល ក្រុង​ប៉ោយ​ប៉ែ​ត ដោយ​ពុំ​ដឹង​ពី​មូលហេតុ​ច្បាស់លាស់​ព្រោះតែ​រថយន្ត​បង្ករ​បាន​គេច​បាត់ ', 'http://kohsantepheapdaily.com.kh/article/116831.html', 'http://i.kspg.co/2015/3/05953549-58a4-4b0c-a470-f946a31e2165.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1136', 'cl000001', 'u0000003', 'ជនជាតិកូរ៉េ​បើក​រថយន្ត ​ESCALADE ទាំងស្រវឹងបុកម៉ូតូ​រងរបួសធ្ងន់​ម្នាក់', 'ខេត្តសៀមរាប ៖ រថយន្តស៊េរី​​ទំនើប​របស់​​ជនជាតិ​កូរ៉េ​ម្នាក់ បាន​បើក​ក្នុង​​ល្បឿនយ៉ាង​​លឿន​ដូច​ហោះ​​មិន​ប្រយ័ត្ន ក៏​ជ្រុល​ទៅ​បុក​ម៉ូតូ​ចំ​មួយ​​ចំហៀង​ផ្នែក​ខាង​ឆ្វេង ​បណ្តាល​ឱ្យ​រងរបួសធ្ងន់​ (​បាក់ឆ្អឹង​ខ្នងស្លាបប្រចៀវ​ខាងក្រោយ​​ផ្នែក​ខាងឆ្វេង និង​​ត្រចៀក​​ខាង​ឆ្វេង​) ហើយ', 'http://kohsantepheapdaily.com.kh/article/116826.html', 'http://i.kspg.co/2015/3/91c4e344-8bc7-4a7b-95ed-1056062c0899.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1137', 'cl000001', 'u0000003', 'សង្ស័យ​ជិះម៉ូតូបុកគូថ​រថយន្តពីក្រោយ​ស្លាប់១របួសធ្ងន់​មួយនៅប៉ោយប៉ែត​រសៀលនេះ', 'ខេត្តបន្ទាយមានជ័យ ​៖ បុរសចំណាស់​ម្នាក់បានស្លាប់ភ្លាមៗ​នៅកន្លែងកើតហេតុ​ និង ម្នាក់ទៀត​រងរបួសធ្ងន់​បាក់ជើង​ត្រូវបញ្ជូន​ទៅសង្គ្រោះ​ភ្លាមៗនៅមន្ទីរ​', 'http://kohsantepheapdaily.com.kh/article/116825.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1138', 'cl000001', 'u0000003', 'សាំង​យ៉ុ​ង​ដឹក​បន្លែ​បុក​ម៉ូតូស្លាប់​ម្នាក់​របួសធ្ងន់​ម្នាក់', 'ខេត្តកណ្ដាល​ ៖ ​រថយន្ត​សាំង​យ៉ុ​ង​ដឹក​បន្លែ១​គ្រឿ​ងបុក​ម៉ូតូ​១​​គ្រឿង​​បើក​ឆ្លងផ្លូវ​បណ្ដាល​ឲ្យ​បុរស​ម្នាក់​ស្លាប់​ភ្លាម​ៗ​នៅ​កន្លែង​កើត​ហេតុ​ និង​ម្នាក់​​ទៀត​របួសធ្ងន់​កាលពី​វេលា​ម៉ោង​១១​ និ​ង១៦​នាទី​ថ្ងៃ​ទី​១៧ ខែ​មីនា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116797.html', 'http://i.kspg.co/2015/3/4995c19d-0293-4288-9ef6-2e8febfd0db1.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1139', 'cl000001', 'u0000003', '​រ៉ឺ​ម៉កម៉ូតូ​ដឹក​ទំនិញ​សំពីងសំពោង​ខ្ពស់កប់ពពក បាំងភ្នែក​ប្រធាន​ច្រក​អន្តរជាតិ​​ព្រំ', 'ខេត្ត​ប៉ៃលិន ៖ ជា​រៀង​រាល់​យប់​ថ្ងៃ​គេ​សង្កេត​ឃើញ​មាន​រ៉ឺ​ម៉ក​ម៉ូតូ​មួយ​ចំនួន​ដឹក​ទំ​និញ​ចេញពី​ច្រក​ទ្វារ​អន្តរជាតិ​ព្រំ ស្ថិត​នៅ​ភូមិ​ផ្សារ​ព្រំ ឃុំ​ស្ទឹង​កាច់ ស្រុក​សាលា​ក្រៅ យ៉ាង​សំពីង​សំពោង​ខ្ពស់កប់ពពក ', 'http://kohsantepheapdaily.com.kh/article/116781.html', 'http://i.kspg.co/2015/3/704eb878-865f-4e69-ab25-5dbeac39e5a1.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1140', 'cl000001', 'u0000003', 'គ្រោះថ្នាក់​ចរាចរណ៍ម្សិលមិញ​សម្លាប់មនុស្ស០៧នាក់ ​និង ​របួស១៤នាក់', 'រាជធានីភ្នំពេញ ៖ ​យ៉ាងហោចណាស់​មនុស្ស០៧នាក់​បានស្លាប់និងរបួស១៤នាក់​ ក្នុងនោះ១០នាក់​របួសធ្ងន់​ដោយសារគ្រោះថ្នាក់​ចរាចរណ៍១២​​លើកកើត​', 'http://kohsantepheapdaily.com.kh/article/116779.html', 'http://i.kspg.co/2015/3/44e20690-9c32-40c0-8561-ea1cbec6e676.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1141', 'cl000001', 'u0000003', 'រថយន្ត​កាម​រី​ឆ្លាម​បុក​រនាំង​ដែក​បាក់​អស់​៥​ផ្ទាំង រួច​គេចខ្លួន​បាត់', 'រាជធានី​ភ្នំពេញ​៖ រថយន្ត​កាម​រី​ឆ្លាម​១​គ្រឿង​បាន​បោះពួយ​ក្នុង​ល្បឿន​លឿន ក៏​រេ​ចង្កូត​ទៅ​បុក​រនាំង​ដែក​ពុះ​ចែក​ទ្រូងផ្លូវ បណ្តាល​ឲ្យ​បាក់​អស់​ចំនួន​៥​ផ្ទាំង រថយន្ត​ដាច់​កាឡេ​ទៅ​មុខ​លែង​រួច ។​ហេតុការណ៍​នេះ​បាន​កើតឡើង​នៅ​វេលា​ម៉ោង​២​និង​៤០​នាទី​ទាប​ភ្លឺ', 'http://kohsantepheapdaily.com.kh/article/116768.html', 'http://i.kspg.co/2015/3/77b85415-6faf-4d03-8c32-afdd07d13343.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1142', 'cl000001', 'u0000003', 'ជិះ​ម៉ូតូ​អត់​ភ្លើង​អត់​កញ្ចក់​មិន​ឈប់​ស្តុ​ប​ត្រូវ​រថយន្ត​បុក​បែក​ចង្កា ចេញ​សមាគម​ន៍​ម៉ូតូ​កង់​បី​អន្តរាគមន៍​ប្រមាណ​៣​ម៉ោង​ទើប​បញ្ចប់', 'រាជធានី​ភ្នំពេញ​៖ បុរស​ម្នាក់​បាន​រង​របួស​បែក​ចង្កា ខណៈ​ពេល​ជិះ​ម៉ូតូ​គុប​ជ្រុង​ចាស់​គ្មាន​ស្លាក​លេខ គ្មាន​កញ្ចក់ និង​ភ្លើង​បំភ្លឺ​ផ្លូវ ថែម​ទាំង​មិន​ឈប់​ស្តុ​ប​ក្រហម​ទៀត​នោះ ជា​ហេតុ​ធ្វើ​ឲ្យ​រថយន្ត​រ៉​ង់​រ៉ូ​វ៉ែ មួយ​គ្រឿង​ជាន់​ហ្វ្រាំង​មិន​ទាន់​បុក​ចំពាក់​កណ្តាល​តែ​ម្តង', 'http://kohsantepheapdaily.com.kh/article/116765.html', 'http://i.kspg.co/2015/3/3aaf556e-5400-4aab-8936-e0463b45e241.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1143', 'cl000001', 'u0000003', 'សម័យកាល​អើយ​...កាល​នេះ​ន៎ែ !', 'សម័យកាល​អើយ​...​កាល​នេះ​ន៎ែ ! មន្ត្រី​មាន​អំណាច​ណា​ក្លាយជា​អ្នកមាន​កំពូល​ក៏​មាន​ទៅ​...​ក្លាយជា​សេដ្ឋី​ដោយសារ​ភារកិច្ច​ក៏​ជា​សេដ្ឋី​ទៅ​...​សម្ងំ​ធ្វើ​សេដ្ឋី​យ៉ាង​សម្បូរ​ណ៍​បែប​ទៅ​ហោង​...​។​ល​។', 'http://kohsantepheapdaily.com.kh/article/116839.html', 'http://i.kspg.co/2015/3/c2825304-2ac5-41c6-810a-5102b10b15d5.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1144', 'cl000001', 'u0000003', 'បាញ់ប្រហារ​ក្នុង​បារ​មួយ​នៅ​ស៊ុ​យ​អែ​ត​ស្លាប់​ច្រើន​នាក់', 'ប៉ូលិស​រាយការណ៍​ថា មានការ​បាញ់ប្រហារ​ដោយ​កាំភ្លើងស្វ័យប្រវត្តិ​ក្នុង​បារ​មួយ​បណ្តាល​ឱ្យ​មនុស្ស​ស្លាប់ និង​របួស​ជា​ច្រើន​នាក់​នៅ​ទីក្រុង GOTEBORG ភាគ​ខាងលិច​ប្រទេស​ស៊ុ​យ​អែ​ត', 'http://kohsantepheapdaily.com.kh/article/116894.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1145', 'cl000001', 'u0000003', 'រុ​ស្ស៊ី​បដិសេធ​បណ្តា​ច្បាប់​របស់​អ៊ុយ​ក្រែ​ន​ស្តី​ពី​លក្ខន្តិកៈ​ពិសេស​សម្រាប់​តំបន់​បំបែក​ខ្លួន', 'ប្រទេស​រុ​ស្ស៊ី​និង​ពួក​ឧទ្ទាម​ស្និទ្ធ​រុ​ស្ស៊ី​នៅ​ភាគ​ខាងកើត​អ៊ុយ​ក្រែ​ន​កាលពី​ថ្ងៃ​ទី​១៨ មីនា​បាន​បដិសេធ​ច្បាប់​ថ្មី​ៗ​ចុះហត្ថលេខា​នា​ថ្ងៃ​នោះ​ដោយ​ប្រធានាធិបតី​អ៊ុយ​ក្រែ​ន ដែល​ផ្តល់​ស្វ័យភាព​ថែម', 'http://kohsantepheapdaily.com.kh/article/116893.html', 'http://i.kspg.co/2015/3/8a982e41-7a30-46bb-a669-535329bd00c8.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1146', 'cl000001', 'u0000003', 'សវនាការ​កាត់ទោស​លោកស្រី​យី​ង​ឡាក់​កំណត់​ធ្វើ​ថ្ងៃ​19 ឧសភា', 'តុលាការ​កំពូល​ថៃ​អះអាង​កាលពី​ថ្ងៃ​ទី​១៩ មីនា​ថា អតីត​នាយក​រដ្ឋមន្ត្រី​ថៃ លោកស្រី​យី​ង​ឡាក់ ស៊ី​ណា​វ៉ា​ត្រា នឹង​ត្រូវ​កាត់ទោស​ពី​បទ​ធ្វេសប្រហែស ដោយ​កំណត់ពេល​បើក​សវនាការ​កាត់ទោស​', 'http://kohsantepheapdaily.com.kh/article/116892.html', 'http://i.kspg.co/2015/3/7caebbfe-2107-4c72-a9a9-f87457921585.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1147', 'cl000001', 'u0000003', 'បាតុកម្មហិង្សា​នៅ​អា​ល្លឺ​ម៉​ង់​ប៉ូលិស​88​នាក់​របួស រួច​មនុស្ស​ជាង​500​នាក់​ត្រូវ​ចាប់ខ្លួន', 'បណ្តាសារ​ព័ត៌មាន​ក្នុងស្រុក​រាយការណ៍​ថា ប៉ូលិស​យ៉ាងតិច​៨៨​នាក់​ត្រូវ​របួស រួច​មនុស្ស​ជាង​៥០០​នាក់​ត្រូវ​ចាប់ខ្លួន​ក្រោយ​បាតុកម្មហិង្សា​ប្រឆាំង​នឹង​វិធានការ​រឹតត្បិត​ដើម្បី​ស្តារ​សេដ្ឋកិច្ច​នៅ​ទីក្រុង', 'http://kohsantepheapdaily.com.kh/article/116875.html', 'http://i.kspg.co/2015/3/b0605e6a-75e8-4236-9ec4-ec72da0fb79d.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1148', 'cl000001', 'u0000003', 'វាយប្រហារ​ភេរវកម្ម​នៅ​ក្នុង​សារមន្ទីរ​មួយ​នៃ​ប្រទេស​ទុ​យ​នី​ស៊ី​ស្លាប់​22​នាក់​ក្នុង​នោះ​មាន​អ្នកទេសចរ​20​នាក់', 'បណ្តាសារ​ព័ត៌មាន​ក្នុងស្រុក​គូសបញ្ជាក់​ថា តុល្យការ​ផ្លូវការ​នៃ​ការ​វាយប្រហារ​ភេរវកម្ម កាលពី​ថ្ងៃ​ទី​១៨ មីនា ក្នុង​សារមន្ទីរ BARDO ស្ថិត​នៅ​ក្បែរ​សភា​ក្រុង​ទុ​យ​នី​ស រដ្ឋធានី​ទុ​យ​នី​ស៊ី​បណ្តាល', 'http://kohsantepheapdaily.com.kh/article/116873.html', 'http://i.kspg.co/2015/3/fff0e76d-8258-406b-b422-c12f1a55e547.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1149', 'cl000001', 'u0000003', 'អា​រ៉ា​ប៊ី សា​អ៊ូ​ឌី​ត គំរាម​លោត​ចូល​ក្នុង​ការ​ប្រណាំងប្រជែង​អាវុធ​នុយក្លេអ៊ែរ​នៅ​ម​ជ្ឈឹ​ម​បូព៌ា', 'ក្នុង​បទ​សម្ភាសន៍​មួយ​ជាមួយ​ទូរទស្សន៍ BBC ព្រះ​អង្គ​ម្ចាស់ TURKI AL-FAISAL នៃ​ប្រទេស​អា​រ៉ា​ប៊ី សា​អ៊ូ​ឌី​ត អតីត​អ្នក​ទទួលខុសត្រូវ​ខាង​ផ្នែក​សើ​ុ​ប​ការណ៍​របស់​ប្រទេស​នេះ បាន​ព្រមាន​ពួក​', 'http://kohsantepheapdaily.com.kh/article/116820.html', 'http://i.kspg.co/2015/3/1829a408-5446-4d4a-92dc-c269cfea62f5.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1150', 'cl000001', 'u0000003', 'បោះ​ឆ្នោត​សភា​អីុ​ស្រា​អែ​ល​គណបក្ស​លោក​នេ​តាន់​យ៉ា​ហ៊ូ​នាំមុខ​គេ', 'គណបក្ស​របស់​នាយក​រដ្ឋមន្ត្រី​ចប់​អាណត្តិ​អីុស្រា​អែ​ល លោក​បេន​ចា​មីន នេ​តាន់​យ៉ា​ហ៊ូ មាន​សំឡេង​នាំមុខ​គេ​ក្នុង​ការ​បោះ​ឆ្នោត​សភា​កាលពី​ថ្ងៃ​ទី​១៧ មីនា ។', 'http://kohsantepheapdaily.com.kh/article/116814.html', 'http://i.kspg.co/2015/3/186f67a1-863e-42cf-ae70-0b5a2c047c4e.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1151', 'cl000001', 'u0000003', 'លោក​ស៊ីជី​ន​ពីង​អំពាវនាវ​ឱ្យ​បង្កើន​ទំនុកចិត្ត​ខាង​យុទ្ធសាស្ត្រ​រវាង​ចិន​និង​អា​មេ​រិ​ក', 'ប្រធានាធិបតី​ចិន​លោក​ស៊ីជី​ន​ពីង កាលពី​ថ្ងៃ​ទី​១៧ មីនា បាន​អំពាវនាវ​ឱ្យ​បង្កើន​ទំនុកចិត្ត​ខាង​យុទ្ធសាស្ត្រ​និង​កិច្ច​សហប្រតិបត្តិការ​គ្នា​ថែម​ទៀត​រវាង​ចិន​និង​សហរដ្ឋអាមេរិក ។  ', 'http://kohsantepheapdaily.com.kh/article/116809.html', 'http://i.kspg.co/2015/3/0e9751ae-a6be-4f04-a6ca-050a434d6a4c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1152', 'cl000001', 'u0000003', 'វាយប្រហារ​លើ​ភូមិ​នី​ហ្សេ​រី​យ៉ា​មួយ​ស្លាប់​82​នាក់', 'ពួក​អ្នក​ចិញ្ចឹមសត្វ​នៃ​កុលសម្ព័ន្ធ FULANI កាន់សាសនា​អ៊ិ​ស្លា​ម កាលពី​ចុង​ស​ប្តា​ហ៍​កន្លង​មកនេះ​បាន​វាយប្រហារ​លើ​ភូមិ AGATU IGA របស់​ពួក​គ្រី​ស្ត​នៅ​រដ្ឋ​បេ​នូ ភាគ​កណ្តាល​នី​ហ្សេ​រី​យ៉ា ', 'http://kohsantepheapdaily.com.kh/article/116807.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1153', 'cl000001', 'u0000003', 'បញ្ចប់​ការ​ចរចា​វគ្គ​3​គុយ​បា​និង​អា​មេ​រិ​ក​ព្រមព្រៀង​រក្សា​ការ​ទាក់ទង​គ្នា', 'សេចក្តី​ប្រកាស​ព័ត៌មាន​មួយ​ចេញផ្សាយ​កាលពី​ថ្ងៃ​ទី​១៧ មីនា ដោយ​ក្រសួងការបរទេស​គុយ​បា​គូសបញ្ជាក់​ថា ដោយ​មាន​បំណង​ស្តារ​ឡើង​វិញ​នូវ​ទំនាក់ទំនង​ការទូត ការ​ចរចា​វគ្គ​ទី​៣ រវាង​គុយ', 'http://kohsantepheapdaily.com.kh/article/116799.html', 'http://i.kspg.co/2015/3/758ff73f-95db-421c-9387-282a07ccf3a8.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1154', 'cl000001', 'u0000003', 'កងទ័ពអាកាស​ស៊ី​រី​អះអាង​ពី​ការ​បាញ់​ធ្លាក់​យន្តហោះ​គ្មាន​មនុស្ស​បើក​អា​មេ​រិ​កមួយ', 'សារព័ត៌មាន​ផ្លូវការ​ស៊ី​រី Sana អះអាង​កាលពី​ថ្ងៃ​ទី​១៧ មីនា ថា កងទ័ពអាកាស​ស៊ី​រី បាន​បាញ់​ធ្លាក់​យន្តហោះ​គ្មាន​មនុស្ស​បើក​១​គ្រឿង​របស់​អា​មេ​រិ​ក ដែល​ហោះហើរ​លើ​តំបន់ Lattaquie ', 'http://kohsantepheapdaily.com.kh/article/116796.html', 'http://i.kspg.co/2015/3/e8ab89eb-9bb7-4436-9ba5-caca3d80c046.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1155', 'cl000001', 'u0000003', 'ផ្ទៃដី​ដាំ​ស្រូវ​បង់​ក្លា​ដែ​ស​លើស​ទិស​ដៅ​រដ្ឋាភិបាល​ជាង​4​ម៉ឺន​ហិ​កតា​ឆ្នាំ​2015', 'ក្រុង​ដា​ក្កា ៖ ប្រភព​ក្នុងស្រុក​បាន​ដកស្រង់​ទិន្នន័យ​ពី​នាយកដ្ឋាន​បច្ចេកទេស​កសិកម្ម​ឱ្យ​ដឹង​ថា ផ្ទៃដី​បង្កបង្កើន​ផល​ស្រូវ​សម្រាប់​ខែមករា​ដល់​ឧសភា​ឆ្នាំ​២០១៥ របស់​ប្រទេស​បង់​ក្លា​ដែ​ស', 'http://kohsantepheapdaily.com.kh/article/116882.html', 'http://i.kspg.co/2015/3/b5d922b9-a469-403d-b082-b4391f48aabb.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1156', 'cl000001', 'u0000003', '2​ខែ​ដំបូង​ប្រេ​ស៊ី​ល​នាំ​ចេញ​អង្ករ​បាន​ជាង​11​ម៉ឺន​តោន', 'ក្រុង​ប្រា​ស៊ី​លី​យ៉ា ៖ យោង​តាម​ទិន្នន័យ​ចេញផ្សាយ​ដោយ​គ្រឹះស្ថាន​អង្ករ​ប្រេ​ស៊ី​ល(IRGA) ឱ្យ​ដឹង​ថា ប្រទេស​ប្រេ​ស៊ី​ល​បាន​នាំ​ចេញ​អង្ករ​ចំនួន​១១៩.៨២៨​តោន ក្នុង​អំ​ទ្បុ​ង​ពេល​២​', 'http://kohsantepheapdaily.com.kh/article/116881.html', 'http://i.kspg.co/2015/3/ce3e783b-396e-4469-9b81-afb361b139d1.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1157', 'cl000001', 'u0000003', 'ព្រុយ​ណេ​អាច​មិន​សម្រេច​ទិស​ដៅ​ប្រើ​អង្ករ​ផលិត​ខ្លួនឯង​60% ឆ្នាំ​នេះ', 'ប្រភព​ក្នុងស្រុក​បាន​ដកស្រង់​សម្តី​រដ្ឋមន្ត្រី​ឧស្សាហកម្ម​ឱ្យ​ដឹង​ថា ផលិតកម្ម​អង្ករ​ព្រុយ​ណេ​បង្កើន​បាន​ក្នុង​រង្វង់​១.៣៨០​តោន​ក្នុង​ឆ្នាំ​២០១៤ កើនឡើង​១២% ពី​បរិមាណ​១.២៣០​តោន​ដែល​ផលិត​', 'http://kohsantepheapdaily.com.kh/article/116877.html', 'http://i.kspg.co/2015/3/45023940-8f59-4fda-816c-5617d1fa0c60.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1158', 'cl000001', 'u0000003', 'កីឡាករ​ល្បី​ៗ​លេចមុខ​ក្នុង​ខ្សែភាពយន្ត​ខ្នាត​ធំ', 'រាជធានី​ភ្នំពេញ ៖ តាំងពី​ពាក់កណ្ដាល​ខែមីនា​មក​ម្ល៉េះ​ដែល​ខ្សែភាពយន្ត​ខ្នាត​ធំ​ក្រោម​ចំណងជើង​ថា «​ល្បុក្កតោ​ចម្លែក​» ចាប់​ដំណើរ​ចេញ​ថត​ដោយ​ប្រមូលផ្ដុំ​នូវ​កីឡាករ​ចាស់​ថ្មី​ល្បីឈ្មោះ​ជា​ច្រើន​រូប​ឲ្យ​ធ្វើ​ជា​តួ​សម្ដែង ', 'http://kohsantepheapdaily.com.kh/article/116890.html', 'http://i.kspg.co/2015/3/fe984ef4-d161-4198-a6da-a7aeb62627f6.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1159', 'cl000001', 'u0000003', 'សមាសភាព​គូប្រកួត​ប្រដាល់​នៅ​លើ​សង្វៀន​ខេត្តសៀមរាប​ថ្ងៃនេះ', 'ខេត្តសៀមរាប ៖ នៅ​លើ​សង្វៀន​ស៊ី​ធី​អិន​ខេត្តសៀមរាប​នា​ថ្ងៃ​ពុធ ទី​១៨ ខែមីនា​នេះ អ្នក​ប្រដាល់​ជើង​ចាស់ និង​ជើង​ថ្មី​ដែល​មាន​ចំនួន​១០​រូប​នឹង​ឡើងសាក​កម្លាំង​គ្នា​ជូន​ទស្សនិកជន​បាន​ទស្សនា​កម្សាន្ត​ទៀត​ហើយ', 'http://kohsantepheapdaily.com.kh/article/116886.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1160', 'cl000001', 'u0000003', 'ក្រុមកីឡា​វាយ​កូន​បាល់​ជម្រើសជាតិ​កម្ពុជា​5​រូប​ត្រៀម​ចេញ ទៅ​ចូលរួម​ប្រកួត DAVIS CUP នៅ​ម៉ា​ឡេ​ស៊ី​ថ្ងៃស្អែក', 'រាជធានី​ភ្នំពេញ ៖ តាម​រយៈ​សន្និសីទកាសែត​ដែល​បាន​ធ្វើ​ឡើង​នៅ​សណ្ឋាគារ​ណា​ហ្គា​វើ​ល កាលពី​ថ្ងៃ​ពុធ ទី​១៨ មីនា កន្លង​មកនេះ​ឱ្យ​ដឹង​ថា សហព័ន្ធ​កីឡា​វាយ​កូន​បាល់​កម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/116885.html', 'http://i.kspg.co/2015/3/851379a8-fa9f-4367-b4f8-3c7cc78ab6d0.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1161', 'cl000001', 'u0000003', 'ម៉​ន ភារ​ម្យ និង​ផា​ន់ គ្រាន់ រង់ចាំ​ស្វាគមន៍​ថៃ​នៅ​លើ​សង្វៀន SEATV', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ថ្ងៃអាទិត្យ ទី​២២ មីនា នា​ស​ប្ដា​ហ៍​នេះ កីឡាករ​កម្ពុជា​២​រូប គឺ ម៉​ន ភារ​ម្យ និង ផា​ន់ គ្រាន់ ដែល​សុទ្ធតែ​ជា​ជើង​ខ្លាំង​នៅ​លើ​សង្វៀន​ក្នុងស្រុក​នឹង​រង់ចាំ​ទទួល​ស្វាគមន៍​កីឡាករ​ជើង​ខ្លាំង​មក​ពី​ថៃ​', 'http://kohsantepheapdaily.com.kh/article/116883.html', 'http://i.kspg.co/2015/3/362f7fc7-660e-45b9-8fa5-978c8838496d.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1162', 'cl000001', 'u0000003', 'រឿង សោ​ភ័​ណ្ឌ ជួប​ប៊ុល​ហ្គា​រី ចំណែក​បឹង កា​យ៉ាក ជួប​ថៃ​នៅ​លើ​រេញ​ទូរទស្សន៍​បុ​៉​ស្តិ៍​លេខ​3', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​ប្រដាល់​ជា​លក្ខណៈ​មិត្តភាព​អន្តរជាតិ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​រាជធានី​ភ្នំពេញ​បុ​៉​ស្តិ៍​លេខ​៣ នា​ថ្ងៃអាទិត្យ ទី​២២ ខែមីនា ខាង​មុខ​នេះ រឿង សោ​ភ័​ណ្ឌ មក​ពី​ក្លិប​មកុដ​រាជសីហ៍ ត្រូវ​បាន', 'http://kohsantepheapdaily.com.kh/article/116878.html', 'http://i.kspg.co/2015/3/d5e659b7-e285-4f2d-b677-e58bc95f9411.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1163', 'cl000001', 'u0000003', 'កម្ពុជា​បួងសួង​សូម​ឱ្យ​គេច​ផុត​ពី​ក្រុម​ខ្លាំង​ក្នុង​ការ​ប្រកួត​បន្ត​ក្រោយ​ឈ្នះ​ម៉ា​កាវ', 'រាជធានី​ភ្នំពេញ ៖ បន្ទាប់​ពី​យកឈ្នះ​លើ​ក្រុម​ម៉ា​កាវ​ក្នុង​លទ្ធផល​៤-១ សម្រាប់​ការ​ប្រកួត​ទាំង​ពីរ​ជើង ក្រុមបាល់ទាត់​របស់​ប្រទេស​កម្ពុជា នឹង​បាន​បោះ​ជំហាន​ទៅ​កាន់ការ​ប្រកួត​ក្នុង​ជុំ​ទី​២ ជា​បន្ត​ជាមួយ​ក្រុម​ប្រទេស​', 'http://kohsantepheapdaily.com.kh/article/116876.html', 'http://i.kspg.co/2015/3/a64b74c6-6525-49d5-8891-89756dadb8af.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1164', 'cl000001', 'u0000003', 'NOCC អំពាវនាវ​ដល់​អ្នកចេះ​លេងអុក​ឱ្យមក​ចុះ​ឈ្មោះ​ចូលរួម​ប្រកួត​ឱ្យ​បាន​ច្រើន​ខណៈ​ការ​ប្រកួត​ជិត​ចាប់ផ្តើម​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ ខណៈ​ការ​ប្រកួត​អុកចត្រង្គ​អបអរ​អង្គរ​សង្ក្រាន្ត​លើក​ទី​២ ឆ្នាំ​២០១៥ វគ្គ​ជម្រុះ​ជុំ​ទី​១ ដែល​នឹង​ចាប់ផ្តើម​ធ្វើ​ឡើង​នៅ​ថ្ងៃ​ទី​២៦-២៧ និង​២៨ ខែមីនា ខាង​មុខ​នៅ​ទីស្នាក់ការ​គណៈកម្មាធិការ​ជាតិ​អូ​ឡាំ​ពិ​ក', 'http://kohsantepheapdaily.com.kh/article/116874.html', 'http://i.kspg.co/2015/3/e6835cdd-b4e8-4ec4-a67f-5bb1ef3ddf8c.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1165', 'cl000001', 'u0000003', 'ការ​ប្រកួត​បាល់ទាត់​និង​បាល់បោះ​ពានរង្វាន់ NTC Group ជិត​ឈាន​ចូល​ដល់ទី​បញ្ចប់​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​កីឡាបាល់ទាត់ និង​បាល់បោះ​ដណ្តើម​ពានរង្វាន់ NTC Group ប្រចាំឆ្នាំ​២០១៥ មក​ដល់​ពេល​នេះ​ជិត​ឈាន​ចូល​ដល់ទី​បញ្ចប់​ហើយ ។ ជាក់ស្តែង នៅ​ក្នុង​ការ​ប្រកួត​ដែល​បាន​ធ្វើ​ឡើង​នៅ', 'http://kohsantepheapdaily.com.kh/article/116872.html', 'http://i.kspg.co/2015/3/f1296caa-2fb7-4012-b202-e3fb3e023ec8.jpg?w=285&h=170&mode=carve', '20150319');
