INSERT INTO "tbnews" VALUES ('1168', 'cl000001', 'u0000003', 'សូមទៅទទួលស្វាគមន៍​ក្រុមកីឡាបាល់ទាត់​ជម្រើសជាតិកម្ពុជា​ដែលនឹងមកដល់​ព្រលានយន្តហោះ​នៅម៉ោងជាង​៤រសៀលនេះ', 'រាជធានីភ្នំពេញ ​៖ បើតាមបណ្តាញ​ទំនាក់ទំនងសង្គម​(Facebook)របស់​សហព័ន្ឋកីឡាបាល់ទាត់​កម្ពុជាបានឲ្យដឹងថា​ ក្រុមកីឡាបាល់ទាត់​​ជម្រើសជាតិ​', 'http://kohsantepheapdaily.com.kh/article/116794.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1169', 'cl000001', 'u0000003', 'បងស្រី​រ៉ូ​ណា​ល់​ដូ​សម្តែង​ការ​រីករាយ​ចំពោះ​ភាព​នៅ​លីវ​របស់​ប្អូន', 'នាង​អេ​ល​ម៉ា បងស្រី​របស់​កីឡាករ​គ្រី​ស្ទៀ​ណូ រ៉ូ​ណា​ល់​ដូ កំពូល​កីឡាករ​ក្លិប Real Madrid បង្ហើប​ឱ្យ​ដឹង​ថា បច្ចុប្បន្ន​មនុស្ស​គ្រប់​រូប​ក្នុង​គ្រួ​សារបាន​បំភ្លេច​នាង​អីុ​រី​ណា​សេក អតីត​គូស្នេហ៍​ដ៏​ស្រស់​ស្អាត', 'http://kohsantepheapdaily.com.kh/article/116851.html', 'http://i.kspg.co/2015/3/b7672dea-a7fb-4008-853f-31ddc5db6ceb.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1170', 'cl000001', 'u0000003', 'ការ​ទស្សន៍​ទាយ​បាល់ទាត់​ចម្រុះ​នៅ​អឺរ៉ុប​សម្រាប់​ថ្ងៃនេះ​មាន​5​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​តំបន់​អឺរ៉ុប​សម្រាប់​ថ្ងៃ​ព្រហស្បតិ៍​នេះ មាន 5 ប្រកួត ។ ក្រុម Olympiakos ត្រូវការ​ឈ្នះ Asteras ដើម្បី​រក្សា​ចំណាត់ថ្នាក់​កំពូល។', 'http://kohsantepheapdaily.com.kh/article/116848.html', 'http://i.kspg.co/2015/3/18d757ac-44e5-4530-8cec-886282270659.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1171', 'cl000001', 'u0000003', 'បាល់ទាត់​យូ​រ៉ូ​ប៉ា​លី​ក​វគ្គ​16​ក្រុម​ជើង​ទី​2', 'ការ​ប្រកួត​បាល់ទាត់​យូ​រ៉ូ​ប៉ា​លី​ក​វគ្គ 16 ក្រុម​ចុង​ក្រោយ​ជើង​ទី 2 សម្រាប់​រាត្រី​ថ្ងៃ​ព្រហស្បតិ៍​នេះ​មាន 8 ប្រកួត ។ Dinamo Mos ត្រូវការ​ឈ្នះ Napoli ក្នុង​ទឹកដី ខណៈ Ajax ប៉ះ​ក្រុម​មិន​ដែល​ចាញ់ Dnipro ។', 'http://kohsantepheapdaily.com.kh/article/116846.html', 'http://i.kspg.co/2015/3/8d074e2c-46ff-42f4-8aa7-5a06d329bd20.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1172', 'cl000001', 'u0000003', 'Atletico ត្រៀម​ប្រមាញ់​ហ្វ៊ី​លី​ប ឡូ​វី​ស​ពី Chelsea', 'ក្លិប​បាល់ទាត់​អេ​ស្ប៉ា​ញ Atletico ត្រៀម​ប្រមាញ់ កីឡាករ ហ្វ៊ី​លី​ប ឡូ​វី​ស សញ្ជាតិ​ប្រេ​ស៊ី​ល វ័យ​២៩​ឆ្នាំ ពី​ក្លិប​តោ​ខៀវ Chelsea បន្ទាប់​ពី​យល់​ឃើញ​ថា កីឡាករ​ល្អ​ៗ​របស់​ខ្លួន​ចំនួន​៣​រូប', 'http://kohsantepheapdaily.com.kh/article/116818.html', 'http://i.kspg.co/2015/3/972bb8e1-d068-4b68-ab9c-629bc87840d7.jpg?w=285&h=170&mode=carve', '20150319');
