INSERT INTO "tbnews" VALUES ('1175', 'cl000001', 'u0000003', 'ស៊ី​ធី​អិន​នាំ​តារា​សម្តែង​ថ្មី​ចាស់​ជិត​៣០០​នាក់ ថត​កម្មវិធី​ច្រៀង​រាំ​អបអរ​សារ​ទរ​ពិធី​បុណ្យចូលឆ្នាំ​ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ តារា​សម្តែង​ចាស់​ថ្មី​ស្រស់​សោភា​ជា​ច្រើន​រូប​មាន​ចំនួន​ជិត​៣០០​នាក់ បាន​ចូលរួម​ថត​កម្មវិធី​បុណ្យចូលឆ្នាំ​ថ្មី ឆ្នាំមមែ ដោយ​ម្នាក់​ៗ​មាន​ទឹកមុខ​ស្រស់ស្រាយ​ជាមួយ​ឈុត​សម្លៀក​បំពាក់', 'http://kohsantepheapdaily.com.kh/article/116773.html', 'http://i.kspg.co/2015/3/f5a91015-56d3-4c9f-81c7-00e812faab9a.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1176', 'cl000001', 'u0000003', 'រូបថត​ដ៏​កម្រ​នៃ​ក្រុងភ្នំពេញ និង​អង្គរវត្ត​ថត​នៅ​ឆ្នាំ​1929ជា​ស្នាដៃ​សិល្បករ​បរទេស​ម្នាក់​ពេល​មក​សម្តែង​នៅ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ការ​តាំងពិព័រណ៍​ក្នុង​ខែមីនា ឆ្នាំ​២០១៥ នៅ​សណ្ឋាគារ​សូ​ហ្វ៊ី​តែ​ល​ភ្នំពេញ ភូ​គី​ត្រា រៀបចំ​ដោយ​សណ្ឋាគារ​នេះ​បង្ហាញ​អំពី​រូបថត​នៃ​ទីក្រុង​ភ្នំពេញ និង​ប្រាសាទអង្គរវត្ត​ដែល​គេ​ថត​', 'http://kohsantepheapdaily.com.kh/article/116778.html', 'http://i.kspg.co/2015/3/41e54a31-8798-439a-97c4-a508f38562a9.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1177', 'cl000001', 'u0000003', 'ភ្ញៀវ​ទេសចរណ៍​ជាតិ​និង​អន្តរជាតិ​ទស្សនា​ប្រាសាទព្រះវិហារ​មានការ​កើនឡើង​ជាង​ឆ្នាំ​២០១៣', 'ខេត្ត​ព្រះ​វិហារ ៖ ប្រាសាទព្រះវិហារ​ទទួលភ្ញៀវ​ទេសចរណ៍​ជាតិ និង​អន្តរជាតិ​ចូល​ទៅ​ទស្សនា​ជា​ច្រើន​នាក់​ក្នុង​មួយ​ថ្ងៃ​ៗ​ក្នុង​ឆ្នាំ​២០១៤ បើ​ប្រៀបធៀប​ទៅ​នឹង​ឆ្នាំ​២០១៣​មានការ​កើនឡើង​គួរ​ឲ្យ​កត់សម្គាល់ ។', 'http://kohsantepheapdaily.com.kh/article/116870.html', 'http://i.kspg.co/2015/3/46701546-a284-46e5-95b6-c418fb73b328.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1178', 'cl000001', 'u0000003', 'រឿងរ៉ាវ​ប្លែក​ៗ​គួរ​ឱ្យ​ចាប់អារម្មណ៍​នៅ​លើ​ពិភពលោក', '១-​ប្រព្រឹត្ត​ខុសច្បាប់​ត្រូវ​ពិន័យ រដ្ឋ​ញូ​វ​យ៉​ក​គ្រោង​ចេញ​ច្បាប់​ចំពោះ​ម្ចាស់​សត្វ​ដែល​យក​សត្វ​ចិញ្ចឹម​របស់​ខ្លួន​ទៅ​សាក់ ត្រូវ​ពិន័យ​៣០០​ដុល្លារ និង​ជាប់​ពន្ធនាគារ​១៥​ថ្ងៃ ដោយ​ចាត់​ទុក​ថា​ធ្វើ​', 'http://kohsantepheapdaily.com.kh/article/116775.html', 'http://i.kspg.co/2015/3/f0b2f88b-3ea5-4095-a387-62f4d3f69745.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1179', 'cl000001', 'u0000003', 'ក្រុម​​យុវជន​សម្ព័ន្ធ​​សេនា​នាគ​​នាំ​យក​អំណោយ​​ចែក​ជូន​ប្រជាជន​​ក្រីក្រ​២០០​​គ្រួសារ', 'រាជ​ធានីភ្នំពេញ ៖ ​ក្រុម​យុវជន​សម្ព័ន្ធ​សេនា​​នាគ​ដែល​ដឹកនាំ​ដោយ​លោក​ឌៀង​ សា​រុន ​សមាជិក​គ​ណៈ​​ក​ម្មា​​ធិ​ការ​កណ្តាល​​គណបក្ស​ប្រជា​ជន​កម្ពុជា ​កាលពី​ថ្ងៃ​ទី​​១៧ មីនា ​២០១៥​ នេះ​ នៅ​ស្នាក់​ការ​បក្ស​​សង្កាត់​ព្រែក​ហូរ ​បាន​នាំ​យក​អំណោយ​មួយ​ចំនួន​រួម​​មាន​គ្រឿង​', 'http://kohsantepheapdaily.com.kh/article/116869.html', 'http://i.kspg.co/2015/3/3a1586cf-4d2c-43eb-b42a-8bae68276e6e.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1180', 'cl000001', 'u0000003', 'ក្រុមហ៊ុន​ធានា​រ៉ាប់រង​ហ្វ​ត​តេ និង​អ៊ី​អន ម៉ា​យ​ក្រូ​ហ្វា​យ​នែ​ន សហការ​ផ្ដល់​គម្រោង​សេវា​ធានា​រ៉ាប់រង​បង់​រំលស់​ដោយ​មិន​គិតការ​ប្រាក់', 'រាជធានី​ភ្នំពេញ ៖ ក្រុមហ៊ុន​ធានា​រ៉ាប់រង ហ្វ​ត​តេ បាន​ចាប់​ដៃគូ​ជាមួយ​អ៊ី​អន ម៉ា​យ​ក្រូ​ហ្វា​យ​នែ​ន បង្កើត​ភាព​ងាយស្រួល​សម្រាប់​អតិថិជន ដើម្បី​ទទួល​សេវា​ធានា​រ៉ាប់រង​ដោយ​បង់​រំលស់', 'http://kohsantepheapdaily.com.kh/article/116896.html', 'http://i.kspg.co/2015/3/e11dde97-73b8-486f-bfff-4462140c315f.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1181', 'cl000001', 'u0000003', 'ពិធី​ប្រគល់​​រង្វាន់​កម្មវិធី «​ប្រមាញ់​រូបភាព​ម៉ូតូ​កង់​បី​តូ​តា​ល់​» ប្រចាំខែ​មក​រា 2015', 'ការ​កើនឡើង​ឥត​ឈប់​ឈរ​នៃ​ចំនួន​អ្នកគាំទ្រ Facebook Page តូ​តា​ល់ កាន់តែ​ច្រើន​នៅ​ក្នុង​រយៈពេល ដ៏​ខ្លី​ស្រប ជាមួយនឹង​ការ​កើនឡើង​គួរ​ឲ្យ​កត់សម្គាល់​នៃ​ចំនួន​រូបភាព​ម៉ូតូ​កង់​បី​ដែល​ត្រូវ​បាន​បង្ហោះ​ដោយ អ្នកចូលរួម​ជា​ច្រើន​នាក់​នៅ​លើ Page របស់ តូ​តា​ល់ ', 'http://kohsantepheapdaily.com.kh/article/116895.html', 'http://i.kspg.co/2015/3/f7d8a5c5-7bad-4ac6-ac49-a0685b890388.jpg?w=285&h=170&mode=carve', '20150319');
INSERT INTO "tbnews" VALUES ('1182', 'cl000001', 'u0000003', 'ម៉ាស៊ីន​បោក​សម្លៀក​បំពាក់​Samsung Activ Dual Wash សម្ភោ​ធ​ដាក់​លក់​ជា​ផ្លូវការ​ក្រោម​វត្តមាន​តារា​ល្បី', 'រាជធានី​ភ្នំពេញ​៖ ម៉ាស៊ីន​បោក​សម្លៀក​បំពាក់​Samsung Activ Dual Wash​បើក​សម្ភោ​ធ​ដាក់​លក់​ជា​ផ្លូវការ​ក្រោម​វត្តមាន​តារា​ល្បី​ប្រចាំ​កម្ពុជា លោក ឆ​ន​សុវណ្ណ​រាជ​និង​កញ្ញា មាន សូ​នី​តា ', 'http://kohsantepheapdaily.com.kh/article/116871.html', 'http://i.kspg.co/2015/3/e1416d82-a01f-4093-9641-3743b3747f10.jpg?w=285&h=170&mode=carve', '20150319');
