INSERT INTO "tbnews" VALUES ('1222', 'cl000001', 'u0000002', 'ជម្រើស​ជាតិ​ឈុត​ធំ​មក​ដល់​កម្ពុជា​ល្ងាច​មិញ អត់​មាន​អ្នក​ស្វាគមន៍​', 'ក្រុម​បាល់​ទាត់​ជម្រើស​ជាតិ​កម្ពុជា​ឈុត​ធំ​ចំនួន​១១​នាក់​បាន​ដល់​ប្រទេស​កំណើត​កាល�:�ពី​វេលា​ម៉ោង​​៤:៣០​នាទី​ល្ងាច​ម្សិល​មិញ​ បន្ទាប់​ពី​បាន​បំពេញ​ភារកិច្ច​នៅ​កោះ​ម៉ាកាវ​រួច​រាល់​​ ខណៈ​កីឡាករ​អាយុ​ក្រោម​២២​ឆ្នាំ​ គ្រូ​បង្វឹក ​និង​ថ្នាក់​ដឹក​នាំ​របស់​សហព័ន្ធ​បាន​បន្ត​ដំណើរ​ពី​ម៉ា�&ាវ​ទៅ​ប្រទេស​សិង្ហបុរី​ដើម្បី​ប្រកួត​មិត្ត​ភាព​ថែម​ទៀត។ &nbsp; ​ ការ​វិល​ត្រឡប់​មក​ដល់​មាតុ​ប្រទេសរបស់​កីឡាករ​ទាំង​១១​នាក់​ ​អ្នក​ថត​រូប​ម្នាក់ និង​ថ្នាក់​ដឹក​នាំ​លោក ជួន ណារិន្ទ​កាល​ពី​​​ល្ងាច​ម្សិល​មិញ​ហាក់​ស្ងប់ស្ងាត់', 'http://news.sabay.com.kh/article/310566', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/03/Featured-NT-arriedhome-285x170.jpg?0bb167', '20150319');
INSERT INTO "tbnews" VALUES ('1223', 'cl000001', 'u0000002', 'កែវ រំចង់ មិន​ហ៊ាន​សន្យា​ឈ្នះ​​ពេល​ប៉ះ​ជើង​ខ្លាំង​មិន​ស្គាល់​ប្រវត្តិ​', ' កែវ​ រំចង់​ មិន​ហ៊ាន​សន្យា​ឈ្នះ​មុន​ការ​ប្រកួត​មក​ដល់​​ពេល​ប៉ះ​ជើង​ខ្លាំង​មិន​ស្គាល់​ប្រវត្តិ​ Sarm Dieng Petch Nop Kav...', 'http://news.sabay.com.kh/article/310809', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/03/featuredkeo-285x170.jpg?0bb167', '20150319');
INSERT INTO "tbnews" VALUES ('1224', 'cl000001', 'u0000003', '​កែ​​ច្នៃម្សៅ TNT បម្រុង​​បំផ្ទុះ​​ចាប់​ត្រី ​​ត្រូវសមត្ថ​កិច្ច​ចាប់​ខ្លួន​​​', 'ខេត្តស្ទឹងត្រែង ​៖ បុរស​ម្នាក់​ត្រូវ​​​មន្ត្រី​បរិ​ស្ថា​នុ​រក្ស ​និង​កម្លាំង​នគរបាល​​ប៉ុស្តិ៍​រដ្ឋបាល​ឃុំ​អូរ​ស្វាយ​ចាប់ខ្លួន​ រួម​ទាំង​វត្ថុ​តាង​ ​បន្ទាប់​ពី​ធ្វើ​សកម្ម​ភាព​បំផ្ទុះ​​គ្រាប់​បែក​កែ​ច្នៃ​​ទៅ​ក្នុង​អន្លង់អភិរក្ស​ ដើម្បី​ចាប់​ត្រី​ ។ ', 'http://kohsantepheapdaily.com.kh/article/116994.html', 'http://i.kspg.co/2015/3/218a3740-194b-4f3d-9fc3-cc1f73e305fe.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1225', 'cl000001', 'u0000003', '៣​ថ្ងៃ​ហើយ​ក្រុង​កំពង់ចាម​ដាច់​ទឹក ខណៈ​អណ្តូង​រីងមិន​អាច​បូម​បាន', 'ខេត្តកំពង់ចាម ​៖ អ្នកក្រុង​កំពង់ចាម​រាប់​ពាន់​គ្រួសារបាន​ជួប​នឹង​បញ្ហា​កង្វះ​ទឹក​ស្អាតសំរាប់​ប្រើប្រាស់ រហូត​មក​ដល់​ព្រឹក​ថ្ងៃ​ទី​២១ មីនា ២០១៥​នេះ គឺ​មាន​រយៈពេល​ចំនួន​៣​ថ្ងៃ​ហើយ ដោយសារ​តែ​ទឹកអណ្តូង​ជា​ប្រភព​ចេញ ', 'http://kohsantepheapdaily.com.kh/article/116989.html', 'http://i.kspg.co/2015/3/e08702c1-994a-4516-9a22-d742aeef9b08.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1226', 'cl000001', 'u0000003', 'គណបក្ស​នយោបាយ សង្គម​ស៊ី​វិល និង​អ្នក​សារព័ត៌មាន ​ពិភាក្សា​លើ​ច្បាប់បោះឆ្នោត ច្បាប់​ប្រព្រឹត្ត​​ (​​គជ​ប)​', 'ខេត្ត​ព្រះ​សីហ​នុ​ ៖ ច្បាប់​បោះឆ្នោត​ថ្មី ច្បាប់​ស្តី​ពី​ការ​ប្រព្រឹត្ត​ទៅ​គណៈកម្មការ​ជាតិ​រៀបចំ​ការ​បោះ​នៅ​តែ​ជា​បញ្ហា​ក្តៅ​សម្រាប​គណបក្ស​នយោបាយ និង​សង្គម​ស៊ី​វិល​ដែល​បាន​តាមដាន​នៃ​ការ​អនុវត្ត​ល្បែង​ប្រជាធិបតេយ្យ​នៃ​ប្រទេស​កម្ពុជា​ក្នុង​ការ​គ្រប់គ្រង ដឹកនាំ​តាម​បែប', 'http://kohsantepheapdaily.com.kh/article/116988.html', 'http://i.kspg.co/2015/3/e6319824-aded-4b00-8245-93160b3833cf.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1227', 'cl000001', 'u0000003', 'ការ​សិក្សា​ថ្មី ៖ ក្តី​រំពឹង​តិចតួច​ពី​ការ​កាត់​បន្ថយ​ការ​ខាតបង់​សេដ្ឋកិច្ច​ពី​គ្រោះ​មហន្តរាយ', 'ទីក្រុង​សេន​ដាយ ប្រទេស​ជប៉ុន ៖ លទ្ធផល​បឋម​នៃ​ការ​សិក្សា​នូវ​ម៉ូ​ដែល​គ្រោះ​មហន្តរាយ​បាន​លើកយក​មក​បង្ហាញ​នៅ​ថ្ងៃ​បិទ​បញ្ចប់​នៃ​ស​និ្ន​សី​ទ​ពិ​ភ​លោក​លើក​ទី​៣​របស់​អង្គការសហប្រជាជាតិ ស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ កាលពី​ល្ងាច​ថ្ងៃ​ទី​១៨ ', 'http://kohsantepheapdaily.com.kh/article/116987.html', 'http://i.kspg.co/2015/3/86871794-6ad8-49a3-9801-294729041b18.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1228', 'cl000001', 'u0000003', 'សន្និសីទ​ពិភពលោក​​លើក​ទី​៣​​បាន​បញ្ចប់​ដោយ​កិច្ចព្រម​ព្រៀង​ជា​​អន្តរជាតិ​​ថ្មី​​១', 'ទីក្រុង​សេន​ដាយប្រទេស​ជប៉ុន ៖ សន្និសីទ​ពិភព​លើ​ទី​៣​របស់​អង្គការសហប្រជាជាតិស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ ត្រូវ​បាន​បញ្ចប់​កាលពី​ល្ងាច​ថ្ងៃ​ទី​១៨ មីនា ដោយ​មាន​កិច្ចព្រមព្រៀង​ជា​អន្តរជាតិ​ថ្មី​មួយ​បាន​ទៅ​ដល់​ថ្នាក់ដឹកនាំ​ពិភពលោក ដែល​បាន​', 'http://kohsantepheapdaily.com.kh/article/116986.html', 'http://i.kspg.co/2015/3/da37d852-a493-4ff7-9ce2-c4d7e0568d49.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1229', 'cl000001', 'u0000003', 'ស្ត្រីម្នាក់ធ្វើដំណើរមក​ពិនិត្យ​ផ្ទៃពោះ ស្រាប់តែ​ឈឺពោះសម្រាលលើ​រថយន្ត​', 'ខេត្តកំពង់ធំ ៖ មាន​​ហេតុការណ៍​​​ភ្ញាក់ផ្អើល​មួយ​ ​​កាលពី​​វេលាម៉ោង​​១១​ព្រឹក​ថ្ងៃ​ទី​២០ មីនា ​២០១៥ ​បន្ទាប់​ពី​ស្ដ្រី�ម្នាក់​​ឈ្មោះ​គន់ ឡៃ​ហ៊ី  អាយុ​​១៩​​ឆ្នាំ រស់នៅ​ភូមិ​វាល​​វែង ឃុំ​ឈូក ស្រុកប្រាសាទ​សំបូរ ខេត្តកំពង់ធំ​ ខណៈកំពុង​ធ្វើ​ដំណើរ​តាម​​រថយន្តឈ្នួល​ មក​កាន់​មន្ទីរ​', 'http://kohsantepheapdaily.com.kh/article/116985.html', 'http://i.kspg.co/2015/3/1611a986-13cb-47d4-91f1-7bb2cecd482a.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1230', 'cl000001', 'u0000003', 'ឥស្សរជន​ជាន់ខ្ពស់ លោក​ឧកញ៉ា លោក​ជំទាវ ចូលរួម​ជូនពរ​ដល់​អ្នកឧកញ៉ា គិត ម៉េ​ង', 'រាជធានី​ភ្នំពេញ ៖ អ្នកឧកញ៉ា គិត ម៉េ​ង ដែល​ពលរដ្ឋ​គ្រប់​រូប​ទទួលស្គាល់​ថា ជា​សេដ្ឋី​ប្រាក់​លាន បាន​សម្រេចចិត្ត​ចូល​រោងការ​តាម​បវេណី​ជាមួយ​អ្នក​នាង ម៉ៅ ចំណាន ដែល​មានឈ្មោះ​ល្បី​ក្នុង​ពិភព​សិល្បៈ​ថា ស្រី​តូច ចំណាន ', 'http://kohsantepheapdaily.com.kh/article/116984.html', 'http://i.kspg.co/2015/3/11ff3ff2-4071-47ee-a0b5-750c548cffcf.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1231', 'cl000001', 'u0000003', 'លទ្ធផល​សន្និសីទ​ពិភពលោក​លើក​ទី​3 ស្តី​ពី​ការ​កាត់​បន្ថយ​គ្រោះ​មហន្តរាយ', 'ទីក្រុង​សេន​ដាយ​, ប្រទេស​ជប៉ុន ៖ លទ្ធផល​នៃ​សន្និសីទ​ពិភពលោក​លើក​ទី​៣ ដែល​បាន​ប្រព្រឹត្ត​ទៅ​រយៈពេល​៤​ថ្ងៃ ត្រូវ​បាន​ប្រកាស​កាលពី​ល្ងាច​ថ្ងៃ​ទី​១៨ មីនា ដោយ​បាន​អនុម័ត​នូវ​ភារកិច្ច​ចម្បង​២​គឺ ១-​សេចក្តីថ្លែងការណ៍​នៃ​ទីក្រុង​សេន​ដាយ និង​២-​ក្របខ័ណ្ឌ​ការងារ​', 'http://kohsantepheapdaily.com.kh/article/116983.html', 'http://i.kspg.co/2015/3/13c28054-a5a4-4cbd-9aab-c0a6524192d1.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1232', 'cl000001', 'u0000003', 'ឆេះផ្ទះឈើប្រក់ស័ង្កសី​មួយកន្លែង​នៅសង្កាត់ដំរី​ជាន់ខ្លា​', 'ខេត្តកំពង់ធំ ៖ កាលពី​វេលា​ម៉ោង​០៥ និង៤០​នាទី​​ល្ងាច​​ថ្ងៃ​ទី២០ ​មីនា ​២០១៥ មាន​​​ករណី​ភ្លើង​ឆេះ​មួយ​កន្លែង​ ស្ថិត​​​ក្នុង​ភូមិប​ល្ល័​ង្ល​លិច សង្កាត់​ដំរី​ជាន់​ខ្លា ក្រុង​ស្ទឹង​សែន ខេត្តកំពង់ធំ ដែលផ្ទះ​ធ្វើ​អំពី​ឈើប្រក់ស័ង្កសី​មួយខ្នង​ត្រូវបានភ្លើងឆេះ បន្ទាប់ពី​ផ្ទុះហ្គាស​​ខណៈ​កំពុង​', 'http://kohsantepheapdaily.com.kh/article/116982.html', 'http://i.kspg.co/2015/3/d62a3b3d-8d89-441e-b3d3-d8d85d1d6ee5.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1233', 'cl000001', 'u0000003', 'ឈាមស្រែកស្បែកហៅ ឃាតកម្ម​​សម្លាប់​ជីដូន​ និង​ចៅប្រុស ត្រូវបានឃាត់ខ្លួនប្រពន្ធ​របស់ឃាតករ​', 'ខេត្តត្បូងឃ្មុំ ៖ ​ក្រោម​ការ​​ស្រាវជ្រាវ​របស់​សមត្ថ​កិច្ច​ពី​ថ្នាក់​ខេត្ត​ដល់​មូលដ្ឋាន​នោះ នៅទីបំផុត​​ជន​សមគំនិត​​ក្នុងអំពើ​សាហាវ​ឃោរឃៅ​ដែល​បាន​​ចាប់​ជីដូន​វាយ​រហូត​ស្លាប់ រួច​អូស​សព​ទម្លាក់​ចូល​រណ្តៅ​ទឹក​ក្រោយ​ផ្ទះ ថែម​ទាំង​យក​ខ្សែ​មក​ចង.ក ដើម្បី​បំភាន់​ចង.ក​ធ្វើ​អត្ត​', 'http://kohsantepheapdaily.com.kh/article/116981.html', 'http://i.kspg.co/2015/3/563a527e-c919-446f-af63-5b3cae116733.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1234', 'cl000001', 'u0000003', 'ស្ពាន​មិត្តភាព​កម្ពុជា​-​ចិន នៅ​ខេត្តស្ទឹងត្រែង​សម្ពោធ ថ្ងៃ​ទី១មេ​សា​ មុន​បុណ្យចូលឆ្នាំ', 'ខេត្តស្ទឹងត្រែង ៖ សម្តេច​អគ្គមហាសេនាបតីតេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជា​ណា​កម្ពុជា និង​សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត ប៊ុ​ន រ៉ា​នី ហ៊ុន សែន នឹង​អញ្ជើញ​ជា​អធិបតីភាព ​ក្នុង​ពិធី​សម្ពោធ', 'http://kohsantepheapdaily.com.kh/article/116980.html', 'http://i.kspg.co/2015/3/313bcb80-94e7-4185-8107-7f95b7f8619b.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1235', 'cl000001', 'u0000003', 'ក្រុមហ៊ុនប៉ៃលិន​កំពុងបង្ហូរទឹកសម្អុយមានជាតិពុល​​ចូលអូរ​ ​​ប៉ះពាល់​បរិស្ថាន​យ៉ាង​ខ្លាំង', 'ខេត្ត​​​ប៉ៃ​លិន ៖ បន្ទាប់​​ពី​មាន​​សេចក្តីរាយការណ៍​​ពី​ប្រជាពលរ​ដ្ឋ​ថា មាន​ស្តង់​ដំឡូង​មួយ​​ចំនួន​ក្នុង​ចំណោម​ ស្តង់​ដំឡូង​មី​ជាង​​១០​កន្លែង ដែល​ស្ថិត​នៅ​តាម​​បណ្តោយ​ផ្លូវជាតិ​លេខ​​៥៧ ​ក្នុងភូមិ​ប៉ា​​ង​រលឹម និង​ភូមិ​​សួស្តី សង្កាត់​​អូរ​តា​វ៉ៅ ក្រុងប៉ៃលិន ត្រូវ​បាន​ម្ចាស់​​បង្ហូរ​សារ​', 'http://kohsantepheapdaily.com.kh/article/116979.html', 'http://i.kspg.co/2015/3/a3a98dee-1a3d-42a5-aa24-88f84228deed.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1236', 'cl000001', 'u0000003', 'កម្លាំង​សន្តិសុខ​ជាង​2000​នាក់​ការពារ លោក​ជំទាវ មី​សែ​ល អូ​បា​ម៉ា', 'ខេត្តសៀមរាប ៖ កងកម្លាំង​ចម្រុះ​រួម​មាន​ប៉ូលិស​, ប៉េ​អឹ​ម​, កម្លាំង​អង្គរក្ស​របស់​ប្រមុខ​រាជរដ្ឋាភិបាល និង​កម្លាំង​អង្គរក្ស​របស់​លោក​ជំទាវ​មី​សែ​ល អូ​បា​ម៉ា (Michelle OBAMA) កំពុង​ត្រូវ​បាន​ដាក់​ពង្រាយ​មាន​ចំនួន​ជាង​២.០០០​នាក់ ', 'http://kohsantepheapdaily.com.kh/article/116978.html', 'http://i.kspg.co/2015/3/62cc08be-8d40-4891-be04-7c7ff696bc17.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1237', 'cl000001', 'u0000003', 'លទ្ធផល​បឋម​ក្នុង​ការ​ដោះស្រាយ​បញ្ចប់​បញ្ហា​ជាមួយ​សហគមន៍​បុរី​កីឡា', 'រាជធានី​ភ្នំពេញ ៖ ដូច​អ្វី​ដែល​បាន​គ្រោងទុក នៅ​ព្រឹក​ថ្ងៃ​ទី​២០ មីនា អាជ្ញាធរ​រាជធានី​ភ្នំពេញ​បាន​ធ្វើ​សន្និសីទ​សារព័ត៌មាន​មួយ​ដើម្បី​បង្ហាញ​សាធារណ​មតិ​ជាតិ និង​អន្តរជាតិ​អំពី​ស្ថានការណ៍​វិវឌ្ឍន៍​ចុង​ក្រោយ​នៃ​', 'http://kohsantepheapdaily.com.kh/article/116977.html', 'http://i.kspg.co/2015/3/28318cfa-90eb-40a9-abae-4c638fe1dbc0.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1238', 'cl000001', 'u0000003', 'យន្តហោះ​របស់​ភរិយា​ប្រធានាធិបតី​សហរដ្ឋអាមេរិក លោកស្រី​Michelle Obama​បាន​ចុះ​ចត​ដោយ​សុវត្ថិភាព​នៅ​អាកាសយានដ្ឋាន​អន្តរជាតិ​ខេត្តសៀមរាប', 'ខេត្តសៀមរាប ៖ នៅ​វេលា​ម៉ោង​៨​និង​៣០​នាទី​ម៉ោង​ក្នុង​ប្រទេស​កម្ពុជា យប់​ថ្�ៃ​ទី​២០ ខែមីនា  ឆ្នាំ​២០១៥​នេះ​ក្នុង​អាកាសយានដ្ឋាន​អន្តរជាតិ​ខេត្តសៀមរាប លោកស្រី Michelle Obama បាន  ធ្វើ​ដំណើរ​មក​ដល់​ខេត្តសៀមរាប​ទឹកដី​អង្គរ​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា​ដោយ​សុវត្ថិភាព​ដើម្បី​មក​បំពេញ​ទស្សនកិច្ច​នៅ​កម្ពុជា​រយៈពេល​៣​ថ្ងៃ', 'http://kohsantepheapdaily.com.kh/article/116971.html', 'http://i.kspg.co/2015/3/1750c127-3d76-46af-bf7b-1da214ad7bb2.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1239', 'cl000001', 'u0000003', 'ទិដ្ឋភាពខាង​ក្រៅ​បរិវេណ​អគារ​ទទួល​ទាន​ភោជនីយ​អាហារ​ក្នុង​ពិធី​មង្គល​ការ​សេដ្ឋី​ក្មេង គិត ម៉េង នៅ​កោះ​ពេជ្រ', 'ទិដ្ឋភាពខាង​ក្រៅ​បរិវេណ​អគារ​ទទួល​ទាន​ភោជនីយ​អាហារ​ក្នុង​ពិធី​មង្គល​ការ​សេដ្ឋី​ក្មេង គិត ម៉េង នៅ​កោះ​ពេជ្រ', 'http://kohsantepheapdaily.com.kh/article/116969.html', 'http://i.kspg.co/2015/3/d830fa3f-7506-413e-bc7f-24318e9458fa.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1240', 'cl000001', 'u0000003', 'អភិបាលក្រុង​ព្រះវិហារ​បំភ្លឺ​ជុំវិញ​បណ្តឹង​តវ៉ាដី​ក្នុង​សាលា​គរុកោសល្យ​ និង​សាលា​បឋម​សិក្សា​អនុវត្តន៍', 'អភិបាលក្រុង​ព្រះវិហារ​បំភ្លឺ​ជុំវិញ​បណ្តឹង​តវ៉ាដី​ក្នុង​សាលា​គរុកោសល្យ​ និង​សាលា​បឋម​សិក្សា​អនុវត្តន៍', 'http://kohsantepheapdaily.com.kh/article/116967.html', 'http://i.kspg.co/2015/3/6f034be2-dc06-4d93-931b-ca5d8c005d02.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1241', 'cl000001', 'u0000003', 'កម្លាំងចម្រុះ​ចុះ​​ត្រួតពិនិត្យគុណភាព នៅទីតាំង​សិប្បកម្ម​ផលិត​ទឹកម្ទេស ប្រហិត និង​ទឹកស៊ុប​', 'រាជធានី​ភ្នំពេញ ៖ នគរបាល​នាយកដ្ឋាន​ប្រឆាំង​បទល្មើស​សេដ្ឋកិច្ច​ក្រសួងមហាផ្ទៃ និង​មន្ត្រី​នាយកដ្ឋាន​កាំកុងត្រូល នៃ​ក្រសួងពាណិជ្ជកម្ម កាលពី​ព្រឹក​ថ្ងៃ​ទី​២០ មីនា បាន​សហការ​គ្នា​ត្រួតពិនិត្យ​សិប្បកម្ម​', 'http://kohsantepheapdaily.com.kh/article/116965.html', 'http://i.kspg.co/2015/3/d3354515-e0b1-43c7-ac2e-f8f837ab2792.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1242', 'cl000001', 'u0000003', '​ក្រុមហ៊ុន​ចិន​មួយរងការចោទ​​ថា ​​​​កំពុង​បង្ហូរទឹក​មាន​​ជាតិ​គីមី​ចូល​ក្នុង​អូរ​​ធម្មជាតិ', 'ខេត្តបាត់​ដំបង ​៖ បើតាម​សេចក្ដី​រាយ​ការ​ណ៍​ពី​ប្រជា​ពលរ​ដ្ធ​​ឱ្យ​ដឹង​ថា ក្រុមហ៊ុន​ផលិត​ម្សៅដំឡូង​មី​​របស់​ក្រុមហ៊ុន​ចិន​មួយ​​នៅ​ក្នុងភូមិ​តា​គ្រក់ ឃុំ​ត្រែង ស្រុក​រតន​មណ្ឌល បាន​កំពុងបង្ហូរទឹក​សំអុយ​​ដែល​មាន​ជាតិ​គីមី​ចូល​ទៅ​ក្នុង​អូរ​​ធម្មជាតិ​មួយ​​នៅ​ក្នុងភូមិ​ (​អូរ​ជ្រនៀង​)​', 'http://kohsantepheapdaily.com.kh/article/116964.html', 'http://i.kspg.co/2015/3/6b3abeee-e10e-4198-a21a-b479511c7e80.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1243', 'cl000001', 'u0000003', 'កម្លាំង​កង​រាជអាវុធហត្ថ​រាជធានីភ្នំពេញ​បញ្ជូនបុរសជំរិត​ទារប្រាក់៨ម៉ឺនដុល្លារ​​ទៅតុលា​ការ​', 'រាជធានីភ្នំពេញ ៖ កម្លាំងសមត្ថ​កិច្ច​កង​រាជធានី​ភ្នំពេញ កាលពី​វេលាព្រឹកថ្ងៃ​ទី​២០ មីនា ២០១៥ បាន​ចាត់​​ប​ញ្ជួ​ន​តៃកុង​​រថយន្ត​ទេស​ចរណ៍​​ពាក់​ព័ន្ធ​ករណី​ជម្រិត​ទារប្រាក់​៨​ម៉ឺន​ដុល្លារ ជា​ថ្នូរនឹង​រូបភាព​វី​ដេ​អូ​រួមរ័ក​ជាមួយ​ជន​រង​គ្រោះ ទៅ​កាន់​សាលាដំបូង​រាជធានី​ភ្នំពេញ', 'http://kohsantepheapdaily.com.kh/article/116963.html', 'http://i.kspg.co/2015/3/03ec65d1-b218-4e87-9cc7-1391b642eb2e.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1244', 'cl000001', 'u0000003', 'ប្រជា​ពល​រដ្ឋ​រអ៊ូរទាំ​រឿង​កង្វះ​ទឹក​ស្អាត​ប្រើប្រាស់​ប្រចាំ​ថ្ងៃ ​ខណៈ​ស្ទឹង​ត្រែង​មាន​ទន្លេ​ដល់​ទៅ​៤', 'ខេត្តស្ទឹងត្រែង​ ៖ ទឹក​ស្អាត​ជា​ប្រភព​ធនធាន​ដ៍​មួយ​សំខាន់​មួយ​សម្រាប់​ផ្គត់​ផ្គង ​ក្នុង​ការ​រស់​របស់​ប្រជា​ពលរ​ដ្ឋ​ជា​ទូទៅ​នា​ពេល​បច្ចុប្បន្ននេះ តែ​ខេត្តស្ទឹងត្រែង​ត្រូវ​បាន​រង​ការ​រិះគន់​យ៉ាង​ពេញទំហឹង​', 'http://kohsantepheapdaily.com.kh/article/116962.html', 'http://i.kspg.co/2015/3/7543cfc1-f026-42a5-b25c-37ed11f39777.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1245', 'cl000001', 'u0000003', 'លទ្ធផល​សន្និសីទ​ពិ​ភ​ពលោក​លើក​ទី​៣ ​អង្គការសហប្រជាជាតិ​ស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ', 'ទីក្រុង​សេន​ដាយ​, ប្រទេស​ជប៉ុន ៖ លទ្ធផល​នៃ​សន្និសីទ​ពិភពលោក​លើក​ទី​៣ ​ដែល​បាន​ប្រព្រឹត្ត​ទៅ​រយៈពេល​៤​ថ្ងៃ ត្រូវ​បាន​ប្រកាស​កាលពី​ល្ងាច​ថ្ងៃ​ទី​១៨ មីនា ដោយ​បាន​អនុម័ត​នូវ​ភារកិច្ច​ចម្បង​២​ គឺ ', 'http://kohsantepheapdaily.com.kh/article/116960.html', 'http://i.kspg.co/2015/3/40c55636-e7ac-43f3-84bb-5aa7fc291410.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1246', 'cl000001', 'u0000003', 'សាណូ​​ហ្វី​ធ្វើ​សិក្ខាសាលា​បង្ហាញ​​ពី​ជំងឺឆ្លង​របស់​​កុមារ និងការ​គ្រប់គ្រង​អាកា​រៈ​​គ្រុន​ក្តៅ', '​រាជធានី​ភ្នំពេញ​៖​ ដើម្បី​ទប់ស្កាត់​កុំ​មាន​ជំងឺឆ្លងកុមារ និង​គ្រប់គ្រង​អាកា​រៈ​គ្រុនក្តៅ ឲ្យ​មាន​ប្រសិទ្ធភាព​ពី​សំណាក​អាណាព្យាបាល​នោះ​ក្រុមហ៊ុន​សាណូ​ហ្វី បាន​ធ្វើ​សិក្ខាសាលា​ស្តី​អំពី « ជំងឺឆ្លង​កុមារ និង​ការ​គ្រប់គ្រង​អាកា​រៈ​គ្រុនក្តៅ » កាលពីវេលា​ព្រឹក​ថ្ងៃ​ទី​២០ មីនា​ ', 'http://kohsantepheapdaily.com.kh/article/116958.html', 'http://i.kspg.co/2015/3/a6152aa2-65b7-4e49-89df-2a0e4210aaa1.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1247', 'cl000001', 'u0000003', 'សំណុំរឿង ​«​បឹង​តា​អឿ​»​ អាជ្ញាធរ​ខេត្ត​ឯកភាព​ឱ្យសហ​គម​ន៍​ជីក​ស្ដារ​ឡើង​វិញ​ តែ​ត្រូវអភិបាលរង​ស្រុក​​​រារាំង​', 'ខេត្តបាត់ដំបង​៖ សំណុំរឿង​ដី​ «​បឹង​តា​អឿ​»​ ជាប់​ផ្លូវជាតិ​លេខ​៥ ស្ថិត​ក្នុង​ភួ​មិ​ក្បាលថ្នល់ ឃុំ​កំពង់​ព្រៀង ស្រុក​សង្កែ ដែល​អ្នក​ភូមិ និង​អាជ្ញាធរ​គ្រប់​ជាន់ថ្នាក់​បាន​ធ្វើការ​វិនិច្ឆ័យ​ថា ជា​ «​បឹង​ធម្មជាតិ ​ឬ​បឹង​សាធារណៈ​» ​នោះ ពេល​នេះ​បាន​ផ្ទុះ​ឡើងជា​ថ្មី ដោយសារ​តែ​មាន​', 'http://kohsantepheapdaily.com.kh/article/116957.html', 'http://i.kspg.co/2015/3/780a6caf-4ac7-48eb-b063-51c09d9fc7e6.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1248', 'cl000001', 'u0000003', 'តើ​សេដ្ឋី​ក្មេងគិត ម៉េ​ង មាន​ក្រុមហ៊ុន និង​ភាគហ៊ុន​នៅ​ក្នុង​ក្រុមហ៊ុន​ណាខ្លះ?', 'អ្នកឧកញ៉ា គិត ម៉េ​ង មហាសេដ្ឋី​លំដាប់​កំពូល​ម្នាក់​របស់​កម្ពុជា ដែលទើបតែ​សម្រេចចិត្ត​រៀបការ​ជាមួយអ្នកនាងស្រីតូច ចំណាន នៅ​ថ្ងៃ​ទី​២០ ខែមីនា ឆ្នាំ​២០១៥ នេះ ។ តើសេដ្ឋី​ក្មេងគិត ម៉េ​ង ដែល​កំពុង', 'http://kohsantepheapdaily.com.kh/article/116948.html', 'http://i.kspg.co/2015/3/0436a95e-9903-4d07-88ed-acc1e8db8e9a.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1249', 'cl000001', 'u0000003', 'បង្គោល​ភ្លើង​ទុ​ស្សេ​ឆេះ​២​ដើម​ត្រូវ​បាន​រលត់​ហើយ', 'រាជធានី​ភ្នំពេញ​៖​បង្គោល​ភ្លើង​ចំនួន​២​ដើម​ជាប់​គ្នា​ដែល​ត្រូវ​បាន​អគ្គី​ភ័យ​ឆាបឆេះ​នៅ​ពេល​នេះ​បាន​រលត់​ទៅ​វិញ​ហើយ ខណៈ​រថយន្ត​ពន្លត់អគ្គីភ័យ​ចំនួន​១​គ្រឿង​បាន​មក​ធ្វើការ​អន្តរាគមន៍​ជួយ​បាញ់​ពន្លត់​ទាន់​ពេលវេលា ។​អគ្គី​ភ័យ​ខាងលើ​ត្រូវ​បាន​រលត់​ជា​ស្ថាពរ​', 'http://kohsantepheapdaily.com.kh/article/116993.html', 'http://i.kspg.co/2015/3/1475395a-0047-467f-8c9c-b0cd6a0c30ce.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1250', 'cl000001', 'u0000003', 'បង្គោល​ភ្លើង​២​ដើម​បាន​ទុ​ស្សេ​ឆេះ​ដាច់​ធ្លាក់​ខ្សែភ្លើង​មក​លើ​ថ្នល់​បង្ក​ការ​ភ្ញាក់ផ្អើល', 'រាជធានី​ភ្នំពេញ ​៖ ​បង្គោល​ភ្លើង​២​ដើម​បាន​កំពុង​ឆេះ​បន្ត​រាលដាល​ដល់​ខ្សែភ្លើង បណ្តាល​ឲ្យ​ដាច់​ធ្លាប់​មក​លើ​ផ្លូវ បង្ក​ការ​ភ្ញាក់​ផ្អើ​ក​និង​ធ្វើ​ឲ្យ​ប្រជាពលរដ្ឋ​នៅ​ក្បែរ​នោះ​មានការ​ភ័យ​ខ្លាច​ជា​ខ្លាំង ខណៈ​ភ្លើង​ឆេះ​កាន់តែ​ខ្លាំង​ទៅ​ៗ ', 'http://kohsantepheapdaily.com.kh/article/116991.html', 'http://i.kspg.co/2015/3/1c9d2ea6-8e63-407c-a8a9-ac1e7761e515.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1251', 'cl000001', 'u0000003', 'ជនសង្ស័យ​ជួញដូរ​គ្រឿងញៀន​​៣​នាក់ត្រូវសមត្ថកិច្ច​​ចាប់​​បាន', 'ខេត្តបន្ទាយ​មានជ័យ ៖ អនុវត្ត​តាម​បទបញ្ជា​យ៉ាង​ម៉ឺងម៉ាត់​របស់​លោក​ឧត្ត​សេនីយ៍​ត្រី​អាត់ ខែ​ម ស្នងការ​នគរបាល​ខេត្តបន្ទាយមានជ័យ កម្លាំង​នគរ​បាល​ការិយាល័យ​ប្រឆាំង​គ្រឿងញៀន ', 'http://kohsantepheapdaily.com.kh/article/116990.html', 'http://i.kspg.co/2015/3/05391641-0930-4bde-9c96-541ad9574e28.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1252', 'cl000001', 'u0000003', 'បុរស​ម្នាក់​ត្រូវ​ជនសង្ស័យ​បាញ់​ធ្លាយ​ក្បាល​ពី​ម្ខាង​ទៅ​ម្ខាង', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​បាន​រង​របួស​ធ្ងន់ធ្ងរ ដោយ​កំពុង​តែ​តែ​ក្រុម​គ្រូពេទ្យ​ជួយ​សង្គ្រោះ ខណៈ​ជន​រង​គ្រោះ​ត្រូវ​ក្រុម​ងងឹត​ប្រើ​កាំភ្លើង​បាញ់​ទៅ​លើ​ជន​រង​គ្រោះ​តែ​ម្តង កាល​វេលា​ម៉ោង​៣​និង​១៥​នាទី​ទាប​ភ្លឺ ឈាន​ចូល​ថ្ងៃ​ទី​២១ ខែ​មិ​នា', 'http://kohsantepheapdaily.com.kh/article/116975.html', 'http://i.kspg.co/2015/3/8654040d-0959-408f-ad63-b96bac6736de.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1253', 'cl000001', 'u0000003', 'ឆក់​កាបូប​បាន​សម្រេច​ទៀត​ហើយ​នៅ​ខណ្ឌ​ឬ​ស្សី​កែវ', 'រាជធានី​ភ្នំពេញ​៖ ជន​រង​គ្រោះ​ម្នាក់​ដែល​ទើបបាន​ទទួល​លុយ​ពី​កូនចិញ្ចឹម​ធ្វើការ​នៅ​ប្រទេស​កូរ៉េ​និង​បាន​ដាក់​ក្នុង​កាបូប​ជិះ​ម៉ូតូ​កង់​បី​តាម​បណ្តោយ​ផ្លូវជាតិ​លេខ​៥​ស្រាប់តែ​អកុសល​ត្រូវ​បាន​ចោរ​ញី​ឈ្មោល​២​នាក់​ឆក់​ទៅ​បាត់​ដោយ​សុ​វត្ថ​ភាព​និង​មិន​រំខាន​ដល់​ប៉ូលិស​មូលដ្ឋាន​នោះ​ទេ', 'http://kohsantepheapdaily.com.kh/article/116974.html', 'http://i.kspg.co/2015/3/fe2c00e4-f4a3-46d1-868c-3244b5bfcfbe.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1254', 'cl000001', 'u0000003', 'ចោរ​ឆ្លងខេត្ត​មក​ពី​រតនៈគិរី​ឆក់​កាបូប​នៅ​ក្រុង​កំពង់ចាម​ជាប់អន្ទាក់​សមត្ថកិច្ច', 'ខេត្តកំពង់ចាម​៖ ស្រប​ពេល​ដែល​ស្នងការ​នគរបាល​ខេត្តកំពង់ចាម​បាន​រឹត​បន្តឹង​សន្តិសុខ​សុវត្ថិភាព​ជូន​ដល់​ប្រជាពលរដ្ឋ និង​បាន​បញ្ជារ​ឲ្យ​ប៉ូលិស​ថ្នាក់​ក្រោមដាក់​ប៉ុស្តិ៍​​យាម​ត្រួតពិនិត្យ​បទល្មើស​២៤​ម៉ោង​លើ​២៤„�៉ោង ប្រយោជន៍​ទប់ស្កាត់​អំពើ​លួច ឆក់ ប្លន់  រួម​ទាំង​បទល្មើស​ផ្សេង​ៗ', 'http://kohsantepheapdaily.com.kh/article/116973.html', 'http://i.kspg.co/2015/3/b2aac15e-cc34-4d29-95c2-364ac53a2a60.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1255', 'cl000001', 'u0000003', 'អាងខ្នង​មន្ត្រី​ប៉ូលិស​ជេ​និង​វាយ​ប៉ូលិស​ពេល​ចុះ​អន្តរាគមន៍', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​ក្នុង​ស្ថានភាព​ស្រវឹងស្រា ហើយ​បាន​ដើរ​ចេញពីផ្ទះ​មក​ហៅ​ម៉ូតូ​កង់​បី​ជិះ ក៏​ប៉ុន្តែ​អ្នក​បើក​ម៉ូតូ​កង់​បី ដោយ​ឃើញ​ស្ថានភាព​បែប​នេះ​មិន​ព្រម​ដឹក​ទេ ក៏​កើតជា​ជម្លោះ​រវៀង​អ្នក​បើក​ម៉ូតូ​កង់​បី​និង​បុរស​ស្រវឹង​រហូត​ឈាន​ទៅ​វាយតប់​គ្នា​ទៅ​វិញ​ទៅ​មក', 'http://kohsantepheapdaily.com.kh/article/116972.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1256', 'cl000001', 'u0000003', 'ព្រោះតែ​យក​ប៊ីយែរ​សល់​ពីលើ​តុ​សន្តិសុខ​ម៉ុង​ឌី​យ៉ា​ល់​ព្រួត​គ្នា​វាយ​រង​របួសធ្ងន់​ម្នាក់', 'រាជធានី​ភ្នំពេញ​៖ កង​សន្តិសុខ​យាម​នៅ​មជ្ឈមណ្ឌល់​ម៉ុង​ឌី​យ៉ា​ល់​ជា​ច្រើន​នាក់​បាន​បើក​ការ​វាយប្រហារ និង​ខ្លះ​បាន​ប្រើ​អាយ​ឌី​ផង​ដែរ​វាយ​ទៅ​លើ​ជន​រង​គ្រោះជា​បុគ្គលិក​ក្រុមហ៊ុន​វណ្ណៈ​ពិធីការ ដែល​ទ​ទួ​រៀបចំ​និង​មើល​ការ​ខុស​ត្រូវ​នៅ​ពិធី​មង្គលការ', 'http://kohsantepheapdaily.com.kh/article/116970.html', 'http://i.kspg.co/2015/3/707e14b1-bcfb-4f8b-b75e-e939d893c17f.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1257', 'cl000001', 'u0000003', 'ចាប់​​រំលោភ​បានសម្រេច២​ដងគេចខ្លួន​បាត់ ​នាងមកប្តឹងសមត្ថកិច្ច', 'ខេត្តកំពង់ស្ពឺ ៖ ជនសង្ស័យ​ម្នាក់​បាន​ចាប់រំលោភ​នារី​ជា​កម្ម​ការិនី​រោងចក្រ​ រហូត​បាន​សម្រេចចំនួន​២​លើក ​ហើយ​គេចខ្លួន​បាត់​ ធ្វើ​ឣោ​យ​នារី​រង​គ្រោះ​ស្វែងរក​ការ​ជួយ​ឣ​ន្ត​រាគ​មន៍ ​ពី​សមត្ថកិច្ច ​ក្នុងករណី​រំលោភ​សេពសន្ថវៈ​មក​លើ​រូប​នាង​នេះ​ ', 'http://kohsantepheapdaily.com.kh/article/116966.html', 'http://i.kspg.co/2015/3/f9e24bde-c69c-4215-812e-fee0c52543fd.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1258', 'cl000001', 'u0000003', 'ចាប់​បក្ខពួក​ក្រុម​ចោរ​ប្រដាប់​កាំបិត​បុ័ងតោ​ប្លន់​និង​រំលោភ​បាន​ម្នាក់​ទៀត', 'ខេត្តស្ទឹងត្រែង​៖ ជន​ប្រដាប់​កាំបិត​បុ័ងតោ​ម្នាក់​ទៀត​ត្រូវ​ចាប់ខ្លួន​បន្ទាប់​ពី​បក្ខពួក​របស់​ខ្លួន​ម្នាក់​បញ្ជូន​ចូល​គុក​ជា​លើ​ទី​៤​នោះ ។ ការ​ចាប់ខ្លួន​ជនសង្ស័យ​ជើង​ខ្លាំង​ខាង​ប្រើ​កាំបិត​ផ្ជ​ង់​អារ ក​ជន​រង​គ្រោះ​ប្លន់​យក​ម៉ូតូ ទូរស័ព្ទ មាសប្រាក់​និង​រំលោភ​យ៉ាង​សកម្ម​ខាងលើ​នេះ​កម្លាំង​នគរបាល​បាន​ធ្វើ​ឡើង​នៅ​ចំណុច​អូរ​ពង់​មាន់', 'http://kohsantepheapdaily.com.kh/article/116912.html', 'http://i.kspg.co/2015/3/bf9d3e23-1e41-4bb3-82cc-c0a691152844.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1259', 'cl000001', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី២០ មីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី២០ មីនា ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​', 'http://kohsantepheapdaily.com.kh/article/116937.html', 'http://i.kspg.co/2015/3/7e71c496-b024-47fe-962f-682e21fa1201.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1260', 'cl000001', 'u0000003', 'សារ​ជូន​លោក​កឹ​ម សុខា ការ​ផ្លាស់​ប្តូរ​គឺជា​ការ​ផ្តួលរំលំ ទុក​រឿង​នេះ​ឱ្យ​តុលាការ​វិនិច្ឆ័យ', 'រាជធានី​ភ្នំពេញ ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា បាន​មាន​ប្រសា​ស​ន៏​បកស្រាយ ករណី​ដែល​លោក​កឹ​ម សុខា បាន​បំផុស​នៅ​សហរដ្ឋអាមេរិក និង​ដើម្បី​កុំអោយ', 'http://kohsantepheapdaily.com.kh/article/116935.html', 'http://i.kspg.co/2015/3/35a99c25-8f97-4ab0-99ad-75fd7e8b7740.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1261', 'cl000001', 'u0000003', 'ជិះម៉ូតូវ៉ាគ្នា​ទាក់​ដៃចង្កូត​ដួល​ រថយន្តមកពីក្រោយ​កិន​ស្លាប់ម្នាក់ របួសធ្ងន់-ស្រាល២នាក់', 'ខេត្តបន្ទាយ​មាន​ជ័យ ៖ ​​​ហេតុការណ៍​​នេះ​កើត​ឡើង​កាលពី​​វេលា​ម៉ោង០​៧ ​និង​២០​នាទី​យប់​​ថ្ងៃ​ទី​២០ មីនា​​ ២០១៥ ស្ថិតនៅ​លើ​កំណាត់​ផ្លូវជាតិ​លេខ​៥៦A ​​ក្នុងភូមិ​ខ្លែ​ពណ៌​ជើង ឃុំ​ស្ល​ក្រាម ស្រុក​ស្វាយចេក ខេត្តបន្ទាយមានជ័យ​។', 'http://kohsantepheapdaily.com.kh/article/116992.html', 'http://i.kspg.co/2015/3/b1c78e37-346c-41bf-8d4d-a474e701015b.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1262', 'cl000001', 'u0000003', 'ម៉ូតូ​បុក​ម៉ូតូ​កង់​បី​ដួល​ត្រូវ​រថយន្ត​កិន​បន្ថែម​​របួសធ្ងន់​៣​ស្រាល​១', 'រាជធានីភ្នំពេញ ៖ យ៉ាង​ហោច​ណាស់​មាន​អ្នក​រង​របួសធ្ងន់​៣​នាក់​ និង​ស្រាល​ម្នាក់ ខណៈ​ពេល​ម៉ូតូ​បើកបរ​មិន​ប្រកាន់​ស្តាំ​រេ​ចង្កូត​ទៅ​ខាងឆ្វេង​បាន​បុក​គ្នា​ជាមួយ​ម៉ូតូ​កង់​បី​ដឹក​ស្រា​ដួល​នៅកណ្តាល​ថ្នល់​ ហើយ​ត្រូវ​រថយន្ត​បើក​មក​ដល់​កិន​បន្ថែម​ទៀត ។', 'http://kohsantepheapdaily.com.kh/article/116923.html', 'http://i.kspg.co/2015/3/aba4531a-b9c2-40d9-bcba-0a48dc81c739.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1263', 'cl000001', 'u0000003', 'ស្រវឹង​ជិះ​ម៉ូតូ​បុក​រថយន្តក្រុង​ចត​ស្ងៀម​របួសធ្ងន់', 'រាជធានី​ភ្នំពេញ ៖ បុរស​ម្នាក់​ទំនង​ស្រវឹង​បាន​ជិះ​ម៉ូតូ​ទៅ បុក​រថយន្តក្រុង​ចត​ស្ងៀម​លើ​ចិញ្ចើម​ផ្លូវ បណ្តាល​ឲ្យ​រង​របួស​ធ្ងន់ធ្ងរ​ត្រូវ​បាន​រថយន្ត​សា​មុ​យ ដឹក​យក​ទៅ​សង្គ្រោះ​ភ្លាម​ៗ កាលពី​វេលា​ម៉ោង​២​និង​៣០​នាទី​ទាប​ភ្លឺ ឈាន​ចូល​ថ្ងៃ​ទី​២០ ខែ​មិ​នា', 'http://kohsantepheapdaily.com.kh/article/116914.html', 'http://i.kspg.co/2015/3/68d28edd-125a-4578-997d-3e7571a01216.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1264', 'cl000001', 'u0000003', '​កាម​រី​បា​ឡែ​ន​បើក​បុក​ម៉ូតូ​ចំ​ពីមុខ​ពេញទំហឹង​របួសធ្ងន់​២​នាក់​រថយន្ត​បង្ក​បើក​គេចខ្លួនបាត់', 'រាជធានី​ភ្នំពេញ ៖ រថយន្ត​កាម​រី​បា​ឡែ​ន​ពណ៌​ស​១​គ្រឿង​មិន​ស្គាល់​ស្លាក​លេខ​បាន​បើក​ទៅ​បុក​ម៉ូតូ​ស៊ុ​យ​សូ​គី​និ​ច​១​គ្រឿង​ទៀត ចំ​ពីមុខ​ពេញទំហឹង​បណ្តាល​ឱ្យ​អ្នក​ជិះ​ម៉ូ​ផ្លោង​ខ្ទាត​ចេញពី​ម៉ូតូ​រង​របួស​ធ្ងន់ធ្ងរ ។ ចំណែកឯ​ម៉ូតូ​ខ្ទេច​មួយ​កំណាត់​មុខ ', 'http://kohsantepheapdaily.com.kh/article/116913.html', 'http://i.kspg.co/2015/3/7f7d1532-1635-4a8c-8c4b-7b89d7e81c2f.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1265', 'cl000001', 'u0000003', 'រថយន្ត​ដឹក​ខ្សាច់​បើក​គេច​រថយន្ត​១​គ្រឿង​ទៀត​ជ្រុល​បុក​រនាំង​ដែក​បាក់​អស់​១១​ផ្ទាំង', 'រាជធានី​ភ្នំពេញ​៖ យ៉ាង​ហោច​ណាស់​រនាំង​ពុះ​ចែក​ទ្រូងផ្លូវ​ចំនួន​១១​ផ្ទាំង​បាន​បាក់​រង​ការ​ខូចខាត និង​ក្បាល​ក្លូ​១​ថែម​ទៀត​ផង ខណៈ​បុរស​ម្នាក់​បាន​បើករថយន្ត​ដឹក​ខ្សាច់ ម៉ាក់​ហ៊ី​ណូ ក្នុង​ល្បឿន​លឿន ហើយ​បាន​បួង​ចង្កូត​គេច​រថយន្ត​១​គ្រឿង​ទៀត​ដែល​ជាន់​ហ្វ្រាំង​បង្អង់​ល្បឿន​ភ្លាម​ៗ', 'http://kohsantepheapdaily.com.kh/article/116910.html', 'http://i.kspg.co/2015/3/ab02aeb1-f311-42c2-b91e-6e03b5db4ed0.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1266', 'cl000001', 'u0000003', 'ទប់​បាន​ខ្លះ ជួយ​សង្គម', 'រឿង​អ្នក​ពុក​រលួយ មិន​ពុក​រលួយ មិនមែន​អ្នកកាសែត​មិនដឹងខ្យល់​នោះទេ ។ ដឹង​ដែរ​...​ដឹង​ណាស់ ! ប៉ុន្តែ​កាសែត​ខ្លះ​អត់​ហ៊ាន​បកអាក្រាត​ឱ្យ​ចំ​ៗ​មុខ​បាន​ទេ​...​ បាន​ត្រឹមតែ​ប្រហើរ​លេង​ៗ​ប៉ុណ្ណោះ​... ​កុំឱ្យ​គេ​ប្រព្រឹត្ត​ដូច​ខ្លែង​ដាច់​ខ្សែ​ពេក​...', 'http://kohsantepheapdaily.com.kh/article/116976.html', 'http://i.kspg.co/2015/3/9e2eab2c-f1c5-4a64-8c1d-070bd477c702.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1267', 'cl000001', 'u0000003', 'ចិត្តមិនដាច់?', 'តាម​ការ​កត់សម្គាល់​ច្រើន​ឆ្នាំ​កន្លង​មក មាន​មន្ត្រី​ជាន់​កណ្តាល មន្ត្រីជាន់ខ្ពស់​ច្រើន​នាក់​ហើយ ត្រូវ​ «​ដក​ចេញ​» យក​កៅ​អៅ​ឲ្យ​អ្នក​ផ្សេង ទោះបី​ត្រូវ​«​ដំឡើង​» ក៏​ម្ចាស់​កៅអី​មិន​សប្បាយចិត្ត​ដែរ', 'http://kohsantepheapdaily.com.kh/article/116933.html', 'http://i.kspg.co/2015/3/dde39bd8-e832-47dc-b41e-c03ed547acfc.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1268', 'cl000001', 'u0000003', 'អា​មេ​រិ​ក​គំរាម​ពិនិត្យ​ឡើង​វិញ​ជំហរ​របស់​ខ្លួន​ចំពោះ​អីុ​ស្រា​អែ​ល', 'សហរដ្ឋអាមេរិក​ព្រមាន​កាលពី​ថ្ងៃ​ទី​១៩ មីនា​ថា នឹង​ធ្វើការ​វាយតម្លៃ​ឡើង​វិញ​លើ​ជំហរ​របស់​ខ្លួន​ចំពោះ​​អីុ​ស្រា​អែ​ល​នៅ អ​.​ស​.​ប ដែល​នៅ​ទីនោះ​ជា​ប្រពៃណី​ខ្លួន​តែង​គាំទ្រ​អីុ​ស្រា​អែ​លឥត​ងាករេ', 'http://kohsantepheapdaily.com.kh/article/116968.html', 'http://i.kspg.co/2015/3/3e8e6522-aae2-4073-988b-fa14a35a9972.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1269', 'cl000001', 'u0000003', 'មហាសេដ្ឋី​ប៊ី​ល​ហ្គេ​ត​យល់​ថា​ពិភពលោក​ត្រូវ​ត្រៀម​សម្រាប់​ការ​ឆ្លង​រាលដាល​ជំ​ងឹ​ថ្មី​អាក្រក់​ជាង​ជំងឺ​អេ​បូ​ឡា​ទៅ​ទៀត', 'មហាសេដ្ឋី​ធំ​ជាងគេ​លើ​ពិភពលោក លោក​ប៊ី​ល​ហ្គេ​ត ពន្យល់​កាលពី​ថ្ងៃ​ទី​១៩ មីនា ថា ពិភពលោក​ត្រូវ​រៀនសូត្រ​ពី​សោកនាដកម្ម​នៃ​ការ​ឆ្លង​រាលដាល​ជំងឺ​អេ​បូ​ឡា ហើយ​ត្រូវ​ត្រៀម​រៀបចំ​ខ្លួន​សម្រាប់​ទប់ទល់​', 'http://kohsantepheapdaily.com.kh/article/116959.html', 'http://i.kspg.co/2015/3/56fd233c-cfbe-4666-9239-c66df8306e32.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1270', 'cl000001', 'u0000003', 'យន្តហោះ​ប្រើ​ថាមពលព្រះអាទិត្យ​ចុះ​ចត​នៅ​ភូមា', 'យន្តហោះ​ប្រើ​ថាមពលព្រះអាទិត្យ SOLAR IMPULES-2 កាលពី​យប់​ថ្ងៃ​ទី​១៩ មីនា បាន​ទៅ​ចុះ​នៅ​ទីក្រុង​ម៉ា​ន់​ដា​ឡៃ ជាទី​ក្រុង​ធំ​ទី​២​របស់​ប្រទេស​នេះ ពេល​ឈាន​ចូល​ដំណាក់​ការ​ទី​៤​នៃ​ការ​ហោះហើរ​', 'http://kohsantepheapdaily.com.kh/article/116956.html', 'http://i.kspg.co/2015/3/1113922e-bd6a-4623-991c-7ade4fa26223.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1271', 'cl000001', 'u0000003', 'យេ​ម៉ែ​ន៖ យន្តហោះ​វាយប្រហារ​លើ​វិមាន​ប្រធានាធិបតី​នៅ​ក្រុង​អា​ដេ​ន', 'អ្នក​ទទួលខុសត្រូវ​រដ្ឋាភិបាល​ម្នាក់​គូសបញ្ជាក់​នៅ​ក្រុង​អា​ដេ​ន​ថា យន្តហោះ​ប្រយុទ្ធ​១​គ្រឿង​ដែល​ហោះ​ចេញពី​រដ្ឋធានី​យេ​ម៉ែ​ន​ក្រុង​សា​ណា បាន​វាយប្រហារ​លើ​វិមាន​ប្រធានាធិបតី​ជា​កន្លែង​', 'http://kohsantepheapdaily.com.kh/article/116952.html', 'http://i.kspg.co/2015/3/7d27d107-6e2d-4a82-b9f3-9d0d906627a3.jpg?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1272', 'cl000001', 'u0000003', 'ឧទ្ធម្ភាគចក្រ​មួយ​របស់​កូរ៉េ​ខាងត្បូង​ធ្លាក់​រក​ឃើញ​បំណែក', 'យោង​តាម​ទីភ្នាក់ងារ​ព័ត៌មាន​កូរ៉េ​ខាងត្បូង YONHAP ថា បំណែក​ឧ​ទ្ធា​ម្ភា​គ​ចក្រ​១​គ្រឿង​ដែល​ធ្លាក់​កាលពី​ស​ប្តា​ហ៍​មុន​ក្នុង​សមុទ្រ​ជិត​កោះ GAGEO ភាគ​និរតី​ប្រទេស ត្រូវ​បាន​រក​ឃើញ', 'http://kohsantepheapdaily.com.kh/article/116949.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150321');
INSERT INTO "tbnews" VALUES ('1273', 'cl000001', 'u0000003', 'សហភាព​អឺ​រ៉ុ​ប​អាច​បន្ត​ដាក់​ទណ្ឌកម្ម​រុ​ស្ស៊ី​ដល់​ដាច់​ឆ្នាំ​2015', 'សហភាព​អឺ​រ៉ុ​ប កាលពី​ថ្ងៃ​ទី​១៩ មីនា បាន​រក​ឃើញ​កិច្ចព្រមព្រៀង​ជា​គោលការណ៍​មួយ​ដើម្បី​ពន្យារពេល​ការ​ដាក់​ទណ្ឌកម្មសេដ្ឋកិច្ច​ប្រឆាំង​រុ​ស្ស៊ី រហូត​ដល់​ដំណាច់ឆ្នាំ​២០១៥​នេះ ដោយ​ផ្សារ​ភ្ជាប់​', 'http://kohsantepheapdaily.com.kh/article/116947.html', 'http://i.kspg.co/2015/3/11b550c1-c42b-4ac0-ac3e-e5f7734dc934.jpg?w=285&h=170&mode=carve', '20150321');
