-- ----------------------------
-- View structure for vw_show_international_news_category
-- ----------------------------
CREATE OR REPLACE VIEW "vw_show_international_news_category" AS 
 SELECT c.cat_name AS "ពតិ៍មានអន្ដរជាតិ"
   FROM (tbcategory c
   JOIN tbcategoryparent cp ON ((c.parent_id = cp.parent_id)))
  WHERE (cp.parent_id = 2);

-- ----------------------------
-- View structure for vw_show_local_news_category
-- ----------------------------
CREATE OR REPLACE VIEW "vw_show_local_news_category" AS 
 SELECT c.cat_name AS "ពតិ៍មានជាតិ"
   FROM (tbcategory c
   JOIN tbcategoryparent cp ON ((c.parent_id = cp.parent_id)))
  WHERE (cp.parent_id = 1)
  ORDER BY c.parent_id;

